/**
 * è®¡ç®—sitemapä¼˜å…ˆçº§
 */
export function calculatePriority(
  pageType: 'home' | 'category' | 'content' | 'profile' | 'other',
  popularity?: number
): number {
  const basePriorities = {
    home: 1.0,
    category: 0.8,
    content: 0.7,
    profile: 0.6,
    other: 0.5
  };

  let priority = basePriorities[pageType];

  // æ ¹æ®å—æ¬¢è¿ç¨‹åº¦è°ƒæ•´ä¼˜å…ˆçº§
  if (popularity && popularity > 0) {
    const popularityBoost = Math.min(popularity / 100, 0.2);
    priority = Math.min(priority + popularityBoost, 1.0);
  }

  return Math.round(priority * 10) / 10; // ä¿ç•™ä¸€ä½å°æ•°
}

import { Metadata } from 'next';
import { SEO_CONFIG, PAGE_SEO_CONFIG, DYNAMIC_SEO_TEMPLATES } from './config';

/**
 * SEOå·¥å…·å‡½æ•°
 */

export interface SEOData {
  title?: string;
  description?: string;
  keywords?: string[];
  path?: string;
  image?: string;
  type?: string;
  publishedTime?: string;
  modifiedTime?: string;
  author?: string;
  section?: string;
  tags?: string[];
}

/**
 * ç”Ÿæˆé¡µé¢å…ƒæ•°æ®
 */
export function generateMetadata(data: SEOData = {}): Metadata {
  const {
    title = SEO_CONFIG.DEFAULT_TITLE,
    description = SEO_CONFIG.DEFAULT_DESCRIPTION,
    keywords = SEO_CONFIG.DEFAULT_KEYWORDS,
    path = '/',
    image = SEO_CONFIG.OG_IMAGE,
    type = SEO_CONFIG.OG_TYPE,
    publishedTime,
    modifiedTime,
    author,
    section,
    tags = []
  } = data;

  const fullUrl = `${SEO_CONFIG.SITE_URL}${path}`;
  const fullImageUrl = image.startsWith('http') ? image : `${SEO_CONFIG.SITE_URL}${image}`;

  const allKeywords = [...new Set([...keywords, ...tags])];

  const metadata: Metadata = {
    title,
    description,
    keywords: allKeywords.join(', '),
    authors: author ? [{ name: author }] : [{ name: 'Inspi.AI Team' }],

    // Open Graph
    openGraph: {
      title,
      description,
      url: fullUrl,
      siteName: SEO_CONFIG.SITE_NAME,
      images: [
        {
          url: fullImageUrl,
          width: 1200,
          height: 630,
          alt: title,
        }
      ],
      locale: SEO_CONFIG.OG_LOCALE,
      type: type as any,
      ...(publishedTime && { publishedTime }),
      ...(modifiedTime && { modifiedTime }),
      ...(section && { section }),
      ...(tags.length > 0 && { tags })
    },

    // Twitter Card
    twitter: {
      card: SEO_CONFIG.TWITTER_CARD as any,
      site: SEO_CONFIG.TWITTER_SITE,
      title,
      description,
      images: [fullImageUrl],
    },

    // å…¶ä»–å…ƒæ•°æ®
    robots: {
      index: true,
      follow: true,
      googleBot: {
        index: true,
        follow: true,
        'max-video-preview': -1,
        'max-image-preview': 'large',
        'max-snippet': -1,
      },
    },

    // è§„èŒƒé“¾æ¥
    alternates: {
      canonical: fullUrl,
    },

    // å…¶ä»–
    category: section,
  };

  return metadata;
}

