/**
 * ç”Ÿæˆç»“æ„åŒ–æ•°æ® (JSON-LD)
 */
export function generateStructuredData(type: string, data: any): string {
  const baseStructure = {
    '@context': 'https://schema.org',
    '@type': type,
    ...data
  };

  return JSON.stringify(baseStructure, null, 2);
}

