 */
export function generateBreadcrumbStructuredData(breadcrumbs: Array<{
  name: string;
  url: string;
}>): string {
  const itemListElement = breadcrumbs.map((item, index) => ({
    '@type': 'ListItem',
    position: index + 1,
    name: item.name,
    item: `${SEO_CONFIG.SITE_URL}${item.url}`
  }));

  return generateStructuredData('BreadcrumbList', {
    itemListElement
  });
}

/**
 * æ¸…ç†å’Œä¼˜åŒ–æ–‡æœ¬å†…å®¹ç”¨äºSEO
 */
export function optimizeTextForSEO(text: string, maxLength: number = 160): string {
  // ç§»é™¤HTMLæ ‡ç­¾
  const cleanText = text.replace(/<[^>]*>/g, '');

  // ç§»é™¤å¤šä½™çš„ç©ºç™½å­—ç¬¦
  const trimmedText = cleanText.replace(/\s+/g, ' ').trim();

  // æˆªæ–­åˆ°æŒ‡å®šé•¿åº¦
  if (trimmedText.length <= maxLength) {
    return trimmedText;
  }

  // åœ¨å•è¯è¾¹ç•Œæˆªæ–­
  const truncated = trimmedText.substring(0, maxLength);
  const lastSpaceIndex = truncated.lastIndexOf(' ');

  if (lastSpaceIndex > maxLength * 0.8) {
    return truncated.substring(0, lastSpaceIndex) + '...';
  }

  return truncated + '...';
}

/**
 * ç”Ÿæˆå…³é”®è¯å¯†åº¦ä¼˜åŒ–çš„å†…å®¹
 */
export function optimizeContentKeywords(
  content: string, 
  primaryKeywords: string[], 
  targetDensity: number = 0.02
): string {
  // è¿™æ˜¯ä¸€ä¸ªç®€åŒ–çš„å…³é”®è¯å¯†åº¦ä¼˜åŒ–å‡½æ•°
  // å®é™…åº”ç”¨ä¸­å¯èƒ½éœ€è¦æ›´å¤æ‚çš„NLPå¤„ç†

  const words = content.split(/\s+/);
  const totalWords = words.length;
  const targetCount = Math.floor(totalWords * targetDensity);

  let optimizedContent = content;

  primaryKeywords.forEach(keyword => {
    const currentCount = (content.match(new RegExp(keyword, 'gi')) || []).length;

    if (currentCount < targetCount) {
      // åœ¨é€‚å½“ä½ç½®æ’å…¥å…³é”®è¯ï¼ˆè¿™é‡Œæ˜¯ç®€åŒ–å®ç°ï¼‰
      const insertions = targetCount - currentCount;
      for (let i = 0; i < insertions; i++) {
        const randomIndex = Math.floor(Math.random() * words.length);
        words.splice(randomIndex, 0, keyword);
      }
      optimizedContent = words.join(' ');
    }
  });

  return optimizedContent;
}

/**
 * éªŒè¯SEOæ•°æ®çš„å®Œæ•´æ€§
 */
export function validateSEOData(data: SEOData): {
  isValid: boolean;
  errors: string[];
  warnings: string[];
} {
  const errors: string[] = [];
  const warnings: string[] = [];

  // æ£€æŸ¥å¿…éœ€å­—æ®µ
  if (!data.title) {
    errors.push('æ ‡é¢˜æ˜¯å¿…éœ€çš„');
  } else if (data.title.length > 60) {
    warnings.push('æ ‡é¢˜é•¿åº¦è¶…è¿‡60ä¸ªå­—ç¬¦ï¼Œå¯èƒ½åœ¨æœç´¢ç»“æœä¸­è¢«æˆªæ–­');
  }

  if (!data.description) {
    errors.push('æè¿°æ˜¯å¿…éœ€çš„');
  } else if (data.description.length > 160) {
    warnings.push('æè¿°é•¿åº¦è¶…è¿‡160ä¸ªå­—ç¬¦ï¼Œå¯èƒ½åœ¨æœç´¢ç»“æœä¸­è¢«æˆªæ–­');
  }

  if (!data.keywords || data.keywords.length === 0) {
    warnings.push('å»ºè®®æ·»åŠ å…³é”®è¯ä»¥æé«˜SEOæ•ˆæœ');
  }

  return {
    isValid: errors.length === 0,
    errors,
    warnings
  };
}

