      // å¦‚æœåŸºäºæ¨¡æ¿åˆ›å»ºï¼ŒåŠ è½½æ¨¡æ¿æ•°æ®
      if (data.templateId) {
        const template = await PresetTemplateModel.findById(data.templateId);
