  /**
   * è·å–ç”¨æˆ·çš„çŸ¥è¯†å›¾è°±åˆ—è¡¨
   */
  static async getUserGraphs(
    userId: string,
    query: GraphQuery = {}
  ): Promise<{ graphs: KnowledgeGraph[]; total: number }> {
    try {
      const {
        subject,
        educationLevel,
        type,
        isPublic,
        search,
        tags,
        page = 1,
        limit = 20,
        sortBy = 'updatedAt',
        sortOrder = 'desc'
      } = query;

      // æ„å»ºæŸ¥è¯¢æ¡ä»¶
      const filter: any = { userId };

      if (subject) filter.subject = subject;
      if (educationLevel) filter.educationLevel = educationLevel;
      if (type) filter.type = type;
      if (typeof isPublic === 'boolean') filter['metadata.isPublic'] = isPublic;
      if (tags?.length) filter['metadata.tags'] = { $in: tags };

      // æ–‡æœ¬æœç´¢
      if (search) {
        filter.$or = [
          { name: { $regex: search, $options: 'i' } },
          { description: { $regex: search, $options: 'i' } }
        ];
      }

      // æ’åº
      const sort: any = {};
      sort[sortBy] = sortOrder === 'asc' ? 1 : -1;

      // æ‰§è¡ŒæŸ¥è¯¢
      const [graphs, total] = await Promise.all([
        KnowledgeGraphModel
          .find(filter)
          .sort(sort)
          .skip((page - 1) * limit)
          .limit(limit)
          .populate('metadata.templateId', 'name description')
          .lean(),
        KnowledgeGraphModel.countDocuments(filter)
      ]);

      return { graphs, total };
    } catch (error) {
      console.error('è·å–ç”¨æˆ·å›¾è°±åˆ—è¡¨å¤±è´¥:', error);
      throw error;
    }
  }

