  /**
   * å¤åˆ¶çŸ¥è¯†å›¾è°±
   */
  static async duplicateGraph(
    graphId: string,
    userId: string,
    newName?: string
  ): Promise<KnowledgeGraph> {
    try {
      const originalGraph = await KnowledgeGraphModel.findOne({
        $or: [
          { _id: graphId, userId },
          { _id: graphId, 'metadata.isPublic': true }
        ]
      });

      if (!originalGraph) {
        throw new Error('å›¾è°±ä¸å­˜åœ¨æˆ–æ— æƒé™è®¿é—®');
      }

      // åˆ›å»ºå‰¯æœ¬
      const duplicateData = {
        ...originalGraph.toObject(),
        _id: undefined,
        userId,
        name: newName || `${originalGraph.name} - å‰¯æœ¬`,
        metadata: {
          ...originalGraph.metadata,
          isPublic: false,
          statistics: {
            nodeCount: originalGraph.nodes.length,
            edgeCount: originalGraph.edges.length,
            maxDepth: 0,
            workCount: 0
          }
        },
        createdAt: new Date(),
        updatedAt: new Date()
      };

      // é‡æ–°ç”ŸæˆèŠ‚ç‚¹å’Œè¾¹çš„ID
      const nodeIdMap = new Map<string, string>();
      duplicateData.nodes = duplicateData.nodes.map(node => {
        const newId = generateId();
        nodeIdMap.set(node.id, newId);
        return {
          ...node,
          id: newId,
          createdAt: new Date(),
          updatedAt: new Date()
        };
      });

      duplicateData.edges = duplicateData.edges.map(edge => ({
        ...edge,
        id: generateId(),
        source: nodeIdMap.get(edge.source) || edge.source,
        target: nodeIdMap.get(edge.target) || edge.target,
        createdAt: new Date(),
        updatedAt: new Date()
      }));

      const duplicateGraph = new KnowledgeGraphModel(duplicateData);
      await duplicateGraph.save();

      return duplicateGraph.toObject();
    } catch (error) {
      console.error('å¤åˆ¶çŸ¥è¯†å›¾è°±å¤±è´¥:', error);
      throw error;
    }
  }
