export class EdgeService {
  /**
   * æ·»åŠ è¾¹
   */
  static async addEdge(
    graphId: string,
    userId: string,
    edgeData: Omit<GraphEdge, 'id' | 'createdAt' | 'updatedAt'>
  ): Promise<GraphEdge> {
    try {
      const graph = await KnowledgeGraphModel.findOne({
        _id: graphId,
        userId
      });

      if (!graph) {
        throw new Error('å›¾è°±ä¸å­˜åœ¨æˆ–æ— æƒé™è®¿é—®');
      }

      // éªŒè¯è¾¹æ•°æ®
      validateEdgeData(edgeData);

      // æ£€æŸ¥æºèŠ‚ç‚¹å’Œç›®æ ‡èŠ‚ç‚¹æ˜¯å¦å­˜åœ¨
      const sourceExists = graph.nodes.some(n => n.id === edgeData.source);
      const targetExists = graph.nodes.some(n => n.id === edgeData.target);

      if (!sourceExists || !targetExists) {
        throw new Error('æºèŠ‚ç‚¹æˆ–ç›®æ ‡èŠ‚ç‚¹ä¸å­˜åœ¨');
      }

      // æ£€æŸ¥æ˜¯å¦å·²å­˜åœ¨ç›¸åŒçš„è¾¹
      const existingEdge = graph.edges.find(e => 
        e.source === edgeData.source && 
        e.target === edgeData.target && 
        e.type === edgeData.type
      );

      if (existingEdge) {
        throw new Error('ç›¸åŒçš„è¾¹å·²å­˜åœ¨');
      }

      // æ£€æŸ¥è¾¹æ•°é‡é™åˆ¶
      if (graph.edges.length >= DEFAULT_KNOWLEDGE_GRAPH_CONFIG.LIMITS.MAX_EDGES) {
        throw new Error(`è¾¹æ•°é‡ä¸èƒ½è¶…è¿‡ ${DEFAULT_KNOWLEDGE_GRAPH_CONFIG.LIMITS.MAX_EDGES}`);
      }

      // ç”Ÿæˆæ–°è¾¹
      const newEdge: GraphEdge = {
        ...edgeData,
        id: generateId(),
        createdAt: new Date(),
        updatedAt: new Date()
      };

      // æ·»åŠ åˆ°å›¾è°±
      const addedEdge = graph.addEdge(newEdge);
      await graph.save();

      return addedEdge;
    } catch (error) {
      console.error('æ·»åŠ è¾¹å¤±è´¥:', error);
      throw error;
    }
  }

  /**
   * åˆ é™¤è¾¹
   */
  static async deleteEdge(
    graphId: string,
    userId: string,
    edgeId: string
  ): Promise<boolean> {
    try {
      const graph = await KnowledgeGraphModel.findOne({
        _id: graphId,
        userId
      });

      if (!graph) {
        throw new Error('å›¾è°±ä¸å­˜åœ¨æˆ–æ— æƒé™è®¿é—®');
      }

      const success = graph.removeEdge(edgeId);
      if (!success) {
        throw new Error('è¾¹ä¸å­˜åœ¨');
      }

      await graph.save();
      return true;
    } catch (error) {
      console.error('åˆ é™¤è¾¹å¤±è´¥:', error);
      throw error;
    }
  }
}

