          reason: 'ä½œå“ä¸å­˜åœ¨',
          canReuse: false
        };
      }

      // 2. æ£€æŸ¥æ˜¯å¦ä¸ºè‡ªå·±çš„ä½œå“
      if (work.author.toString() === userId) {
        return {
          permission: 'self_work',
          reason: 'ä¸èƒ½å¤ç”¨è‡ªå·±çš„ä½œå“',
          canReuse: false
        };
      }

      // 3. æ£€æŸ¥ä½œå“æ˜¯å¦å…è®¸å¤ç”¨
      if (work.status !== 'published') {
        return {
          permission: 'denied',
          reason: 'ä½œå“æœªå‘å¸ƒï¼Œä¸å…è®¸å¤ç”¨',
          canReuse: false
        };
      }

      // 4. æ£€æŸ¥ç”¨æˆ·å¤ç”¨é…é¢
      const quota = await this.getUserReuseQuota(userId);
      if (quota.dailyUsed >= quota.limits.dailyLimit) {
        return {
          permission: 'quota_exceeded',
          reason: 'ä»Šæ—¥å¤ç”¨æ¬¡æ•°å·²è¾¾ä¸Šé™',
          quotaUsed: quota.dailyUsed,
          quotaLimit: quota.limits.dailyLimit,
          canReuse: false
        };
      }

      // 5. æ£€æŸ¥å†·å´æœŸ
      const lastReuse = await this.getLastReuseTime(userId, workId);
      if (lastReuse) {
        const cooldownEnd = new Date(lastReuse.getTime() + quota.limits.cooldownPeriod * 60 * 60 * 1000);
        if (new Date() < cooldownEnd) {
          return {
            permission: 'denied',
            reason: `è¯¥ä½œå“å¤ç”¨å†·å´ä¸­ï¼Œè¯·åœ¨${cooldownEnd.toLocaleString()}åé‡è¯•`,
            canReuse: false
          };
        }
      }

      return {
        permission: 'allowed',
        quotaUsed: quota.dailyUsed,
        quotaLimit: quota.limits.dailyLimit,
        canReuse: true
      };
    } catch (error) {
      handleServiceError(error, 'æ£€æŸ¥å¤ç”¨æƒé™');
      return {
        permission: 'denied',
        reason: 'æ£€æŸ¥å¤ç”¨æƒé™æ—¶å‘ç”Ÿé”™è¯¯',
        canReuse: false
      };
    }
  }

