  /**
   * æ‰¹é‡æŒ‚è½½ä½œå“
   */
  static async batchMountWorks(
    userId: string,
    graphId: string,
    mounts: Array<{
      nodeId: string;
      workId: string;
      mountType?: 'manual' | 'auto';
      notes?: string;
    }>
  ): Promise<WorkMount[]> {
    try {
      // éªŒè¯å›¾è°±å­˜åœ¨
      const graph = await KnowledgeGraphModel.findOne({
        _id: graphId,
        userId
      });

      if (!graph) {
        throw new Error('å›¾è°±ä¸å­˜åœ¨æˆ–æ— æƒé™è®¿é—®');
      }

      const results: WorkMount[] = [];
      const nodeWorkCounts = new Map<string, number>();

      for (const mountData of mounts) {
        try {
          // éªŒè¯èŠ‚ç‚¹å­˜åœ¨
          const node = graph.nodes.find(n => n.id === mountData.nodeId);
          if (!node) {
            console.warn(`èŠ‚ç‚¹ ${mountData.nodeId} ä¸å­˜åœ¨ï¼Œè·³è¿‡`);
            continue;
          }

          // éªŒè¯ä½œå“å­˜åœ¨
          const work = await WorkModel.findOne({
            _id: mountData.workId,
            $or: [
              { userId },
              { isPublic: true }
            ]
          });

          if (!work) {
            console.warn(`ä½œå“ ${mountData.workId} ä¸å­˜åœ¨æˆ–æ— æƒé™ï¼Œè·³è¿‡`);
            continue;
          }

          // æ£€æŸ¥æ˜¯å¦å·²æŒ‚è½½
          const existingMount = await WorkMountModel.findOne({
            userId,
            graphId,
            nodeId: mountData.nodeId,
            workId: mountData.workId
          });

          if (existingMount) {
            console.warn(`ä½œå“ ${mountData.workId} å·²æŒ‚è½½åˆ°èŠ‚ç‚¹ ${mountData.nodeId}ï¼Œè·³è¿‡`);
            continue;
          }

          // åˆ›å»ºæŒ‚è½½è®°å½•
          const mount = new WorkMountModel({
            userId,
            graphId,
            nodeId: mountData.nodeId,
            workId: mountData.workId,
            mountType: mountData.mountType || 'manual',
            metadata: {
              notes: mountData.notes,
              tags: [],
              reason: mountData.mountType === 'auto' ? 'è‡ªåŠ¨æŒ‚è½½' : 'æ‰‹åŠ¨æŒ‚è½½'
            }
          });

          await mount.save();
          results.push(mount.toObject());

          // è®°å½•éœ€è¦æ›´æ–°çš„èŠ‚ç‚¹
          nodeWorkCounts.set(mountData.nodeId, 
            (nodeWorkCounts.get(mountData.nodeId) || 0) + 1
          );

        } catch (error) {
          console.error(`æŒ‚è½½ä½œå“ ${mountData.workId} åˆ°èŠ‚ç‚¹ ${mountData.nodeId} å¤±è´¥:`, error);
          continue;
        }
      }

      // æ‰¹é‡æ›´æ–°èŠ‚ç‚¹ä½œå“æ•°é‡
      for (const [nodeId] of nodeWorkCounts) {
        await this.updateNodeWorkCount(graphId, nodeId);
      }

      return results;
    } catch (error) {
      console.error('æ‰¹é‡æŒ‚è½½ä½œå“å¤±è´¥:', error);
      throw error;
    }
  }

  /**
   * è·å–èŠ‚ç‚¹çš„æŒ‚è½½ä½œå“åˆ—è¡¨
   */
  static async getNodeWorks(
    graphId: string,
    nodeId: string,
    userId?: string,
    options: {
      page?: number;
      limit?: number;
      sortBy?: 'createdAt' | 'updatedAt';
      sortOrder?: 'asc' | 'desc';
    } = {}
  ): Promise<{
    mounts: (WorkMount & { work: any })[];
    total: number;
  }> {
    try {
      const {
        page = 1,
        limit = 20,
        sortBy = 'createdAt',
        sortOrder = 'desc'
      } = options;

      // æ„å»ºæŸ¥è¯¢æ¡ä»¶
      const filter: any = { graphId, nodeId };

      // å¦‚æœæŒ‡å®šäº†ç”¨æˆ·IDï¼Œåªè¿”å›è¯¥ç”¨æˆ·çš„æŒ‚è½½æˆ–å…¬å¼€ä½œå“çš„æŒ‚è½½
      if (userId) {
        // è¿™é‡Œéœ€è¦é€šè¿‡èšåˆæŸ¥è¯¢æ¥å…³è”ä½œå“è¡¨è¿›è¡Œæƒé™è¿‡æ»¤
        const pipeline = [
          { $match: filter },
          {
            $lookup: {
              from: 'works',
              localField: 'workId',
              foreignField: '_id',
              as: 'work'
            }
          },
          { $unwind: '$work' },
          {
            $match: {
              $or: [
                { userId },
                { 'work.isPublic': true }
              ]
            }
          },
          { $sort: { [sortBy]: sortOrder === 'asc' ? 1 : -1 } },
          { $skip: (page - 1) * limit },
          { $limit: limit }
        ];

        const [mounts, totalResult] = await Promise.all([
          WorkMountModel.aggregate(pipeline),
          WorkMountModel.aggregate([
            { $match: filter },
            {
              $lookup: {
                from: 'works',
                localField: 'workId',
                foreignField: '_id',
                as: 'work'
              }
            },
            { $unwind: '$work' },
            {
              $match: {
                $or: [
                  { userId },
                  { 'work.isPublic': true }
                ]
              }
            },
            { $count: 'total' }
          ])
        ]);

        const total = totalResult[0]?.total || 0;
        return { mounts, total };
      } else {
        // å…¬å¼€æŸ¥è¯¢ï¼Œåªè¿”å›å…¬å¼€ä½œå“çš„æŒ‚è½½
        const pipeline = [
          { $match: filter },
          {
            $lookup: {
              from: 'works',
              localField: 'workId',
              foreignField: '_id',
              as: 'work'
            }
          },
          { $unwind: '$work' },
          { $match: { 'work.isPublic': true } },
          { $sort: { [sortBy]: sortOrder === 'asc' ? 1 : -1 } },
          { $skip: (page - 1) * limit },
          { $limit: limit }
        ];

        const [mounts, totalResult] = await Promise.all([
          WorkMountModel.aggregate(pipeline),
          WorkMountModel.aggregate([
            { $match: filter },
            {
              $lookup: {
                from: 'works',
                localField: 'workId',
                foreignField: '_id',
                as: 'work'
              }
            },
            { $unwind: '$work' },
            { $match: { 'work.isPublic': true } },
            { $count: 'total' }
          ])
        ]);

        const total = totalResult[0]?.total || 0;
        return { mounts, total };
      }
    } catch (error) {
      console.error('è·å–èŠ‚ç‚¹ä½œå“åˆ—è¡¨å¤±è´¥:', error);
      throw error;
    }
  }

  /**
   * è·å–ç”¨æˆ·çš„æŒ‚è½½è®°å½•
