}

export interface UpdateWorkRequest {
  title?: string;
  knowledgePoint?: string;
  subject?: string;
  gradeLevel?: string;
  cards?: TeachingCard[];
  tags?: string[];
  status?: 'draft' | 'published' | 'archived';
}

export interface WorkListQuery {
  author?: string;
  status?: 'draft' | 'published' | 'archived';
  subject?: string;
  gradeLevel?: string;
  page?: number;
  limit?: number;
  sortBy?: 'createdAt' | 'updatedAt' | 'reuseCount';
  sortOrder?: 'asc' | 'desc';
}

export interface WorkListResponse {
