}

class WorkService {
  /**
   * åˆ›å»ºæ–°ä½œå“
   */
  async createWork(userId: string, data: CreateWorkRequest): Promise<WorkDocument> {
    try {
      const work = new Work({
        ...data,
        author: new Types.ObjectId(userId),
        status: data.status || 'draft'
      });

      const savedWork = await work.save();

      // å¦‚æœæ˜¯å‘å¸ƒçŠ¶æ€ï¼Œè®°å½•è´¡çŒ®åº¦
      if (data.status === 'published') {
        await this.recordContribution(userId, savedWork._id, 'creation');
      }

      return savedWork;
    } catch (error) {
      throw new Error(`åˆ›å»ºä½œå“å¤±è´¥: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * æ›´æ–°ä½œå“
   */
  async updateWork(workId: string, userId: string, data: UpdateWorkRequest): Promise<WorkDocument> {
    try {
      const work = await Work.findOne({ 
        _id: workId, 
        author: userId 
      });

      if (!work) {
        throw new Error('ä½œå“ä¸å­˜åœ¨æˆ–æ— æƒé™ä¿®æ”¹');
      }

      const wasPublished = work.status === 'published';
      const willBePublished = data.status === 'published';

      // æ›´æ–°ä½œå“æ•°æ®
      Object.assign(work, data);
      const updatedWork = await work.save();

      // å¦‚æœä»è‰ç¨¿å˜ä¸ºå‘å¸ƒï¼Œè®°å½•è´¡çŒ®åº¦
      if (!wasPublished && willBePublished) {
        await this.recordContribution(userId, workId, 'creation');
      }

      return updatedWork;
    } catch (error) {
      throw new Error(`æ›´æ–°ä½œå“å¤±è´¥: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * è·å–ä½œå“è¯¦æƒ…
   */
  async getWorkById(workId: string, userId?: string): Promise<WorkDocument | null> {
    try {
      const query: any = { _id: workId };

      // å¦‚æœæä¾›äº†ç”¨æˆ·IDï¼Œå¯ä»¥æŸ¥çœ‹è‡ªå·±çš„è‰ç¨¿
      if (!userId) {
        query.status = 'published';
      } else {
        query.$or = [
          { status: 'published' },
          { author: userId }
        ];
      }

      const work = await Work.findOne(query)
        .populate('author', 'name avatar')
        .populate('originalWork', 'title author');

      return work;
    } catch (error) {
      throw new Error(`è·å–ä½œå“å¤±è´¥: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * è·å–ä½œå“åˆ—è¡¨
   */
  async getWorksList(query: WorkListQuery): Promise<WorkListResponse> {
    try {
      const {
        author,
        status = 'published',
        subject,
        gradeLevel,
        page = 1,
        limit = 20,
        sortBy = 'createdAt',
        sortOrder = 'desc'
      } = query;

      const filter: any = { status };

      if (author) {
        filter.author = author;
      }
      if (subject) {
        filter.subject = subject;
      }
      if (gradeLevel) {
        filter.gradeLevel = gradeLevel;
      }

      const sort: any = {};
      sort[sortBy] = sortOrder === 'desc' ? -1 : 1;

      const skip = (page - 1) * limit;

      const [works, total] = await Promise.all([
        Work.find(filter)
          .populate('author', 'name avatar')
          .sort(sort)
          .skip(skip)
          .limit(limit),
        Work.countDocuments(filter)
      ]);

      return {
        works,
        total,
        page,
        totalPages: Math.ceil(total / limit)
      };
    } catch (error) {
      throw new Error(`è·å–ä½œå“åˆ—è¡¨å¤±è´¥: ${error instanceof Error ? error.message : 'æœªçŸ¥é”™è¯¯'}`);
    }
  }

  /**
   * åˆ é™¤ä½œå“
   */
  async deleteWork(workId: string, userId: string): Promise<void> {
    try {
      const work = await Work.findOne({ 
        _id: workId, 
        author: userId 
      });

      if (!work) {
        throw new Error('ä½œå“ä¸å­˜åœ¨æˆ–æ— æƒé™åˆ é™¤');
      }

      await Work.findByIdAndDelete(workId);
    } catch (error) {
      throw new Error(`åˆ é™¤ä½œå“å¤±è´¥: ${error instanceof Error ? error.message : 'æœªçŸ¥é”™è¯¯'}`);
