  /**
   * æœç´¢ä½œå“
   */
  async searchWorks(keyword: string, filters?: Partial<WorkListQuery>): Promise<WorkDocument[]> {
    try {
      const query: any = {
        status: 'published',
        $or: [
          { title: { $regex: keyword, $options: 'i' } },
          { knowledgePoint: { $regex: keyword, $options: 'i' } },
          { tags: { $in: [new RegExp(keyword, 'i')] } }
        ]
      };

      if (filters?.subject) {
        query.subject = filters.subject;
      }
      if (filters?.gradeLevel) {
        query.gradeLevel = filters.gradeLevel;
      }

      return await Work.find(query)
        .populate('author', 'name avatar')
        .sort({ reuseCount: -1, createdAt: -1 })
        .limit(filters?.limit || 20);
    } catch (error) {
      throw new Error(`æœç´¢ä½œå“å¤±è´¥: ${error instanceof Error ? error.message : 'æœªçŸ¥é”™è¯¯'}`);
    }
  }

  /**
   * è·å–æ™ºæ…§å¹¿åœºä½œå“åˆ—è¡¨ï¼ˆä¸“ç”¨æ–¹æ³•ï¼‰
   */
  async getWorksForSquare(query: SquareQuery): Promise<SquareResponse> {
    try {
      const {
        subject,
        gradeLevel,
        page = 1,
        limit = 12,
        sortBy = 'latest',
        search,
        tags
      } = query;

      const filter: any = { status: 'published' };

      // ç­›é€‰æ¡ä»¶
      if (subject) filter.subject = subject;
      if (gradeLevel) filter.gradeLevel = gradeLevel;
      if (tags && tags.length > 0) {
        filter.tags = { $in: tags };
      }

      // æœç´¢æ¡ä»¶
      if (search) {
        filter.$or = [
          { title: { $regex: search, $options: 'i' } },
          { knowledgePoint: { $regex: search, $options: 'i' } },
          { tags: { $in: [new RegExp(search, 'i')] } }
        ];
      }

      // æ’åºæ¡ä»¶
      const sort: any = {};
      switch (sortBy) {
        case 'popular':
          sort.reuseCount = -1;
          sort.createdAt = -1;
          break;
        case 'reuse_count':
          sort.reuseCount = -1;
          break;
        case 'latest':
        default:
          sort.createdAt = -1;
          break;
      }

      const skip = (page - 1) * limit;

      // å¹¶è¡ŒæŸ¥è¯¢ä½œå“å’Œç»Ÿè®¡ä¿¡æ¯
      const [works, total, filters] = await Promise.all([
        Work.find(filter)
          .populate('author', 'name avatar')
          .sort(sort)
          .skip(skip)
          .limit(limit)
          .lean(),
        Work.countDocuments(filter),
        this.getSquareFilters()
      ]);

      // è½¬æ¢ä¸ºå¡ç‰‡æ•°æ®æ ¼å¼
      const workCards = works.map(work => this.transformToWorkCard(work));

      return {
        works: workCards,
        pagination: {
          page,
          limit,
          total,
          totalPages: Math.ceil(total / limit),
          hasNext: page < Math.ceil(total / limit),
          hasPrev: page > 1
        },
        filters
      };
    } catch (error) {
      throw new Error(`è·å–æ™ºæ…§å¹¿åœºä½œå“å¤±è´¥: ${error instanceof Error ? error.message : 'æœªçŸ¥é”™è¯¯'}`);
    }
  }

  /**
   * è·å–ç­›é€‰é€‰é¡¹
   */
  private async getSquareFilters(): Promise<any> {
    try {
      const [subjectStats, gradeLevelStats] = await Promise.all([
        Work.aggregate([
          { $match: { status: 'published' } },
          { $group: { _id: '$subject', count: { $sum: 1 } } },
          { $sort: { count: -1 } }
        ]),
        Work.aggregate([
          { $match: { status: 'published' } },
          { $group: { _id: '$gradeLevel', count: { $sum: 1 } } },
          { $sort: { _id: 1 } }
        ])
      ]);

      return {
        subjects: subjectStats.map(item => ({
          value: item._id,
          label: item._id,
          count: item.count
        })),
        gradeLevels: gradeLevelStats.map(item => ({
          value: item._id,
          label: item._id,
          count: item.count
        })),
        availableTags: [] // TODO: å®ç°æ ‡ç­¾ç»Ÿè®¡
      };
    } catch (error) {
      console.error('è·å–ç­›é€‰é€‰é¡¹å¤±è´¥:', error);
      return { subjects: [], gradeLevels: [], availableTags: [] };
    }
  }

  /**
   * è½¬æ¢ä½œå“ä¸ºå¡ç‰‡å±•ç¤ºæ ¼å¼
   */
  private transformToWorkCard(work: any): any {
    return {
      id: work._id.toString(),
      title: work.title,
      knowledgePoint: work.knowledgePoint,
      subject: work.subject,
      gradeLevel: work.gradeLevel,
      author: {
        id: work.author._id.toString(),
        name: work.author.name,
        avatar: work.author.avatar
      },
      reuseCount: work.reuseCount || 0,
      createdAt: work.createdAt.toISOString(),
      updatedAt: work.updatedAt.toISOString(),
      tags: work.tags || [],
      cardCount: work.cards?.length || 0,
      cardTypes: work.cards?.map((card: any) => card.type) || []
    };
  }
}

