import { NextResponse } from 'next/server';

export interface ApiError extends Error {
  statusCode?: number;
  code?: string;
}

/**
 * å¤„ç†æœåŠ¡å±‚é”™è¯¯ï¼Œè¿”å›æ ‡å‡†åŒ–çš„APIå“åº”
 */
export function handleServiceError(error: unknown): NextResponse {
  console.error('Service error:', error);

  if (error instanceof Error) {
    const apiError = error as ApiError;

    // æ ¹æ®é”™è¯¯ç±»å‹è¿”å›ä¸åŒçš„çŠ¶æ€ç 
    let statusCode = apiError.statusCode || 500;
    let message = apiError.message;

    // å¤„ç†å¸¸è§é”™è¯¯ç±»å‹
    if (message.includes('ä¸å­˜åœ¨') || message.includes('not found')) {
      statusCode = 404;
    } else if (message.includes('æ— æƒé™') || message.includes('unauthorized')) {
      statusCode = 403;
    } else if (message.includes('ä¸èƒ½ä¸ºç©º') || message.includes('æ— æ•ˆ') || message.includes('æ ¼å¼')) {
      statusCode = 400;
    } else if (message.includes('å·²å­˜åœ¨') || message.includes('é‡å¤')) {
      statusCode = 409;
    } else if (message.includes('è¶…è¿‡é™åˆ¶') || message.includes('å·²è¾¾ä¸Šé™')) {
      statusCode = 429;
    }

    return NextResponse.json({
      success: false,
      error: message,
      code: apiError.code
    }, { status: statusCode });
  }

  // æœªçŸ¥é”™è¯¯
  return NextResponse.json({
    success: false,
    error: 'æœåŠ¡å™¨å†…éƒ¨é”™è¯¯'
  }, { status: 500 });
}

/**
 * åˆ›å»ºAPIé”™è¯¯
 */
export function createApiError(message: string, statusCode = 500, code?: string): ApiError {
  const error = new Error(message) as ApiError;
  error.statusCode = statusCode;
  error.code = code;
  return error;
}

/**
 * éªŒè¯é”™è¯¯
 */
export function createValidationError(message: string): ApiError {
  return createApiError(message, 400, 'VALIDATION_ERROR');
}

/**
 * æƒé™é”™è¯¯
 */
export function createPermissionError(message = 'æ— æƒé™è®¿é—®'): ApiError {
