/**
 * éªŒè¯å›¾ç»“æ„çš„å®Œæ•´æ€§
 */
export function validateGraphStructure(
  nodes: GraphNode[], 
  edges: GraphEdge[]
): { isValid: boolean; errors: string[] } {
  const errors: string[] = [];

  // éªŒè¯èŠ‚ç‚¹
  const nodeValidation = validateNodes(nodes);
  if (!nodeValidation.isValid) {
    errors.push(...nodeValidation.errors);
  }

  // éªŒè¯è¾¹
  const edgeValidation = validateEdges(edges, nodes);
  if (!edgeValidation.isValid) {
    errors.push(...edgeValidation.errors);
  }

  return {
    isValid: errors.length === 0,
    errors
  };
}

/**
 * éªŒè¯èŠ‚ç‚¹æ•°æ®
 */
export function validateNodes(nodes: GraphNode[]): { isValid: boolean; errors: string[] } {
  const errors: string[] = [];
  const nodeIds = new Set<string>();
  // æ£€æŸ¥èŠ‚ç‚¹æ•°é‡é™åˆ¶
  if (nodes.length > GRAPH_LIMITS.MAX_NODES_PER_GRAPH) {
    errors.push(`èŠ‚ç‚¹æ•°é‡è¶…è¿‡é™åˆ¶ (${GRAPH_LIMITS.MAX_NODES_PER_GRAPH})`);
  }

  for (const node of nodes) {
    // æ£€æŸ¥å¿…å¡«å­—æ®µ
    if (!node.id) {
      errors.push('èŠ‚ç‚¹ç¼ºå°‘ID');
      continue;
    }
    if (!node.label?.trim()) {
      errors.push(`èŠ‚ç‚¹ ${node.id} ç¼ºå°‘æ ‡ç­¾`);
    }
    if (!Object.values(NodeType).includes(node.type)) {
      errors.push(`èŠ‚ç‚¹ ${node.id} ç±»å‹æ— æ•ˆ: ${node.type}`);
    }

    // æ£€æŸ¥IDå”¯ä¸€æ€§
    if (nodeIds.has(node.id)) {
      errors.push(`èŠ‚ç‚¹IDé‡å¤: ${node.id}`);
    }
    nodeIds.add(node.id);

    // éªŒè¯å±‚çº§
    if (typeof node.level !== 'number' || node.level < 0) {
      errors.push(`èŠ‚ç‚¹ ${node.id} å±‚çº§æ— æ•ˆ`);
    }
  }

  return { isValid: errors.length === 0, errors };
}

/**
 * éªŒè¯è¾¹æ•°æ®
 */
export function validateEdges(
  edges: GraphEdge[], 
  nodes: GraphNode[]
): { isValid: boolean; errors: string[] } {
  const errors: string[] = [];
  const edgeIds = new Set<string>();
  const nodeIds = new Set(nodes.map(n => n.id));

  // æ£€æŸ¥è¾¹æ•°é‡é™åˆ¶
  if (edges.length > GRAPH_LIMITS.MAX_EDGES_PER_GRAPH) {
    errors.push(`è¾¹æ•°é‡è¶…è¿‡é™åˆ¶ (${GRAPH_LIMITS.MAX_EDGES_PER_GRAPH})`);
  }

  for (const edge of edges) {
    // æ£€æŸ¥å¿…å¡«å­—æ®µ
    if (!edge.id) {
      errors.push('è¾¹ç¼ºå°‘ID');
      continue;
    }
    if (!edge.source) {
      errors.push(`è¾¹ ${edge.id} ç¼ºå°‘æºèŠ‚ç‚¹`);
    }
    if (!edge.target) {
      errors.push(`è¾¹ ${edge.id} ç¼ºå°‘ç›®æ ‡èŠ‚ç‚¹`);
    }
    if (!Object.values(EdgeType).includes(edge.type)) {
      errors.push(`è¾¹ ${edge.id} ç±»å‹æ— æ•ˆ: ${edge.type}`);
    }

    // æ£€æŸ¥IDå”¯ä¸€æ€§
    if (edgeIds.has(edge.id)) {
      errors.push(`è¾¹IDé‡å¤: ${edge.id}`);
    }
    edgeIds.add(edge.id);

    // éªŒè¯èŠ‚ç‚¹å­˜åœ¨æ€§
    if (edge.source && !nodeIds.has(edge.source)) {
      errors.push(`è¾¹ ${edge.id} çš„æºèŠ‚ç‚¹ä¸å­˜åœ¨: ${edge.source}`);
    }
    if (edge.target && !nodeIds.has(edge.target)) {
      errors.push(`è¾¹ ${edge.id} çš„ç›®æ ‡èŠ‚ç‚¹ä¸å­˜åœ¨: ${edge.target}`);
    }

    // éªŒè¯æƒé‡
    if (typeof edge.weight !== 'number' || edge.weight < 0) {
      errors.push(`è¾¹ ${edge.id} æƒé‡æ— æ•ˆ`);
    }

    // éªŒè¯è‡ªç¯
    if (edge.source === edge.target) {
      errors.push(`è¾¹ ${edge.id} ä¸èƒ½è¿æ¥åˆ°è‡ªèº«`);
    }
  }

  return { isValid: errors.length === 0, errors };
}

/**
 * éªŒè¯å®Œæ•´çš„çŸ¥è¯†å›¾è°±
 */
export function validateKnowledgeGraph(graph: KnowledgeGraph): { isValid: boolean; errors: string[] } {
  const errors: string[] = [];

  // éªŒè¯åŸºç¡€ä¿¡æ¯
  if (!graph.name?.trim()) {
    errors.push('å›¾è°±åç§°ä¸èƒ½ä¸ºç©º');
  }
  if (graph.name && graph.name.length > VALIDATION_RULES.GRAPH_NAME.MAX_LENGTH) {
    errors.push('å›¾è°±åç§°è¿‡é•¿');
  }

  if (graph.description && graph.description.length > VALIDATION_RULES.DESCRIPTION.MAX_LENGTH) {
    errors.push('å›¾è°±æè¿°è¿‡é•¿');
  }

  // éªŒè¯å­¦ç§‘åˆ†ç±»ï¼ˆç®€åŒ–éªŒè¯ï¼‰
  if (graph.subject && typeof graph.subject !== 'string') {
    errors.push('å­¦ç§‘åˆ†ç±»æ— æ•ˆ');
  }

  // éªŒè¯å­¦æ®µï¼ˆç®€åŒ–éªŒè¯ï¼‰
  if (graph.gradeLevel && typeof graph.gradeLevel !== 'string') {
    errors.push('æ•™è‚²é˜¶æ®µæ— æ•ˆ');
  }

  // éªŒè¯å›¾ç»“æ„
  const structureValidation = validateGraphStructure(graph.nodes, graph.edges);
  if (!structureValidation.isValid) {
    errors.push(...structureValidation.errors);
  }

  // éªŒè¯å¸ƒå±€é…ç½®
  if (!graph.layout) {
    errors.push('ç¼ºå°‘å¸ƒå±€é…ç½®');
  }

  // éªŒè¯ç‰ˆæœ¬å·
  if (typeof graph.version !== 'number' || graph.version < 1) {
    errors.push('ç‰ˆæœ¬å·æ— æ•ˆ');
  }

  // éªŒè¯å…¬å¼€çŠ¶æ€
  if (typeof graph.isPublic !== 'boolean') {
    errors.push('å…¬å¼€çŠ¶æ€æ— æ•ˆ');
  }

  return { isValid: errors.length === 0, errors };
}

