/**
 * é”™è¯¯å¤„ç†ä¸­é—´ä»¶é…ç½®
 */
interface ErrorHandlerConfig {
  enabled: boolean;
  logErrors: boolean;
  includeStackTrace: boolean;
  sanitizeErrors: boolean;
  rateLimitErrors: boolean;
  maxErrorsPerMinute: number;
}

/**
 * é»˜è®¤é…ç½®
 */
const defaultConfig: ErrorHandlerConfig = {
  enabled: true,
  logErrors: true,
  includeStackTrace: process.env.NODE_ENV === 'development',
  sanitizeErrors: process.env.NODE_ENV === 'production',
  rateLimitErrors: true,
  maxErrorsPerMinute: 60
};

/**
 * è·å–é…ç½®
 */
const getConfig = (): ErrorHandlerConfig => {
  const env = process.env.NODE_ENV || 'development';

  if (env === 'test') {
    return {
      ...defaultConfig,
      enabled: false,
      logErrors: false
    };
  }

  if (env === 'production') {
    return {
      ...defaultConfig,
      includeStackTrace: false,
      sanitizeErrors: true
    };
  }

  return defaultConfig;
};

/**
 * é”™è¯¯é¢‘ç‡é™åˆ¶å™¨
 */
class ErrorRateLimiter {
  private errorCounts = new Map<string, number[]>();
  private readonly windowMs = 60 * 1000; // 1åˆ†é’Ÿçª—å£

  /**
   * æ£€æŸ¥æ˜¯å¦è¶…è¿‡é”™è¯¯é¢‘ç‡é™åˆ¶
   */
  isRateLimited(identifier: string, maxErrors: number): boolean {
    const now = Date.now();
    const windowStart = now - this.windowMs;

    // è·å–å½“å‰é”™è¯¯è®¡æ•°
    const errors = this.errorCounts.get(identifier) || [];

    // æ¸…ç†è¿‡æœŸçš„é”™è¯¯è®°å½•
    const recentErrors = errors.filter(timestamp => timestamp > windowStart);

    // æ›´æ–°é”™è¯¯è®¡æ•°
    this.errorCounts.set(identifier, recentErrors);

    // æ£€æŸ¥æ˜¯å¦è¶…è¿‡é™åˆ¶
    return recentErrors.length >= maxErrors;
  }

  /**
   * è®°å½•é”™è¯¯
   */
  recordError(identifier: string): void {
    const now = Date.now();
    const errors = this.errorCounts.get(identifier) || [];
    errors.push(now);
    this.errorCounts.set(identifier, errors);
  }

  /**
   * æ¸…ç†è¿‡æœŸè®°å½•
   */
  cleanup(): void {
    const now = Date.now();
    const windowStart = now - this.windowMs;

    for (const [identifier, errors] of this.errorCounts.entries()) {
      const recentErrors = errors.filter(timestamp => timestamp > windowStart);
      if (recentErrors.length === 0) {
        this.errorCounts.delete(identifier);
      } else {
        this.errorCounts.set(identifier, recentErrors);
      }
    }
  }
}

// å…¨å±€é”™è¯¯é¢‘ç‡é™åˆ¶å™¨å®ä¾‹
const errorRateLimiter = new ErrorRateLimiter();

// å®šæœŸæ¸…ç†è¿‡æœŸè®°å½•
setInterval(() => {
  errorRateLimiter.cleanup();
}, 5 * 60 * 1000); // æ¯5åˆ†é’Ÿæ¸…ç†ä¸€æ¬¡

/**
 * ä»è¯·æ±‚ä¸­æå–é”™è¯¯ä¸Šä¸‹æ–‡
 */
const extractErrorContext = (request: NextRequest): ErrorContext => {
  const requestContext = extractRequestContext(request);

  return {
    url: request.url,
    method: request.method,
    userAgent: requestContext.context?.userAgent,
    ip: requestContext.context?.ip,
    traceId: requestContext.traceId,
    timestamp: new Date().toISOString()
  };
};

/**
 * æ¸…ç†æ•æ„Ÿä¿¡æ¯
 */
const sanitizeError = (error: CustomError): CustomError => {
  // åœ¨ç”Ÿäº§ç¯å¢ƒä¸­ï¼Œç§»é™¤å¯èƒ½åŒ…å«æ•æ„Ÿä¿¡æ¯çš„è¯¦ç»†é”™è¯¯ä¿¡æ¯
  if (process.env.NODE_ENV === 'production') {
    // å¯¹äºç³»ç»Ÿé”™è¯¯ï¼Œä½¿ç”¨é€šç”¨é”™è¯¯æ¶ˆæ¯
    if (error.isServerError()) {
      return new CustomError(
        ErrorCode.INTERNAL_SERVER_ERROR,
        'An internal server error occurred',
        undefined,
        error.context
      );
    }
  }

  return error;
};

/**
 * æ£€æŸ¥æ˜¯å¦ä¸ºå¯å¿½ç•¥çš„é”™è¯¯
 */
const isIgnorableError = (error: Error): boolean => {
  const ignorableMessages = [
    'ECONNRESET',
    'ECONNABORTED',
    'EPIPE',
    'Client closed connection'
  ];

  return ignorableMessages.some(msg => 
    error.message.includes(msg) || error.name.includes(msg)
  );
};

/**
 * å…¨å±€é”™è¯¯å¤„ç†ä¸­é—´ä»¶
 */
export const errorHandlerMiddleware = async (
  request: NextRequest,
  error: unknown
): Promise<NextResponse> => {
  const config = getConfig();

  if (!config.enabled) {
    throw error;
  }

  // æå–é”™è¯¯ä¸Šä¸‹æ–‡
  const errorContext = extractErrorContext(request);
  const traceId = errorContext.traceId || request.headers.get('x-trace-id') || undefined;

  // åˆ›å»ºå¸¦è¿½è¸ªçš„æ—¥å¿—å™¨
  const requestLogger = createTracedLogger(traceId, errorContext);

  let customError: CustomError;

  // è½¬æ¢ä¸ºCustomError
  if (error instanceof CustomError) {
    customError = error.withContext(errorContext);
  } else if (error instanceof Error) {
    // æ£€æŸ¥æ˜¯å¦ä¸ºå¯å¿½ç•¥çš„é”™è¯¯
    if (isIgnorableError(error)) {
      requestLogger.debug(`Ignorable error: ${error.message}`, {
        metadata: { ignorable: true }
      });
      return NextResponse.json(
        { success: false, error: { message: 'Request aborted' } },
        { status: 499 }
      );
    }

    customError = CustomError.fromError(error, ErrorCode.INTERNAL_SERVER_ERROR, errorContext);
  } else {
    customError = new CustomError(
      ErrorCode.UNKNOWN_ERROR,
      'An unknown error occurred',
      undefined,
      errorContext
    );
  }

  // é”™è¯¯é¢‘ç‡é™åˆ¶æ£€æŸ¥
  if (config.rateLimitErrors && errorContext.ip) {
    const identifier = `${errorContext.ip}:${customError.code}`;

    if (errorRateLimiter.isRateLimited(identifier, config.maxErrorsPerMinute)) {
      requestLogger.warn('Error rate limit exceeded', {
        metadata: {
          ip: errorContext.ip,
          errorCode: customError.code,
          rateLimited: true
        }
      });

      return createErrorResponse(
        CustomError.rateLimit('Too many errors from this client'),
        traceId
      );
    }

    errorRateLimiter.recordError(identifier);
  }

  // æ¸…ç†æ•æ„Ÿä¿¡æ¯
  if (config.sanitizeErrors) {
    customError = sanitizeError(customError);
  }

  // è®°å½•é”™è¯¯æ—¥å¿—
  if (config.logErrors) {
    const logLevel = customError.getLogLevel();
    const logContext = {
      traceId,
      metadata: {
        code: customError.code,
        type: customError.type,
        severity: customError.severity,
        httpStatus: customError.httpStatus,
        retryable: customError.retryable,
        url: errorContext.url,
        method: errorContext.method,
        userAgent: errorContext.userAgent,
        ip: errorContext.ip
      },
      context: customError.context
    };

    switch (logLevel) {
      case 'error':
        requestLogger.error(
          `API Error: ${customError.message}`,
          error instanceof Error ? error : undefined,
          logContext
        );
        break;
      case 'warn':
        requestLogger.warn(`API Warning: ${customError.message}`, logContext);
        break;
      case 'info':
        requestLogger.info(`API Info: ${customError.message}`, logContext);
        break;
    }
  }

  // åˆ›å»ºé”™è¯¯å“åº”
  return createErrorResponse(customError, traceId);
};

/**
 * APIè·¯ç”±é”™è¯¯å¤„ç†è£…é¥°å™¨
 */
export const withErrorHandler = <T extends any[], R>(
  handler: (...args: T) => Promise<R>
) => {
  return async (...args: T): Promise<R | NextResponse> => {
    try {
      return await handler(...args);
    } catch (error) {
      const request = args[0] as NextRequest;
      return errorHandlerMiddleware(request, error);
    }
  };
};

/**
 * å¼‚æ­¥æ“ä½œé”™è¯¯å¤„ç†è£…é¥°å™¨
 */
export const withAsyncErrorHandler = <T extends any[], R>(
  operation: (...args: T) => Promise<R>,
  context?: ErrorContext
) => {
  return async (...args: T): Promise<R> => {
    try {
      return await operation(...args);
    } catch (error) {
      if (error instanceof CustomError) {
        throw context ? error.withContext(context) : error;
      }

      if (error instanceof Error) {
        throw CustomError.fromError(error, ErrorCode.INTERNAL_SERVER_ERROR, context);
      }

      throw new CustomError(
        ErrorCode.UNKNOWN_ERROR,
        'An unknown error occurred',
        undefined,
        context
      );
    }
  };
};

/**
 * é”™è¯¯è¾¹ç•Œå¤„ç†å‡½æ•°
 */
export const handleErrorBoundary = (
  error: Error,
  errorInfo: { componentStack: string }
): void => {
  const customError = CustomError.fromError(error, ErrorCode.INTERNAL_SERVER_ERROR, {
    componentStack: errorInfo.componentStack
  });

  logger.error('React Error Boundary caught an error', error, {
    metadata: {
      code: customError.code,
      type: customError.type,
      componentStack: errorInfo.componentStack
    }
  });
};

/**
 * æœªå¤„ç†çš„Promiseæ‹’ç»å¤„ç†
 */
export const handleUnhandledRejection = (reason: any, promise: Promise<any>): void => {
  const error = reason instanceof Error ? reason : new Error(String(reason));
  const customError = CustomError.fromError(error, ErrorCode.INTERNAL_SERVER_ERROR, {
    unhandledRejection: true,
    promise: promise.toString()
  });

  logger.error('Unhandled Promise Rejection', error, {
    metadata: {
      code: customError.code,
      type: customError.type,
      unhandledRejection: true
    }
  });
};

/**
 * æœªæ•è·å¼‚å¸¸å¤„ç†
 */
export const handleUncaughtException = (error: Error): void => {
  const customError = CustomError.fromError(error, ErrorCode.INTERNAL_SERVER_ERROR, {
    uncaughtException: true
  });

  logger.error('Uncaught Exception', error, {
    metadata: {
      code: customError.code,
      type: customError.type,
      uncaughtException: true
    }
  });

  // åœ¨ç”Ÿäº§ç¯å¢ƒä¸­ï¼Œè®°å½•é”™è¯¯åä¼˜é›…é€€å‡º
  if (process.env.NODE_ENV === 'production') {
    process.exit(1);
  }
};

/**
 * åˆå§‹åŒ–å…¨å±€é”™è¯¯å¤„ç†
 */
export const initializeGlobalErrorHandling = (): void => {
  // å¤„ç†æœªå¤„ç†çš„Promiseæ‹’ç»
  process.on('unhandledRejection', handleUnhandledRejection);

  // å¤„ç†æœªæ•è·çš„å¼‚å¸¸
  process.on('uncaughtException', handleUncaughtException);

  logger.info('Global error handling initialized');
};

/**
 * é”™è¯¯å¤„ç†ä¸­é—´ä»¶å¥åº·æ£€æŸ¥
 */
export const checkErrorHandlerHealth = (): {
  healthy: boolean;
  config: ErrorHandlerConfig;
  rateLimiterStats: {
    activeClients: number;
    totalErrors: number;
  };
} => {
  const config = getConfig();

  // è®¡ç®—é¢‘ç‡é™åˆ¶å™¨ç»Ÿè®¡ä¿¡æ¯
  const rateLimiterStats = {
    activeClients: errorRateLimiter['errorCounts'].size,
    totalErrors: Array.from(errorRateLimiter['errorCounts'].values())
      .reduce((total, errors) => total + errors.length, 0)
  };

  return {
    healthy: config.enabled,
    config,
    rateLimiterStats
  };
};

/**
 * è¯·æ±‚æ—¥å¿—ä¸­é—´ä»¶
 */
import { NextRequest, NextResponse } from 'next/server';
import { v4 as uuidv4 } from 'uuid';
import { logger, createTracedLogger } from '@/lib/logging/logger';
import { extractRequestContext, createTimer } from '@/lib/logging/utils';
import { LOG_TAGS } from '@/lib/logging/config';

/**
 * è¯·æ±‚æ—¥å¿—ä¸­é—´ä»¶é…ç½®
 */
interface LoggingMiddlewareConfig {
  enabled: boolean;
  logRequests: boolean;
  logResponses: boolean;
  logHeaders: boolean;
  logBody: boolean;
  excludePaths: string[];
  sensitiveHeaders: string[];
  maxBodySize: number;
}

/**
 * é»˜è®¤é…ç½®
 */
const defaultConfig: LoggingMiddlewareConfig = {
  enabled: true,
  logRequests: true,
  logResponses: true,
  logHeaders: false,
  logBody: false,
  excludePaths: [
    '/api/health',
    '/favicon.ico',
    '/_next',
    '/static'
  ],
  sensitiveHeaders: [
    'authorization',
    'cookie',
    'x-api-key',
    'x-auth-token'
  ],
  maxBodySize: 1024 * 10 // 10KB
};

/**
 * è·å–é…ç½®
 */
const getConfig = (): LoggingMiddlewareConfig => {
  const env = process.env.NODE_ENV || 'development';

  if (env === 'test') {
    return {
      ...defaultConfig,
      enabled: false
    };
  }

  if (env === 'production') {
    return {
      ...defaultConfig,
      logHeaders: false,
      logBody: false
    };
  }

  return defaultConfig;
};

/**
 * æ£€æŸ¥è·¯å¾„æ˜¯å¦åº”è¯¥è¢«æ’é™¤
 */
const shouldExcludePath = (pathname: string, excludePaths: string[]): boolean => {
  return excludePaths.some(path => pathname.startsWith(path));
};

/**
 * è¿‡æ»¤æ•æ„Ÿå¤´ä¿¡æ¯
 */
const filterSensitiveHeaders = (
  headers: Headers, 
  sensitiveHeaders: string[]
): Record<string, string> => {
  const filtered: Record<string, string> = {};

  headers.forEach((value, key) => {
    const lowerKey = key.toLowerCase();
    if (sensitiveHeaders.includes(lowerKey)) {
      filtered[key] = '[REDACTED]';
    } else {
      filtered[key] = value;
    }
  });

  return filtered;
};

/**
 * å®‰å…¨åœ°è¯»å–è¯·æ±‚ä½“
 */
const safeReadBody = async (
  request: NextRequest, 
  maxSize: number
): Promise<string | null> => {
  try {
    const contentType = request.headers.get('content-type') || '';

    // åªè®°å½•æ–‡æœ¬ç±»å‹çš„è¯·æ±‚ä½“
    if (!contentType.includes('application/json') && 
        !contentType.includes('application/x-www-form-urlencoded') &&
        !contentType.includes('text/')) {
      return null;
    }

    const body = await request.text();

    if (body.length > maxSize) {
      return body.substring(0, maxSize) + '... [TRUNCATED]';
    }

    return body;
  } catch (error) {
    return null;
  }
};

/**
 * è¯·æ±‚æ—¥å¿—ä¸­é—´ä»¶
 */
export const loggingMiddleware = async (
  request: NextRequest,
  next: () => Promise<NextResponse>
): Promise<NextResponse> => {
  const config = getConfig();

  if (!config.enabled) {
    return next();
  }

  const { pathname } = new URL(request.url);

  // æ£€æŸ¥æ˜¯å¦åº”è¯¥æ’é™¤æ­¤è·¯å¾„
  if (shouldExcludePath(pathname, config.excludePaths)) {
    return next();
  }

  // ç”Ÿæˆè¿½è¸ªID
  const traceId = request.headers.get('x-trace-id') || uuidv4();
  const requestId = uuidv4();

  // åˆ›å»ºå¸¦è¿½è¸ªIDçš„æ—¥å¿—å™¨
  const requestLogger = createTracedLogger(traceId, { requestId });

  // æå–è¯·æ±‚ä¸Šä¸‹æ–‡
  const requestContext = extractRequestContext(request);

  // å¼€å§‹æ€§èƒ½è®¡æ—¶
  const timer = createTimer(`${request.method} ${pathname}`, requestLogger, requestContext);

  // è®°å½•è¯·æ±‚å¼€å§‹
  if (config.logRequests) {
    const logContext = {
      ...requestContext,
      tag: LOG_TAGS.API,
      metadata: {
        method: request.method,
        url: request.url,
        pathname,
        query: Object.fromEntries(new URL(request.url).searchParams),
        headers: config.logHeaders ? 
          filterSensitiveHeaders(request.headers, config.sensitiveHeaders) : 
          undefined,
        body: config.logBody ? 
          await safeReadBody(request, config.maxBodySize) : 
          undefined
      }
    };

    requestLogger.http(`Request started: ${request.method} ${pathname}`, logContext);
  }

  let response: NextResponse;
  let error: Error | null = null;

  try {
    // æ‰§è¡Œä¸‹ä¸€ä¸ªä¸­é—´ä»¶/å¤„ç†å™¨
    response = await next();

    // æ·»åŠ è¿½è¸ªå¤´åˆ°å“åº”
    response.headers.set('x-trace-id', traceId);
    response.headers.set('x-request-id', requestId);

  } catch (err) {
    error = err instanceof Error ? err : new Error('Unknown error');

    // è®°å½•é”™è¯¯
    requestLogger.error(`Request failed: ${request.method} ${pathname}`, error, {
      ...requestContext,
      tag: LOG_TAGS.API
    });

    // é‡æ–°æŠ›å‡ºé”™è¯¯
    throw error;
  } finally {
    // ç»“æŸè®¡æ—¶
    const duration = timer.end({
      metadata: {
        status: response?.status,
        success: !error && response?.status < 400
      }
    });

    // è®°å½•å“åº”
    if (config.logResponses && response) {
      const logContext = {
        ...requestContext,
        tag: LOG_TAGS.API,
        metadata: {
          method: request.method,
          url: request.url,
          pathname,
          status: response.status,
          duration,
          headers: config.logHeaders ? 
            Object.fromEntries(response.headers.entries()) : 
            undefined
        },
        performance: {
          duration
        }
      };

      const level = response.status >= 500 ? 'error' : 
                   response.status >= 400 ? 'warn' : 'info';

      const message = `Request completed: ${request.method} ${pathname} ${response.status} ${duration}ms`;

      switch (level) {
        case 'error':
          requestLogger.error(message, undefined, logContext);
          break;
        case 'warn':
          requestLogger.warn(message, logContext);
          break;
        default:
          requestLogger.http(message, logContext);
      }
    }

    // è®°å½•è®¿é—®æ—¥å¿—
    if (response) {
      requestLogger.access(
        request.method,
        pathname,
        response.status,
        duration,
        requestContext
      );
    }
  }

  return response;
};

/**
 * APIè·¯ç”±æ—¥å¿—è£…é¥°å™¨
 */
export const withRequestLogging = <T extends any[], R>(
  handler: (...args: T) => Promise<R>,
  operationName?: string
) => {
  return async (...args: T): Promise<R> => {
    const request = args[0] as NextRequest;
    const traceId = request?.headers?.get('x-trace-id') || uuidv4();
    const requestContext = request ? extractRequestContext(request) : {};

    const requestLogger = createTracedLogger(traceId, requestContext);
    const operation = operationName || 'API Handler';
    const timer = createTimer(operation, requestLogger, requestContext);

    try {
      requestLogger.debug(`${operation} started`, {
        ...requestContext,
        tag: LOG_TAGS.API
      });

      const result = await handler(...args);

      timer.end({
        metadata: { success: true }
      });

      requestLogger.debug(`${operation} completed successfully`, {
        ...requestContext,
        tag: LOG_TAGS.API
      });

      return result;
    } catch (error) {
      timer.end({
        metadata: { success: false }
      });

      requestLogger.error(`${operation} failed`, error instanceof Error ? error : new Error('Unknown error'), {
        ...requestContext,
        tag: LOG_TAGS.API
      });

      throw error;
    }
  };
};

/**
 * ä¸­é—´ä»¶å¥åº·æ£€æŸ¥
 */
export const checkLoggingMiddlewareHealth = (): {
  healthy: boolean;
