'use client';

import Link from 'next/link';
import { useRouter, useSearchParams } from 'next/navigation';
import React, { useState, useEffect } from 'react';

export default function VerifyEmailPage() {
  const router = useRouter();
  const searchParams = useSearchParams();
  const [verificationStatus, setVerificationStatus] = useState<'loading' | 'success' | 'error' | 'expired'>('loading');
  const [error, setError] = useState<string | null>(null);
  const [canResend, setCanResend] = useState(false);
  const [resendCooldown, setResendCooldown] = useState(0);

  const token = searchParams.get('token');
  const email = searchParams.get('email');

  useEffect(() => {
    if (token) {
      // token
      verifyEmail(token);
    } else {
      // token
      setVerificationStatus('loading');
      setCanResend(true);
    }
  }, [token]);

  const verifyEmail = async (verificationToken: string) => {
    try {
      const response = await fetch('/api/auth/verify-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          token: verificationToken,
        }),
      });

      const result = await response.json();

      if (response.ok && result.success) {
        setVerificationStatus('success');
        // 3
        setTimeout(() => {
          router.push('/auth/login?verified=true');
        }, 3000);
      } else {
        if (result.error === 'Token expired') {
          setVerificationStatus('expired');
        } else {
          setVerificationStatus('error');
          setError(result.error || '');
        }
      }
    } catch (err) {
      setVerificationStatus('error');
      setError('');
    }
  };

  const resendVerificationEmail = async () => {
    if (!email || resendCooldown > 0) return;

    try {
      const response = await fetch('/api/auth/resend-verification', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email,
        }),
      });

      const result = await response.json();

      if (response.ok && result.success) {
        // 60
        let countdown = 60;
        setResendCooldown(countdown);
        const timer = setInterval(() => {
          countdown -= 1;
          if (countdown <= 0) {
            clearInterval(timer);
            setResendCooldown(0);
          } else {
            setResendCooldown(countdown);
          }
        }, 1000);
      } else {
        setError(result.error || '');
      }
    } catch (err) {
      setError('');
    }
  };

  const renderContent = () => {
    switch (verificationStatus) {
      case 'loading':
        if (token) {
          return (
            <div className="text-center">
              <div className="mx-auto flex items-center justify-center h-16 w-16 rounded-full bg-blue-100 mb-6">
                <svg className="animate-spin h-8 w-8 text-blue-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-4">
                ...
              </h3>
              <p className="text-gray-600">

              </p>
            </div>
          );
        } else {
          return (
            <div className="text-center">
              <div className="mx-auto flex items-center justify-center h-16 w-16 rounded-full bg-yellow-100 mb-6">
                <svg className="h-8 w-8 text-yellow-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                </svg>
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-4">

              </h3>
              <p className="text-gray-600 mb-6">

              </p>
              {email && (
                <p className="text-sm text-gray-500 mb-6">
                  <span className="font-medium">{email}</span>
                </p>
              )}
              <div className="space-y-3">
                <button
                  onClick={resendVerificationEmail}
                  disabled={!canResend || resendCooldown > 0}
                  className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
                    !canResend || resendCooldown > 0
                      ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
                      : 'bg-blue-600 hover:bg-blue-700 text-white'
                  }`}
                >
                  {resendCooldown > 0 ? ` (${resendCooldown}s)` : ''}
                </button>
              </div>
            </div>
          );
        }

      case 'success':
        return (
          <div className="text-center">
            <div className="mx-auto flex items-center justify-center h-16 w-16 rounded-full bg-green-100 mb-6">
              <svg className="h-8 w-8 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
              </svg>
            </div>
            <h3 className="text-lg font-semibold text-gray-900 mb-4">

            </h3>
            <p className="text-gray-600 mb-6">

            </p>
            <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
              <p className="text-sm text-green-800">
                 AI31
              </p>
            </div>
            <p className="text-sm text-gray-500 mb-6">
              3...
            </p>
            <Link
              href="/auth/login"
              className="inline-block bg-green-600 hover:bg-green-700 text-white font-medium py-3 px-6 rounded-lg transition-colors"
            >

            </Link>
          </div>
        );

      case 'expired':
        return (
          <div className="text-center">
            <div className="mx-auto flex items-center justify-center h-16 w-16 rounded-full bg-orange-100 mb-6">
              <svg className="h-8 w-8 text-orange-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h3 className="text-lg font-semibold text-gray-900 mb-4">

            </h3>
            <p className="text-gray-600 mb-6">

            </p>
            <div className="space-y-3">
              <button
                onClick={resendVerificationEmail}
                disabled={resendCooldown > 0}
                className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
                  resendCooldown > 0
                    ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
                    : 'bg-orange-600 hover:bg-orange-700 text-white'
                }`}
              >
                {resendCooldown > 0 ? ` (${resendCooldown}s)` : ''}
              </button>
            </div>
          </div>
        );

      case 'error':
        return (
          <div className="text-center">
            <div className="mx-auto flex items-center justify-center h-16 w-16 rounded-full bg-red-100 mb-6">
              <svg className="h-8 w-8 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
              </svg>
            </div>
            <h3 className="text-lg font-semibold text-gray-900 mb-4">

            </h3>
            <p className="text-gray-600 mb-6">
              {error || ''}
            </p>
            <div className="space-y-3">
              <button
                onClick={resendVerificationEmail}
                disabled={resendCooldown > 0}
                className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
                  resendCooldown > 0
                    ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
                    : 'bg-red-600 hover:bg-red-700 text-white'
                }`}
              >
                {resendCooldown > 0 ? ` (${resendCooldown}s)` : ''}
              </button>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-cyan-100 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        {/*  */}
        <div className="text-center">
          <Link href="/" className="inline-block">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">
              Inspi.AI
            </h1>
          </Link>
          <h2 className="text-xl font-semibold text-gray-700 mb-2">

          </h2>
        </div>

        {/*  */}
        <div className="bg-white rounded-2xl shadow-xl p-8">
          {renderContent()}

          {/*  */}
          {error && verificationStatus !== 'error' && (
            <div className="mt-4 bg-red-50 border border-red-200 rounded-lg p-4">
              <div className="flex">
                <div className="flex-shrink-0">
                  <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                  </svg>
                </div>
                <div className="ml-3">
                  <p className="text-sm text-red-800">{error}</p>
                </div>
              </div>
            </div>
          )}

          {/*  */}
          <div className="mt-6 text-center">
            <p className="text-sm text-gray-600">
              {' '}
              <Link
                href="/support"
                className="font-medium text-blue-600 hover:text-blue-500 transition-colors"
              >

              </Link>
            </p>
          </div>
        </div>

        {/*  */}
        <div className="text-center">
          <Link
            href="/"
            className="text-sm text-gray-500 hover:text-gray-700 transition-colors"
          >

          </Link>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useEffect } from 'react';

import { AppLayout } from '@/components/layout/AppLayout';
import { paymentService } from '@/core/subscription/payment-service';
import { subscriptionService } from '@/core/subscription/subscription-service';
import { PaymentStatusIndicator, PaymentAmount } from '@/features/subscription/PaymentPage';
import { useAuthContext } from '@/shared/contexts/AuthContext';
import { Subscription, PaymentRecord } from '@/shared/types/subscription';

export default function SubscriptionManagePage() {
  const { user } = useAuthContext();
  const [currentSubscription, setCurrentSubscription] = useState<Subscription | null>(null);
  const [paymentHistory, setPaymentHistory] = useState<PaymentRecord[]>([]);
  const [subscriptionHistory, setSubscriptionHistory] = useState<Subscription[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'overview' | 'payments' | 'history'>('overview');

  useEffect(() => {
    if (user) {
      loadSubscriptionData();
    }
  }, [user]);

  const loadSubscriptionData = async () => {
    try {
      setIsLoading(true);

      const userId = (user?.id || (user as any)?._id) || 'test-user-123';

      // 
      const subscription = await subscriptionService.getCurrentSubscription(userId);
      setCurrentSubscription(subscription);

      // 
      const paymentsResult = await paymentService.getUserPaymentHistory(userId, 10);
      setPaymentHistory(paymentsResult.payments);

      // 
      const subscriptionsResult = await subscriptionService.getSubscriptionHistory(userId);
      setSubscriptionHistory(subscriptionsResult.subscriptions);

    } catch (error) {
      console.error(':', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancelSubscription = async () => {
    if (!currentSubscription) return;

    if (!confirm('')) {
      return;
    }

    try {
      setActionLoading('cancel');

      const result = await subscriptionService.cancelSubscription(
        currentSubscription.id,
        '',
      );

      setCurrentSubscription(result);
      alert('');

    } catch (error) {
      console.error(':', error);
      alert('');
    } finally {
      setActionLoading(null);
    }
  };

  const handleRenewSubscription = async () => {
    if (!currentSubscription) return;

    try {
      setActionLoading('renew');

      const result = await subscriptionService.reactivateSubscription(currentSubscription.id);

      setCurrentSubscription(result);
      alert('');

    } catch (error) {
      console.error(':', error);
      alert('');
    } finally {
      setActionLoading(null);
    }
  };

  if (isLoading) {
    return (
      <AppLayout>
        <div className="min-h-screen bg-gray-50 flex items-center justify-center">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">...</p>
          </div>
        </div>
      </AppLayout>
    );
  }

  return (
    <AppLayout>
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-6xl mx-auto px-4">
        {/*  */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2"></h1>
          <p className="text-gray-600"></p>
        </div>

        {/*  */}
        <div className="bg-white rounded-lg shadow-sm mb-6">
          <div className="border-b border-gray-200">
            <nav className="flex space-x-8 px-6">
              {[
                { key: 'overview', label: '', icon: '' },
                { key: 'payments', label: '', icon: '' },
                { key: 'history', label: '', icon: '' },
              ].map((tab) => (
                <button
                  key={tab.key}
                  onClick={() => setActiveTab(tab.key as any)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm ${
                    activeTab === tab.key
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <span className="mr-2">{tab.icon}</span>
                  {tab.label}
                </button>
              ))}
            </nav>
          </div>
        </div>

        {/*  */}
        {activeTab === 'overview' && (
          <div className="space-y-6">
            {currentSubscription ? (
              <>
                {/*  */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h2 className="text-xl font-semibold text-gray-900 mb-6"></h2>

                  <div className="grid md:grid-cols-2 gap-8">
                    {/*  */}
                    <div>
                      <div className="flex items-center mb-4">
                        <div className="bg-blue-100 rounded-full p-3 mr-4">
                          <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                          </svg>
                        </div>
                        <div>
                          <h3 className="text-lg font-medium text-gray-900">{currentSubscription.planName}</h3>
                          <p className="text-sm text-gray-500">
                            : <span className={`font-medium ${
                              currentSubscription.status === 'active' ? 'text-green-600' :
                              currentSubscription.status === 'cancelled' ? 'text-red-600' : 'text-yellow-600'
                            }`}>
                              {currentSubscription.status === 'active' ? '' :
                               currentSubscription.status === 'cancelled' ? '' :
                               currentSubscription.status === 'expired' ? '' :
                               currentSubscription.status}
                            </span>
                          </p>
                        </div>
                      </div>

                      <div className="space-y-3 text-sm">
                        <div className="flex justify-between">
                          <span className="text-gray-600">:</span>
                          <span className="font-medium">{currentSubscription.monthlyPrice}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">:</span>
                          <span className="font-medium">{new Date(currentSubscription.startDate).toLocaleDateString()}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">:</span>
                          <span className="font-medium">{new Date(currentSubscription.endDate).toLocaleDateString()}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">:</span>
                          <span className="font-medium">
                            {currentSubscription.nextBillingDate
                              ? new Date(currentSubscription.nextBillingDate).toLocaleDateString()
                              : ''
                            }
                          </span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">:</span>
                          <span className="font-medium text-blue-600">
                            {currentSubscription.nextBillingDate
                              ? Math.max(0, Math.ceil((new Date(currentSubscription.nextBillingDate).getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24)))
                              : 0
                            } 
                          </span>
                        </div>
                      </div>
                    </div>

                    {/*  */}
                    <div>
                      <h4 className="font-medium text-gray-900 mb-4"></h4>
                      <div className="space-y-3 text-sm">
                        <div className="flex justify-between">
                          <span className="text-gray-600">:</span>
                          <span className="font-medium">
                            {currentSubscription.quotas.dailyCreateQuota === -1 ? '' : currentSubscription.quotas.dailyCreateQuota}
                          </span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">:</span>
                          <span className="font-medium">
                            {currentSubscription.quotas.dailyReuseQuota === -1 ? '' : currentSubscription.quotas.dailyReuseQuota}
                          </span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">:</span>
                          <span className="font-medium">
                            {currentSubscription.quotas.maxExportsPerDay === -1 ? '' : currentSubscription.quotas.maxExportsPerDay}
                          </span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">:</span>
                          <span className="font-medium text-green-600"></span>
                        </div>
                      </div>

                      <div className="mt-6">
                        <h4 className="font-medium text-gray-900 mb-3"></h4>
                        <div className="flex flex-wrap gap-2">
                          {currentSubscription.features.map((feature, index) => (
                            <span key={index} className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                              {feature}
                            </span>
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/*  */}
                  {currentSubscription.nextBillingDate &&
                   new Date(currentSubscription.nextBillingDate).getTime() - new Date().getTime() < 7 * 24 * 60 * 60 * 1000 && (
                    <div className="mt-6 bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                      <div className="flex">
                        <svg className="w-5 h-5 text-yellow-400 mr-3 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                        </svg>
                        <div>
                          <h4 className="text-yellow-800 font-medium"></h4>
                          <p className="text-yellow-700 text-sm mt-1">
                             {currentSubscription.nextBillingDate
                              ? Math.max(0, Math.ceil((new Date(currentSubscription.nextBillingDate).getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24)))
                              : 0
                            } 
                          </p>
                        </div>
                      </div>
                    </div>
                  )}

                  {/*  */}
                  <div className="mt-8 pt-6 border-t border-gray-200">
                    <div className="flex flex-wrap gap-4">
                      <button
                        onClick={handleRenewSubscription}
                        disabled={actionLoading === 'renew'}
                        className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        {actionLoading === 'renew' ? '...' : ''}
                      </button>

                      <button
                        onClick={() => window.location.href = '/subscription/upgrade'}
                        className="px-6 py-2 border border-blue-600 text-blue-600 rounded-lg hover:bg-blue-50"
                      >

                      </button>

                      <button
                        onClick={handleCancelSubscription}
                        disabled={actionLoading === 'cancel' || currentSubscription.status === 'cancelled'}
                        className="px-6 py-2 border border-red-300 text-red-700 rounded-lg hover:bg-red-50 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        {actionLoading === 'cancel' ? '...' : ''}
                      </button>
                    </div>
                  </div>
                </div>
              </>
            ) : (
              /*  */
              <div className="bg-white rounded-lg shadow-sm p-8 text-center">
                <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2"></h3>
                <p className="text-gray-600 mb-6"></p>
                <button
                  onClick={() => window.location.href = '/subscription/plans'}
                  className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                >

                </button>
              </div>
            )}
          </div>
        )}

        {/*  */}
        {activeTab === 'payments' && (
          <div className="bg-white rounded-lg shadow-sm">
            <div className="p-6 border-b border-gray-200">
              <h2 className="text-xl font-semibold text-gray-900"></h2>
            </div>

            {paymentHistory.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {paymentHistory.map((payment) => (
                      <tr key={payment.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-mono text-gray-900">
                          {payment.id}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <PaymentAmount amount={payment.amount} />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <PaymentStatusIndicator status={payment.status} />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {payment.paymentMethod === 'wechat_pay' ? '' : payment.paymentMethod}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {new Date(payment.createdAt).toLocaleString()}
                        </td>
                      </tr>
                    ))}
