'use client';

import React, { useState, useEffect } from 'react';

import { PlanConfig } from '@/core/subscription/plan-model';
import { planService } from '@/core/subscription/plan-service';
import { subscriptionService } from '@/core/subscription/subscription-service';
import { useAuthContext } from '@/shared/contexts/AuthContext';
import { UserTier, Subscription } from '@/shared/types/subscription';

export default function PricingPage() {
  const { user } = useAuthContext();
  const [plans, setPlans] = useState<PlanConfig[]>([]);
  const [currentSubscription, setCurrentSubscription] = useState<Subscription | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedPlan, setSelectedPlan] = useState<string | null>(null);
  const [billingCycle, setBillingCycle] = useState<'monthly' | 'yearly'>('monthly');
  const [isProcessing, setIsProcessing] = useState(false);

  useEffect(() => {
    loadData();
  }, [user]);

  const loadData = async () => {
    try {
      setIsLoading(true);

      // 
      const plansResult = await planService.queryPlans({
        status: 'active',
        sortBy: 'tier',
      });
      setPlans(plansResult.plans);

      // 
      if (user) {
        const subscription = await subscriptionService.getCurrentSubscription((user.id || (user as any)._id));
        setCurrentSubscription(subscription);
      }

    } catch (error) {
      console.error(':', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSelectPlan = async (planId: string) => {
    if (!user) {
      alert('');
      return;
    }

    try {
      setIsProcessing(true);
      setSelectedPlan(planId);

      // 
      if (currentSubscription) {
        const result = await subscriptionService.upgradeSubscription(
          currentSubscription.id,
          planId,
        );

        if (result.paymentInfo) {
          // 
          window.location.href = `/payment/${result.paymentInfo.paymentId}`;
        } else {
          // 
          alert('');
          await loadData();
        }
      } else {
        // 
        const result = await subscriptionService.createSubscription({
          userId: (user.id || (user as any)._id),
          planId,
          paymentMethod: 'wechat_pay',
          billingCycle,
        });

        // 
        window.location.href = `/payment/${result.paymentInfo.paymentId}`;
      }

    } catch (error) {
      console.error(':', error);
      alert('');
    } finally {
      setIsProcessing(false);
      setSelectedPlan(null);
    }
  };

  const formatPrice = (plan: PlanConfig) => {
    if (plan.monthlyPrice === 0) return '';

    const price = billingCycle === 'yearly' && plan.yearlyPrice
      ? plan.yearlyPrice / 12
      : plan.monthlyPrice;

    return `${price}`;
  };

  const getSavings = (plan: PlanConfig) => {
    if (!plan.yearlyPrice || plan.monthlyPrice === 0) return null;

    const monthlyCost = plan.monthlyPrice * 12;
    const savings = Math.round((1 - plan.yearlyPrice / monthlyCost) * 100);

    return savings > 0 ? `${savings}%` : null;
  };

  const isCurrentPlan = (planId: string) => {
    return currentSubscription?.planId === planId;
  };

  const canUpgrade = (tier: UserTier) => {
    if (!currentSubscription) return true;

    const tierOrder = { free: 0, basic: 1, pro: 2, admin: 3 };
    const currentOrder = tierOrder[currentSubscription.tier];
    const targetOrder = tierOrder[tier];

    return targetOrder > currentOrder;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="max-w-7xl mx-auto px-4">
        {/*  */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4"></h1>
          <p className="text-xl text-gray-600 mb-8">

          </p>

          {/*  */}
          <div className="inline-flex items-center bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => setBillingCycle('monthly')}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                billingCycle === 'monthly'
                  ? 'bg-white text-gray-900 shadow-sm'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >

            </button>
            <button
              onClick={() => setBillingCycle('yearly')}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                billingCycle === 'yearly'
                  ? 'bg-white text-gray-900 shadow-sm'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >

              <span className="ml-1 text-xs text-green-600 font-semibold"></span>
            </button>
          </div>
        </div>

        {/*  */}
        <div className="grid md:grid-cols-3 gap-8 max-w-5xl mx-auto">
          {plans.map((plan) => {
            const savings = billingCycle === 'yearly' ? getSavings(plan) : null;
            const isCurrent = isCurrentPlan(plan.id);
            const canSelect = canUpgrade(plan.tier);

            return (
              <div
                key={plan.id}
                className={`relative bg-white rounded-2xl shadow-lg overflow-hidden ${
                  plan.popular ? 'ring-2 ring-blue-500' : ''
                } ${isCurrent ? 'ring-2 ring-green-500' : ''}`}
              >
                {/*  */}
                {plan.popular && !isCurrent && (
                  <div className="absolute top-0 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                    <span className="bg-blue-500 text-white px-4 py-1 rounded-full text-sm font-medium">

                    </span>
                  </div>
                )}

                {/*  */}
                {isCurrent && (
                  <div className="absolute top-0 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                    <span className="bg-green-500 text-white px-4 py-1 rounded-full text-sm font-medium">

                    </span>
                  </div>
                )}

                <div className="p-8">
                  {/*  */}
                  <div className="text-center mb-8">
                    <h3 className="text-2xl font-bold text-gray-900 mb-2">{plan.name}</h3>
                    <div className="mb-4">
                      <span className="text-4xl font-bold text-gray-900">
                        {formatPrice(plan)}
                      </span>
                      {plan.monthlyPrice > 0 && (
                        <span className="text-gray-600 ml-1">
                          /{billingCycle === 'yearly' ? '' : ''}
                        </span>
                      )}
                    </div>

                    {savings && (
                      <div className="inline-flex items-center px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm font-medium">
                        <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
                        </svg>
                        {savings}
                      </div>
                    )}

                    <p className="text-gray-600 mt-4">{plan.description}</p>
                  </div>

                  {/*  */}
                  <div className="mb-8">
                    <h4 className="font-semibold text-gray-900 mb-4">:</h4>
                    <ul className="space-y-3">
                      {plan.features.map((feature, index) => (
                        <li key={index} className="flex items-center text-sm text-gray-600">
                          <svg className="w-5 h-5 text-green-500 mr-3 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                            <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                          </svg>
                          {feature}
                        </li>
                      ))}
                    </ul>
                  </div>

                  {/*  */}
                  <div className="mb-8">
                    <h4 className="font-semibold text-gray-900 mb-4">:</h4>
                    <div className="space-y-2 text-sm text-gray-600">
                      <div className="flex justify-between">
                        <span>:</span>
                        <span className="font-medium">
                          {plan.quotas.dailyCreateQuota === -1 ? '' : plan.quotas.dailyCreateQuota}
                        </span>
                      </div>
                      <div className="flex justify-between">
                        <span>:</span>
                        <span className="font-medium">
                          {plan.quotas.dailyReuseQuota === -1 ? '' : plan.quotas.dailyReuseQuota}
                        </span>
                      </div>
                      <div className="flex justify-between">
                        <span>:</span>
                        <span className="font-medium">
                          {plan.quotas.maxExportsPerDay === -1 ? '' : plan.quotas.maxExportsPerDay}
                        </span>
                      </div>
                    </div>
                  </div>

                  {/*  */}
                  <button
                    onClick={() => handleSelectPlan(plan.id)}
                    disabled={!canSelect || isProcessing || isCurrent}
                    className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
                      isCurrent
                        ? 'bg-green-100 text-green-800 cursor-default'
                        : canSelect
                        ? plan.popular
                          ? 'bg-blue-600 text-white hover:bg-blue-700'
                          : 'bg-gray-900 text-white hover:bg-gray-800'
                        : 'bg-gray-100 text-gray-400 cursor-not-allowed'
                    } ${isProcessing && selectedPlan === plan.id ? 'opacity-50' : ''}`}
                  >
                    {isProcessing && selectedPlan === plan.id ? (
                      <span className="flex items-center justify-center">
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                        ...
                      </span>
                    ) : isCurrent ? (
                      ''
                    ) : canSelect ? (
                      currentSubscription ? '' : ''
                    ) : (
                      ''
                    )}
                  </button>
                </div>
              </div>
            );
          })}
        </div>

        {/*  */}
        <div className="mt-16 max-w-3xl mx-auto">
          <h2 className="text-2xl font-bold text-gray-900 text-center mb-8"></h2>
          <div className="space-y-6">
            <div className="bg-white rounded-lg p-6 shadow-sm">
              <h3 className="font-semibold text-gray-900 mb-2"></h3>
              <p className="text-gray-600">

              </p>
            </div>

            <div className="bg-white rounded-lg p-6 shadow-sm">
              <h3 className="font-semibold text-gray-900 mb-2"></h3>
              <p className="text-gray-600">

              </p>
            </div>

            <div className="bg-white rounded-lg p-6 shadow-sm">
              <h3 className="font-semibold text-gray-900 mb-2"></h3>
              <p className="text-gray-600">

              </p>
            </div>

            <div className="bg-white rounded-lg p-6 shadow-sm">
              <h3 className="font-semibold text-gray-900 mb-2"></h3>
              <p className="text-gray-600">
                217%
              </p>
            </div>
          </div>
        </div>

        {/*  */}
        <div className="mt-16 text-center">
          <div className="bg-blue-50 rounded-lg p-8 max-w-2xl mx-auto">
            <h3 className="text-xl font-semibold text-gray-900 mb-2"></h3>
            <p className="text-gray-600 mb-4">

            </p>
            <div className="flex justify-center space-x-4">
              <button className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">

              </button>
              <button className="px-6 py-2 border border-blue-600 text-blue-600 rounded-lg hover:bg-blue-50">

              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

