'use client';

import {
  EyeIcon,
  PlusIcon,
  MagnifyingGlassIcon,
  ChevronLeftIcon,
  ChevronRightIcon,
} from '@heroicons/react/24/outline';
import React, { useState, useEffect } from 'react';

// 
interface InviteCode {
  id: string;
  code: string;
  inviterName: string;
  inviterEmail: string;
  createdAt: Date;
  usageCount: number;
  maxUsage: number | null;
  isActive: boolean;
  expiresAt: Date | null;
}

interface InviteFilters {
  search: string;
  status: 'all' | 'active' | 'inactive' | 'expired';
  sortBy: 'createdAt' | 'usageCount' | 'code';
  sortOrder: 'asc' | 'desc';
}

interface PaginationInfo {
  page: number;
  limit: number;
  totalPages: number;
  totalCount: number;
}

export function InviteManagement() {
  const [invites, setInvites] = useState<InviteCode[]>([]);
  const [filters, setFilters] = useState<InviteFilters>({
    search: '',
    status: 'all',
    sortBy: 'createdAt',
    sortOrder: 'desc',
  });
  const [pagination, setPagination] = useState<PaginationInfo>({
    page: 1,
    limit: 20,
    totalPages: 1,
    totalCount: 0,
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // 
  useEffect(() => {
    loadInvites();
  }, [filters, pagination.page]);

  const loadInvites = async () => {
    try {
      setIsLoading(true);
      setError(null);

      // API
      await new Promise(resolve => setTimeout(resolve, 800));

      // 
      const mockInvites: InviteCode[] = [
        {
          id: '1',
          code: 'INVITE2024001',
          inviterName: '',
          inviterEmail: 'zhangsan@example.com',
          createdAt: new Date('2024-01-15'),
          usageCount: 3,
          maxUsage: 5,
          isActive: true,
          expiresAt: new Date('2024-12-31'),
        },
        {
          id: '2',
          code: 'INVITE2024002',
          inviterName: '',
          inviterEmail: 'lisi@example.com',
          createdAt: new Date('2024-01-10'),
          usageCount: 1,
          maxUsage: 3,
          isActive: true,
          expiresAt: null,
        },
        {
          id: '3',
          code: 'INVITE2024003',
          inviterName: '',
          inviterEmail: 'wangwu@example.com',
          createdAt: new Date('2024-01-05'),
          usageCount: 5,
          maxUsage: 5,
          isActive: false,
          expiresAt: new Date('2024-06-30'),
        },
      ];

      // 
      let filteredInvites = mockInvites;

      if (filters.search) {
        filteredInvites = filteredInvites.filter(invite =>
          invite.code.toLowerCase().includes(filters.search.toLowerCase()) ||
          invite.inviterName.toLowerCase().includes(filters.search.toLowerCase()) ||
          invite.inviterEmail.toLowerCase().includes(filters.search.toLowerCase()),
        );
      }

      if (filters.status !== 'all') {
        filteredInvites = filteredInvites.filter(invite => {
          switch (filters.status) {
            case 'active':
              return invite.isActive && (!invite.expiresAt || invite.expiresAt > new Date());
            case 'inactive':
              return !invite.isActive;
            case 'expired':
              return invite.expiresAt && invite.expiresAt <= new Date();
            default:
              return true;
          }
        });
      }

      setInvites(filteredInvites);
      const newPagination = {
        ...pagination,
        totalCount: filteredInvites.length,
        totalPages: Math.ceil(filteredInvites.length / pagination.limit),
      };
      setPagination(newPagination);

    } catch (err) {
      console.error(':', err);
      setError('');
    } finally {
      setIsLoading(false);
    }
  };

  // 
  const handleSearch = (value: string) => {
    setFilters({ ...filters, search: value });
    setPagination({ ...pagination, page: 1 });
  };

  // 
  const handleStatusFilter = (status: InviteFilters['status']) => {
    setFilters({ ...filters, status });
    setPagination({ ...pagination, page: 1 });
  };

  // 
  const handlePageChange = (page: number) => {
    setPagination({ ...pagination, page });
  };

  // 
  const formatDate = (date: Date | null) => {
    if (!date) return '';
    return date.toLocaleDateString('zh-CN');
  };

  // 
  const getStatusBadge = (invite: InviteCode) => {
    if (!invite.isActive) {
      return <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-red-100 text-red-800"></span>;
    }
    if (invite.expiresAt && invite.expiresAt <= new Date()) {
      return <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800"></span>;
    }
    if (invite.maxUsage && invite.usageCount >= invite.maxUsage) {
      return <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800"></span>;
    }
    return <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800"></span>;
  };

  // 
  const getUsagePercentage = (invite: InviteCode) => {
    if (!invite.maxUsage) return 0;
    return Math.min((invite.usageCount / invite.maxUsage) * 100, 100);
  };

  if (error) {
    return (
      <div className="text-center py-12">
        <div className="text-red-500">
          <p className="text-lg font-medium"></p>
          <p className="mt-1">{error}</p>
          <button
            onClick={loadInvites}
            className="mt-4 bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 transition-colors"
          >

          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white shadow rounded-lg">
      {/*  */}
      <div className="px-4 py-5 sm:px-6 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg leading-6 font-medium text-gray-900">

            </h3>
            <p className="mt-1 max-w-2xl text-sm text-gray-500">

            </p>
          </div>
          <button className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 transition-colors flex items-center">
            <PlusIcon className="h-4 w-4 mr-2" />

          </button>
        </div>
      </div>

      {/*  */}
      <div className="px-4 py-4 sm:px-6 border-b border-gray-200">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-3 sm:space-y-0 sm:space-x-4">
          {/*  */}
          <div className="flex-1 min-w-0">
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="text"
                className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500"
                placeholder="..."
                value={filters.search}
                onChange={(e) => handleSearch(e.target.value)}
              />
            </div>
          </div>

          {/*  */}
          <div className="flex space-x-2">
            {[
              { key: 'all', label: '' },
              { key: 'active', label: '' },
              { key: 'inactive', label: '' },
              { key: 'expired', label: '' },
            ].map(({ key, label }) => (
              <button
                key={key}
                onClick={() => handleStatusFilter(key as InviteFilters['status'])}
                className={`px-3 py-2 text-sm font-medium rounded-md transition-colors ${
                  filters.status === key
                    ? 'bg-indigo-100 text-indigo-700'
                    : 'text-gray-500 hover:text-gray-700 hover:bg-gray-100'
                }`}
              >
                {label}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/*  */}
      {isLoading ? (
        <div className="text-center py-12">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600 mx-auto mb-4"></div>
          <p className="text-gray-500">...</p>
        </div>
      ) : invites.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">

                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {invites.map((invite) => (
                <tr key={invite.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">
                      {invite.code}
                    </div>
                    <div className="text-sm text-gray-500">
                       {formatDate(invite.createdAt)}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">
                      {invite.inviterName}
                    </div>
                    <div className="text-sm text-gray-500">
                      {invite.inviterEmail}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">
                      {invite.usageCount} / {invite.maxUsage || ''}
                    </div>
                    {invite.maxUsage && (
                      <div className="w-full bg-gray-200 rounded-full h-2 mt-1">
                        <div
                          className="bg-indigo-600 h-2 rounded-full"
                          style={{ width: `${getUsagePercentage(invite)}%` }}
                        ></div>
                      </div>
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {getStatusBadge(invite)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {formatDate(invite.expiresAt)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <button className="text-indigo-600 hover:text-indigo-900 mr-3">
                      <EyeIcon className="h-4 w-4" />
                    </button>
                    <button className="text-gray-600 hover:text-gray-900">

                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="text-center py-12">
          <p className="text-gray-500"></p>
        </div>
      )}

      {/*  */}
      {pagination.totalPages > 1 && (
        <div className="px-4 py-3 border-t border-gray-200 sm:px-6">
          <div className="flex items-center justify-between">
            <div className="flex-1 flex justify-between sm:hidden">
              <button
                onClick={() => handlePageChange(pagination.page - 1)}
                disabled={pagination.page <= 1}
                className="relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >

              </button>
              <button
                onClick={() => handlePageChange(pagination.page + 1)}
                disabled={pagination.page >= pagination.totalPages}
                className="ml-3 relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >

              </button>
            </div>
            <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
              <div>
                <p className="text-sm text-gray-700">
                   <span className="font-medium">{(pagination.page - 1) * pagination.limit + 1}</span> {' '}
                  <span className="font-medium">
                    {Math.min(pagination.page * pagination.limit, pagination.totalCount)}
                  </span>{' '}
                   <span className="font-medium">{pagination.totalCount}</span> 
                </p>
              </div>
              <div>
                <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px">
                  <button
                    onClick={() => handlePageChange(pagination.page - 1)}
                    disabled={pagination.page <= 1}
                    className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <ChevronLeftIcon className="h-5 w-5" />
                  </button>
                  {Array.from({ length: pagination.totalPages }, (_, i) => i + 1).map((page) => (
                    <button
                      key={page}
                      onClick={() => handlePageChange(page)}
                      className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                        page === pagination.page
                          ? 'z-10 bg-indigo-50 border-indigo-500 text-indigo-600'
                          : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50'
                      }`}
                    >
                      {page}
                    </button>
                  ))}
                  <button
                    onClick={() => handlePageChange(pagination.page + 1)}
                    disabled={pagination.page >= pagination.totalPages}
                    className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <ChevronRightIcon className="h-5 w-5" />
                  </button>
                </nav>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import React, { useState, useRef } from 'react';

import {
  exportElementToImage,
  downloadImage,
  copyImageToClipboard,
  exportPresets,
  type ExportOptions,
} from '@/lib/export/html-to-image';
import {
  shareToSocial,
  generateShareLink,
  trackShareEvent,
  sharePlatforms,
  type ShareContent,
  type SharePlatform,
} from '@/lib/share/share-service';

interface ExportSharePanelProps {
  cardElement: HTMLElement | null;
  cardData: {
    id: string;
    title: string;
    content: string;
    type: string;
  };
  className?: string;
}

export function ExportSharePanel({ cardElement, cardData, className = '' }: ExportSharePanelProps) {
  const [isExporting, setIsExporting] = useState(false);
  const [isSharing, setIsSharing] = useState(false);
  const [showExportOptions, setShowExportOptions] = useState(false);
  const [showShareOptions, setShowShareOptions] = useState(false);
  const [exportProgress, setExportProgress] = useState('');

  // 
  const exportFormats = [
    {
      key: 'social',
      name: ' (PNG)',
      description: '800x800 ',
      icon: '',
    },
    {
      key: 'print',
      name: ' (PNG)',
      description: '3',
      icon: '',
    },
    {
      key: 'web',
      name: ' (JPG)',
      description: '',
      icon: '',
    },
    {
      key: 'transparent',
      name: ' (PNG)',
      description: '',
      icon: '',
    },
  ];

  /**
   * 
   */
  const handleExport = async (presetKey: keyof typeof exportPresets) => {
    if (!cardElement) {
      alert('');
      return;
    }

    setIsExporting(true);
    setExportProgress('...');

    try {
      const options = exportPresets[presetKey];
      setExportProgress('...');

      const result = await exportElementToImage(cardElement, options);

      setExportProgress('...');
      downloadImage(result);

      setExportProgress('');

      // 
      console.log(`: ${cardData.id}, : ${presetKey}`);

    } catch (error) {
      console.error(':', error);
      alert(`: ${error instanceof Error ? error.message : ''}`);
    } finally {
      setIsExporting(false);
      setShowExportOptions(false);
      setTimeout(() => setExportProgress(''), 2000);
    }
  };

  /**
   * 
   */
  const handleCopyImage = async () => {
    if (!cardElement) {
      alert('');
      return;
    }

    setIsExporting(true);
    setExportProgress('...');

    try {
      const result = await exportElementToImage(cardElement, exportPresets.social);
      await copyImageToClipboard(result);
      setExportProgress('');
    } catch (error) {
      console.error(':', error);
      alert(`: ${error instanceof Error ? error.message : ''}`);
    } finally {
      setIsExporting(false);
      setTimeout(() => setExportProgress(''), 2000);
    }
  };

  /**
   * 
   */
  const handleShare = async (platform: SharePlatform) => {
    setIsSharing(true);

    try {
      // 
      const shareUrl = await generateShareLink(cardData.id, cardData);

      // 
      const shareContent: ShareContent = {
        title: cardData.title || 'AI',
        description: `${cardData.content.substring(0, 100)}...`,
        url: shareUrl,
        hashtags: ['AI', '', 'InspiAI'],
      };

      // 
      if (['weibo', 'qq'].includes(platform) && cardElement) {
        setExportProgress('...');
        const result = await exportElementToImage(cardElement, exportPresets.social);

        // URL
        // DataURL ()
        shareContent.imageUrl = result.dataUrl;
      }

      // 
      await shareToSocial({ platform, content: shareContent });

      // 
      await trackShareEvent(cardData.id, platform);

    } catch (error) {
      console.error(':', error);
      alert(`: ${error instanceof Error ? error.message : ''}`);
    } finally {
      setIsSharing(false);
      setShowShareOptions(false);
      setExportProgress('');
    }
  };

  return (
    <div className={`export-share-panel ${className}`}>
      {/*  */}
      <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
        {/*  */}
        <button
          onClick={() => handleExport('social')}
          disabled={isExporting || !cardElement}
          style={{
            padding: '8px 16px',
            backgroundColor: '#3b82f6',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            fontSize: '14px',
            fontWeight: '500',
            cursor: isExporting ? 'not-allowed' : 'pointer',
            opacity: isExporting ? 0.6 : 1,
            display: 'flex',
            alignItems: 'center',
            gap: '6px',
          }}
        >
          {isExporting ? '' : ''}
          {isExporting ? '...' : ''}
        </button>


        {/*  */}
        <button
          onClick={() => handleShare('wechat' as SharePlatform)}
          disabled={isSharing}
          style={{
            padding: '8px 16px',
            backgroundColor: '#10b981',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            fontSize: '14px',
            fontWeight: '500',
            cursor: isSharing ? 'not-allowed' : 'pointer',
            opacity: isSharing ? 0.6 : 1,
            display: 'flex',
            alignItems: 'center',
            gap: '6px',
          }}
        >
          {isSharing ? '' : ''}
          {isSharing ? '...' : ''}
        </button>
      </div>

      {/*  */}
      {exportProgress && (
        <div style={{
          marginTop: '8px',
          padding: '8px 12px',
          backgroundColor: '#f3f4f6',
          borderRadius: '6px',
          fontSize: '14px',
          color: '#374151',
        }}>
          {exportProgress}
        </div>
      )}

    </div>
  );
}

import type { User } from '@/shared/hooks/useAuth';
/**
 * 
 * 
 */
'use client';

import Image from 'next/image';
import React, { useState, useEffect } from 'react';

import { useAuth } from '@/shared/hooks/useAuth';

interface Comment {
  _id: string
  content: string
  author: {
    _id: string
    name: string
    avatar?: string
  }
  likesCount: number
  repliesCount: number
  isEdited: boolean
  editedAt?: string
  createdAt: string
  replies?: Comment[]
  userInteractions?: {
    isLiked: boolean
  }
}

interface CommentSectionProps {
  workId: string
  allowComments: boolean
  initialCommentsCount?: number
  className?: string
}

export function CommentSection({
  workId,
  allowComments,
  initialCommentsCount = 0,
  className = '',
}: CommentSectionProps) {
  const { user } = useAuth();

  const [comments, setComments] = useState<Comment[]>([]);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [newComment, setNewComment] = useState('');
  const [replyingTo, setReplyingTo] = useState<string | null>(null);
  const [replyContent, setReplyContent] = useState('');
  const [editingComment, setEditingComment] = useState<string | null>(null);
  const [editContent, setEditContent] = useState('');
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [total, setTotal] = useState(initialCommentsCount);

  // 
  const loadComments = async (reset: boolean = false) => {
    try {
      setLoading(true);

      const currentPage = reset ? 1 : page;
      const response = await fetch(`/api/works/${workId}/comments?page=${currentPage}&limit=20`);
      const result = await response.json();

      if (result.success) {
        if (reset) {
          setComments(result.comments);
          setPage(1);
        } else {
          setComments([...comments, ...result.comments]);
        }

        setTotal(result.total);
        setHasMore(currentPage < result.totalPages);
      }
    } catch (error) {
      console.error('Load comments error:', error);
    } finally {
      setLoading(false);
    }
  };

  // 
  useEffect(() => {
    loadComments(true);
  }, [workId]);

  // 
  const handleSubmitComment = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!user || !newComment.trim()) return;

    try {
      setSubmitting(true);

      const response = await fetch(`/api/works/${workId}/comments`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
        body: JSON.stringify({ content: newComment.trim() }),
      });

      const result = await response.json();

      if (result.success) {
        setComments([result.comment, ...comments]);
        setNewComment('');
        setTotal(total + 1);
      }
    } catch (error) {
      console.error('Submit comment error:', error);
    } finally {
      setSubmitting(false);
    }
  };

  // 
  const handleSubmitReply = async (parentCommentId: string) => {
    if (!user || !replyContent.trim()) return;

    try {
      setSubmitting(true);

      const response = await fetch(`/api/works/${workId}/comments`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
        body: JSON.stringify({
          content: replyContent.trim(),
          parentCommentId,
        }),
      });

      const result = await response.json();

      if (result.success) {
        // 
        const updatedComments = comments.map((comment: any) => {
          if (comment._id === parentCommentId) {
            return {
              ...comment,
              repliesCount: comment.repliesCount + 1,
              replies: [...(comment.replies || []), result.comment],
            };
          }
          return comment;
        });
        setComments(updatedComments);

        setReplyContent('');
        setReplyingTo(null);
      }
    } catch (error) {
      console.error('Submit reply error:', error);
    } finally {
      setSubmitting(false);
    }
  };

  // 
  const handleLikeComment = async (commentId: string) => {
    if (!user) return;

    try {
      const response = await fetch(`/api/comments/${commentId}/like`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });

      if (response.ok) {
        const updatedComments2 = comments.map((comment: any) => {
          if (comment._id === commentId) {
            const isLiked = comment.userInteractions?.isLiked;
            return {
              ...comment,
              likesCount: isLiked ? comment.likesCount - 1 : comment.likesCount + 1,
              userInteractions: {
                ...comment.userInteractions,
                isLiked: !isLiked,
              },
            };
          }
          return comment;
        });
        setComments(updatedComments2);
      }
    } catch (error) {
      console.error('Like comment error:', error);
    }
  };

  // 
  const handleEditComment = async (commentId: string) => {
    if (!editContent.trim()) return;

    try {
      const response = await fetch(`/api/comments/${commentId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
        body: JSON.stringify({ content: editContent.trim() }),
      });

      const result = await response.json();

      if (result.success) {
        const updatedComments = comments.map((comment: any) => {
          if (comment._id === commentId) {
            return {
              ...comment,
              content: editContent.trim(),
              isEdited: true,
              editedAt: new Date().toISOString(),
            };
          }
          return comment;
        });
        setComments(updatedComments);

        setEditingComment(null);
        setEditContent('');
      }
    } catch (error) {
      console.error('Edit comment error:', error);
    }
  };

  // 
  const handleDeleteComment = async (commentId: string) => {
    if (!confirm('')) return;

    try {
      const response = await fetch(`/api/comments/${commentId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });

      if (response.ok) {
        setComments(comments.filter((comment: any) => comment._id !== commentId));
        setTotal(total - 1);
      }
    } catch (error) {
      console.error('Delete comment error:', error);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const minutes = Math.floor(diff / (1000 * 60));
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (minutes < 1) return '';
    if (minutes < 60) return `${minutes}`;
    if (hours < 24) return `${hours}`;
    if (days < 7) return `${days}`;
    return date.toLocaleDateString();
  };

  const renderComment = (comment: Comment, isReply: boolean = false) => (
    <div key={comment._id} className={`${isReply ? 'ml-8 mt-3' : 'mb-6'}`}>
      <div className="flex space-x-3">
        <div className="flex-shrink-0">
          {comment.author.avatar ? (
            <Image
              src={comment.author.avatar}
              alt={comment.author.name}
              width={isReply ? 24 : 32}
              height={isReply ? 24 : 32}
              className="rounded-full"
            />
          ) : (
            <div className={`${isReply ? 'w-6 h-6' : 'w-8 h-8'} bg-gray-300 rounded-full flex items-center justify-center`}>
              <span className={`${isReply ? 'text-xs' : 'text-sm'} text-gray-600`}>
                {comment.author.name.charAt(0)}
              </span>
            </div>
          )}
        </div>

        <div className="flex-1">
          <div className="flex items-center space-x-2 mb-1">
            <span className={`${isReply ? 'text-sm' : 'text-base'} font-medium text-gray-900`}>
              {comment.author.name}
            </span>
            <span className="text-xs text-gray-500">
              {formatDate(comment.createdAt)}
            </span>
            {comment.isEdited && (
              <span className="text-xs text-gray-400">()</span>
            )}
          </div>

          {editingComment === comment._id ? (
            <div className="space-y-2">
              <textarea
                value={editContent}
                onChange={(e) => setEditContent(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows={3}
                maxLength={1000}
              />
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => handleEditComment(comment._id)}
                  className="px-3 py-1 text-sm bg-blue-600 text-white rounded hover:bg-blue-700"
                >

                </button>
                <button
                  onClick={() => {
                    setEditingComment(null);
                    setEditContent('');
                  }}
                  className="px-3 py-1 text-sm text-gray-600 hover:text-gray-800"
                >

                </button>
              </div>
            </div>
          ) : (
            <>
              <p className={`${isReply ? 'text-sm' : 'text-base'} text-gray-700 mb-2`}>
                {comment.content}
              </p>

              <div className="flex items-center space-x-4 text-sm text-gray-500">
                {user && (
                  <>
                    <button
                      onClick={() => handleLikeComment(comment._id)}
                      className={`flex items-center space-x-1 hover:text-red-500 ${
                        comment.userInteractions?.isLiked ? 'text-red-500' : ''
                      }`}
                    >
                      <svg className="w-4 h-4" fill={comment.userInteractions?.isLiked ? 'currentColor' : 'none'} stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                      </svg>
                      <span>{comment.likesCount}</span>
                    </button>

                    {!isReply && (
                      <button
                        onClick={() => setReplyingTo(comment._id)}
                        className="hover:text-blue-500"
                      >

                      </button>
                    )}

                    {comment.author._id === ((user as any)?._id || '') && (
                      <>
                        <button
                          onClick={() => {
                            setEditingComment(comment._id);
                            setEditContent(comment.content);
                          }}
                          className="hover:text-blue-500"
                        >

                        </button>
                        <button
                          onClick={() => handleDeleteComment(comment._id)}
                          className="hover:text-red-500"
                        >

                        </button>
                      </>
                    )}
                  </>
                )}
              </div>
            </>
          )}

          {/*  */}
          {replyingTo === comment._id && (
            <div className="mt-3 space-y-2">
              <textarea
                value={replyContent}
                onChange={(e) => setReplyContent(e.target.value)}
                placeholder={` ${comment.author.name}...`}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows={3}
                maxLength={1000}
              />
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => handleSubmitReply(comment._id)}
                  disabled={submitting || !replyContent.trim()}
                  className="px-3 py-1 text-sm bg-blue-600 text-white rounded hover:bg-blue-700 disabled:opacity-50"
                >
                  {submitting ? '...' : ''}
                </button>
                <button
                  onClick={() => {
                    setReplyingTo(null);
                    setReplyContent('');
                  }}
                  className="px-3 py-1 text-sm text-gray-600 hover:text-gray-800"
                >

                </button>
              </div>
            </div>
          )}

          {/*  */}
          {comment.replies && comment.replies.length > 0 && (
            <div className="mt-4 space-y-3">
              {comment.replies.map(reply => renderComment(reply, true))}

              {comment.repliesCount > comment.replies.length && (
                <button
                  onClick={() => loadMoreReplies(comment._id)}
                  className="ml-8 text-sm text-blue-600 hover:text-blue-800"
                >
                   ({comment.repliesCount - comment.replies.length})
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );

  const loadMoreReplies = async (commentId: string) => {
    try {
      const response = await fetch(`/api/comments/${commentId}/replies`);
      const result = await response.json();

      if (result.success) {
        const updatedComments = comments.map((comment: any) => {
          if (comment._id === commentId) {
            return {
              ...comment,
              replies: result.comments,
            };
          }
          return comment;
        });
        setComments(updatedComments);
      }
    } catch (error) {
      console.error('Load more replies error:', error);
    }
  };

  const handleLoadMore = () => {
    setPage(page + 1);
    loadComments(false);
  };

  return (
    <div className={`bg-white rounded-lg shadow-sm ${className}`}>
      <div className="p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">
           ({total})
        </h3>

        {/*  */}
        {user && allowComments && (
          <form onSubmit={handleSubmitComment} className="mb-6">
            <div className="flex space-x-3">
              <div className="flex-shrink-0">
                {user.avatar ? (
                  <Image
                    src={user.avatar}
                    alt={user.name}
                    width={32}
                    height={32}
                    className="rounded-full"
                  />
                ) : (
                  <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center">
                    <span className="text-sm text-gray-600">
                      {user.name.charAt(0)}
                    </span>
                  </div>
                )}
              </div>
              <div className="flex-1 space-y-2">
                <textarea
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  placeholder="..."
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  rows={3}
                  maxLength={1000}
                />
                <div className="flex items-center justify-between">
                  <span className="text-xs text-gray-500">
                    {newComment.length}/1000
                  </span>
                  <button
                    type="submit"
                    disabled={submitting || !newComment.trim()}
                    className="px-4 py-2 text-sm bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
                  >
                    {submitting ? '...' : ''}
                  </button>
                </div>
              </div>
            </div>
          </form>
        )}

        {!user && allowComments && (
          <div className="mb-6 p-4 bg-gray-50 rounded-lg text-center">
            <p className="text-gray-600 mb-2"></p>
            <a
              href="/auth/login"
              className="text-blue-600 hover:text-blue-800 font-medium"
            >

            </a>
          </div>
        )}

        {!allowComments && (
          <div className="mb-6 p-4 bg-yellow-50 rounded-lg text-center">
            <p className="text-yellow-800"></p>
          </div>
        )}

        {/*  */}
        <div className="space-y-6">
          {comments.map(comment => renderComment(comment))}
        </div>

        {/*  */}
        {hasMore && (
          <div className="text-center mt-6">
            <button
              onClick={handleLoadMore}
              disabled={loading}
              className="px-6 py-2 text-sm text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50"
            >
              {loading ? '...' : ''}
            </button>
          </div>
        )}

        {/*  */}
        {comments.length === 0 && !loading && (
          <div className="text-center py-8">
            <svg className="w-12 h-12 text-gray-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
            </svg>
            <p className="text-gray-600">
              {allowComments ? '' : ''}
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default CommentSection;

/**
 * 
 * 
