'use client';

import { useRouter } from 'next/navigation';
import React, { useState, useEffect } from 'react';

import { WorkService, CreateWorkRequest, UpdateWorkRequest } from '@/core/community/work-service';
import { useAuth } from '@/shared/hooks/useAuth';

interface WorkEditorProps {
  workId?: string // 
  onSave?: (work: any) => void
  onCancel?: () => void
  className?: string
}

interface FormData extends CreateWorkRequest {
  status?: 'draft' | 'published' | 'archived' | 'private'
}

export function WorkEditor({ workId, onSave, onCancel, className = '' }: WorkEditorProps) {
  const router = useRouter();
  const { user } = useAuth();

  const [formData, setFormData] = useState<FormData>({
    title: '',
    description: '',
    knowledgePoint: '',
    subject: '',
    gradeLevel: '',
    cards: [],
    tags: [],
    category: '',
    difficulty: 'beginner',
    estimatedTime: 30,
    visibility: 'public',
    allowReuse: true,
    allowComments: true,
  });

  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [tagInput, setTagInput] = useState('');
  const [previewMode, setPreviewMode] = useState(false);

  // 
  const subjects = [
    '', '', '', '', '', '',
    '', '', '', '', '', '',
  ];

  const gradeLevels = [
    '', '', '', '', '', '',
    '', '', '',
    '', '', '',
  ];

  const categories = [
    '', '', '', '',
    '', '', '', '',
  ];

  // 
  useEffect(() => {
    if (workId) {
      loadWork();
    }
  }, [workId]);

  const loadWork = async () => {
    if (!workId) return;

    try {
      setLoading(true);
      const response = await fetch(`/api/works/${workId}`);
      const result = await response.json();

      if (result.success && result.work) {
        const work = result.work;
        setFormData({
          title: work.title,
          description: work.description || '',
          knowledgePoint: work.knowledgePoint,
          subject: work.subject,
          gradeLevel: work.gradeLevel,
          cards: work.cards,
          tags: work.tags,
          category: work.category,
          difficulty: work.difficulty,
          estimatedTime: work.estimatedTime,
          visibility: work.visibility,
          allowReuse: work.allowReuse,
          allowComments: work.allowComments,
          status: work.status,
        });
      } else {
        setErrors({ general: result.error || '' });
      }
    } catch (error) {
      setErrors({ general: '' });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value, type } = e.target;
    const checked = (e.target as HTMLInputElement).checked;

    const newValue = type === 'checkbox' ? checked :
                     type === 'number' ? parseInt(value, 10) || 0 : value;
    setFormData({
      ...formData,
      [name]: newValue,
    });

    // 
    if (errors[name]) {
      setErrors({ ...errors, [name]: '' });
    }
  };

  const handleTagAdd = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && tagInput.trim()) {
      e.preventDefault();
      const newTag = tagInput.trim();

      if (!formData.tags.includes(newTag) && formData.tags.length < 10) {
        setFormData({
          ...formData,
          tags: [...formData.tags, newTag],
        });
      }

      setTagInput('');
    }
  };

  const handleTagRemove = (tagToRemove: string) => {
    setFormData({
      ...formData,
      tags: formData.tags.filter(tag => tag !== tagToRemove),
    });
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    if (!formData.title.trim()) {
      newErrors.title = '';
    } else if (formData.title.length < 2 || formData.title.length > 100) {
      newErrors.title = '2-100';
    }

    if (!formData.knowledgePoint.trim()) {
      newErrors.knowledgePoint = '';
    }

    if (!formData.subject) {
      newErrors.subject = '';
    }

    if (!formData.gradeLevel) {
      newErrors.gradeLevel = '';
    }

    if (!formData.category) {
      newErrors.category = '';
    }

    if (formData.cards.length === 0) {
      newErrors.cards = '';
    }

    if (formData.description && formData.description.length > 500) {
      newErrors.description = '500';
    }

    if (formData.estimatedTime < 5 || formData.estimatedTime > 300) {
      newErrors.estimatedTime = '5-300';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = async (publish: boolean = false) => {
    if (!validateForm()) return;

    try {
      setSaving(true);
      setErrors({});

      const saveData = { ...formData };
      if (publish) {
        saveData.status = 'published';
      }

      let result;
      if (workId) {
        // 
        const response = await fetch(`/api/works/${workId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
          },
          body: JSON.stringify(saveData),
        });
        result = await response.json();
      } else {
        // 
        const response = await fetch('/api/works', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
          },
          body: JSON.stringify(saveData),
        });
        result = await response.json();
      }

      if (result.success) {
        onSave && onSave(result.work);

        if (publish) {
          router.push(`/works/${result.work._id}`);
        } else {
          router.push('/dashboard/works');
        }
      } else {
        setErrors({ general: result.error || '' });
      }
    } catch (error) {
      setErrors({ general: '' });
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    if (onCancel) {
      onCancel();
    } else {
      router.back();
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className={`max-w-4xl mx-auto p-6 ${className}`}>
      <div className="bg-white rounded-lg shadow-lg">
        {/*  */}
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-bold text-gray-900">
              {workId ? '' : ''}
            </h1>
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setPreviewMode(!previewMode)}
                className="px-4 py-2 text-sm border border-gray-300 rounded-lg hover:bg-gray-50"
              >
                {previewMode ? '' : ''}
              </button>
            </div>
          </div>
        </div>

        {/*  */}
        {errors.general && (
          <div className="mx-6 mt-4 p-4 bg-red-50 border border-red-200 rounded-lg">
            <p className="text-red-600 text-sm">{errors.general}</p>
          </div>
        )}

        <div className="p-6">
          {previewMode ? (
            // 
            <div className="space-y-6">
              <div>
                <h2 className="text-xl font-bold text-gray-900">{formData.title || ''}</h2>
                {formData.description && (
                  <p className="mt-2 text-gray-600">{formData.description}</p>
                )}
              </div>

              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                <div>
                  <span className="font-medium text-gray-700"></span>
                  <span className="text-gray-600">{formData.subject || '-'}</span>
                </div>
                <div>
