              {/*  */}
              {formData.cards.length === 0 && (
                <div className="p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
                  <div className="flex">
                    <svg className="w-5 h-5 text-yellow-400 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                    </svg>
                    <div className="ml-3">
                      <p className="text-sm text-yellow-800">
                        AI
                      </p>
                    </div>
                  </div>
                </div>
              )}
            </form>
          )}
        </div>

        {/*  */}
        <div className="px-6 py-4 border-t border-gray-200 bg-gray-50">
          <div className="flex items-center justify-between">
            <button
              type="button"
              onClick={handleCancel}
              className="px-4 py-2 text-sm text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50"
              disabled={saving}
            >

            </button>

            <div className="flex items-center space-x-3">
              <button
                type="button"
                onClick={() => handleSave(false)}
                disabled={saving}
                className="px-4 py-2 text-sm text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50"
              >
                {saving ? '...' : ''}
              </button>

              <button
                type="button"
                onClick={() => handleSave(true)}
                disabled={saving || formData.cards.length === 0}
                className="px-6 py-2 text-sm text-white bg-blue-600 rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {saving ? '...' : ''}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default WorkEditor;

/**
 * 
 * 
 */
'use client';

import Image from 'next/image';
import Link from 'next/link';
import React, { useState, useEffect } from 'react';

import { useAuth } from '@/shared/hooks/useAuth';

interface Work {
  _id: string
  title: string
  description?: string
  knowledgePoint: string
  subject: string
  gradeLevel: string
  category: string
  difficulty: 'beginner' | 'intermediate' | 'advanced'
  estimatedTime: number
  tags: string[]
  author: {
    _id: string
    name: string
    avatar?: string
  }
  likesCount: number
  views: number
  reuseCount: number
  commentsCount: number
  qualityScore: number
  publishedAt: string
  userInteractions?: {
    isLiked: boolean
    isBookmarked: boolean
    isFollowing: boolean
  }
}

interface WorkListProps {
  initialWorks?: Work[]
  searchQuery?: string
  filters?: {
    subject?: string
    gradeLevel?: string
    category?: string
    difficulty?: string
    tags?: string[]
    author?: string
  }
  sortBy?: 'latest' | 'popular' | 'trending' | 'views'
  viewMode?: 'grid' | 'list'
  showFilters?: boolean
  showSearch?: boolean
  className?: string
}

export function WorkList({
  initialWorks = [],
  searchQuery = '',
  filters = {},
  sortBy = 'latest',
  viewMode = 'grid',
  showFilters = true,
  showSearch = true,
  className = '',
}: WorkListProps) {
  const { user } = useAuth();

  const [works, setWorks] = useState<Work[]>(initialWorks);
  const [loading, setLoading] = useState(false);
  const [searchInput, setSearchInput] = useState(searchQuery);
  const [currentFilters, setCurrentFilters] = useState(filters);
  const [currentSort, setCurrentSort] = useState(sortBy);
  const [currentViewMode, setCurrentViewMode] = useState(viewMode);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [total, setTotal] = useState(0);

  // 
  const subjects = [
    '', '', '', '', '', '',
    '', '', '', '', '', '',
  ];

  const gradeLevels = [
    '', '', '', '', '', '',
    '', '', '',
    '', '', '',
  ];

  const categories = [
    '', '', '', '',
    '', '', '', '',
  ];

  // 
  const loadWorks = async (reset: boolean = false) => {
    try {
      setLoading(true);

      const params = new URLSearchParams({
        page: reset ? '1' : page.toString(),
        limit: '20',
        sortBy: currentSort,
      });

      if (searchInput.trim()) {
        params.append('query', searchInput.trim());
      }

      Object.entries(currentFilters).forEach(([key, value]) => {
        if (value) {
          if (Array.isArray(value)) {
            value.forEach(v => params.append(key, v));
          } else {
            params.append(key, value);
          }
        }
      });

      const response = await fetch(`/api/works/search?${params}`);
      const result = await response.json();

      if (result.success) {
        if (reset) {
          setWorks(result.works);
          setPage(1);
        } else {
          setWorks([...works, ...result.works]);
        }

        setTotal(result.total);
        setHasMore(result.page < result.totalPages);
      }
    } catch (error) {
      console.error('Load works error:', error);
    } finally {
      setLoading(false);
    }
  };

  // 
  useEffect(() => {
    if (initialWorks.length === 0) {
      loadWorks(true);
    }
  }, []);

  // 
  useEffect(() => {
    loadWorks(true);
  }, [searchInput, currentFilters, currentSort]);

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    loadWorks(true);
  };

  const handleFilterChange = (key: string, value: any) => {
    setCurrentFilters({
      ...currentFilters,
      [key]: value,
    });
  };

  const handleLoadMore = () => {
    setPage(page + 1);
    loadWorks(false);
  };

  const handleLike = async (workId: string) => {
    if (!user) return;

    try {
      const response = await fetch(`/api/works/${workId}/like`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });

      if (response.ok) {
        const updatedWorks = works.map((work: any) => {
          if (work._id === workId) {
            const isLiked = work.userInteractions?.isLiked;
            return {
              ...work,
              likesCount: isLiked ? work.likesCount - 1 : work.likesCount + 1,
              userInteractions: {
                ...work.userInteractions,
                isLiked: !isLiked,
              },
            };
          }
          return work;
        });
        setWorks(updatedWorks);
      }
    } catch (error) {
      console.error('Like error:', error);
    }
  };

  const handleBookmark = async (workId: string) => {
    if (!user) return;

    try {
      const response = await fetch(`/api/works/${workId}/bookmark`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });

      if (response.ok) {
        const updatedWorks = works.map((work: any) => {
          if (work._id === workId) {
            return {
              ...work,
              userInteractions: {
                ...work.userInteractions,
                isBookmarked: !work.userInteractions?.isBookmarked,
              },
            };
          }
          return work;
        });
        setWorks(updatedWorks);
      }
    } catch (error) {
      console.error('Bookmark error:', error);
    }
  };

  const getDifficultyLabel = (difficulty: string) => {
    switch (difficulty) {
      case 'beginner': return '';
      case 'intermediate': return '';
      case 'advanced': return '';
      default: return difficulty;
    }
  };

  const getSortLabel = (sort: string) => {
    switch (sort) {
      case 'latest': return '';
      case 'popular': return '';
      case 'trending': return '';
      case 'views': return '';
      default: return sort;
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));

    if (days === 0) return '';
    if (days === 1) return '';
    if (days < 7) return `${days}`;
    if (days < 30) return `${Math.floor(days / 7)}`;
    if (days < 365) return `${Math.floor(days / 30)}`;
    return `${Math.floor(days / 365)}`;
  };

  return (
    <div className={`space-y-6 ${className}`}>
      {/*  */}
      {showSearch && (
        <div className="bg-white rounded-lg shadow-sm p-6">
          <form onSubmit={handleSearch} className="flex gap-4">
            <div className="flex-1">
              <input
                type="text"
                value={searchInput}
                onChange={(e) => setSearchInput(e.target.value)}
                placeholder="..."
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <button
              type="submit"
              className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >

            </button>
          </form>
        </div>
      )}

      {/*  */}
      {showFilters && (
        <div className="bg-white rounded-lg shadow-sm p-6">
          <div className="grid grid-cols-1 md:grid-cols-4 lg:grid-cols-6 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1"></label>
              <select
                value={currentFilters.subject || ''}
                onChange={(e) => handleFilterChange('subject', e.target.value || undefined)}
                className="w-full px-3 py-2 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value=""></option>
                {subjects.map(subject => (
                  <option key={subject} value={subject}>{subject}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1"></label>
              <select
                value={currentFilters.gradeLevel || ''}
                onChange={(e) => handleFilterChange('gradeLevel', e.target.value || undefined)}
                className="w-full px-3 py-2 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value=""></option>
                {gradeLevels.map(grade => (
                  <option key={grade} value={grade}>{grade}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1"></label>
              <select
                value={currentFilters.category || ''}
                onChange={(e) => handleFilterChange('category', e.target.value || undefined)}
                className="w-full px-3 py-2 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value=""></option>
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1"></label>
              <select
                value={currentFilters.difficulty || ''}
                onChange={(e) => handleFilterChange('difficulty', e.target.value || undefined)}
                className="w-full px-3 py-2 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value=""></option>
                <option value="beginner"></option>
                <option value="intermediate"></option>
                <option value="advanced"></option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1"></label>
              <select
                value={currentSort}
                onChange={(e) => setCurrentSort(e.target.value as any)}
                className="w-full px-3 py-2 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="latest"></option>
                <option value="popular"></option>
                <option value="trending"></option>
                <option value="views"></option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1"></label>
              <div className="flex border border-gray-300 rounded-lg overflow-hidden">
                <button
                  onClick={() => setCurrentViewMode('grid')}
                  className={`flex-1 px-3 py-2 text-sm ${
                    currentViewMode === 'grid'
                      ? 'bg-blue-600 text-white'
                      : 'bg-white text-gray-700 hover:bg-gray-50'
                  }`}
                >

                </button>
                <button
                  onClick={() => setCurrentViewMode('list')}
                  className={`flex-1 px-3 py-2 text-sm ${
                    currentViewMode === 'list'
                      ? 'bg-blue-600 text-white'
                      : 'bg-white text-gray-700 hover:bg-gray-50'
                  }`}
                >

                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/*  */}
      <div className="flex items-center justify-between text-sm text-gray-600">
        <span> {total} </span>
        <span>{getSortLabel(currentSort)}</span>
      </div>

      {/*  */}
      <div className={
        currentViewMode === 'grid'
          ? 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6'
          : 'space-y-4'
      }>
        {works.map(work => (
          <div
            key={work._id}
            className={`bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow ${
              currentViewMode === 'list' ? 'flex p-4' : 'overflow-hidden'
            }`}
          >
            {currentViewMode === 'grid' ? (
              // 
              <>
                <div className="p-4">
                  <div className="flex items-start justify-between mb-3">
                    <div className="flex-1">
                      <Link
                        href={`/works/${work._id}`}
                        className="text-lg font-semibold text-gray-900 hover:text-blue-600 line-clamp-2"
                      >
                        {work.title}
                      </Link>
                      {work.description && (
                        <p className="text-sm text-gray-600 mt-1 line-clamp-2">
                          {work.description}
                        </p>
                      )}
                    </div>
                  </div>

                  <div className="flex items-center space-x-2 text-xs text-gray-500 mb-3">
                    <span className="px-2 py-1 bg-blue-100 text-blue-800 rounded-full">
                      {work.subject}
                    </span>
                    <span className="px-2 py-1 bg-green-100 text-green-800 rounded-full">
                      {work.gradeLevel}
                    </span>
                    <span className="px-2 py-1 bg-purple-100 text-purple-800 rounded-full">
                      {getDifficultyLabel(work.difficulty)}
                    </span>
