'use client';

import React, { useState, useEffect } from 'react';

/**
 * 
 */
interface NetworkErrorProps {
  error?: Error;
  onRetry?: () => void;
  retryDelay?: number;
  maxRetries?: number;
  showDetails?: boolean;
  className?: string;
}

/**
 * Hook
 */
function useNetworkStatus() {
  const [isOnline, setIsOnline] = useState(true);
  const [connectionType, setConnectionType] = useState<string>('unknown');

  useEffect(() => {
    // 
    setIsOnline(navigator.onLine);

    // 
    if ('connection' in navigator) {
      const connection = (navigator as any).connection;
      setConnectionType(connection?.effectiveType || 'unknown');
    }

    // 
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    // 
    if ('connection' in navigator) {
      const connection = (navigator as any).connection;
      const handleConnectionChange = () => {
        setConnectionType(connection?.effectiveType || 'unknown');
      };
      connection?.addEventListener('change', handleConnectionChange);

      return () => {
        window.removeEventListener('online', handleOnline);
        window.removeEventListener('offline', handleOffline);
        connection?.removeEventListener('change', handleConnectionChange);
      };
    }

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  return { isOnline, connectionType };
}

/**
 * 
 */
export const NetworkError: React.FC<NetworkErrorProps> = ({
  error,
  onRetry,
  retryDelay = 3000,
  maxRetries = 3,
  showDetails = false,
  className = '',
}) => {
  const [retryCount, setRetryCount] = useState(0);
  const [isRetrying, setIsRetrying] = useState(false);
  const [countdown, setCountdown] = useState(0);
  const { isOnline, connectionType } = useNetworkStatus();

  // 
  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
      return () => clearTimeout(timer);
    } else if (countdown === 0 && isRetrying) {
      handleRetry();
    }
  }, [countdown, isRetrying]);

  /**
   * 
   */
  const handleRetry = async () => {
    if (retryCount >= maxRetries) {
      return;
    }

    setIsRetrying(true);
    setRetryCount(retryCount + 1);

    try {
      if (onRetry) {
        await onRetry();
      }
    } catch (error) {
      console.error('Retry failed:', error);
    } finally {
      setIsRetrying(false);
    }
  };

  /**
   * 
   */
  const startAutoRetry = () => {
    if (retryCount >= maxRetries) {
      return;
    }

    setCountdown(Math.ceil(retryDelay / 1000));
    setIsRetrying(true);
  };

  /**
   * 
   */
  const cancelAutoRetry = () => {
    setCountdown(0);
    setIsRetrying(false);
  };

  /**
   * 
   */
  const resetRetries = () => {
    setRetryCount(0);
    setIsRetrying(false);
    setCountdown(0);
  };

  /**
   * 
   */
  const getErrorType = () => {
    if (!isOnline) {
      return 'offline';
    }

    if (error?.message.includes('timeout')) {
      return 'timeout';
    }

    if (error?.message.includes('fetch')) {
      return 'fetch';
    }

    return 'network';
  };

  /**
   * 
   */
  const getErrorInfo = () => {
    const errorType = getErrorType();

    switch (errorType) {
      case 'offline':
        return {
          title: '',
          message: '',
          icon: (
            <svg className="w-8 h-8 text-red-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18.364 5.636l-3.536 3.536m0 5.656l3.536 3.536M9.172 9.172L5.636 5.636m3.536 9.192L5.636 18.364M12 12h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
          ),
        };
      case 'timeout':
        return {
          title: '',
          message: '',
          icon: (
            <svg className="w-8 h-8 text-yellow-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
          ),
        };
      default:
        return {
          title: '',
          message: '',
          icon: (
            <svg className="w-8 h-8 text-red-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
          ),
        };
    }
  };

  const errorInfo = getErrorInfo();
  const canRetry = retryCount < maxRetries && onRetry;

  return (
    <div className={`bg-white border border-red-200 rounded-lg p-6 ${className}`}>
      {/*  */}
      <div className="flex items-center mb-4">
        <div className="flex-shrink-0 mr-3">
          {errorInfo.icon}
        </div>
        <div>
          <h3 className="text-lg font-medium text-gray-900">
            {errorInfo.title}
          </h3>
          <p className="text-sm text-gray-600 mt-1">
            {errorInfo.message}
          </p>
        </div>
      </div>

      {/*  */}
      <div className="mb-4 p-3 bg-gray-50 rounded-md">
        <div className="flex items-center justify-between text-sm">
          <span className="text-gray-600">:</span>
          <span className={`font-medium ${isOnline ? 'text-green-600' : 'text-red-600'}`}>
            {isOnline ? '' : ''}
          </span>
        </div>
        {isOnline && connectionType !== 'unknown' && (
          <div className="flex items-center justify-between text-sm mt-1">
            <span className="text-gray-600">:</span>
            <span className="font-medium text-gray-900">
              {connectionType.toUpperCase()}
            </span>
          </div>
        )}
        {retryCount > 0 && (
          <div className="flex items-center justify-between text-sm mt-1">
            <span className="text-gray-600">:</span>
            <span className="font-medium text-gray-900">
              {retryCount}/{maxRetries}
            </span>
          </div>
        )}
      </div>

      {/*  */}
      {showDetails && error && process.env.NODE_ENV === 'development' && (
        <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-md">
          <h4 className="text-sm font-medium text-red-800 mb-2">
             ()
          </h4>
          <pre className="text-xs text-red-700 whitespace-pre-wrap overflow-x-auto">
            {error.message}
          </pre>
          {error.stack && (
            <pre className="text-xs text-red-600 whitespace-pre-wrap overflow-x-auto mt-2 max-h-32">
              {error.stack}
            </pre>
          )}
        </div>
      )}

      {/*  */}
      <div className="flex flex-col sm:flex-row gap-3">
        {canRetry && (
          <button
            onClick={handleRetry}
            disabled={isRetrying || !isOnline}
            className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors text-sm font-medium"
          >
            {isRetrying ? '...' : ''}
          </button>
        )}

        {canRetry && !isRetrying && (
          <button
            onClick={startAutoRetry}
            disabled={!isOnline}
            className="flex-1 bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors text-sm font-medium"
          >

          </button>
        )}

        {isRetrying && countdown > 0 && (
          <button
            onClick={cancelAutoRetry}
            className="flex-1 bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors text-sm font-medium"
          >
             ({countdown}s)
          </button>
        )}

        {retryCount >= maxRetries && (
          <button
            onClick={resetRetries}
            className="flex-1 bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition-colors text-sm font-medium"
          >

          </button>
        )}
      </div>

      {/*  */}
      {!isOnline && (
        <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-md">
          <h4 className="text-sm font-medium text-blue-800 mb-2">

          </h4>
          <ul className="text-sm text-blue-700 space-y-1">
            <li> WiFi</li>
            <li> </li>
            <li> </li>
            <li> </li>
          </ul>
        </div>
      )}
    </div>
  );
};

/**
 * Hook
 */
export function useNetworkError() {
  const [networkError, setNetworkError] = useState<Error | null>(null);
  const { isOnline } = useNetworkStatus();

  const handleNetworkError = (error: Error) => {
    setNetworkError(error);
  };

  const clearNetworkError = () => {
    setNetworkError(null);
  };

  const isNetworkError = (error: Error): boolean => {
    return (
      error.message.includes('fetch') ||
      error.message.includes('network') ||
      error.message.includes('timeout') ||
      error.name === 'NetworkError' ||
      !isOnline
    );
  };

  return {
    networkError,
    isOnline,
    handleNetworkError,
    clearNetworkError,
    isNetworkError,
  };
}

'use client';

import React, { useState, useEffect } from 'react';

/**
 * 
 */
interface RetryButtonProps {
  onRetry: () => Promise<void> | void;
  maxRetries?: number;
  retryDelay?: number;
  autoRetry?: boolean;
  disabled?: boolean;
  className?: string;
  size?: 'sm' | 'md' | 'lg';
  variant?: 'primary' | 'secondary' | 'outline';
  showCount?: boolean;
  resetOnSuccess?: boolean;
  children?: React.ReactNode;
}

/**
 * 
 */
export const RetryButton: React.FC<RetryButtonProps> = ({
  onRetry,
  maxRetries = 3,
  retryDelay = 3000,
  autoRetry = false,
  disabled = false,
  className = '',
  size = 'md',
  variant = 'primary',
  showCount = true,
  resetOnSuccess = true,
  children,
}) => {
  const [retryCount, setRetryCount] = useState(0);
  const [isRetrying, setIsRetrying] = useState(false);
  const [countdown, setCountdown] = useState(0);
  const [lastError, setLastError] = useState<Error | null>(null);

  // 
  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => {
        setCountdown(countdown - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (countdown === 0 && autoRetry && retryCount > 0 && retryCount < maxRetries) {
      handleRetry();
    }
  }, [countdown, autoRetry, retryCount, maxRetries]);

  const handleRetry = async () => {
    if (isRetrying || retryCount >= maxRetries) {
      return;
    }

    setIsRetrying(true);
    setLastError(null);

    try {
      await onRetry();

      // 
      if (resetOnSuccess) {
        setRetryCount(0);
      }
    } catch (error) {
      const newRetryCount = retryCount + 1;
      setRetryCount(newRetryCount);
      setLastError(error instanceof Error ? error : new Error('Retry failed'));

      // 
      if (autoRetry && newRetryCount < maxRetries) {
        setCountdown(Math.floor(retryDelay / 1000));
      }
    } finally {
      setIsRetrying(false);
    }
  };

  const resetRetries = () => {
    setRetryCount(0);
    setCountdown(0);
    setLastError(null);
  };

  // 
  const sizeClasses = {
    sm: 'px-3 py-1.5 text-sm',
    md: 'px-4 py-2 text-base',
    lg: 'px-6 py-3 text-lg',
  };

  const variantClasses = {
    primary: 'bg-blue-600 text-white hover:bg-blue-700 disabled:bg-blue-400',
    secondary: 'bg-gray-600 text-white hover:bg-gray-700 disabled:bg-gray-400',
    outline: 'border-2 border-blue-600 text-blue-600 hover:bg-blue-50 disabled:border-gray-300 disabled:text-gray-400',
  };

  const baseClasses = 'font-medium rounded-md transition-colors duration-200 disabled:cursor-not-allowed focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500';

  const buttonClasses = `${baseClasses} ${sizeClasses[size]} ${variantClasses[variant]} ${className}`;

  const canRetry = retryCount < maxRetries;
  const isDisabled = disabled || isRetrying || !canRetry;

  return (
    <div className="inline-flex flex-col items-center space-y-2">
      {/*  */}
      <button
        onClick={handleRetry}
        disabled={isDisabled}
        className={buttonClasses}
      >
        {isRetrying ? (
          <span className="flex items-center justify-center">
            <svg className="animate-spin -ml-1 mr-2 h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
            ...
          </span>
        ) : countdown > 0 ? (
          `${countdown}`
        ) : (
          children || (
            <span>

              {showCount && retryCount > 0 && ` (${retryCount}/${maxRetries})`}
            </span>
          )
        )}
      </button>

      {/*  */}
      {retryCount > 0 && (
        <div className="text-center">
          {canRetry ? (
            <p className="text-sm text-gray-600">
               {retryCount}/{maxRetries} 
              {countdown > 0 && autoRetry && (
                <span className="block text-xs text-gray-500 mt-1">
                  {countdown} 
                </span>
              )}
            </p>
          ) : (
            <div className="text-center">
              <p className="text-sm text-red-600 mb-2">
                 ({maxRetries})
              </p>
              <button
                onClick={resetRetries}
                className="text-xs text-blue-600 hover:text-blue-800 underline"
              >

              </button>
            </div>
          )}
        </div>
      )}

      {/*  */}
      {lastError && (
        <div className="text-center max-w-xs">
          <p className="text-xs text-red-600">
            {lastError.message}
          </p>
        </div>
      )}
    </div>
  );
};

/**
 * 
 */
export const SmartRetryButton: React.FC<Omit<RetryButtonProps, 'retryDelay'> & {
  baseDelay?: number;
  maxDelay?: number;
  backoffFactor?: number;
}> = ({
  baseDelay = 1000,
  maxDelay = 30000,
  backoffFactor = 2,
  ...props
}) => {
  const [currentDelay, setCurrentDelay] = useState(baseDelay);

  // 
  const calculateDelay = (retryCount: number): number => {
    const delay = baseDelay * Math.pow(backoffFactor, retryCount);
    return Math.min(delay, maxDelay);
  };

  const handleRetry = async () => {
    try {
      await props.onRetry();
      // 
      setCurrentDelay(baseDelay);
    } catch (error) {
      // 
      setCurrentDelay(calculateDelay(Math.log(currentDelay / baseDelay) / Math.log(backoffFactor) + 1));
      throw error;
    }
  };

  return (
    <RetryButton
      {...props}
      onRetry={handleRetry}
      retryDelay={currentDelay}
    />
  );
};

/**
 * 
 */
export const RetryButtonGroup: React.FC<{
  actions: Array<{
    label: string;
    action: () => Promise<void> | void;
    variant?: 'primary' | 'secondary' | 'outline';
  }>;
  className?: string;
}> = ({ actions, className = '' }) => {
  return (
    <div className={`flex flex-wrap gap-2 justify-center ${className}`}>
      {actions.map((action, index) => (
        <RetryButton
          key={index}
          onRetry={action.action}
          variant={action.variant || (index === 0 ? 'primary' : 'secondary')}
          size="sm"
          showCount={false}
          maxRetries={1}
        >
          {action.label}
        </RetryButton>
      ))}
    </div>
  );
};

/**
 * Hook
 */
export function useRetry({
  maxRetries = 3,
  baseDelay = 1000,
  maxDelay = 30000,
  backoffFactor = 2,
}: {
  maxRetries?: number;
  baseDelay?: number;
  maxDelay?: number;
  backoffFactor?: number;
} = {}) {
  const [retryCount, setRetryCount] = useState(0);
  const [isRetrying, setIsRetrying] = useState(false);
  const [lastError, setLastError] = useState<Error | null>(null);

  const retry = async (fn: () => Promise<void> | void) => {
    if (isRetrying || retryCount >= maxRetries) {
      return;
    }

    setIsRetrying(true);
    setLastError(null);

    try {
      await fn();
      setRetryCount(0); // 
    } catch (error) {
      const newRetryCount = retryCount + 1;
      setRetryCount(newRetryCount);
      setLastError(error instanceof Error ? error : new Error('Operation failed'));

      // 
      if (newRetryCount < maxRetries) {
        const delay = Math.min(baseDelay * Math.pow(backoffFactor, newRetryCount - 1), maxDelay);
        setTimeout(() => {
          retry(fn);
        }, delay);
      }
    } finally {
      setIsRetrying(false);
    }
  };

  const reset = () => {
    setRetryCount(0);
    setLastError(null);
    setIsRetrying(false);
  };

  return {
    retry,
    reset,
    retryCount,
    isRetrying,
    lastError,
    canRetry: retryCount < maxRetries,
  };
}

