'use client';

import React, { useState, useEffect } from 'react';

import LoadingSpinner from '@/shared/components/LoadingSpinner';
import { LeaderboardEntry, LeaderboardResponse } from '@/shared/types/contribution';

import { LeaderboardCard } from './LeaderboardCard';

interface RankingListProps {
  type: 'total' | 'weekly' | 'monthly' | 'creation' | 'reuse';
  currentUserId?: string;
  limit?: number;
  className?: string;
}

export function RankingList({
  type,
  currentUserId,
  limit = 20,
  className = '',
}: RankingListProps) {
  const [leaderboard, setLeaderboard] = useState<LeaderboardResponse | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [loadingMore, setLoadingMore] = useState(false);
  const [hasMore, setHasMore] = useState(true);

  // 
  const fetchLeaderboard = async (offset = 0, append = false) => {
    try {
      if (offset === 0) setLoading(true);
      else setLoadingMore(true);

      const params = new URLSearchParams({
        type,
        limit: limit.toString(),
        offset: offset.toString(),
        includeUserRank: currentUserId ? 'true' : 'false',
        ...(currentUserId && { userId: currentUserId }),
      });

      const response = await fetch(`/api/leaderboard?${params}`);
      const result = await response.json();

      if (!result.success) {
        throw new Error(result.error || '');
      }

      const newData = result.data as LeaderboardResponse;

      if (append && leaderboard) {
        setLeaderboard({
          ...newData,
          entries: [...leaderboard.entries, ...newData.entries],
        });
      } else {
        setLeaderboard(newData);
      }

      // 
      setHasMore(newData.entries.length === limit);

    } catch (err) {
      setError(err instanceof Error ? err.message : '');
    } finally {
      setLoading(false);
      setLoadingMore(false);
    }
  };

  // 
  const loadMore = () => {
    if (!loadingMore && hasMore && leaderboard) {
      fetchLeaderboard(leaderboard.entries.length, true);
    }
  };

  // 
  useEffect(() => {
    fetchLeaderboard();
  }, [type, currentUserId]);

  // 
  const getTypeTitle = (type: string) => {
    switch (type) {
      case 'total':
        return '';
      case 'weekly':
        return '';
      case 'monthly':
        return '';
      case 'creation':
        return '';
      case 'reuse':
        return '';
      default:
        return '';
    }
  };

  if (loading) {
    return (
      <div className={`flex items-center justify-center py-12 ${className}`}>
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (error) {
    return (
      <div className={`text-center py-12 ${className}`}>
        <div className="text-red-600 mb-4">
          <svg className="w-12 h-12 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
          </svg>
          <p className="font-medium"></p>
          <p className="text-sm text-gray-600 mt-1">{error}</p>
        </div>
        <button
          onClick={() => fetchLeaderboard()}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >

        </button>
      </div>
    );
  }

  if (!leaderboard || leaderboard.entries.length === 0) {
    return (
      <div className={`text-center py-12 ${className}`}>
        <div className="text-gray-500">
          <svg className="w-12 h-12 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <p className="font-medium"></p>
          <p className="text-sm mt-1"></p>
        </div>
      </div>
    );
  }

  return (
    <div className={className}>
      {/*  */}
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">
          {getTypeTitle(type)}
        </h2>
        <div className="flex items-center space-x-6 text-sm text-gray-600">
          <span> {leaderboard.entries.length} </span>
          <span>: {new Date(leaderboard.lastUpdated).toLocaleString()}</span>
        </div>
      </div>

      {/*  */}
      {leaderboard.userRank && leaderboard.userRank.rank > limit && (
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-3"></h3>
          <LeaderboardCard
            entry={leaderboard.userRank}
            isCurrentUser={true}
            showRankChange={true}
          />
        </div>
      )}

      {/*  */}
      <div className="space-y-3">
        {leaderboard.entries.map((entry, index) => (
          <LeaderboardCard
            key={entry.userId}
            entry={entry}
            isCurrentUser={entry.userId === currentUserId}
            showRankChange={type !== 'total'}
            className={index < 3 ? 'ring-2 ring-yellow-200' : ''}
          />
        ))}
      </div>

      {/*  */}
      {hasMore && (
        <div className="text-center mt-6">
          <button
            onClick={loadMore}
            disabled={loadingMore}
            className="px-6 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loadingMore ? (
              <span className="flex items-center">
                <LoadingSpinner size="sm" className="mr-2" />
                ...
              </span>
            ) : (
              ''
            )}
          </button>
        </div>
      )}
    </div>
  );
}

export default RankingList;

'use client';
import React, { createContext, useContext, useRef } from 'react';

import { useKeyboardNavigation, useFocusManagement } from '@/shared/hooks/useKeyboardNavigation';

interface KeyboardNavigationContextType {
  containerRef: React.RefObject<HTMLElement>;
  focusFirst: () => void;
  focusLast: () => void;
  focusNext: () => void;
  focusPrevious: () => void;
}

const KeyboardNavigationContext = createContext<KeyboardNavigationContextType | null>(null);

export function useKeyboardNavigationContext() {
  const context = useContext(KeyboardNavigationContext);
  if (!context) {
    throw new Error('useKeyboardNavigationContext must be used within KeyboardNavigationProvider');
  }
  return context;
}

interface KeyboardNavigationProviderProps {
  children: React.ReactNode;
}

export function KeyboardNavigationProvider({ children }: KeyboardNavigationProviderProps) {
  const containerRef = useRef<HTMLElement>(null!);
  const { focusFirst, focusLast, focusNext, focusPrevious } = useFocusManagement(containerRef as React.RefObject<HTMLElement>);

  // 
  useKeyboardNavigation({
    shortcuts: [
      {
        key: 'ArrowUp',
        action: focusPrevious,
        description: '',
      },
      {
        key: 'ArrowDown',
        action: focusNext,
        description: '',
      },
    ],
    disabled: false,
  });

  const value = {
    containerRef,
    focusFirst,
    focusLast,
    focusNext,
    focusPrevious,
  };

  return (
    <KeyboardNavigationContext.Provider value={value}>
      <div ref={containerRef as React.RefObject<HTMLDivElement>}>
        {children}
      </div>
    </KeyboardNavigationContext.Provider>
  );
}

'use client';

import React, { useState, useEffect } from 'react';

import { WorkDocument } from '@/lib/models/Work';

interface DraftsListProps {
  onSelectDraft?: (draft: WorkDocument) => void;
  onDeleteDraft?: (draftId: string) => void;
  className?: string;
}

export default function DraftsList({
  onSelectDraft,
  onDeleteDraft,
  className = '',
}: DraftsListProps) {
  const [drafts, setDrafts] = useState<WorkDocument[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchDrafts();
  }, []);

  const fetchDrafts = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/works/drafts');
      const data = await response.json();

      if (data.success) {
        setDrafts(data.data);
      } else {
        setError(data.message || '');
      }
    } catch (err) {
      setError('');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (draftId: string, e: React.MouseEvent) => {
    e.stopPropagation();

    if (!confirm('')) {
      return;
    }

    try {
      const response = await fetch(`/api/works/${draftId}`, {
        method: 'DELETE',
      });

      const data = await response.json();

      if (data.success) {
        setDrafts(drafts.filter(draft => String(draft._id) !== draftId));
        onDeleteDraft && onDeleteDraft(draftId);
      } else {
        alert(data.message || '');
      }
    } catch (err) {
      alert('');
    }
  };

  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleString('zh-CN', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  if (loading) {
    return (
      <div className={`${className}`}>
        <div className="animate-pulse space-y-3">
          {[1, 2, 3].map(i => (
            <div key={i} className="bg-gray-200 h-20 rounded-lg"></div>
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`${className}`}>
        <div className="text-center py-8">
          <div className="text-red-600 mb-2">{error}</div>
          <button
            onClick={fetchDrafts}
            className="text-blue-600 hover:text-blue-800 text-sm"
          >

          </button>
        </div>
      </div>
    );
  }

  if (drafts.length === 0) {
    return (
      <div className={`${className}`}>
        <div className="text-center py-8 text-gray-500">
          <svg className="w-12 h-12 mx-auto mb-3 text-gray-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <p className="text-sm"></p>
          <p className="text-xs text-gray-400 mt-1"></p>
        </div>
      </div>
    );
  }

  return (
    <div className={`${className}`}>
      <div className="space-y-3">
        {drafts.map((draft) => (
          <div
            key={String(draft._id)}
            onClick={() => onSelectDraft && onSelectDraft(draft)}
            className="bg-white border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow cursor-pointer group"
          >
            <div className="flex justify-between items-start">
              <div className="flex-1 min-w-0">
                <h3 className="font-medium text-gray-900 truncate group-hover:text-blue-600">
                  {draft.title || ''}
                </h3>
                <p className="text-sm text-gray-600 mt-1 truncate">
                  {draft.knowledgePoint || ''}
                </p>

                <div className="flex items-center mt-2 text-xs text-gray-500 space-x-3">
                  <span>{draft.subject || ''}</span>
                  <span></span>
                  <span>{draft.gradeLevel || ''}</span>
                  <span></span>
                  <span>{draft.cards?.length || 0} </span>
                </div>

                <div className="flex items-center justify-between mt-2">
                  <span className="text-xs text-gray-400">
                     {formatDate(draft.updatedAt)}
                  </span>

                  <div className="flex items-center space-x-1">
                    <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">

                    </span>
                  </div>
                </div>
              </div>

              <div className="ml-3 flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity">
                <button
                  onClick={(e) => handleDelete(String(draft._id), e)}
                  className="p-1 text-gray-400 hover:text-red-600 rounded"
                  title=""
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                  </svg>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {drafts.length > 0 && (
        <div className="text-center mt-4">
          <p className="text-xs text-gray-500">
             {drafts.length} 
          </p>
        </div>
      )}
    </div>
  );
