'use client';

import React, { useState, useEffect, useCallback } from 'react';

import CardEditor from '@/features/magic/CardEditor';
import { TeachingCard } from '@/shared/types/teaching';

interface WorkEditorProps {
  initialData?: {
    id?: string;
    title?: string;
    knowledgePoint?: string;
    subject?: string;
    gradeLevel?: string;
    cards?: TeachingCard[];
    tags?: string[];
    status?: 'draft' | 'published';
  };
  onSave?: (data: any) => void;
  onPublish?: (data: any) => void;
  onAutoSave?: (data: any) => void;
  isLoading?: boolean;
}

const SUBJECTS = [
  '', '', '', '', '', '',
  '', '', '', '', '', '',
];

const GRADE_LEVELS = [
  '', '', '', '', '', '',
  '', '', '',
  '', '', '',
];

export default function WorkEditor({
  initialData,
  onSave,
  onPublish,
  onAutoSave,
  isLoading = false,
}: WorkEditorProps) {
  const [formData, setFormData] = useState({
    title: initialData?.title || '',
    knowledgePoint: initialData?.knowledgePoint || '',
    subject: initialData?.subject || '',
    gradeLevel: initialData?.gradeLevel || '',
    cards: initialData?.cards || [],
    tags: initialData?.tags || [],
    status: initialData?.status || 'draft',
  });

  const [tagInput, setTagInput] = useState('');
  const [hasChanges, setHasChanges] = useState(false);
  const [autoSaveStatus, setAutoSaveStatus] = useState<'idle' | 'saving' | 'saved' | 'error'>('idle');

  // 
  const debouncedAutoSave = useCallback(
    debounce((data: any) => {
      if (onAutoSave && hasChanges) {
        setAutoSaveStatus('saving');
        try {
          const result = onAutoSave(data) as any;
          if (result && typeof result?.then === 'function') {
            result
              .then(() => {
                setAutoSaveStatus('saved');
                setHasChanges(false);
                setTimeout(() => setAutoSaveStatus('idle'), 2000);
              })
              .catch(() => {
                setAutoSaveStatus('error');
                setTimeout(() => setAutoSaveStatus('idle'), 3000);
              });
          } else {
            setAutoSaveStatus('saved');
            setHasChanges(false);
            setTimeout(() => setAutoSaveStatus('idle'), 2000);
          }
        } catch (error) {
          setAutoSaveStatus('error');
          setTimeout(() => setAutoSaveStatus('idle'), 3000);
        }
      }
    }, 2000),
    [onAutoSave, hasChanges],
  );

  // 
  useEffect(() => {
    if (hasChanges) {
      debouncedAutoSave(formData);
    }
  }, [formData, hasChanges, debouncedAutoSave]);

  const handleInputChange = (field: string, value: any) => {
    setFormData({ ...formData, [field]: value });
    setHasChanges(true);
  };

  const handleCardUpdate = (cardIndex: number, updatedCard: TeachingCard) => {
    const newCards = [...formData.cards];
    newCards[cardIndex] = updatedCard;
    handleInputChange('cards', newCards);
  };

  const handleCardDelete = (cardIndex: number) => {
    const newCards = formData.cards.filter((_, index) => index !== cardIndex);
    handleInputChange('cards', newCards);
  };

  const handleAddTag = () => {
    if (tagInput.trim() && !formData.tags.includes(tagInput.trim())) {
      handleInputChange('tags', [...formData.tags, tagInput.trim()]);
      setTagInput('');
