function debounce<T extends(...args: any[]) => any>(
  func: T,
  wait: number,
): (...args: Parameters<T>) => void {
  let timeout: NodeJS.Timeout;
  return (...args: Parameters<T>) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => func(...args), wait);
  };
}

import { OAuth2Client } from 'google-auth-library';

import ContributionLog from '@/lib/models/ContributionLog';
import User, { UserDocument } from '@/lib/models/User';
import connectDB from '@/lib/mongodb';

import { generateToken, generateRefreshToken } from './jwt';

const client = new OAuth2Client(
  process.env.GOOGLE_CLIENT_ID,
  process.env.GOOGLE_CLIENT_SECRET,
  process.env.GOOGLE_REDIRECT_URI,
);

export interface GoogleAuthResponse {
  success: boolean;
  user?: Omit<UserDocument, 'password'>;
  token?: string;
  refreshToken?: string;
  error?: string;
}

/**
 * Verify Google ID token and authenticate user
 */
export async function verifyGoogleToken(idToken: string): Promise<GoogleAuthResponse> {
  try {
    await connectDB();

    // Verify the token with Google
    const ticket = await client.verifyIdToken({
      idToken,
      audience: process.env.GOOGLE_CLIENT_ID,
    });

    const payload = ticket.getPayload();
    if (!payload) {
      return {
        success: false,
        error: 'Invalid Google token',
      };
    }

    const { email, name, picture, sub: googleId } = payload;

    if (!email) {
      return {
        success: false,
        error: 'Email not provided by Google',
      };
    }

    // Check if user already exists
    let user = await (User.findOne as any)({ email: email.toLowerCase() });

    if (user) {
      // Update Google ID if not set
      if (!user.googleId) {
        user.googleId = googleId;
        user.avatar = picture || user.avatar;
        await user.save();
      }

      // Reset daily usage if needed
      user.resetDailyUsage();
      await user.save();
    } else {
      // Create new user
      user = new User({
        email: email.toLowerCase(),
        name: name || 'Google User',
        googleId,
        avatar: picture,
        subscription: {
          plan: 'free',
          startDate: new Date(),
          isActive: true,
        },
        usage: {
          dailyGenerations: 0,
          dailyReuses: 0,
          lastResetDate: new Date(),
        },
        settings: {
          emailNotifications: true,
          publicProfile: false,
        },
      });

      const savedUser = await user.save();

      // Log registration contribution
      await (ContributionLog.create as any)({
        user: savedUser._id,
        action: 'create',
        target: {
          type: 'user',
          id: savedUser._id.toString(),
          title: 'Google OAuth Registration',
        },
        points: 10, // Welcome bonus
        metadata: {
          description: 'User registration via Google OAuth',
          category: 'account',
        },
      });

      user = savedUser;
    }

    // Generate tokens
    const token = generateToken(user);
    const refreshToken = generateRefreshToken(user);

    // Remove password from response
    const userResponse = user.toObject();
    delete userResponse.password;

    return {
      success: true,
      user: userResponse,
      token,
      refreshToken,
    };
  } catch (error) {
    console.error('Google auth error:', error);
    return {
      success: false,
      error: 'Google authentication failed',
    };
  }
}

/**
 * Get Google OAuth URL for authentication
 */
export function getGoogleAuthUrl(state?: string): string {
  const scopes = [
    'https://www.googleapis.com/auth/userinfo.email',
    'https://www.googleapis.com/auth/userinfo.profile',
  ];

  const authOptions: Parameters<typeof client.generateAuthUrl>[0] = {
    access_type: 'offline',
    scope: scopes,
    include_granted_scopes: true,
    prompt: 'consent',
  };

  if (state) {
    authOptions.state = state;
  }

  const url = client.generateAuthUrl(authOptions);

  return url;
}

/**
 * Exchange authorization code for tokens
 */
export async function exchangeCodeForTokens(code: string): Promise<{
  success: boolean;
  idToken?: string;
  error?: string;
}> {
  try {
    const { tokens } = await client.getToken(code);

    if (!tokens.id_token) {
      return {
        success: false,
        error: 'No ID token received from Google',
      };
    }

    return {
      success: true,
      idToken: tokens.id_token,
    };
  } catch (error) {
    console.error('Token exchange error:', error);
    return {
      success: false,
      error: 'Failed to exchange code for tokens',
    };
  }
}

export default {
  verifyGoogleToken,
  getGoogleAuthUrl,
  exchangeCodeForTokens,
};

import jwt, { SignOptions } from 'jsonwebtoken';

import { UserDocument } from '@/lib/models/User';

const BASE_SECRET = process.env.JWT_SECRET ?? process.env.NEXTAUTH_SECRET ?? 'your-secret-key';
const REFRESH_SECRET = process.env.JWT_REFRESH_SECRET ?? BASE_SECRET;
const ACCESS_TOKEN_EXPIRES_IN = process.env.JWT_EXPIRES_IN ?? '7d';
const REFRESH_TOKEN_EXPIRES_IN = process.env.JWT_REFRESH_EXPIRES_IN ?? '30d';

export interface JWTPayload {
  userId: string;
  email: string;
  name?: string;
  [key: string]: any;
  iat?: number;
  exp?: number;
}

type TokenInput = JWTPayload | Pick<UserDocument, '_id' | 'email' | 'name'> & {
  [key: string]: any;
};

function toPayload(input: TokenInput): JWTPayload {
  if ('userId' in input) {
    return {
      ...input,
      userId: input.userId,
      email: input.email,
      name: input.name,
    };
  }

  return {
    userId: (input._id as any)?.toString?.() ?? '',
    email: input.email,
    name: input.name,
  };
}

function signToken(payload: JWTPayload, secret: string, expiresIn: string | number): string {
  const options: SignOptions = { expiresIn: expiresIn as SignOptions['expiresIn'] };
  return jwt.sign(payload, secret, options);
}

export function generateToken(input: TokenInput, expiresIn: string | number = ACCESS_TOKEN_EXPIRES_IN): string {
  const payload = toPayload(input);
  return signToken(payload, BASE_SECRET, expiresIn);
}

export function generateRefreshToken(input: TokenInput, expiresIn: string | number = REFRESH_TOKEN_EXPIRES_IN): string {
  const payload = toPayload(input);
  return signToken(payload, REFRESH_SECRET, expiresIn);
}

export function verifyToken(token: string, secret: string = BASE_SECRET): JWTPayload | null {
  try {
    return jwt.verify(token, secret) as JWTPayload;
  } catch (error) {
    console.error('JWT verification failed:', error);
    return null;
  }
}

export function verifyRefreshToken(token: string): JWTPayload | null {
  return verifyToken(token, REFRESH_SECRET);
}

export function extractTokenFromHeader(authHeader: string | undefined): string | null {
  if (!authHeader) return null;
  const parts = authHeader.split(' ');
  if (parts.length !== 2 || parts[0] !== 'Bearer') return null;
  return parts[1];
}

export function decodeToken(token: string): JWTPayload | null {
  try {
    return jwt.decode(token) as JWTPayload;
  } catch (error) {
    console.error('JWT decode failed:', error);
    return null;
  }
}

export default {
  generateToken,
  generateRefreshToken,
  verifyToken,
  verifyRefreshToken,
  extractTokenFromHeader,
  decodeToken,
};

import { NextRequest, NextResponse } from 'next/server';

import User from '@/lib/models/User';
import connectDB from '@/lib/mongodb';

import { verifyToken, extractTokenFromHeader, JWTPayload } from './jwt';

export interface AuthenticatedRequest extends NextRequest {
  user?: JWTPayload & {
    dbUser?: any;
  };
}

/**
 * Authentication middleware for API routes
 */
export async function authenticateToken(request: NextRequest): Promise<{
  success: boolean;
  user?: JWTPayload & { dbUser?: any };
  error?: string;
}> {
  try {
    const authHeader = request.headers.get('authorization');
    let token = extractTokenFromHeader(authHeader || '');

    if (!token) {
      token = request.cookies.get('token')?.value ||
        request.cookies.get('auth-token')?.value || null;
    }

    if (!token) {
      return {
        success: false,
        error: 'No token provided',
      };
    }

    const payload = verifyToken(token);
    if (!payload) {
      return {
        success: false,
        error: 'Invalid or expired token',
      };
    }

    // In development mode with demo login enabled, skip database lookup
    if (
      process.env.NODE_ENV === 'development' &&
      process.env.DEMO_LOGIN_ENABLED === 'true'
    ) {
      // Create mock dbUser for demo mode
      const mockDbUser = {
        _id: payload.userId,
        email: payload.email,
        name: 'Demo User',
        subscription: {
          plan: 'pro',
          status: 'active',
        },
        usage: {
          dailyGenerations: 0,
          dailyReuses: 0,
        },
      };

      return {
        success: true,
        user: {
          ...payload,
          dbUser: mockDbUser,
        },
      };
    }

    // Production mode: fetch user from database
    try {
      await connectDB();
      const dbUser = await (User.findById as any)(payload.userId).select('-password');

      if (!dbUser) {
        return {
          success: false,
          error: 'User not found',
        };
      }

      return {
        success: true,
        user: {
          ...payload,
          dbUser: dbUser.toObject(),
        },
      };
    } catch (dbError) {
      console.error('Database connection error:', dbError);

      // Fallback for dev mode if DB fails
      if (process.env.NODE_ENV === 'development') {
        const fallbackUser = {
          _id: payload.userId,
          email: payload.email,
          name: 'Demo User',
          subscription: {
            plan: 'pro',
            status: 'active',
          },
          usage: {
            dailyGenerations: 0,
            dailyReuses: 0,
          },
        };

        return {
          success: true,
          user: {
            ...payload,
            dbUser: fallbackUser,
          },
        };
      }

      return {
        success: false,
        error: 'Database connection failed',
      };
    }
  } catch (error) {
    console.error('Authentication middleware error:', error);
    return {
      success: false,
      error: 'Authentication failed',
    };
  }
}

/**
 * Require authentication middleware
 */
export function requireAuth(handler: (req: AuthenticatedRequest) => Promise<NextResponse>) {
  return async (request: NextRequest) => {
    const authResult = await authenticateToken(request);

    if (!authResult.success) {
      return NextResponse.json(
        { error: authResult.error },
        { status: 401 },
      );
    }

    // Add user to request object
    (request as AuthenticatedRequest).user = authResult.user;

    return handler(request as AuthenticatedRequest);
  };
}

/**
 * Optional authentication middleware (doesn't fail if no token)
 */
export function optionalAuth(handler: (req: AuthenticatedRequest) => Promise<NextResponse>) {
  return async (request: NextRequest) => {
    const authResult = await authenticateToken(request);

    // Add user to request object if authentication succeeded
    if (authResult.success) {
      (request as AuthenticatedRequest).user = authResult.user;
    }

    return handler(request as AuthenticatedRequest);
  };
}

/**
 * Check if user has required subscription level
 */
export function requireSubscription(requiredLevel: 'free' | 'pro' | 'super') {
  return (handler: (req: AuthenticatedRequest) => Promise<NextResponse>) => {
    return requireAuth(async (request: AuthenticatedRequest) => {
      const user = request.user;

      if (!user?.dbUser) {
        return NextResponse.json(
          { error: 'User data not available' },
          { status: 500 },
        );
      }

      const userSubscription = user.dbUser.subscription?.plan || 'free';
      const levels = ['free', 'pro', 'super'];
      const userLevel = levels.indexOf(userSubscription);
      const requiredLevelIndex = levels.indexOf(requiredLevel);

      if (userLevel < requiredLevelIndex) {
        return NextResponse.json(
          {
            error: 'Insufficient subscription level',
            required: requiredLevel,
            current: userSubscription,
          },
          { status: 403 },
        );
      }

      return handler(request);
    });
  };
}

/**
 * Rate limiting middleware (basic implementation)
 */
export function rateLimit(maxRequests: number = 100, windowMs: number = 15 * 60 * 1000) {
  const requests = new Map<string, { count: number; resetTime: number }>();

  return (handler: (req: NextRequest) => Promise<NextResponse>) => {
    return async (request: NextRequest) => {
      const ip = request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip') || 'unknown';
      const now = Date.now();

      const userRequests = requests.get(ip);

      if (!userRequests || now > userRequests.resetTime) {
        requests.set(ip, { count: 1, resetTime: now + windowMs });
      } else {
        userRequests.count++;

        if (userRequests.count > maxRequests) {
          return NextResponse.json(
            { error: 'Too many requests' },
            { status: 429 },
          );
        }
      }

      return handler(request);
    };
  };
}

export default {
  authenticateToken,
  requireAuth,
  optionalAuth,
  requireSubscription,
  rateLimit,
};

import bcrypt from 'bcryptjs';

const SALT_ROUNDS = 12;

/**
 * Hash password using bcrypt
 */
export async function hashPassword(password: string): Promise<string> {
  try {
    const salt = await bcrypt.genSalt(SALT_ROUNDS);
    const hashedPassword = await bcrypt.hash(password, salt);
    return hashedPassword;
  } catch (error) {
    console.error('Password hashing failed:', error);
    throw new Error('Failed to hash password');
  }
}

/**
 * Compare password with hash
 */
export async function comparePassword(password: string, hash: string): Promise<boolean> {
  try {
    const isMatch = await bcrypt.compare(password, hash);
    return isMatch;
  } catch (error) {
    console.error('Password comparison failed:', error);
    return false;
  }
}

/**
 * Validate password strength
 */
export function validatePassword(password: string): {
  isValid: boolean;
  errors: string[];
} {
  const errors: string[] = [];

  if (password.length < 8) {
    errors.push('Password must be at least 8 characters long');
  }

  if (password.length > 128) {
    errors.push('Password must be less than 128 characters long');
  }

  if (!/[a-z]/.test(password)) {
    errors.push('Password must contain at least one lowercase letter');
  }

  if (!/[A-Z]/.test(password)) {
    errors.push('Password must contain at least one uppercase letter');
  }

  if (!/\d/.test(password)) {
    errors.push('Password must contain at least one number');
  }

  if (!/[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/.test(password)) {
    errors.push('Password must contain at least one special character');
  }

  return {
    isValid: errors.length === 0,
    errors,
  };
}

/**
 * Generate random password
 */
export function generateRandomPassword(length: number = 12): string {
  const lowercase = 'abcdefghijklmnopqrstuvwxyz';
  const uppercase = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  const numbers = '0123456789';
  const symbols = '!@#$%^&*()_+-=[]{}|;:,.<>?';

  const allChars = lowercase + uppercase + numbers + symbols;

  let password = '';

  // Ensure at least one character from each category
  password += lowercase[Math.floor(Math.random() * lowercase.length)];
  password += uppercase[Math.floor(Math.random() * uppercase.length)];
  password += numbers[Math.floor(Math.random() * numbers.length)];
  password += symbols[Math.floor(Math.random() * symbols.length)];

  // Fill the rest randomly
  for (let i = 4; i < length; i++) {
    password += allChars[Math.floor(Math.random() * allChars.length)];
  }

  // Shuffle the password
  return password.split('').sort(() => Math.random() - 0.5).join('');
}

export default {
  hashPassword,
  comparePassword,
  validatePassword,
  generateRandomPassword,
};

