/**
 * Register new user
 */
export async function registerUser(data: RegisterData): Promise<AuthResponse> {
  try {
    await connectDB();

    // Validate input
    if (!data.email || !data.password || !data.name) {
      return {
        success: false,
        error: 'Email, password, and name are required',
      };
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(data.email)) {
      return {
        success: false,
        error: 'Invalid email format',
      };
    }

    // Validate password strength
    const passwordValidation = validatePassword(data.password);
    if (!passwordValidation.isValid) {
      return {
        success: false,
        error: passwordValidation.errors.join(', '),
      };
    }

    // Check if user already exists
    const existingUser = await (User.findOne as any)({ email: data.email.toLowerCase() });
    if (existingUser) {
      return {
        success: false,
        error: 'User with this email already exists',
      };
    }

    // Hash password
    const hashedPassword = await hashPassword(data.password);

    // Create user
    const user = new User({
      email: data.email.toLowerCase(),
      name: data.name.trim(),
      password: hashedPassword,
      subscription: {
        plan: 'free',
        startDate: new Date(),
        isActive: true,
      },
      usage: {
        dailyGenerations: 0,
        dailyReuses: 0,
        lastResetDate: new Date(),
      },
      settings: {
        emailNotifications: true,
        publicProfile: false,
      },
    });

    const savedUser = await user.save();

    // Log registration contribution
    await (ContributionLog.create as any)({
      user: savedUser._id,
      action: 'create',
      target: {
        type: 'user',
        id: savedUser._id.toString(),
        title: 'User Registration',
      },
      points: 10, // Welcome bonus
      metadata: {
        description: 'User registration',
        category: 'account',
      },
    });

    // Generate tokens
    const token = generateToken(savedUser);
    const refreshToken = generateRefreshToken(savedUser);

    // Remove password from response
    const userResponse = savedUser.toObject();
    delete userResponse.password;

    return {
      success: true,
      user: userResponse,
      token,
      refreshToken,
    };
  } catch (error) {
    console.error('Registration error:', error);
    return {
      success: false,
      error: 'Registration failed. Please try again.',
    };
  }
}

/**
 * Login user
 */
export async function loginUser(data: LoginData): Promise<AuthResponse> {
  try {
    await connectDB();

    // Validate input
    if (!data.email || !data.password) {
      return {
        success: false,
        error: 'Email and password are required',
      };
    }

    // Find user
    const user = await (User.findOne as any)({ email: data.email.toLowerCase() });
    if (!user) {
      return {
        success: false,
        error: 'Invalid email or password',
      };
    }

    // Check password
    const isPasswordValid = await comparePassword(data.password, user.password);
    if (!isPasswordValid) {
      return {
        success: false,
        error: 'Invalid email or password',
      };
    }

    // Reset daily usage if needed
    user.resetDailyUsage();
    await user.save();

    // Generate tokens
    const token = generateToken(user);
    const refreshToken = generateRefreshToken(user);

    // Remove password from response
    const userResponse = user.toObject();
    delete userResponse.password;

    return {
      success: true,
      user: userResponse,
      token,
      refreshToken,
    };
  } catch (error) {
    console.error('Login error:', error);
    return {
      success: false,
      error: 'Login failed. Please try again.',
    };
  }
}

/**
 * Get user profile
 */
export async function getUserProfile(userId: string): Promise<{
  success: boolean;
  user?: Omit<UserDocument, 'password'>;
  error?: string;
}> {
  try {
    await connectDB();

    const user = await (User.findById as any)(userId).select('-password');
    if (!user) {
      return {
        success: false,
        error: 'User not found',
      };
    }

    // Reset daily usage if needed
    user.resetDailyUsage();
    await user.save();

    return {
      success: true,
      user: user.toObject(),
    };
  } catch (error) {
    console.error('Get profile error:', error);
    return {
      success: false,
      error: 'Failed to get user profile',
    };
  }
}

/**
 * Update user profile
 */
export async function updateUserProfile(
  userId: string,
  updates: Partial<Pick<UserDocument, 'name'>>,
): Promise<{
  success: boolean;
  user?: Omit<UserDocument, 'password'>;
  error?: string;
}> {
  try {
    await connectDB();

    const user = await (User.findByIdAndUpdate as any)(
      userId,
      { $set: updates },
      { new: true, runValidators: true },
    ).select('-password');

    if (!user) {
      return {
        success: false,
        error: 'User not found',
      };
    }

    return {
      success: true,
      user: user.toObject(),
    };
  } catch (error) {
    console.error('Update profile error:', error);
    return {
      success: false,
      error: 'Failed to update profile',
    };
  }
}

/**
 * Change user password
 */
export async function changePassword(
  userId: string,
  currentPassword: string,
  newPassword: string,
): Promise<{
  success: boolean;
  error?: string;
}> {
  try {
    await connectDB();

    // Find user with password
    const user = await (User.findById as any)(userId);
    if (!user) {
      return {
        success: false,
        error: 'User not found',
      };
    }

    // Verify current password
    const isCurrentPasswordValid = await comparePassword(currentPassword, user.password);
    if (!isCurrentPasswordValid) {
      return {
        success: false,
        error: 'Current password is incorrect',
      };
    }

    // Validate new password
    const passwordValidation = validatePassword(newPassword);
    if (!passwordValidation.isValid) {
      return {
        success: false,
        error: passwordValidation.errors.join(', '),
      };
    }

    // Hash new password
    const hashedNewPassword = await hashPassword(newPassword);

    // Update password
    user.password = hashedNewPassword;
    await user.save();

    return {
      success: true,
    };
  } catch (error) {
    console.error('Change password error:', error);
    return {
      success: false,
      error: 'Failed to change password',
    };
  }
}

export default {
  registerUser,
  loginUser,
  getUserProfile,
  updateUserProfile,
  changePassword,
};

/**
 * 
 * D3.js
 */
'use client';

import React, { useRef, useEffect, useState, useCallback } from 'react';

import { transformGraphData } from '@/core/graph/d3-utils';
import { D3GraphRenderer } from '@/core/graph/graph-renderer';
import {
  GraphVisualizationData,
  LayoutConfig,
  VisualConfig,
  InteractionConfig,
  GraphEvent,
  DEFAULT_LAYOUT_CONFIG,
  DEFAULT_VISUAL_CONFIG,
  DEFAULT_INTERACTION_CONFIG,
} from '@/core/graph/types';
import useKnowledgeGraph from '@/shared/hooks/useKnowledgeGraph';
import { KnowledgeGraph } from '@/shared/types/knowledgeGraph';

interface KnowledgeGraphViewerProps {
  graphId?: string
  graph?: KnowledgeGraph
  width?: number
  height?: number
  layoutConfig?: Partial<LayoutConfig>
  visualConfig?: Partial<VisualConfig>
  interactionConfig?: Partial<InteractionConfig>
  className?: string
  onNodeClick?: (nodeId: string, event: GraphEvent) => void
  onNodeDoubleClick?: (nodeId: string, event: GraphEvent) => void
  onEdgeClick?: (edgeId: string, event: GraphEvent) => void
  onSelectionChange?: (selection: { nodes: string[]; edges: string[] }) => void
  onError?: (error: string) => void
}

export function KnowledgeGraphViewer({
  graphId,
  graph: externalGraph,
  width = 800,
  height = 600,
  layoutConfig = {},
  visualConfig = {},
  interactionConfig = {},
  className = '',
  onNodeClick,
  onNodeDoubleClick,
  onEdgeClick,
  onSelectionChange,
  onError,
}: KnowledgeGraphViewerProps) {
  // DOM
  const containerRef = useRef<HTMLDivElement>(null);
  const rendererRef = useRef<D3GraphRenderer | null>(null);

  // 
  const [isInitialized, setIsInitialized] = useState(false);
  const [currentSelection, setCurrentSelection] = useState<{ nodes: string[]; edges: string[] }>({
    nodes: [],
    edges: [],
  });

  // Hook
  const {
    graph: hookGraph,
    visualizationData: hookVisualizationData,
    loading,
    error,
  } = useKnowledgeGraph({
    graphId: externalGraph ? undefined : graphId,
    autoFetch: !externalGraph && !!graphId,
  });

  // 
  const currentGraph = externalGraph || hookGraph;
  const currentVisualizationData = externalGraph
    ? transformGraphData(externalGraph.nodes, externalGraph.edges, {
        ...DEFAULT_VISUAL_CONFIG,
        ...visualConfig,
      })
    : hookVisualizationData;

  // 
  const mergedLayoutConfig: LayoutConfig = {
    ...DEFAULT_LAYOUT_CONFIG,
    width,
    height,
    ...layoutConfig,
  };

  const mergedVisualConfig: VisualConfig = {
    ...DEFAULT_VISUAL_CONFIG,
    ...visualConfig,
  };

  const mergedInteractionConfig: InteractionConfig = {
    ...DEFAULT_INTERACTION_CONFIG,
    ...interactionConfig,
  };

  // 
  const initializeRenderer = useCallback(() => {
    if (!containerRef.current || !currentVisualizationData) return;

    // 
    if (rendererRef.current) {
      rendererRef.current.destroy();
    }

    // 
    rendererRef.current = new D3GraphRenderer({
      container: containerRef.current,
      layout: mergedLayoutConfig,
      visual: mergedVisualConfig,
      interaction: mergedInteractionConfig,
      data: currentVisualizationData,
    });

    // 
    bindEventHandlers();

    setIsInitialized(true);
  }, [currentVisualizationData, mergedLayoutConfig, mergedVisualConfig, mergedInteractionConfig]);

  // 
  const bindEventHandlers = useCallback(() => {
    if (!rendererRef.current) return;

    const renderer = rendererRef.current;

    // 
    renderer.on('node:click', (event) => {
      if (event.target && onNodeClick) {
        onNodeClick(event.target.id, event);
      }
    });

    // 
    renderer.on('node:dblclick', (event) => {
      if (event.target && onNodeDoubleClick) {
        onNodeDoubleClick(event.target.id, event);
      }
    });

    // 
    renderer.on('edge:click', (event) => {
      if (event.target && onEdgeClick) {
        onEdgeClick(event.target.id, event);
      }
    });

    // 
    renderer.on('selection:change', (event) => {
      const selection = event.data as { nodes: string[]; edges: string[] };
      setCurrentSelection(selection);
      if (onSelectionChange) {
        onSelectionChange(selection);
      }
    });

    // 
    renderer.on('canvas:click', () => {
      setCurrentSelection({ nodes: [], edges: [] });
      if (onSelectionChange) {
        onSelectionChange({ nodes: [], edges: [] });
      }
    });
  }, [onNodeClick, onNodeDoubleClick, onEdgeClick, onSelectionChange]);

  // 
  const updateRenderer = useCallback(() => {
    if (!rendererRef.current || !currentVisualizationData) return;

    rendererRef.current.update(currentVisualizationData);
  }, [currentVisualizationData]);

  // 
  useEffect(() => {
    if (error && onError) {
      onError(error);
    }
  }, [error, onError]);

  // 
  useEffect(() => {
    if (currentVisualizationData) {
      if (isInitialized) {
        updateRenderer();
      } else {
        initializeRenderer();
      }
    }
  }, [currentVisualizationData, isInitialized, initializeRenderer, updateRenderer]);

  // 
  useEffect(() => {
    if (rendererRef.current) {
      rendererRef.current.setLayout(mergedLayoutConfig);
      rendererRef.current.setVisualConfig(mergedVisualConfig);
      rendererRef.current.enableInteraction(mergedInteractionConfig);
    }
  }, [mergedLayoutConfig, mergedVisualConfig, mergedInteractionConfig]);

  // 
  useEffect(() => {
    return () => {
      if (rendererRef.current) {
        rendererRef.current.destroy();
      }
    };
  }, []);

  // 
  const zoomToFit = useCallback((padding?: number) => {
    if (rendererRef.current) {
      rendererRef.current.zoomToFit(padding);
    }
  }, []);

  const zoomToNodes = useCallback((nodeIds: string[], padding?: number) => {
    if (rendererRef.current) {
      rendererRef.current.zoomToNodes(nodeIds, padding);
    }
  }, []);

  const centerView = useCallback(() => {
    if (rendererRef.current) {
      rendererRef.current.centerView();
    }
  }, []);

  const selectNodes = useCallback((nodeIds: string[]) => {
    if (rendererRef.current) {
      rendererRef.current.selectNodes(nodeIds);
    }
  }, []);

  const selectEdges = useCallback((edgeIds: string[]) => {
    if (rendererRef.current) {
      rendererRef.current.selectEdges(edgeIds);
    }
  }, []);

  const clearSelection = useCallback(() => {
    if (rendererRef.current) {
      rendererRef.current.clearSelection();
    }
  }, []);

  const getSelection = useCallback(() => {
    return rendererRef.current ? rendererRef.current.getSelection() : { nodes: [], edges: [] };
  }, []);

  // ref
  // forwardRef
  // React.useImperativeHandle(ref, () => ({
  //   zoomToFit,
  //   zoomToNodes,
  //   centerView,
  //   selectNodes,
  //   selectEdges,
  //   clearSelection,
  //   getSelection,
  //   getRenderer: () => rendererRef.current
  // }))

  // 
  if (loading) {
    return (
      <div
        className={`flex items-center justify-center bg-gray-50 ${className}`}
        style={{ width, height }}
      >
        <div className="flex flex-col items-center space-y-4">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          <p className="text-gray-600">...</p>
        </div>
      </div>
    );
  }

  // 
  if (error) {
    return (
      <div
        className={`flex items-center justify-center bg-red-50 border border-red-200 rounded-lg ${className}`}
        style={{ width, height }}
      >
        <div className="text-center">
          <div className="text-red-600 mb-2">
            <svg className="w-12 h-12 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
            </svg>
          </div>
          <p className="text-red-800 font-medium"></p>
          <p className="text-red-600 text-sm mt-1">{error}</p>
        </div>
      </div>
    );
  }

  // 
  if (!currentGraph || !currentVisualizationData) {
    return (
      <div
        className={`flex items-center justify-center bg-gray-50 border-2 border-dashed border-gray-300 rounded-lg ${className}`}
        style={{ width, height }}
      >
        <div className="text-center">
          <div className="text-gray-400 mb-2">
            <svg className="w-12 h-12 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
          </div>
          <p className="text-gray-600 font-medium"></p>
          <p className="text-gray-500 text-sm mt-1"></p>
        </div>
      </div>
    );
  }

  // 
  return (
    <div className={`relative ${className}`}>
      {/*  */}
      <div
        ref={containerRef}
        className="w-full h-full border border-gray-200 rounded-lg overflow-hidden bg-white"
        style={{ width, height }}
      />

      {/*  */}
      <div className="absolute top-4 left-4 bg-white bg-opacity-90 rounded-lg px-3 py-2 shadow-sm">
        <h3 className="font-medium text-gray-900 text-sm">{currentGraph.name}</h3>
        <div className="flex items-center space-x-4 text-xs text-gray-600 mt-1">
          <span>{currentGraph.nodes.length} </span>
          <span>{currentGraph.edges.length} </span>
          {currentSelection.nodes.length > 0 && (
            <span className="text-blue-600">
               {currentSelection.nodes.length} 
            </span>
          )}
        </div>
      </div>

      {/*  */}
      <div className="absolute top-4 right-4 flex flex-col space-y-2">
        <button
          onClick={() => zoomToFit()}
          className="p-2 bg-white bg-opacity-90 rounded-lg shadow-sm hover:bg-opacity-100 transition-all"
          title=""
        >
          <svg className="w-4 h-4 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 8V4m0 0h4M4 4l5 5m11-1V4m0 0h-4m4 0l-5 5M4 16v4m0 0h4m-4 0l5-5m11 5l-5-5m5 5v-4m0 4h-4" />
          </svg>
        </button>

        <button
          onClick={() => centerView()}
          className="p-2 bg-white bg-opacity-90 rounded-lg shadow-sm hover:bg-opacity-100 transition-all"
          title=""
        >
          <svg className="w-4 h-4 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
          </svg>
        </button>

        {currentSelection.nodes.length > 0 && (
          <button
            onClick={() => zoomToNodes(currentSelection.nodes)}
            className="p-2 bg-blue-600 bg-opacity-90 rounded-lg shadow-sm hover:bg-opacity-100 transition-all text-white"
            title=""
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </button>
        )}
      </div>
    </div>
  );
}

export default KnowledgeGraphViewer;

/**
 * D3.js 
 * 
 */
import * as d3 from 'd3';

import { GraphNode, GraphEdge } from '@/shared/types/knowledgeGraph';

import {
  D3Node,
  D3Edge,
  GraphVisualizationData,
  VisualConfig,
  DEFAULT_VISUAL_CONFIG,
} from './types';

/**
 * D3
 */
export function transformGraphData(
  nodes: GraphNode[],
  edges: GraphEdge[],
  visualConfig: VisualConfig = DEFAULT_VISUAL_CONFIG,
): GraphVisualizationData {
  // 
  const d3Nodes: D3Node[] = nodes.map(node => ({
    id: node.id,
    label: node.label,
    type: node.type,
    level: node.level,
    x: node.position?.x || 0,
    y: node.position?.y || 0,
    fx: null,
    fy: null,
    // 
    radius: calculateNodeRadius(node, visualConfig),
    color: getNodeColor(node.type, visualConfig),
    strokeColor: visualConfig.colors.nodes[node.type] || '#666',
    strokeWidth: visualConfig.node.strokeWidth,
    opacity: visualConfig.node.opacity,
    // 
    originalData: node,
    // 
    selected: false,
    hovered: false,
    dragging: false,
  }));

  // ID
  const nodeMap = new Map(d3Nodes.map(node => [node.id, node]));

  // 
  const d3Links: D3Edge[] = edges
    .filter(edge => nodeMap.has(edge.source) && nodeMap.has(edge.target))
    .map(edge => ({
      id: edge.id,
      source: nodeMap.get(edge.source)!,
      target: nodeMap.get(edge.target)!,
      type: edge.type,
      weight: edge.weight,
      // 
      color: getEdgeColor(edge.type, visualConfig),
      strokeWidth: calculateEdgeWidth(edge, visualConfig),
      strokeDasharray: getEdgeStyle(edge.type),
      opacity: visualConfig.edge.opacity,
      // 
      originalData: edge,
      // 
      selected: false,
      hovered: false,
    }));

  // 
  const bounds = calculateBounds(d3Nodes);

  return {
    nodes: d3Nodes,
    links: d3Links,
    bounds,
  };
}

/**
 * 
 */
function calculateNodeRadius(node: GraphNode, config: VisualConfig): number {
  const { defaultRadius, minRadius, maxRadius } = config.node;

  // 
  let radius = defaultRadius;

  // 
  switch (node.type) {
    case 'subject':
      radius = defaultRadius * 1.5;
      break;
    case 'chapter':
      radius = defaultRadius * 1.2;
      break;
    case 'topic':
      radius = defaultRadius;
      break;
    case 'concept':
      radius = defaultRadius * 0.8;
      break;
    case 'skill':
      radius = defaultRadius * 0.9;
      break;
  }

  // 
  if (node.metadata.importance) {
    radius *= (0.7 + node.metadata.importance * 0.6);
  }

  // 
  if (node.metadata.workCount > 0) {
    radius *= (1 + Math.log(node.metadata.workCount + 1) * 0.1);
  }

  return Math.max(minRadius, Math.min(maxRadius, radius));
}

/**
 * 
 */
function getNodeColor(nodeType: string, config: VisualConfig): string {
  return config.colors.nodes[nodeType] || '#666666';
}

/**
 * 
 */
function calculateEdgeWidth(edge: GraphEdge, config: VisualConfig): number {
  const { defaultStrokeWidth, minStrokeWidth, maxStrokeWidth } = config.edge;

  let width = defaultStrokeWidth;

  // 
  width *= edge.weight;

  // 
  if (edge.metadata?.strength) {
    width *= edge.metadata.strength;
  }

  return Math.max(minStrokeWidth, Math.min(maxStrokeWidth, width));
}

/**
 * 
 */
function getEdgeColor(edgeType: string, config: VisualConfig): string {
  return config.colors.edges[edgeType] || '#999999';
}

/**
 * 
 */
function getEdgeStyle(edgeType: string): string | undefined {
  switch (edgeType) {
    case 'prerequisite':
      return '5,5';
    case 'related':
      return '2,3';
    default:
      return undefined;
  }
}

/**
 * 
 */
function calculateBounds(nodes: D3Node[]): { minX: number; maxX: number; minY: number; maxY: number } {
  if (nodes.length === 0) {
    return { minX: 0, maxX: 0, minY: 0, maxY: 0 };
  }

  let minX = Infinity, maxX = -Infinity;
  let minY = Infinity, maxY = -Infinity;

  nodes.forEach(node => {
    const x = node.x || 0;
    const y = node.y || 0;
    const r = node.radius;

    minX = Math.min(minX, x - r);
    maxX = Math.max(maxX, x + r);
    minY = Math.min(minY, y - r);
    maxY = Math.max(maxY, y + r);
  });

  return { minX, maxX, minY, maxY };
}

/**
 * 
 */
export function createZoomBehavior(
  svg: d3.Selection<SVGSVGElement, unknown, null, undefined>,
  container: d3.Selection<SVGGElement, unknown, null, undefined>,
  scaleExtent: [number, number] = [0.1, 3],
) {
  const zoom = d3.zoom<SVGSVGElement, unknown>()
    .scaleExtent(scaleExtent)
    .on('zoom', (event) => {
      container.attr('transform', event.transform);
    });

  svg.call(zoom);

  return zoom;
}

/**
 * 
 */
export function createDragBehavior(
  simulation: d3.Simulation<D3Node, D3Edge>,
  onDragStart?: (node: D3Node) => void,
  onDrag?: (node: D3Node) => void,
  onDragEnd?: (node: D3Node) => void,
) {
  return d3.drag<SVGCircleElement, D3Node>()
    .on('start', (event, d) => {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
      d.dragging = true;
      onDragStart && onDragStart(d);
    })
    .on('drag', (event, d) => {
      d.fx = event.x;
      d.fy = event.y;
      onDrag && onDrag(d);
    })
    .on('end', (event, d) => {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
      d.dragging = false;
      onDragEnd && onDragEnd(d);
    });
}

/**
 * 
 */
export function createTooltip() {
  return d3.select('body')
    .append('div')
    .attr('class', 'graph-tooltip')
    .style('position', 'absolute')
    .style('visibility', 'hidden')
    .style('background', 'rgba(0, 0, 0, 0.8)')
    .style('color', 'white')
    .style('padding', '8px 12px')
    .style('border-radius', '4px')
    .style('font-size', '12px')
    .style('pointer-events', 'none')
    .style('z-index', '1000');
}

/**
 * 
 */
export function showTooltip(
  tooltip: d3.Selection<HTMLDivElement, unknown, HTMLElement, any>,
  content: string,
  event: MouseEvent,
  offset: { x: number; y: number } = { x: 10, y: -10 },
) {
  tooltip
    .style('visibility', 'visible')
    .html(content)
    .style('left', (event.pageX + offset.x) + 'px')
    .style('top', (event.pageY + offset.y) + 'px');
}

/**
 * 
 */
export function hideTooltip(
  tooltip: d3.Selection<HTMLDivElement, unknown, HTMLElement, any>,
) {
  tooltip.style('visibility', 'hidden');
}

/**
 * 
 */
export function distance(p1: { x: number; y: number }, p2: { x: number; y: number }): number {
  const dx = p1.x - p2.x;
  const dy = p1.y - p2.y;
  return Math.sqrt(dx * dx + dy * dy);
}

/**
 * 
 */
export function constrainToRect(
  point: { x: number; y: number },
  rect: { x: number; y: number; width: number; height: number },
): { x: number; y: number } {
  return {
    x: Math.max(rect.x, Math.min(rect.x + rect.width, point.x)),
    y: Math.max(rect.y, Math.min(rect.y + rect.height, point.y)),
  };
}

/**
 * 
 */
export function snapToGrid(
  point: { x: number; y: number },
  gridSize: number,
): { x: number; y: number } {
  return {
    x: Math.round(point.x / gridSize) * gridSize,
    y: Math.round(point.y / gridSize) * gridSize,
  };
}

/**
 * 
 */
export function interpolateColor(color1: string, color2: string, t: number): string {
  const interpolator = d3.interpolate(color1, color2);
  return interpolator(t);
}

/**
 * 
 */
export function generateNodeTooltip(node: D3Node): string {
  const { originalData } = node;
  return `
    <div>
      <strong>${node.label}</strong><br/>
      : ${node.type}<br/>
      : ${node.level}<br/>
      : ${originalData.metadata.workCount}<br/>
      ${originalData.metadata.description ? `: ${originalData.metadata.description}` : ''}
    </div>
  `;
}

/**
 * 
 */
export function generateEdgeTooltip(edge: D3Edge): string {
  const sourceLabel = typeof edge.source === 'object' ? edge.source.label : edge.source;
  const targetLabel = typeof edge.target === 'object' ? edge.target.label : edge.target;

  return `
    <div>
      <strong>${sourceLabel}  ${targetLabel}</strong><br/>
      : ${edge.type}<br/>
      : ${edge.weight}<br/>
      ${edge.originalData.metadata?.description ? `: ${edge.originalData.metadata.description}` : ''}
    </div>
  `;
}

export default {
  transformGraphData,
  createZoomBehavior,
  createDragBehavior,
  createTooltip,
  showTooltip,
  hideTooltip,
  distance,
  constrainToRect,
  snapToGrid,
  interpolateColor,
  generateNodeTooltip,
  generateEdgeTooltip,
};

import * as d3 from 'd3';

// D3.js v6+ 
type D3Selection = d3.Selection<any, any, any, any>;
type D3Simulation = d3.Simulation<any, any>;
type D3Force = d3.Force<any, any>;
type D3Zoom = d3.ZoomBehavior<any, any>;
type D3Drag = d3.DragBehavior<any, any, any>;


import { SimulationNodeDatum, SimulationLinkDatum } from 'd3-force';

interface GraphNode extends SimulationNodeDatum {
  id: string;
  label: string;
  radius?: number;
  color?: string;
}

interface GraphLink extends SimulationLinkDatum<GraphNode> {
  source: string | GraphNode;
  target: string | GraphNode;
}

/**
 * D3.js 
 * 
 */

import {
  createZoomBehavior,
  createDragBehavior,
  createTooltip,
  showTooltip,
  hideTooltip,
  generateNodeTooltip,
  generateEdgeTooltip,
} from './d3-utils';
import {
  GraphRenderer,
  GraphRendererConfig,
  GraphVisualizationData,
  LayoutConfig,
  VisualConfig,
  InteractionConfig,
  D3Node,
  D3Edge,
  GraphEvent,
  GraphEventType,
  GraphEventHandler,
  DEFAULT_LAYOUT_CONFIG,
  DEFAULT_VISUAL_CONFIG,
  DEFAULT_INTERACTION_CONFIG,
} from './types';

export class D3GraphRenderer implements GraphRenderer {
  private container: HTMLElement;
  private svg: d3.Selection<SVGSVGElement, unknown, null, undefined>;
  private g: d3.Selection<SVGGElement, unknown, null, undefined>;
  private simulation: d3.Simulation<D3Node, D3Edge>;
  private tooltip: d3.Selection<HTMLDivElement, unknown, HTMLElement, any>;
  private zoom: d3.ZoomBehavior<SVGSVGElement, unknown>;

  private layoutConfig: LayoutConfig;
  private visualConfig: VisualConfig;
  private interactionConfig: InteractionConfig;
  private data: GraphVisualizationData;

  private eventHandlers: Map<GraphEventType, GraphEventHandler[]> = new Map();
  private selection: { nodes: string[]; edges: string[] } = { nodes: [], edges: [] };

  // DOM
  private nodeSelection!: d3.Selection<SVGCircleElement, D3Node, SVGGElement, unknown>;
  private linkSelection!: d3.Selection<SVGLineElement, D3Edge, SVGGElement, unknown>;
  private labelSelection!: d3.Selection<SVGTextElement, D3Node, SVGGElement, unknown>;

  constructor(config: GraphRendererConfig) {
    this.container = config.container;
    this.layoutConfig = { ...DEFAULT_LAYOUT_CONFIG, ...config.layout };
    this.visualConfig = { ...DEFAULT_VISUAL_CONFIG, ...config.visual };
    this.interactionConfig = { ...DEFAULT_INTERACTION_CONFIG, ...config.interaction };
    this.data = config.data;

    this.initializeSVG();
    this.initializeSimulation();
    this.initializeTooltip();
    this.render(this.data);
  }

  /**
   * SVG
   */
  private initializeSVG(): void {
    // 
    d3.select<any, any>(this.container).selectAll('*').remove();

    // SVG
    this.svg = d3.select<any, any>(this.container)
      .append('svg')
      .attr('width', this.layoutConfig.width)
      .attr('height', this.layoutConfig.height)
      .style('background-color', this.visualConfig.colors.background);

    // 
    this.g = this.svg.append('g')
      .attr('class', 'graph-container');

    // 
    this.g.append('g').attr('class', 'links');
    this.g.append('g').attr('class', 'nodes');
    this.g.append('g').attr('class', 'labels');

    // 
    if (this.interactionConfig.zoom.enabled) {
      this.zoom = createZoomBehavior(
        this.svg,
        this.g,
        this.interactionConfig.zoom.scaleExtent,
      );
    }

    // 
    this.svg.on('click', (event: any) => {
      if (event.target === event.currentTarget) {
        this.clearSelection();
        this.emit({
          type: 'canvas:click',
          target: null,
          originalEvent: event,
          position: { x: d3.pointer(event)[0], y: d3.pointer(event)[1] },
        });
      }
    });
  }

  /**
   * 
   */
  private initializeSimulation(): void {
    this.simulation = d3.forceSimulation<D3Node>()
      .force('link', d3.forceLink<D3Node, D3Edge>()
        .id(d => d.id)
        .distance(this.layoutConfig.options.linkDistance || 80)
        .strength(this.layoutConfig.options.linkStrength || 0.5),
      )
      .force('charge', d3.forceManyBody()
        .strength(this.layoutConfig.options.chargeStrength || -300),
      )
      .force('center', d3.forceCenter(
        this.layoutConfig.width / 2,
        this.layoutConfig.height / 2,
      ).strength(this.layoutConfig.options.centerStrength || 0.1))
      .force('collision', d3.forceCollide<D3Node>()
        .radius(d => d.radius + (this.layoutConfig.options.collisionRadius || 5)),
      )
      .alpha(this.layoutConfig.options.alpha || 0.3)
      .alphaDecay(this.layoutConfig.options.alphaDecay || 0.02)
      .velocityDecay(this.layoutConfig.options.velocityDecay || 0.4);

    // tick
    this.simulation.on('tick', () => this.tick());
  }

  /**
   * 
   */
  private initializeTooltip(): void {
    if (this.interactionConfig.tooltip.enabled) {
      this.tooltip = createTooltip();
    }
  }

  /**
   * 
   */
  render(data: GraphVisualizationData): void {
    this.data = data;
    this.updateLinks();
    this.updateNodes();
    this.updateLabels();
    this.updateSimulation();
  }

  /**
   * 
   */
  update(data: GraphVisualizationData): void {
    this.render(data);
  }

  /**
   * 
   */
  clear(): void {
    this.g.selectAll('.links line').remove();
    this.g.selectAll('.nodes circle').remove();
    this.g.selectAll('.labels text').remove();
    this.simulation.nodes([]);
    this.simulation.force('link', d3.forceLink([]));
  }

  /**
   * 
   */
  private updateLinks(): void {
    const linkSelection = this.g
      .select<SVGGElement>('g.links')
      .selectAll<SVGLineElement, D3Edge>('line')
      .data(this.data.links, (d: D3Edge) => d.id);

    // 
    linkSelection.exit().remove();

    // 
    const linkEnter = linkSelection.enter()
      .append('line')
      .attr('class', 'link')
      .style('cursor', 'pointer');

    // 
    this.linkSelection = linkEnter.merge(linkSelection) as d3.Selection<SVGLineElement, D3Edge, SVGGElement, unknown>;

    // 
    this.linkSelection
      .attr('stroke', d => d.color)
      .attr('stroke-width', d => d.strokeWidth)
      .attr('stroke-opacity', d => d.opacity)
      .attr('stroke-dasharray', d => d.strokeDasharray || null);

    // 
    this.bindLinkEvents();
  }

  /**
   * 
   */
  private updateNodes(): void {
    const nodeSelection = this.g
      .select<SVGGElement>('g.nodes')
      .selectAll<SVGCircleElement, D3Node>('circle')
      .data(this.data.nodes, (d: D3Node) => d.id);

    // 
    nodeSelection.exit().remove();

    // 
    const nodeEnter = nodeSelection.enter()
      .append('circle')
      .attr('class', 'node')
      .style('cursor', 'pointer');

    // 
    this.nodeSelection = nodeEnter.merge(nodeSelection) as d3.Selection<SVGCircleElement, D3Node, SVGGElement, unknown>;

    // 
    this.nodeSelection
      .attr('r', d => d.radius)
      .attr('fill', d => d.color)
      .attr('stroke', d => d.strokeColor)
      .attr('stroke-width', d => d.strokeWidth)
      .attr('fill-opacity', d => d.opacity);

    // 
    if (this.interactionConfig.drag.enabled) {
      const drag = createDragBehavior(
        this.simulation,
        (node) => this.onNodeDragStart(node),
        (node) => this.onNodeDrag(node),
        (node) => this.onNodeDragEnd(node),
      );
      this.nodeSelection.call(drag);
    }

    // 
    this.bindNodeEvents();
  }

  /**
   * 
   */
  private updateLabels(): void {
    const labelSelection = this.g
      .select<SVGGElement>('g.labels')
      .selectAll<SVGTextElement, D3Node>('text')
      .data(this.data.nodes, (d: D3Node) => d.id);

    // 
    labelSelection.exit().remove();

    // 
    const labelEnter = labelSelection.enter()
      .append('text')
      .attr('class', 'label')
      .style('pointer-events', 'none')
      .style('user-select', 'none');

    // 
    this.labelSelection = labelEnter.merge(labelSelection) as d3.Selection<SVGTextElement, D3Node, SVGGElement, unknown>;

    // 
    this.labelSelection
      .text(d => d.label)
      .attr('text-anchor', 'middle')
      .attr('dominant-baseline', 'central')
      .style('font-size', '12px')
      .style('font-family', 'Arial, sans-serif')
      .style('fill', '#333')
      .style('font-weight', 'bold');
  }

  /**
   * 
   */
  private updateSimulation(): void {
    this.simulation.nodes(this.data.nodes);

    const linkForce = this.simulation.force('link') as d3.ForceLink<D3Node, D3Edge>;
    if (linkForce) {
      linkForce.links(this.data.links);
    }

    this.simulation.alpha(0.3).restart();
  }

  /**
   * tick
   */
  private tick(): void {
    // 
    this.linkSelection
      .attr('x1', d => (d.source as D3Node).x!)
      .attr('y1', d => (d.source as D3Node).y!)
      .attr('x2', d => (d.target as D3Node).x!)
      .attr('y2', d => (d.target as D3Node).y!);

    // 
    this.nodeSelection
      .attr('cx', d => d.x!)
      .attr('cy', d => d.y!);

    // 
    this.labelSelection
      .attr('x', d => d.x!)
      .attr('y', d => d.y!);
  }

  /**
   * 
   */
  private bindNodeEvents(): void {
    this.nodeSelection
      .on('click', (event: any, d) => {
        event.stopPropagation();
        this.handleNodeClick(event, d);
      })
      .on('dblclick', (event: any, d) => {
        event.stopPropagation();
        this.emit({
          type: 'node:dblclick',
          target: d,
          originalEvent: event,
          position: { x: d3.pointer(event)[0], y: d3.pointer(event)[1] },
        });
      })
      .on('mouseenter', (event: any, d) => {
        this.handleNodeMouseEnter(event, d);
      })
      .on('mouseleave', (event: any, d) => {
        this.handleNodeMouseLeave(event, d);
      });
  }

  /**
   * 
   */
  private bindLinkEvents(): void {
    this.linkSelection
      .on('click', (event: any, d) => {
        event.stopPropagation();
        this.handleEdgeClick(event, d);
      })
      .on('mouseenter', (event: any, d) => {
        this.handleEdgeMouseEnter(event, d);
      })
      .on('mouseleave', (event: any, d) => {
        this.handleEdgeMouseLeave(event, d);
      });
  }

  /**
   * 
   */
  private handleNodeClick(event: MouseEvent, node: D3Node): void {
    if (this.interactionConfig.selection.enabled) {
      if (this.interactionConfig.selection.multiSelect && event.ctrlKey) {
        // 
        this.toggleNodeSelection(node.id);
      } else {
        // 
        this.selectNodes([node.id]);
      }
    }

    this.emit({
      type: 'node:click',
      target: node,
      originalEvent: event,
      position: { x: d3.pointer(event)[0], y: d3.pointer(event)[1] },
    });
  }

  /**
   * 
   */
  private handleNodeMouseEnter(event: MouseEvent, node: D3Node): void {
    node.hovered = true;
    this.updateNodeStyle(node);

    if (this.tooltip && this.interactionConfig.tooltip.enabled) {
      const content = generateNodeTooltip(node);
      showTooltip(this.tooltip, content, event, this.interactionConfig.tooltip.offset);
    }

    this.emit({
      type: 'node:mouseenter',
      target: node,
      originalEvent: event,
      position: { x: d3.pointer(event)[0], y: d3.pointer(event)[1] },
    });
  }

  /**
   * 
   */
  private handleNodeMouseLeave(event: MouseEvent, node: D3Node): void {
    node.hovered = false;
    this.updateNodeStyle(node);

    if (this.tooltip) {
      hideTooltip(this.tooltip);
    }

    this.emit({
      type: 'node:mouseleave',
      target: node,
      originalEvent: event,
      position: { x: d3.pointer(event)[0], y: d3.pointer(event)[1] },
    });
  }

  /**
   * 
   */
  private handleEdgeClick(event: MouseEvent, edge: D3Edge): void {
    if (this.interactionConfig.selection.enabled) {
      if (this.interactionConfig.selection.multiSelect && event.ctrlKey) {
        this.toggleEdgeSelection(edge.id);
      } else {
        this.selectEdges([edge.id]);
      }
    }

    this.emit({
      type: 'edge:click',
      target: edge,
      originalEvent: event,
      position: { x: d3.pointer(event)[0], y: d3.pointer(event)[1] },
    });
  }

  /**
   * 
   */
  private handleEdgeMouseEnter(event: MouseEvent, edge: D3Edge): void {
    edge.hovered = true;
    this.updateEdgeStyle(edge);

    if (this.tooltip && this.interactionConfig.tooltip.enabled) {
      const content = generateEdgeTooltip(edge);
      showTooltip(this.tooltip, content, event, this.interactionConfig.tooltip.offset);
    }

    this.emit({
      type: 'edge:mouseenter',
      target: edge,
      originalEvent: event,
      position: { x: d3.pointer(event)[0], y: d3.pointer(event)[1] },
    });
  }

  /**
   * 
   */
  private handleEdgeMouseLeave(event: MouseEvent, edge: D3Edge): void {
    edge.hovered = false;
    this.updateEdgeStyle(edge);

    if (this.tooltip) {
      hideTooltip(this.tooltip);
    }

    this.emit({
      type: 'edge:mouseleave',
      target: edge,
      originalEvent: event,
      position: { x: d3.pointer(event)[0], y: d3.pointer(event)[1] },
    });
  }

  /**
   * 
   */
  private onNodeDragStart(node: D3Node): void {
    this.emit({
      type: 'node:dragstart',
      target: node,
      originalEvent: event,
      position: { x: node.x!, y: node.y! },
    });
  }

  /**
   * 
   */
  private onNodeDrag(node: D3Node): void {
    this.emit({
      type: 'node:drag',
      target: node,
      originalEvent: event,
      position: { x: node.x!, y: node.y! },
    });
  }

  /**
   * 
   */
  private onNodeDragEnd(node: D3Node): void {
    this.emit({
      type: 'node:dragend',
      target: node,
      originalEvent: event,
      position: { x: node.x!, y: node.y! },
    });
  }

  /**
   * 
   */
  private updateNodeStyle(node: D3Node): void {
    const nodeElement = this.nodeSelection.filter(d => d.id === node.id);

    let opacity = this.visualConfig.node.opacity;
    if (node.selected) {
      opacity = this.visualConfig.node.selectedOpacity;
    } else if (node.hovered) {
      opacity = this.visualConfig.node.hoveredOpacity;
    }

    nodeElement
      .transition()
      .duration(this.visualConfig.animation.duration)
      .attr('fill-opacity', opacity)
      .attr('stroke', node.selected ? this.visualConfig.colors.selection : node.strokeColor)
      .attr('stroke-width', node.selected ? node.strokeWidth * 2 : node.strokeWidth);
  }

  /**
   * 
   */
  private updateEdgeStyle(edge: D3Edge): void {
    const edgeElement = this.linkSelection.filter(d => d.id === edge.id);

    let opacity = this.visualConfig.edge.opacity;
    if (edge.selected) {
      opacity = this.visualConfig.edge.selectedOpacity;
    } else if (edge.hovered) {
      opacity = this.visualConfig.edge.hoveredOpacity;
    }

    edgeElement
      .transition()
      .duration(this.visualConfig.animation.duration)
      .attr('stroke-opacity', opacity)
      .attr('stroke', edge.selected ? this.visualConfig.colors.selection : edge.color)
      .attr('stroke-width', edge.selected ? edge.strokeWidth * 1.5 : edge.strokeWidth);
  }

  /**
   * 
   */
  private toggleNodeSelection(nodeId: string): void {
    const index = this.selection.nodes.indexOf(nodeId);
    if (index > -1) {
      this.selection.nodes.splice(index, 1);
    } else {
      this.selection.nodes.push(nodeId);
    }
    this.updateSelectionStyles();
    this.emitSelectionChange();
  }

  /**
   * 
   */
  private toggleEdgeSelection(edgeId: string): void {
    const index = this.selection.edges.indexOf(edgeId);
    if (index > -1) {
      this.selection.edges.splice(index, 1);
    } else {
      this.selection.edges.push(edgeId);
    }
    this.updateSelectionStyles();
    this.emitSelectionChange();
  }

  /**
   * 
   */
  private updateSelectionStyles(): void {
    // 
    this.data.nodes.forEach(node => {
      node.selected = this.selection.nodes.includes(node.id);
      this.updateNodeStyle(node);
    });

    // 
    this.data.links.forEach(edge => {
      edge.selected = this.selection.edges.includes(edge.id);
      this.updateEdgeStyle(edge);
    });
  }

  /**
   * 
   */
  private emitSelectionChange(): void {
    this.emit({
      type: 'selection:change',
      target: null,
      originalEvent: new Event('selection:change'),
      position: { x: 0, y: 0 },
      data: { ...this.selection },
    });
  }

  // 

  setLayout(config: LayoutConfig): void {
    this.layoutConfig = { ...this.layoutConfig, ...config };
    this.applyLayout();
  }

  applyLayout(): void {
    // 
    const linkForce = this.simulation.force('link') as d3.ForceLink<D3Node, D3Edge>;
    if (linkForce) {
      linkForce
        .distance(this.layoutConfig.options.linkDistance || 80)
        .strength(this.layoutConfig.options.linkStrength || 0.5);
    }

    const chargeForce = this.simulation.force('charge') as d3.ForceManyBody<D3Node>;
    if (chargeForce) {
      chargeForce.strength(this.layoutConfig.options.chargeStrength || -300);
    }

    const centerForce = this.simulation.force('center') as d3.ForceCenter<D3Node>;
    if (centerForce) {
      centerForce.strength(this.layoutConfig.options.centerStrength || 0.1);
    }

    this.simulation
      .alpha(this.layoutConfig.options.alpha || 0.3)
      .alphaDecay(this.layoutConfig.options.alphaDecay || 0.02)
      .velocityDecay(this.layoutConfig.options.velocityDecay || 0.4)
      .restart();
  }

  setVisualConfig(config: VisualConfig): void {
    this.visualConfig = { ...this.visualConfig, ...config };
    this.updateNodeStyles([]);
    this.updateEdgeStyles([]);
  }

  updateNodeStyles(nodeIds: string[]): void {
    const targetNodes = nodeIds.length > 0
      ? this.data.nodes.filter(node => nodeIds.includes(node.id))
      : this.data.nodes;

    targetNodes.forEach(node => this.updateNodeStyle(node));
  }

  updateEdgeStyles(edgeIds: string[]): void {
    const targetEdges = edgeIds.length > 0
      ? this.data.links.filter(edge => edgeIds.includes(edge.id))
      : this.data.links;

    targetEdges.forEach(edge => this.updateEdgeStyle(edge));
  }

  enableInteraction(config: InteractionConfig): void {
    this.interactionConfig = { ...this.interactionConfig, ...config };
    // 
    this.initializeSVG();
    this.render(this.data);
  }

  disableInteraction(): void {
    this.interactionConfig = {
      zoom: { enabled: false, scaleExtent: [1, 1] },
      drag: { enabled: false, constrainToCanvas: false, snapToGrid: false, gridSize: 20 },
      selection: { enabled: false, multiSelect: false, selectOnClick: false },
      tooltip: { enabled: false, delay: 0, offset: { x: 0, y: 0 } },
    };
  }

  selectNodes(nodeIds: string[]): void {
    this.selection.nodes = [...nodeIds];
    this.updateSelectionStyles();
    this.emitSelectionChange();
  }

  selectEdges(edgeIds: string[]): void {
    this.selection.edges = [...edgeIds];
    this.updateSelectionStyles();
    this.emitSelectionChange();
  }

  clearSelection(): void {
    this.selection = { nodes: [], edges: [] };
    this.updateSelectionStyles();
    this.emitSelectionChange();
  }

  getSelection(): { nodes: string[]; edges: string[] } {
    return { ...this.selection };
  }

  zoomToFit(padding: number = 50): void {
    if (!this.zoom) return;

    const bounds = this.data.bounds;
    const width = this.layoutConfig.width;
    const height = this.layoutConfig.height;

    const dx = bounds.maxX - bounds.minX;
    const dy = bounds.maxY - bounds.minY;
    const x = (bounds.minX + bounds.maxX) / 2;
    const y = (bounds.minY + bounds.maxY) / 2;

    const scale = Math.min(
      (width - padding * 2) / dx,
      (height - padding * 2) / dy,
    );

    const transform = d3.zoomIdentity
      .translate(width / 2, height / 2)
      .scale(scale)
      .translate(-x, -y);

    this.svg.transition()
      .duration(750)
      .call(this.zoom.transform, transform);
  }

  zoomToNodes(nodeIds: string[], padding: number = 50): void {
    if (!this.zoom || nodeIds.length === 0) return;

    const targetNodes = this.data.nodes.filter(node => nodeIds.includes(node.id));
    if (targetNodes.length === 0) return;

    let minX = Infinity, maxX = -Infinity;
    let minY = Infinity, maxY = -Infinity;

    targetNodes.forEach(node => {
      const x = node.x || 0;
      const y = node.y || 0;
      const r = node.radius;

      minX = Math.min(minX, x - r);
      maxX = Math.max(maxX, x + r);
      minY = Math.min(minY, y - r);
      maxY = Math.max(maxY, y + r);
    });

    const width = this.layoutConfig.width;
    const height = this.layoutConfig.height;
    const dx = maxX - minX;
    const dy = maxY - minY;
    const x = (minX + maxX) / 2;
    const y = (minY + maxY) / 2;

    const scale = Math.min(
      (width - padding * 2) / dx,
      (height - padding * 2) / dy,
    );

    const transform = d3.zoomIdentity
      .translate(width / 2, height / 2)
      .scale(scale)
      .translate(-x, -y);

    this.svg.transition()
      .duration(750)
      .call(this.zoom.transform, transform);
  }

  centerView(): void {
    if (!this.zoom) return;

    const transform = d3.zoomIdentity
      .translate(this.layoutConfig.width / 2, this.layoutConfig.height / 2)
      .scale(1);

    this.svg.transition()
      .duration(500)
      .call(this.zoom.transform, transform);
  }

  on(event: GraphEventType, handler: GraphEventHandler): void {
    if (!this.eventHandlers.has(event)) {
      this.eventHandlers.set(event, []);
    }
    this.eventHandlers.get(event)!.push(handler);
  }

  off(event: GraphEventType, handler?: GraphEventHandler): void {
    if (!this.eventHandlers.has(event)) return;

    if (handler) {
      const handlers = this.eventHandlers.get(event)!;
      const index = handlers.indexOf(handler);
      if (index > -1) {
        handlers.splice(index, 1);
      }
    } else {
      this.eventHandlers.set(event, []);
    }
  }

  emit(event: GraphEvent): void {
    const handlers = this.eventHandlers.get(event.type);
    if (handlers) {
      handlers.forEach(handler => handler(event));
    }
  }

  destroy(): void {
    // 
    this.simulation.stop();

    // 
    if (this.tooltip) {
      this.tooltip.remove();
    }

    // 
    this.eventHandlers.clear();

    // DOM
    d3.select<any, any>(this.container).selectAll('*').remove();
  }
}

