import {
  QuotaType,
  UserTier,
  Subscription,
  QuotaLimits,
} from '@/shared/types/subscription';

import { DEFAULT_PLANS, QUOTA_THRESHOLDS } from './constants';
import {
  formatQuota,
  calculateQuotaUsagePercentage,
  getQuotaWarningLevel,
  getQuotaTypeIcon,
} from './utils';

type DailyQuotaType = 'create' | 'reuse' | 'export';

export interface QuotaDataSource {
  getDailyUsage(userId: string, type: DailyQuotaType, isoDate: string): Promise<number>;
  getTotalGraphNodes(userId: string): Promise<number>;
  consumeQuota(input: { userId: string; type: QuotaType; amount: number; subscriptionId?: string | null }): Promise<boolean>;
  resetDailyQuotas(userId: string): Promise<boolean>;
}

export interface QuotaDataSourceOptions {
  baseUrl?: string;
  fetcher?: typeof fetch;
}

export interface EnhancedQuotaCheckerOptions {
  dataSource?: QuotaDataSource;
  dataSourceOptions?: QuotaDataSourceOptions;
}

const defaultQuotaDataSource = createDefaultQuotaDataSource();

export interface QuotaUsage {
  type: QuotaType;
  used: number;
  limit: number;
  resetTime: Date;
  isUnlimited: boolean;
  percentage: number;
  warningLevel: 'safe' | 'warning' | 'critical' | 'exceeded';
}

export interface QuotaCheckResult {
  allowed: boolean;
  usage: QuotaUsage;
  requestedAmount: number;
  reason?: 'quota_exceeded' | 'subscription_expired' | 'subscription_suspended' | 'permission_denied';
  upgradePrompt?: {
    title: string;
    description: string;
    icon: string;
    currentPlan: string;
    recommendedPlan: string;
    benefits: string[];
    priceIncrease: number;
    urgency: 'low' | 'medium' | 'high';
  };
}

/**
 * 
 */
export class EnhancedQuotaChecker {
  private dataSource: QuotaDataSource;

  constructor(
    private userId: string,
    private subscription: Subscription | null,
    options: EnhancedQuotaCheckerOptions = {},
  ) {
    const { dataSource, dataSourceOptions } = options;
    this.dataSource = dataSource ?? (dataSourceOptions ? createDefaultQuotaDataSource(dataSourceOptions) : defaultQuotaDataSource);
  }

  /**
   * 
   */
  async checkQuota(type: QuotaType, amount: number = 1): Promise<QuotaCheckResult> {
    const requestedAmount = Number.isFinite(amount) && amount > 0 ? amount : 0;
    // 
    const quotaLimits = this.getQuotaLimits();
    const usage = await this.getQuotaUsage(type, quotaLimits);

    // 
    if (this.subscription && this.subscription.status === 'suspended') {
      return {
        allowed: false,
        usage,
        requestedAmount,
        reason: 'subscription_suspended',
      };
    }

    if (this.subscription && this.subscription.status === 'expired') {
      return {
        allowed: false,
        usage,
        requestedAmount,
        reason: 'subscription_expired',
      };
    }

    // 
    if (usage.isUnlimited) {
      return {
        allowed: true,
        usage,
        requestedAmount,
      };
    }

    // 
    if (usage.used + requestedAmount > usage.limit) {
      return {
        allowed: false,
        usage,
        requestedAmount,
        reason: 'quota_exceeded',
        upgradePrompt: this.generateUpgradePrompt(type, usage),
      };
    }

    return {
      allowed: true,
      usage,
      requestedAmount,
    };
  }

  /**
   * 
   */
  private getQuotaLimits(): QuotaLimits {
    if (this.subscription && this.subscription.status === 'active') {
      return this.subscription.quotas;
    }

    // 
    const freePlan = (DEFAULT_PLANS.find as any)(p => p.tier === 'free');
    return freePlan?.quotas || {
      dailyCreateQuota: 3,
      dailyReuseQuota: 1,
      maxExportsPerDay: 10,
      maxGraphNodes: 50,
    };
  }

  /**
   * 
   */
  private async getQuotaUsage(type: QuotaType, quotaLimits: QuotaLimits): Promise<QuotaUsage> {
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    tomorrow.setHours(0, 0, 0, 0);

    let limit: number;
    let used: number;
    let isUnlimited: boolean;
    let resetTime: Date;

    switch (type) {
      case 'create':
        limit = quotaLimits.dailyCreateQuota;
        used = await this.getDailyUsage('create');
        isUnlimited = limit === -1;
        resetTime = tomorrow;
        break;
      case 'reuse':
        limit = quotaLimits.dailyReuseQuota;
        used = await this.getDailyUsage('reuse');
        isUnlimited = limit === -1;
        resetTime = tomorrow;
        break;
      case 'export':
        limit = quotaLimits.maxExportsPerDay;
        used = await this.getDailyUsage('export');
        isUnlimited = limit === -1;
        resetTime = tomorrow;
        break;
      case 'graph_nodes':
        limit = quotaLimits.maxGraphNodes;
        used = await this.getTotalGraphNodes();
        isUnlimited = limit === -1;
        resetTime = new Date(0); // 
        break;
      default:
        throw new Error(`Unknown quota type: ${type}`);
    }

    const percentage = isUnlimited ? 0 : calculateQuotaUsagePercentage(used, limit);
    const warningLevel = isUnlimited ? 'safe' : getQuotaWarningLevel(used, limit);

    return {
      type,
      used: isUnlimited ? 0 : used,
      limit: isUnlimited ? Infinity : limit,
      resetTime,
      isUnlimited,
      percentage,
      warningLevel,
    };
  }

  /**
   * 
   */
  private async getDailyUsage(type: DailyQuotaType): Promise<number> {
    const today = new Date().toISOString().split('T')[0];

    try {
      return await this.dataSource.getDailyUsage(this.userId, type, today);
    } catch (error) {
      console.error('Failed to get daily usage:', error);
      return 0;
    }
  }

  /**
   * 
   */
  private async getTotalGraphNodes(): Promise<number> {
    try {
      return await this.dataSource.getTotalGraphNodes(this.userId);
    } catch (error) {
      console.error('Failed to get graph nodes count:', error);
      return 0;
    }
  }

  /**
   * 
   */
  private generateUpgradePrompt(type: QuotaType, usage: QuotaUsage): {
    title: string;
    description: string;
    icon: string;
    currentPlan: string;
    recommendedPlan: string;
    benefits: string[];
    priceIncrease: number;
    urgency: 'low' | 'medium' | 'high';
  } {
    const currentTier = this.subscription?.plan || 'free';

    // 
    let recommendedTier: UserTier;
    if (currentTier === 'free') {
      recommendedTier = 'basic';
    } else if (currentTier === 'basic') {
      recommendedTier = 'pro';
    } else {
      recommendedTier = 'pro'; // 
    }

    const currentPlan = (DEFAULT_PLANS.find as any)(p => p.tier === currentTier);
    const recommendedPlan = (DEFAULT_PLANS.find as any)(p => p.tier === recommendedTier);

    if (!currentPlan || !recommendedPlan) {
      throw new Error('Plan configuration not found');
    }

    const prompts = {
      create: {
        title: '',
        description: ` ${usage.used} `,
        benefits: [
          ` ${formatQuota(recommendedPlan.quotas.dailyCreateQuota)} `,
          '',
          '',
          '',
        ],
      },
      reuse: {
        title: '',
        description: ` ${usage.used} `,
        benefits: [
          ` ${formatQuota(recommendedPlan.quotas.dailyReuseQuota)} `,
          '',
          '',
          ' 10 ',
        ],
      },
      export: {
        title: '',
        description: ` ${usage.used} `,
        benefits: [
          ` ${formatQuota(recommendedPlan.quotas.maxExportsPerDay)} `,
          '',
          '',
          '',
        ],
      },
      graph_nodes: {
        title: '',
        description: ` ${usage.used} `,
        benefits: [
          ` ${formatQuota(recommendedPlan.quotas.maxGraphNodes)} `,
          '',
          '',
          '',
        ],
      },
    };

    const prompt = prompts[type];
    const urgency = usage.warningLevel === 'exceeded' ? 'high' : 'medium';

    return {
      title: prompt.title,
      description: prompt.description,
      icon: getQuotaTypeIcon(type),
      currentPlan: currentPlan.displayName,
      recommendedPlan: recommendedPlan.displayName,
      benefits: prompt.benefits,
      priceIncrease: recommendedPlan.monthlyPrice - currentPlan.monthlyPrice,
      urgency,
    };
  }

  /**
   * 
   */
  async consumeQuota(type: QuotaType, amount: number = 1): Promise<boolean> {
    const checkResult = await this.checkQuota(type, amount);

    if (!checkResult.allowed) {
      return false;
    }

    // 
    if (checkResult.usage.isUnlimited) {
      return true;
    }

    try {
      return await this.dataSource.consumeQuota({
        userId: this.userId,
        type,
        amount,
        subscriptionId: this.subscription?.id ?? null,
      });
    } catch (error) {
      console.error('Failed to consume quota:', error);
      return false;
    }
  }

  /**
   * 
   */
  async getAllQuotaStatus(): Promise<Record<QuotaType, QuotaUsage>> {
    const types: QuotaType[] = ['create', 'reuse', 'export', 'graph_nodes'];
    const results: Record<QuotaType, QuotaUsage> = {} as any;
    const quotaLimits = this.getQuotaLimits();

    for (const type of types) {
      const usage = await this.getQuotaUsage(type, quotaLimits);
      results[type] = usage;
    }

    return results;
  }

  /**
   * 
   */
  async shouldShowUpgradePrompt(type: QuotaType): Promise<boolean> {
    const usage = await this.getQuotaUsage(type, this.getQuotaLimits());

    // 
    if (usage.isUnlimited) {
      return false;
    }

    // 
    if (this.subscription?.plan === 'pro') {
      return false;
    }

    // 80%
    return usage.percentage >= QUOTA_THRESHOLDS.WARNING * 100;
  }

  /**
   * 
   */
  async resetDailyQuotas(): Promise<boolean> {
    try {
      return await this.dataSource.resetDailyQuotas(this.userId);
    } catch (error) {
      console.error('Failed to reset daily quotas:', error);
      return false;
    }
  }
}

export function createDefaultQuotaDataSource(options: QuotaDataSourceOptions = {}): QuotaDataSource {
  const fetcher = options.fetcher ?? (typeof fetch === 'function' ? fetch.bind(globalThis) : undefined);
  const baseUrlOverride = options.baseUrl;
  let missingBaseUrlWarned = false;

  const warnMissingBaseUrl = () => {
    if (!missingBaseUrlWarned) {
      console.warn('[QuotaDataSource]  API  INTERNAL_API_BASE_URL');
      missingBaseUrlWarned = true;
    }
  };

  const buildUrl = (path: string, params?: Record<string, string | number | undefined>): string | null => {
    const query = params
      ? Object.entries(params)
          .filter(([, value]) => value !== undefined && value !== null)
          .reduce((search, [key, value]) => {
            search.append(key, String(value));
            return search;
          }, new URLSearchParams())
      : null;

    if (typeof window !== 'undefined') {
      const url = new URL(path, window.location.origin);
      if (query) {
        query.forEach((value, key) => url.searchParams.set(key, value));
      }
      return url.toString();
    }

    const base = normalizeBaseUrl(baseUrlOverride);
    if (!base) {
      warnMissingBaseUrl();
      return null;
    }

    const url = new URL(path, base);
    if (query) {
      query.forEach((value, key) => url.searchParams.set(key, value));
    }
    return url.toString();
  };

  const safeFetch: typeof fetch | undefined = fetcher;

  return {
    async getDailyUsage(userId, type, isoDate) {
      if (!safeFetch) {
        return 0;
      }

      const url = buildUrl('/api/subscription/quota/daily-usage', {
        userId,
        type,
        date: isoDate,
      });

      if (!url) {
        return 0;
      }

      try {
        const response = await safeFetch(url);
        if (!response.ok) {
          return 0;
        }
        const result = await response.json();
        return typeof result.usage === 'number' ? result.usage : 0;
      } catch (error) {
        console.error('Failed to fetch daily quota usage:', error);
        return 0;
      }
    },

    async getTotalGraphNodes(userId) {
      if (!safeFetch) {
        return 0;
      }

      const url = buildUrl('/api/subscription/quota/graph-nodes', { userId });
      if (!url) {
        return 0;
      }

      try {
        const response = await safeFetch(url);
        if (!response.ok) {
          return 0;
        }
        const result = await response.json();
        return typeof result.count === 'number' ? result.count : 0;
      } catch (error) {
        console.error('Failed to fetch graph nodes count:', error);
        return 0;
      }
    },

    async consumeQuota({ userId, type, amount, subscriptionId }) {
      if (!safeFetch) {
        return false;
      }

      const url = buildUrl('/api/subscription/quota/consume');
      if (!url) {
        return false;
      }

      try {
        const response = await safeFetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            userId,
            type,
            amount,
            subscriptionId: subscriptionId ?? undefined,
          }),
        });

        return response.ok;
      } catch (error) {
        console.error('Failed to consume quota via data source:', error);
        return false;
      }
    },

    async resetDailyQuotas(userId) {
      if (!safeFetch) {
        return false;
      }

      const url = buildUrl('/api/subscription/quota/reset-daily');
      if (!url) {
        return false;
      }

      try {
        const response = await safeFetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ userId }),
        });

        return response.ok;
      } catch (error) {
        console.error('Failed to reset quotas via data source:', error);
        return false;
      }
    },
  };
}

function normalizeBaseUrl(baseUrl?: string | null): string | null {
  if (!baseUrl) {
    const envUrl = process.env.INTERNAL_API_BASE_URL
      || process.env.API_BASE_URL
      || process.env.NEXT_PUBLIC_APP_URL
      || process.env.APP_BASE_URL
      || null;

    baseUrl = envUrl || null;
  }

  if (!baseUrl) {
    return null;
  }

  try {
    return new URL(baseUrl).toString();
  } catch (error) {
    try {
      return new URL(`http://${baseUrl}`).toString();
    } catch {
      console.warn('[QuotaDataSource]  API :', baseUrl);
      return null;
    }
  }
}

/**
 * 
 * 
 */

import {
  Subscription,
  SubscriptionPlan,
  PaymentRecord,
  SubscriptionUsage,
  UserTier,
  SubscriptionStatus,
  CreatePlanRequest,
} from '@/shared/types/subscription';

import { DEFAULT_PLANS } from './constants';
import { validatePlanData, validateSubscriptionData } from './validators';

export interface CreateSubscriptionRequest {
  userId: string;
  planId: string;
  paymentMethod: 'wechat_pay';
  billingCycle: 'monthly' | 'yearly';
}

export interface UpdateSubscriptionRequest {
  planId?: string;
  status?: SubscriptionStatus;
  endDate?: Date;
  metadata?: Record<string, any>;
}

export interface SubscriptionServiceOptions {
  enableAutoRenewal?: boolean;
  enableProration?: boolean;
  gracePeriodDays?: number;
}

/**
 * 
 */
export class SubscriptionService {
  private options: SubscriptionServiceOptions;

  constructor(options: SubscriptionServiceOptions = {}) {
    this.options = {
      enableAutoRenewal: true,
      enableProration: true,
      gracePeriodDays: 3,
      ...options,
    };
  }

  /**
   * 
   */
  async getCurrentSubscription(userId: string): Promise<Subscription | null> {
    try {
      const response = await fetch(`/api/subscription/current?userId=${userId}`);
      if (response.ok) {
        const result = await response.json();
        return result.subscription || null;
      }
      return null;
    } catch (error) {
      console.error('Failed to get current subscription:', error);
      return null;
    }
  }

  /**
   * 
   */
  async getAvailablePlans(): Promise<SubscriptionPlan[]> {
    try {
      const response = await fetch('/api/subscription/plans');
      if (response.ok) {
        const result = await response.json();
        return result.plans || DEFAULT_PLANS;
      }
      return DEFAULT_PLANS;
    } catch (error) {
      console.error('Failed to get available plans:', error);
      return DEFAULT_PLANS;
    }
  }

  /**
   * 
   */
  async getPlanByTier(tier: UserTier): Promise<SubscriptionPlan | null> {
    const plans = await this.getAvailablePlans();
    return (plans.find as any)(plan => plan.tier === tier) || null;
  }

  /**
   * 
   */
  async createSubscription(request: CreateSubscriptionRequest): Promise<{
    subscription: Subscription;
    paymentInfo: any;
  }> {
    // 
    if (!request.userId || !request.planId) {
      throw new Error('IDID');
    }

    // 
    const plans = await this.getAvailablePlans();
    const plan = (plans.find as any)(p => p.id === request.planId);
    if (!plan) {
      throw new Error('');
    }

    // 
    const existingSubscription = await this.getCurrentSubscription(request.userId);
    if (existingSubscription && existingSubscription.status === 'active') {
      throw new Error('');
    }

    // 
    const startDate = new Date();
    const endDate = new Date();
    if (request.billingCycle === 'yearly') {
      endDate.setFullYear(endDate.getFullYear() + 1);
    } else {
      endDate.setMonth(endDate.getMonth() + 1);
    }

    // 
    const subscription: Subscription = {
      id: `sub_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      userId: request.userId,
      planId: request.planId,
      planName: plan.displayName,
      tier: plan.tier,
      status: 'pending',
      monthlyPrice: plan.monthlyPrice,
      currency: 'CNY',
      startDate,
      endDate,
      nextBillingDate: endDate,
      paymentMethod: request.paymentMethod,
      quotas: { ...plan.quotas },
      features: [...plan.features],
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    // 
    const validation = validateSubscriptionData(subscription);
    if (!validation.isValid) {
      throw new Error(`: ${validation.errors.join(', ')}`);
    }

    try {
      // API
      const response = await fetch('/api/subscription/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ subscription, billingCycle: request.billingCycle }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || '');
      }

      const result = await response.json();
      return {
        subscription: result.subscription,
        paymentInfo: result.paymentInfo,
      };
    } catch (error) {
      console.error('Failed to create subscription:', error);
      throw error;
    }
  }

  /**
   * 
   */
  async updateSubscription(
    subscriptionId: string,
    updates: UpdateSubscriptionRequest,
  ): Promise<Subscription> {
    if (!subscriptionId) {
      throw new Error('ID');
    }

    try {
      const response = await fetch(`/api/subscription/${subscriptionId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updates),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || '');
      }

      return await response.json();
    } catch (error) {
      console.error('Failed to update subscription:', error);
      throw error;
    }
  }

  /**
   * 
   */
  async cancelSubscription(
    subscriptionId: string,
    reason?: string,
    immediate: boolean = false,
  ): Promise<Subscription> {
    if (!subscriptionId) {
      throw new Error('ID');
    }

    const cancelDate = immediate ? new Date() : undefined;

    try {
      const response = await fetch(`/api/subscription/${subscriptionId}/cancel`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          reason,
          immediate,
          cancelDate: cancelDate?.toISOString(),
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || '');
      }

      return await response.json();
    } catch (error) {
      console.error('Failed to cancel subscription:', error);
      throw error;
    }
  }

  /**
   * 
   */
  async upgradeSubscription(
    subscriptionId: string,
    newPlanId: string,
    prorationMode: 'immediate' | 'next_cycle' = 'immediate',
  ): Promise<{
    subscription: Subscription;
    paymentInfo?: any;
    prorationAmount?: number;
  }> {
    if (!subscriptionId || !newPlanId) {
      throw new Error('IDID');
    }

    // 
    const currentSubscription = await this.getSubscriptionById(subscriptionId);
    if (!currentSubscription) {
      throw new Error('');
    }

    // 
    const plans = await this.getAvailablePlans();
    const newPlan = (plans.find as any)(p => p.id === newPlanId);
    if (!newPlan) {
      throw new Error('');
    }

    // 
    const currentPlan = (plans.find as any)(p => p.id === currentSubscription.planId);
    if (!currentPlan) {
      throw new Error('');
    }

    if (newPlan.monthlyPrice <= currentPlan.monthlyPrice) {
      throw new Error('');
    }

    // 
    let prorationAmount = 0;
    if (this.options.enableProration && prorationMode === 'immediate') {
      prorationAmount = this.calculateProrationAmount(
        currentSubscription,
        currentPlan,
        newPlan,
      );
    }

    try {
      const response = await fetch(`/api/subscription/${subscriptionId}/upgrade`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          newPlanId,
          prorationMode,
          prorationAmount,
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || '');
      }

      return await response.json();
    } catch (error) {
      console.error('Failed to upgrade subscription:', error);
      throw error;
    }
  }

  /**
   * 
   */
  async downgradeSubscription(
    subscriptionId: string,
    newPlanId: string,
    effectiveDate: 'immediate' | 'next_cycle' = 'next_cycle',
  ): Promise<Subscription> {
    if (!subscriptionId || !newPlanId) {
      throw new Error('IDID');
    }

    try {
      const response = await fetch(`/api/subscription/${subscriptionId}/downgrade`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          newPlanId,
          effectiveDate,
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || '');
      }

      return await response.json();
    } catch (error) {
      console.error('Failed to downgrade subscription:', error);
      throw error;
    }
  }

  /**
   * 
   */
  async reactivateSubscription(subscriptionId: string): Promise<Subscription> {
    if (!subscriptionId) {
      throw new Error('ID');
    }

    try {
      const response = await fetch(`/api/subscription/${subscriptionId}/reactivate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || '');
      }

      return await response.json();
    } catch (error) {
      console.error('Failed to reactivate subscription:', error);
      throw error;
    }
  }

  /**
   * 
   */
  async getSubscriptionById(subscriptionId: string): Promise<Subscription | null> {
    try {
      const response = await fetch(`/api/subscription/${subscriptionId}`);
      if (response.ok) {
        return await response.json();
      }
      return null;
    } catch (error) {
      console.error('Failed to get subscription:', error);
      return null;
    }
  }

  /**
   * 
   */
  async getSubscriptionHistory(
    userId: string,
    limit: number = 10,
    offset: number = 0,
  ): Promise<{
    subscriptions: Subscription[];
    total: number;
    hasMore: boolean;
  }> {
    try {
      const response = await fetch(
        `/api/subscription/history?userId=${userId}&limit=${limit}&offset=${offset}`,
      );

      if (response.ok) {
        return await response.json();
      }

      return { subscriptions: [], total: 0, hasMore: false };
    } catch (error) {
      console.error('Failed to get subscription history:', error);
      return { subscriptions: [], total: 0, hasMore: false };
    }
  }

  /**
   * 
   */
  async getPaymentHistory(
    subscriptionId: string,
    limit: number = 10,
  ): Promise<PaymentRecord[]> {
    try {
      const response = await fetch(
        `/api/subscription/${subscriptionId}/payments?limit=${limit}`,
      );

      if (response.ok) {
        const result = await response.json();
        return result.payments || [];
      }

      return [];
    } catch (error) {
      console.error('Failed to get payment history:', error);
      return [];
    }
  }

  /**
   * 
   */
  async getUsageStatistics(
    subscriptionId: string,
    startDate?: Date,
    endDate?: Date,
  ): Promise<SubscriptionUsage[]> {
    try {
      const params = new URLSearchParams();
      if (startDate) params.append('startDate', startDate.toISOString());
      if (endDate) params.append('endDate', endDate.toISOString());

      const response = await fetch(
        `/api/subscription/${subscriptionId}/usage?${params.toString()}`,
      );

      if (response.ok) {
        const result = await response.json();
        return result.usage || [];
      }

      return [];
    } catch (error) {
      console.error('Failed to get usage statistics:', error);
      return [];
    }
  }

  /**
   * 
   */
  async checkSubscriptionStatus(subscriptionId: string): Promise<{
    isActive: boolean;
    isExpired: boolean;
    daysUntilExpiry: number;
    needsRenewal: boolean;
  }> {
    const subscription = await this.getSubscriptionById(subscriptionId);

    if (!subscription) {
      return {
        isActive: false,
        isExpired: true,
        daysUntilExpiry: 0,
        needsRenewal: false,
      };
    }

    const now = new Date();
    const endDate = new Date(subscription.endDate);
    const daysUntilExpiry = Math.ceil((endDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));

    const isActive = subscription.status === 'active' && endDate > now;
    const isExpired = endDate <= now;
    const needsRenewal = daysUntilExpiry <= (this.options.gracePeriodDays || 3) && isActive;

    return {
      isActive,
      isExpired,
      daysUntilExpiry: Math.max(0, daysUntilExpiry),
      needsRenewal,
    };
  }

  /**
   * 
   */
  private calculateProrationAmount(
    subscription: Subscription,
    currentPlan: SubscriptionPlan,
    newPlan: SubscriptionPlan,
  ): number {
    const now = new Date();
    const endDate = new Date(subscription.endDate);
    const totalDays = Math.ceil((endDate.getTime() - new Date(subscription.startDate).getTime()) / (1000 * 60 * 60 * 24));
    const remainingDays = Math.ceil((endDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));

    if (remainingDays <= 0) return 0;

    // 
    const remainingValue = (currentPlan.monthlyPrice * remainingDays) / totalDays;
    const newPlanValue = (newPlan.monthlyPrice * remainingDays) / totalDays;

    return Math.max(0, newPlanValue - remainingValue);
  }

  /**
   * 
   */
  async createPlan(planData: CreatePlanRequest): Promise<SubscriptionPlan> {
    const validation = validatePlanData(planData);
    if (!validation.isValid) {
      throw new Error(`: ${validation.errors.join(', ')}`);
    }

    try {
      const response = await fetch('/api/subscription/plans', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(planData),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || '');
      }

      return await response.json();
    } catch (error) {
      console.error('Failed to create plan:', error);
      throw error;
    }
  }

  /**
   * 
   */
  async updatePlan(planId: string, updates: Partial<CreatePlanRequest>): Promise<SubscriptionPlan> {
    try {
      const response = await fetch(`/api/subscription/plans/${planId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updates),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || '');
      }

      return await response.json();
    } catch (error) {
      console.error('Failed to update plan:', error);
      throw error;
    }
  }

  /**
   * 
   */
  async getSubscriptionAnalytics(userId: string): Promise<{
    totalSpent: number;
    monthsSubscribed: number;
    averageMonthlySpend: number;
    upgradeHistory: Array<{
      date: Date;
      fromPlan: string;
      toPlan: string;
      reason?: string;
    }>;
    usageTrends: Array<{
      month: string;
      usage: Record<string, number>;
    }>;
  }> {
    try {
      const response = await fetch(`/api/subscription/analytics?userId=${userId}`);

      if (response.ok) {
        return await response.json();
      }

      return {
        totalSpent: 0,
        monthsSubscribed: 0,
        averageMonthlySpend: 0,
        upgradeHistory: [],
        usageTrends: [],
      };
    } catch (error) {
      console.error('Failed to get subscription analytics:', error);
      return {
        totalSpent: 0,
        monthsSubscribed: 0,
        averageMonthlySpend: 0,
        upgradeHistory: [],
        usageTrends: [],
      };
    }
  }
}

