/**
 * 
 */
export interface PaymentPageProps {
  paymentRecord: PaymentRecord;
  onPaymentSuccess: (paymentId: string) => void;
  onCancel: () => void;
}

export function PaymentPage({
  paymentRecord,
  onPaymentSuccess,
  onCancel,
}: PaymentPageProps) {
  const [paymentStatus, setPaymentStatus] = useState<PaymentStatus>(paymentRecord.status);
  const [qrCodeUrl, setQrCodeUrl] = useState<string>('');
  const [countdown, setCountdown] = useState(300); // 5
  const [isPolling, setIsPolling] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string>('');

  // 
  useEffect(() => {
    if (paymentRecord.paymentMethod === 'wechat_pay') {
      generateQRCode();
    }
  }, [paymentRecord]);

  // 
  useEffect(() => {
    if (countdown > 0 && paymentStatus === 'pending') {
      const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
      return () => clearTimeout(timer);
    }
  }, [countdown, paymentStatus]);

  // 
  const generateQRCode = async () => {
    try {
      const result = await WeChatPayUtils.createWeChatPayOrder({
        amount: paymentRecord.amount,
        description: ` - ${paymentRecord.planId}`,
        userId: paymentRecord.userId,
      });
      setQrCodeUrl(result.qrCodeUrl);
    } catch (error) {
      console.error(':', error);
      setErrorMessage('');
    }
  };

  // 
  const handleRefresh = async () => {
    setIsPolling(true);
    try {
      const response = await fetch(`/api/payment?paymentId=${paymentRecord.id}`);
      const data = await response.json();

      if (data.success) {
        setPaymentStatus(data.data.status);
        if (data.data.status === 'completed') {
          onPaymentSuccess(paymentRecord.id);
        }
      }
    } catch (error) {
      console.error(':', error);
    } finally {
      setIsPolling(false);
    }
  };

  // 
  const formatCountdown = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  // 
  const getStatusIcon = () => {
    switch (paymentStatus) {
      case 'completed':
        return (
          <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
            <svg className="w-8 h-8 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
            </svg>
          </div>
        );
      case 'failed':
        return (
          <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mb-4">
            <svg className="w-8 h-8 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </div>
        );
      default:
        return (
          <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mb-4">
            <svg className="w-8 h-8 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
            </svg>
          </div>
        );
    }
  };

  // 
  if (paymentStatus === 'completed') {
    return (
      <div className="max-w-md mx-auto bg-white rounded-lg shadow-lg p-8 text-center">
        {getStatusIcon()}
        <h2 className="text-2xl font-bold text-gray-900 mb-2"></h2>
        <p className="text-gray-600 mb-6"></p>
        <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-600">:</span>
            <span className="font-medium text-gray-900">
              {WeChatPayUtils.formatAmount(paymentRecord.amount)}
            </span>
          </div>
          <div className="flex items-center justify-between text-sm mt-2">
            <span className="text-gray-600">:</span>
            <span className="font-medium text-gray-900 font-mono text-xs">
              {paymentRecord.id}
            </span>
          </div>
        </div>
        <button
          onClick={() => onPaymentSuccess(paymentRecord.id)}
          className="w-full bg-green-600 text-white font-medium py-3 px-4 rounded-lg hover:bg-green-700 transition-colors"
        >

        </button>
      </div>
    );
  }

  // 
  if (paymentStatus === 'failed') {
    return (
      <div className="max-w-md mx-auto bg-white rounded-lg shadow-lg p-8 text-center">
        {getStatusIcon()}
        <h2 className="text-2xl font-bold text-gray-900 mb-2"></h2>
        <p className="text-gray-600 mb-6">{errorMessage || ''}</p>
