/**
 * API
 */

import { NextResponse } from 'next/server';

import { logger } from '@/lib/logging/logger';
import { reportError } from '@/lib/monitoring';
import { CustomError } from '@/shared/errors/CustomError';
import { ErrorCode } from '@/shared/errors/types';

/**
 * API
 */
export interface ApiResponse<T = any> {
  success: boolean;
  data?: T;
  error?: ApiError;
  meta?: ApiMeta;
  status?: number;
}

/**
 * API
 */
export interface ApiError {
  code: ErrorCode;
  message: string;
  details?: any;
  timestamp: string;
  traceId: string;
  field?: string; // 
  stack?: string; // 
}

/**
 * API
 */
export interface ApiMeta {
  timestamp: string;
  version: string;
  requestId: string;
  pagination?: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
  };
  performance?: {
    duration: number;
    queries?: number;
  };
}

/**
 * 
 */
export interface PaginationParams {
  page?: number;
  limit?: number;
  offset?: number;
}

/**
 * ID
 */
function generateTraceId(): string {
  return `trace-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
}

/**
 * 
 */
export function createSuccessResponse<T>(
  data: T,
  meta?: Partial<ApiMeta>,
  status: number = 200,
): NextResponse<ApiResponse<T>> {
  const response: ApiResponse<T> = {
    success: true,
    data,
    meta: {
      timestamp: new Date().toISOString(),
      version: process.env.NEXT_PUBLIC_APP_VERSION || '1.0.0',
      requestId: generateTraceId(),
      ...meta,
    },
  };

  return NextResponse.json(response, { status });
}

/**
 * 
 */
export function createErrorResponse(
  error: Error | CustomError | string,
  status?: number,
  details?: any,
  field?: string,
): NextResponse<ApiResponse> {
  let customError: CustomError;
  let httpStatus = status || 500;

  // CustomError
  if (error instanceof CustomError) {
    customError = error;
    httpStatus = status || error.httpStatus;
  } else if (error instanceof Error) {
    customError = CustomError.fromError(error, ErrorCode.INTERNAL_SERVER_ERROR);
  } else {
    const message = typeof error === 'string' ? error : 'Unknown error';
    customError = new CustomError(ErrorCode.INTERNAL_SERVER_ERROR, message);
  }

  const traceId = generateTraceId();
  const isDevelopment = process.env.NODE_ENV === 'development';

  // 
  logger.error('API Error Response', customError, {
    metadata: {
      traceId,
      httpStatus,
      field,
      details,
    },
  });

  // 
  reportError(customError, {
    tags: {
      api_error: 'true',
      http_status: httpStatus.toString(),
      error_code: customError.code,
    },
    extra: {
      traceId,
      field,
      details,
    },
  });

  const apiError: ApiError = {
    code: customError.code,
    message: customError.userMessage,
    timestamp: new Date().toISOString(),
    traceId,
    ...(details && { details }),
    ...(field && { field }),
    ...(isDevelopment && customError.stack && { stack: customError.stack }),
  };

  const response: ApiResponse = {
    success: false,
    error: apiError,
  };

  return NextResponse.json(response, { status: httpStatus });
}

/**
 * 
 */
export function createValidationErrorResponse(
  message: string,
  field?: string,
  details?: any,
): NextResponse<ApiResponse> {
  const error = new CustomError(ErrorCode.VALIDATION_ERROR, message);
  return createErrorResponse(error, 400, details, field);
}

/**
 * 
 */
export function createAuthErrorResponse(
  message: string = '',
): NextResponse<ApiResponse> {
  const error = new CustomError(ErrorCode.AUTHENTICATION_ERROR, message);
  return createErrorResponse(error, 401);
}

/**
 * 
 */
export function createPermissionErrorResponse(
  message: string = '',
): NextResponse<ApiResponse> {
  const error = new CustomError(ErrorCode.AUTHORIZATION_ERROR, message);
  return createErrorResponse(error, 403);
}

/**
 * 
 */
export function createNotFoundErrorResponse(
  resource: string = '',
): NextResponse<ApiResponse> {
  const error = new CustomError(ErrorCode.RESOURCE_NOT_FOUND, `${resource}`);
  return createErrorResponse(error, 404);
}

/**
 * 
 */
export function createBusinessErrorResponse(
  message: string,
  code?: ErrorCode,
  details?: any,
): NextResponse<ApiResponse> {
  const errorCode = code || ErrorCode.BUSINESS_LOGIC_ERROR;
  const error = new CustomError(errorCode, message);
  return createErrorResponse(error, 400, details);
}

/**
 * 
 */
export function createServerErrorResponse(
  message: string = '',
): NextResponse<ApiResponse> {
  const error = new CustomError(ErrorCode.INTERNAL_SERVER_ERROR, message);
  return createErrorResponse(error, 500);
}

/**
 * 
 */
export function createPaginatedResponse<T>(
  data: T[],
  pagination: {
    page: number;
    limit: number;
    total: number;
  },
  meta?: Partial<ApiMeta>,
): NextResponse<ApiResponse<T[]>> {
  const totalPages = Math.ceil(pagination.total / pagination.limit);

  return createSuccessResponse(data, {
    ...meta,
    pagination: {
      page: pagination.page,
      limit: pagination.limit,
      total: pagination.total,
      totalPages,
    },
  });
}

/**
 * 
 */
export function parsePaginationParams(searchParams: URLSearchParams): {
  page: number;
  limit: number;
  offset: number;
} {
  const page = Math.max(1, parseInt(searchParams.get('page') || '1', 10));
  const limit = Math.min(100, Math.max(1, parseInt(searchParams.get('limit') || '10', 10)));
  const offset = (page - 1) * limit;

  return { page, limit, offset };
}

/**
 * API
 */
export function withErrorHandling<T extends any[], R>(
  handler: (...args: T) => Promise<R>,
) {
  return async (...args: T): Promise<R | NextResponse<ApiResponse>> => {
    try {
      return await handler(...args);
    } catch (error) {
      if (error instanceof CustomError) {
        return createErrorResponse(error);
      } else if (error instanceof Error) {
        return createErrorResponse(error);
      } else {
        return createServerErrorResponse('');
      }
    }
  };
}

/**
 * 
 */
export async function validateRequestBody<T>(
  request: Request,
  validator: (data: any) => T | Promise<T>,
): Promise<T> {
  try {
    const body = await request.json();
    return await validator(body);
  } catch (error) {
    if (error instanceof SyntaxError) {
      throw new CustomError(ErrorCode.VALIDATION_ERROR, 'JSON');
    }
    throw error;
  }
}

/**
 * 
 */
export function validateQueryParams<T>(
  searchParams: URLSearchParams,
  validator: (params: Record<string, string>) => T,
): T {
  const params: Record<string, string> = {};
  searchParams.forEach((value, key) => {
    params[key] = value;
  });

  return validator(params);
}

/**
 * 
 */
export const ERROR_CODE_MAP: Partial<Record<ErrorCode, { status: number; message: string }>> = {
  [ErrorCode.VALIDATION_ERROR]: {
    status: 400,
    message: '',
  },
  [ErrorCode.AUTHENTICATION_ERROR]: {
    status: 401,
    message: '',
  },
  [ErrorCode.AUTHORIZATION_ERROR]: {
    status: 403,
    message: '',
  },
  [ErrorCode.RESOURCE_NOT_FOUND]: {
    status: 404,
    message: '',
  },
  [ErrorCode.BUSINESS_LOGIC_ERROR]: {
    status: 400,
    message: '',
  },
  [ErrorCode.RATE_LIMIT_EXCEEDED]: {
    status: 429,
    message: '',
  },
  [ErrorCode.CIRCUIT_BREAKER_OPEN]: {
    status: 503,
    message: '',
  },
  [ErrorCode.CIRCUIT_BREAKER_HALF_OPEN_LIMIT]: {
    status: 503,
    message: '',
  },
  [ErrorCode.INTERNAL_SERVER_ERROR]: {
    status: 500,
    message: '',
  },
  [ErrorCode.SERVICE_UNAVAILABLE]: {
    status: 503,
    message: '',
  },
};

/**
 * HTTP
 */
export function getHttpStatusFromErrorCode(code: ErrorCode): number {
  return ERROR_CODE_MAP[code]?.status || 500;
}

/**
 * 
 */
export function getDefaultErrorMessage(code: ErrorCode): string {
  return ERROR_CODE_MAP[code]?.message || '';
}

export default {
  createSuccessResponse,
  createErrorResponse,
  createValidationErrorResponse,
  createAuthErrorResponse,
  createPermissionErrorResponse,
  createNotFoundErrorResponse,
  createBusinessErrorResponse,
  createServerErrorResponse,
  createPaginatedResponse,
  parsePaginationParams,
  withErrorHandling,
  validateRequestBody,
  validateQueryParams,
};

/**
 * API
 */

import { logger } from '@/lib/logging/logger';
import { ErrorCode } from '@/shared/errors/types';

import { ApiError } from './client';

/**
 * 
 */
export interface RetryStrategy {
  maxRetries: number;
  baseDelay: number;
  maxDelay: number;
  backoffFactor: number;
  jitter: boolean;
  retryCondition: (error: ApiError, attempt: number) => boolean;
}

/**
 * 
 */
export interface RetryResult<T> {
  success: boolean;
  data?: T;
  error?: ApiError;
  attempts: number;
  totalDuration: number;
}

/**
 * 
 */
export const DEFAULT_RETRY_STRATEGY: RetryStrategy = {
  maxRetries: 3,
  baseDelay: 1000,
  maxDelay: 30000,
  backoffFactor: 2,
  jitter: true,
  retryCondition: (error: ApiError, attempt: number) => {
    // 
    return error.isRetryable() && attempt < 3;
  },
};

/**
 * 
 */
export const EXPONENTIAL_BACKOFF_STRATEGY: RetryStrategy = {
  maxRetries: 5,
  baseDelay: 500,
  maxDelay: 60000,
  backoffFactor: 2,
  jitter: true,
  retryCondition: (error: ApiError, attempt: number) => {
    // 
    return (error.isNetworkError() || error.isServerError()) && attempt < 5;
  },
};

/**
 * 
 */
export const LINEAR_RETRY_STRATEGY: RetryStrategy = {
  maxRetries: 3,
  baseDelay: 2000,
  maxDelay: 10000,
  backoffFactor: 1,
  jitter: false,
  retryCondition: (error: ApiError, attempt: number) => {
    // 5xx
    return error.isServerError() && attempt < 3;
  },
};

/**
 * 
 */
export const FAST_RETRY_STRATEGY: RetryStrategy = {
  maxRetries: 2,
  baseDelay: 100,
  maxDelay: 1000,
  backoffFactor: 2,
  jitter: true,
  retryCondition: (error: ApiError, attempt: number) => {
    // 
    return error.isNetworkError() && attempt < 2;
  },
};

/**
 * 
 */
export class RetryManager {
  private strategy: RetryStrategy;

  constructor(strategy: RetryStrategy = DEFAULT_RETRY_STRATEGY) {
    this.strategy = strategy;
  }

  /**
   * 
   */
  async execute<T>(
    operation: () => Promise<T>,
    customStrategy?: Partial<RetryStrategy>,
  ): Promise<RetryResult<T>> {
    const strategy = { ...this.strategy, ...customStrategy };
    const startTime = Date.now();
    let lastError: ApiError;
    let attempts = 0;

    for (let attempt = 0; attempt <= strategy.maxRetries; attempt++) {
      attempts = attempt + 1;

      try {
        if (attempt > 0) {
          // 
          const delay = this.calculateDelay(attempt, strategy);

          logger.info('Retrying operation', {
            metadata: {
              attempt,
              maxRetries: strategy.maxRetries,
              delay,
              lastError: lastError?.message,
            },
          });

          await this.delay(delay);
        }

        const result = await operation();

        return {
          success: true,
          data: result,
          attempts,
          totalDuration: Date.now() - startTime,
        };
      } catch (error) {
        lastError = error instanceof ApiError ? error : new ApiError(
          error instanceof Error ? error.message : 'Unknown error',
          0,
        );

        // 
        if (!strategy.retryCondition(lastError, attempt)) {
          break;
        }
      }
    }

    return {
      success: false,
      error: lastError!,
      attempts,
      totalDuration: Date.now() - startTime,
    };
  }

  /**
   * 
   */
  private calculateDelay(attempt: number, strategy: RetryStrategy): number {
    let delay: number;

    if (strategy.backoffFactor === 1) {
      // 
      delay = strategy.baseDelay * attempt;
    } else {
      // 
      delay = strategy.baseDelay * Math.pow(strategy.backoffFactor, attempt - 1);
    }

    // 
    delay = Math.min(delay, strategy.maxDelay);

    // 
    if (strategy.jitter) {
      delay = delay * (0.5 + Math.random() * 0.5);
    }

    return Math.floor(delay);
  }

  /**
   * 
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * 
   */
  updateStrategy(strategy: Partial<RetryStrategy>): void {
    this.strategy = { ...this.strategy, ...strategy };
  }

  /**
   * 
   */
  getStrategy(): RetryStrategy {
    return { ...this.strategy };
  }
}

/**
 * 
 */
export enum CircuitBreakerState {
  CLOSED = 'closed',
  OPEN = 'open',
  HALF_OPEN = 'half_open'
}

/**
 * 
 */
export interface CircuitBreakerConfig {
  failureThreshold: number;
  recoveryTimeout: number;
  monitoringPeriod: number;
  halfOpenMaxCalls: number;
}

/**
 * 
 */
export class CircuitBreaker {
  private state: CircuitBreakerState = CircuitBreakerState.CLOSED;
  private failureCount = 0;
  private lastFailureTime = 0;
  private halfOpenCalls = 0;
  private config: CircuitBreakerConfig;

  constructor(config: Partial<CircuitBreakerConfig> = {}) {
    this.config = {
      failureThreshold: 5,
      recoveryTimeout: 60000, // 1
      monitoringPeriod: 10000, // 10
      halfOpenMaxCalls: 3,
      ...config,
    };
  }

  /**
   * 
   */
  async execute<T>(operation: () => Promise<T>): Promise<T> {
    if (this.state === CircuitBreakerState.OPEN) {
      if (this.shouldAttemptReset()) {
        this.state = CircuitBreakerState.HALF_OPEN;
        this.halfOpenCalls = 0;
      } else {
        throw new ApiError(
          '',
          503,
          ErrorCode.CIRCUIT_BREAKER_OPEN,
        );
      }
    }

    if (this.state === CircuitBreakerState.HALF_OPEN &&
        this.halfOpenCalls >= this.config.halfOpenMaxCalls) {
      throw new ApiError(
        '',
        503,
        ErrorCode.CIRCUIT_BREAKER_HALF_OPEN_LIMIT,
      );
    }

    try {
      const result = await operation();
      this.onSuccess();
      return result;
    } catch (error) {
      this.onFailure();
      throw error;
    }
  }

  /**
   * 
   */
  private onSuccess(): void {
    this.failureCount = 0;

    if (this.state === CircuitBreakerState.HALF_OPEN) {
      this.halfOpenCalls++;

      if (this.halfOpenCalls >= this.config.halfOpenMaxCalls) {
        this.state = CircuitBreakerState.CLOSED;
        this.halfOpenCalls = 0;
      }
    }
  }

  /**
   * 
   */
  private onFailure(): void {
    this.failureCount++;
    this.lastFailureTime = Date.now();

    if (this.state === CircuitBreakerState.HALF_OPEN) {
      this.state = CircuitBreakerState.OPEN;
      this.halfOpenCalls = 0;
    } else if (this.failureCount >= this.config.failureThreshold) {
      this.state = CircuitBreakerState.OPEN;
    }
  }

  /**
   * 
   */
  private shouldAttemptReset(): boolean {
    return Date.now() - this.lastFailureTime >= this.config.recoveryTimeout;
  }

  /**
   * 
   */
  getState(): CircuitBreakerState {
    return this.state;
  }

  /**
   * 
   */
  getStats() {
    return {
      state: this.state,
      failureCount: this.failureCount,
      lastFailureTime: this.lastFailureTime,
      halfOpenCalls: this.halfOpenCalls,
    };
  }

  /**
   * 
   */
  reset(): void {
    this.state = CircuitBreakerState.CLOSED;
    this.failureCount = 0;
    this.lastFailureTime = 0;
    this.halfOpenCalls = 0;
  }
}

/**
 * 
 */
export function createRetryManager(strategy?: RetryStrategy): RetryManager {
  return new RetryManager(strategy);
}

/**
 * 
 */
export function createCircuitBreaker(config?: Partial<CircuitBreakerConfig>): CircuitBreaker {
  return new CircuitBreaker(config);
}

/**
 * 
 */
export function withRetry<T extends any[], R>(
  fn: (...args: T) => Promise<R>,
  strategy?: Partial<RetryStrategy>,
): (...args: T) => Promise<R> {
  const retryManager = new RetryManager({ ...DEFAULT_RETRY_STRATEGY, ...strategy });

  return async (...args: T): Promise<R> => {
    const result = await retryManager.execute(() => fn(...args));

    if (result.success) {
      return result.data!;
    }

    if (result.error instanceof Error) {
      throw result.error;
    }

    throw new Error(String(result.error));
  };
}

/**
 * 
 */
export function withCircuitBreaker<T extends any[], R>(
  fn: (...args: T) => Promise<R>,
  config?: Partial<CircuitBreakerConfig>,
): (...args: T) => Promise<R> {
  const circuitBreaker = new CircuitBreaker(config);

  return async (...args: T): Promise<R> => {
    return circuitBreaker.execute(() => fn(...args));
  };
}

export default {
  RetryManager,
  CircuitBreaker,
  createRetryManager,
  createCircuitBreaker,
  withRetry,
  withCircuitBreaker,
  DEFAULT_RETRY_STRATEGY,
  EXPONENTIAL_BACKOFF_STRATEGY,
  LINEAR_RETRY_STRATEGY,
  FAST_RETRY_STRATEGY,
};

