import { DatabaseService } from '../database';
import { TimePeriod, SharePlatform } from '../types';

export interface ConversionMetrics {
  // 
  inviteToClick: number        // 
  clickToRegister: number      // 
  registerToActivate: number   // 
  overallConversion: number    // 

  // 
  totalInvites: number
  totalClicks: number
  totalRegistrations: number
  totalActivations: number

  // 
  averageTimeToRegister: number  // 
  averageTimeToActivate: number  // 
}

export interface PlatformConversionAnalysis {
  platform: SharePlatform
  metrics: ConversionMetrics
  trend: 'improving' | 'declining' | 'stable'
  recommendations: string[]
}

export interface ConversionFunnelData {
  step: string
  count: number
  percentage: number
  dropoffRate: number
}

export interface ConversionAnalysisService {
  // 
  getOverallConversionAnalysis(period: TimePeriod): Promise<ConversionMetrics>

  // 
  getPlatformConversionAnalysis(period: TimePeriod): Promise<PlatformConversionAnalysis[]>

  // 
  getUserConversionAnalysis(userId: string, period: TimePeriod): Promise<ConversionMetrics>

  // 
  getConversionFunnelData(period: TimePeriod): Promise<ConversionFunnelData[]>

  // 
  getConversionTrend(period: TimePeriod, granularity: 'day' | 'week' | 'month'): Promise<Array<{
    date: string
    metrics: ConversionMetrics
  }>>

  // 
  getOptimizationRecommendations(userId?: string): Promise<{
    priority: 'high' | 'medium' | 'low'
    category: string
    recommendation: string
    expectedImprovement: string
  }[]>

  // 
  analyzeConversionFactors(period: TimePeriod): Promise<{
    timeOfDay: { [hour: string]: number }
    dayOfWeek: { [day: string]: number }
    deviceType: { [device: string]: number }
    referrerType: { [referrer: string]: number }
  }>
}

export class ConversionAnalysisServiceImpl implements ConversionAnalysisService {
  constructor(private db: DatabaseService) {}

  /**
   * 
   */
  async getOverallConversionAnalysis(period: TimePeriod): Promise<ConversionMetrics> {
    try {
      const query = `
        SELECT 
          COUNT(DISTINCT CASE WHEN ie.type = 'code_generated' THEN ie.invite_code_id END) as total_invites,
          COUNT(DISTINCT CASE WHEN ie.type = 'link_clicked' THEN ie.invite_code_id END) as total_clicks,
          COUNT(DISTINCT CASE WHEN ie.type = 'user_registered' THEN ie.invite_code_id END) as total_registrations,
          COUNT(DISTINCT CASE WHEN ie.type = 'user_activated' THEN ie.invite_code_id END) as total_activations,
          AVG(CASE 
            WHEN ie_reg.timestamp IS NOT NULL AND ie_gen.timestamp IS NOT NULL 
            THEN TIMESTAMPDIFF(HOUR, ie_gen.timestamp, ie_reg.timestamp) 
          END) as avg_time_to_register,
          AVG(CASE 
            WHEN ie_act.timestamp IS NOT NULL AND ie_reg.timestamp IS NOT NULL 
            THEN TIMESTAMPDIFF(HOUR, ie_reg.timestamp, ie_act.timestamp) 
          END) as avg_time_to_activate
        FROM invite_events ie
        LEFT JOIN invite_events ie_gen ON ie.invite_code_id = ie_gen.invite_code_id AND ie_gen.type = 'code_generated'
        LEFT JOIN invite_events ie_reg ON ie.invite_code_id = ie_reg.invite_code_id AND ie_reg.type = 'user_registered'
        LEFT JOIN invite_events ie_act ON ie.invite_code_id = ie_act.invite_code_id AND ie_act.type = 'user_activated'
        WHERE ie.timestamp BETWEEN ? AND ?
      `;

      const result = await this.db.queryOne(query, [period.start, period.end]);

      return this.calculateConversionMetrics(result);
    } catch (error) {
      console.error('Failed to get overall conversion analysis:', error);
      throw new Error('Failed to get overall conversion analysis');
    }
  }

  /**
   * 
   */
  async getPlatformConversionAnalysis(period: TimePeriod): Promise<PlatformConversionAnalysis[]> {
    try {
      const query = `
        SELECT 
          se.platform,
          COUNT(DISTINCT CASE WHEN se.event_type = 'share' THEN se.invite_code_id END) as total_invites,
          COUNT(DISTINCT CASE WHEN se.event_type = 'click' THEN se.invite_code_id END) as total_clicks,
          COUNT(DISTINCT CASE WHEN se.event_type = 'conversion' THEN se.invite_code_id END) as total_registrations,
          COUNT(DISTINCT CASE WHEN ie.type = 'user_activated' THEN ie.invite_code_id END) as total_activations
        FROM share_events se
        LEFT JOIN invite_events ie ON se.invite_code_id = ie.invite_code_id AND ie.type = 'user_activated'
        WHERE se.timestamp BETWEEN ? AND ?
        GROUP BY se.platform
      `;

      const results = await this.db.query<{
        platform: SharePlatform;
        total_invites: number | string;
        total_clicks: number | string;
        total_registrations: number | string;
        total_activations: number | string;
        avg_time_to_register?: number | string;
        avg_time_to_activate?: number | string;
      }>(query, [period.start, period.end]);

      const analyses: PlatformConversionAnalysis[] = [];

      for (const row of results) {
        const metrics = this.calculateConversionMetrics(row);
        const trend = await this.calculatePlatformTrend(row.platform, period);
        const recommendations = this.generatePlatformRecommendations(row.platform, metrics);

        analyses.push({
          platform: row.platform as SharePlatform,
          metrics,
          trend,
          recommendations,
        });
      }

      return analyses;
    } catch (error) {
      console.error('Failed to get platform conversion analysis:', error);
      throw new Error('Failed to get platform conversion analysis');
    }
  }

  /**
   * 
   */
  async getUserConversionAnalysis(userId: string, period: TimePeriod): Promise<ConversionMetrics> {
    try {
      const query = `
        SELECT 
          COUNT(DISTINCT CASE WHEN ie.type = 'code_generated' THEN ie.invite_code_id END) as total_invites,
          COUNT(DISTINCT CASE WHEN ie.type = 'link_clicked' THEN ie.invite_code_id END) as total_clicks,
          COUNT(DISTINCT CASE WHEN ie.type = 'user_registered' THEN ie.invite_code_id END) as total_registrations,
          COUNT(DISTINCT CASE WHEN ie.type = 'user_activated' THEN ie.invite_code_id END) as total_activations,
          AVG(CASE 
            WHEN ie_reg.timestamp IS NOT NULL AND ie_gen.timestamp IS NOT NULL 
            THEN TIMESTAMPDIFF(HOUR, ie_gen.timestamp, ie_reg.timestamp) 
          END) as avg_time_to_register,
          AVG(CASE 
            WHEN ie_act.timestamp IS NOT NULL AND ie_reg.timestamp IS NOT NULL 
            THEN TIMESTAMPDIFF(HOUR, ie_reg.timestamp, ie_act.timestamp) 
          END) as avg_time_to_activate
        FROM invite_events ie
        LEFT JOIN invite_events ie_gen ON ie.invite_code_id = ie_gen.invite_code_id AND ie_gen.type = 'code_generated'
        LEFT JOIN invite_events ie_reg ON ie.invite_code_id = ie_reg.invite_code_id AND ie_reg.type = 'user_registered'
        LEFT JOIN invite_events ie_act ON ie.invite_code_id = ie_act.invite_code_id AND ie_act.type = 'user_activated'
        WHERE ie.inviter_id = ? AND ie.timestamp BETWEEN ? AND ?
      `;

      const result = await this.db.queryOne<{
        total_invites: number | string;
        total_clicks: number | string;
        total_registrations: number | string;
        total_activations: number | string;
        avg_time_to_register: number | string;
        avg_time_to_activate: number | string;
      }>(query, [userId, period.start, period.end]);

      return this.calculateConversionMetrics(result);
    } catch (error) {
      console.error('Failed to get user conversion analysis:', error);
      throw new Error('Failed to get user conversion analysis');
    }
  }

  /**
   * 
   */
  async getConversionFunnelData(period: TimePeriod): Promise<ConversionFunnelData[]> {
    try {
      const metrics = await this.getOverallConversionAnalysis(period);

      const funnelData: ConversionFunnelData[] = [
        {
          step: '',
          count: metrics.totalInvites,
          percentage: 100,
          dropoffRate: 0,
        },
        {
          step: '',
          count: metrics.totalClicks,
          percentage: metrics.totalInvites > 0 ? (metrics.totalClicks / metrics.totalInvites) * 100 : 0,
          dropoffRate: metrics.totalInvites > 0 ? ((metrics.totalInvites - metrics.totalClicks) / metrics.totalInvites) * 100 : 0,
        },
        {
          step: '',
          count: metrics.totalRegistrations,
          percentage: metrics.totalInvites > 0 ? (metrics.totalRegistrations / metrics.totalInvites) * 100 : 0,
          dropoffRate: metrics.totalClicks > 0 ? ((metrics.totalClicks - metrics.totalRegistrations) / metrics.totalClicks) * 100 : 0,
        },
        {
          step: '',
          count: metrics.totalActivations,
          percentage: metrics.totalInvites > 0 ? (metrics.totalActivations / metrics.totalInvites) * 100 : 0,
          dropoffRate: metrics.totalRegistrations > 0 ? ((metrics.totalRegistrations - metrics.totalActivations) / metrics.totalRegistrations) * 100 : 0,
        },
      ];

      return funnelData;
    } catch (error) {
      console.error('Failed to get conversion funnel data:', error);
      throw new Error('Failed to get conversion funnel data');
    }
  }

  /**
   * 
   */
  async getConversionTrend(
    period: TimePeriod,
    granularity: 'day' | 'week' | 'month' = 'day',
  ): Promise<Array<{
    date: string
    metrics: ConversionMetrics
  }>> {
    try {
      let dateFormat: string;
      let groupBy: string;

      switch (granularity) {
        case 'week':
          dateFormat = '%Y-%u';
          groupBy = 'YEARWEEK(ie.timestamp)';
          break;
        case 'month':
          dateFormat = '%Y-%m';
          groupBy = 'DATE_FORMAT(ie.timestamp, "%Y-%m")';
          break;
        default:
          dateFormat = '%Y-%m-%d';
          groupBy = 'DATE(ie.timestamp)';
      }

      const query = `
        SELECT 
          DATE_FORMAT(ie.timestamp, '${dateFormat}') as date,
          COUNT(DISTINCT CASE WHEN ie.type = 'code_generated' THEN ie.invite_code_id END) as total_invites,
          COUNT(DISTINCT CASE WHEN ie.type = 'link_clicked' THEN ie.invite_code_id END) as total_clicks,
          COUNT(DISTINCT CASE WHEN ie.type = 'user_registered' THEN ie.invite_code_id END) as total_registrations,
          COUNT(DISTINCT CASE WHEN ie.type = 'user_activated' THEN ie.invite_code_id END) as total_activations
        FROM invite_events ie
        WHERE ie.timestamp BETWEEN ? AND ?
        GROUP BY ${groupBy}
        ORDER BY date
      `;

      const results = await this.db.query(query, [period.start, period.end]);

      return results.map((row: any) => ({
        date: row.date,
        metrics: this.calculateConversionMetrics(row),
      }));
    } catch (error) {
      console.error('Failed to get conversion trend:', error);
      throw new Error('Failed to get conversion trend');
    }
  }

  /**
   * 
   */
  async getOptimizationRecommendations(userId?: string): Promise<{
    priority: 'high' | 'medium' | 'low'
    category: string
    recommendation: string
    expectedImprovement: string
  }[]> {
    try {
      const recommendations = [];

      // 
      const period: TimePeriod = {
        start: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000), // 30
        end: new Date(),
      };

      const metrics = userId
        ? await this.getUserConversionAnalysis(userId, period)
        : await this.getOverallConversionAnalysis(period);

      // 
      if (metrics.inviteToClick < 20) {
        recommendations.push({
          priority: 'high' as const,
          category: '',
          recommendation: '',
          expectedImprovement: '15-25%',
        });
      }

      // 
      if (metrics.clickToRegister < 30) {
        recommendations.push({
          priority: 'high' as const,
          category: '',
          recommendation: '',
          expectedImprovement: '20-30%',
        });
      }

      // 
      if (metrics.registerToActivate < 60) {
        recommendations.push({
          priority: 'medium' as const,
          category: '',
          recommendation: '',
          expectedImprovement: '10-20%',
        });
      }

      // 
      if (metrics.averageTimeToRegister > 24) {
        recommendations.push({
          priority: 'medium' as const,
          category: '',
          recommendation: '',
          expectedImprovement: '30-50%',
        });
      }

      // 
      if (recommendations.length === 0) {
        recommendations.push({
          priority: 'low' as const,
          category: '',
          recommendation: 'A/B',
          expectedImprovement: '5-10%',
        });
      }

      return recommendations;
    } catch (error) {
      console.error('Failed to get optimization recommendations:', error);
      throw new Error('Failed to get optimization recommendations');
    }
  }

  /**
   * 
   */
  async analyzeConversionFactors(period: TimePeriod): Promise<{
    timeOfDay: { [hour: string]: number }
    dayOfWeek: { [day: string]: number }
    deviceType: { [device: string]: number }
    referrerType: { [referrer: string]: number }
  }> {
    try {
      // 
      const timeQuery = `
        SELECT 
          HOUR(ie.timestamp) as hour,
          COUNT(CASE WHEN ie.type = 'user_registered' THEN 1 END) / COUNT(CASE WHEN ie.type = 'link_clicked' THEN 1 END) * 100 as conversion_rate
        FROM invite_events ie
        WHERE ie.timestamp BETWEEN ? AND ?
        AND ie.type IN ('link_clicked', 'user_registered')
        GROUP BY HOUR(ie.timestamp)
        HAVING COUNT(CASE WHEN ie.type = 'link_clicked' THEN 1 END) > 0
      `;

      // 
      const dayQuery = `
        SELECT 
          DAYNAME(ie.timestamp) as day_name,
          COUNT(CASE WHEN ie.type = 'user_registered' THEN 1 END) / COUNT(CASE WHEN ie.type = 'link_clicked' THEN 1 END) * 100 as conversion_rate
        FROM invite_events ie
        WHERE ie.timestamp BETWEEN ? AND ?
        AND ie.type IN ('link_clicked', 'user_registered')
        GROUP BY DAYNAME(ie.timestamp), DAYOFWEEK(ie.timestamp)
        HAVING COUNT(CASE WHEN ie.type = 'link_clicked' THEN 1 END) > 0
        ORDER BY DAYOFWEEK(ie.timestamp)
      `;

      const [timeResults, dayResults] = await Promise.all([
        this.db.query(timeQuery, [period.start, period.end]),
        this.db.query(dayQuery, [period.start, period.end]),
      ]);

      const timeOfDay: { [hour: string]: number } = {};
      timeResults.forEach((row: any) => {
        timeOfDay[row.hour] = parseFloat(row.conversion_rate) || 0;
      });

      const dayOfWeek: { [day: string]: number } = {};
      dayResults.forEach((row: any) => {
        dayOfWeek[row.day_name] = parseFloat(row.conversion_rate) || 0;
      });

      return {
        timeOfDay,
        dayOfWeek,
        deviceType: {}, // 
        referrerType: {}, // 
      };
    } catch (error) {
      console.error('Failed to analyze conversion factors:', error);
      throw new Error('Failed to analyze conversion factors');
    }
  }

  /**
   * 
   */
  private calculateConversionMetrics(data: any): ConversionMetrics {
    const totalInvites = parseInt(data.total_invites, 10) || 0;
    const totalClicks = parseInt(data.total_clicks, 10) || 0;
    const totalRegistrations = parseInt(data.total_registrations, 10) || 0;
    const totalActivations = parseInt(data.total_activations, 10) || 0;

    return {
      inviteToClick: totalInvites > 0 ? (totalClicks / totalInvites) * 100 : 0,
      clickToRegister: totalClicks > 0 ? (totalRegistrations / totalClicks) * 100 : 0,
      registerToActivate: totalRegistrations > 0 ? (totalActivations / totalRegistrations) * 100 : 0,
      overallConversion: totalInvites > 0 ? (totalActivations / totalInvites) * 100 : 0,
      totalInvites,
      totalClicks,
      totalRegistrations,
      totalActivations,
      averageTimeToRegister: parseFloat(data.avg_time_to_register) || 0,
      averageTimeToActivate: parseFloat(data.avg_time_to_activate) || 0,
    };
  }

  /**
   * 
   */
  private async calculatePlatformTrend(platform: SharePlatform, period: TimePeriod): Promise<'improving' | 'declining' | 'stable'> {
    try {
      const midPoint = new Date((period.start.getTime() + period.end.getTime()) / 2);

      const firstHalfQuery = `
        SELECT 
          COUNT(CASE WHEN event_type = 'conversion' THEN 1 END) / COUNT(CASE WHEN event_type = 'click' THEN 1 END) * 100 as conversion_rate
        FROM share_events 
        WHERE platform = ? AND timestamp BETWEEN ? AND ?
        HAVING COUNT(CASE WHEN event_type = 'click' THEN 1 END) > 0
      `;

      const secondHalfQuery = `
        SELECT 
          COUNT(CASE WHEN event_type = 'conversion' THEN 1 END) / COUNT(CASE WHEN event_type = 'click' THEN 1 END) * 100 as conversion_rate
        FROM share_events 
        WHERE platform = ? AND timestamp BETWEEN ? AND ?
        HAVING COUNT(CASE WHEN event_type = 'click' THEN 1 END) > 0
      `;

      const [firstHalf, secondHalf] = await Promise.all([
        this.db.queryOne<{ conversion_rate: number | string }>(firstHalfQuery, [platform, period.start, midPoint]),
        this.db.queryOne<{ conversion_rate: number | string }>(secondHalfQuery, [platform, midPoint, period.end]),
      ]);

      const firstRate = Number(firstHalf?.conversion_rate) || 0;
      const secondRate = Number(secondHalf?.conversion_rate) || 0;

      if (secondRate > firstRate * 1.1) return 'improving';
      if (secondRate < firstRate * 0.9) return 'declining';
      return 'stable';
    } catch (error) {
      console.error('Failed to calculate platform trend:', error);
      return 'stable';
    }
  }

  /**
   * 
   */
  private generatePlatformRecommendations(platform: SharePlatform, metrics: ConversionMetrics): string[] {
    const recommendations: string[] = [];

    switch (platform) {
      case SharePlatform.WECHAT:
        if (metrics.clickToRegister < 25) {
          recommendations.push('');
          recommendations.push('');
        }
        break;

      case SharePlatform.QQ:
        if (metrics.inviteToClick < 15) {
          recommendations.push('QQ');
        }
        break;

      case SharePlatform.EMAIL:
        if (metrics.averageTimeToRegister > 48) {
          recommendations.push('');
          recommendations.push('');
        }
        break;

      default:
        if (metrics.overallConversion < 10) {
          recommendations.push('');
        }
    }

    if (recommendations.length === 0) {
      recommendations.push('');
    }

    return recommendations;
  }
}

/**
 * 
 * AI
 */

import { logger } from '../../utils/logger';
import { DatabaseFactory } from '../database';
import { generateUUID } from '../utils';

// 
export interface CreditRecord {
  id: string
  userId: string
  amount: number
  type: CreditType
  source: CreditSource
  sourceId: string
  description: string
  expiresAt?: Date
  createdAt: Date
  usedAt?: Date
}

// 
export enum CreditType {
  EARNED = 'earned',    // 
  USED = 'used',        // 
  EXPIRED = 'expired',  // 
  REFUNDED = 'refunded' // 
}

// 
export enum CreditSource {
  INVITE_REWARD = 'invite_reward',      // 
  MILESTONE_REWARD = 'milestone_reward', // 
  ACTIVITY_REWARD = 'activity_reward',   // 
  PURCHASE = 'purchase',                 // 
  ADMIN_GRANT = 'admin_grant',          // 
  SYSTEM_REFUND = 'system_refund'       // 
}

// 
export interface UserCreditBalance {
  userId: string
  totalEarned: number
  totalUsed: number
  totalExpired: number
  availableCredits: number
  expiringCredits: number // 30
  lastUpdated: Date
}

// 
export interface CreditUsage {
  id: string
  userId: string
  amount: number
  purpose: string
  metadata?: Record<string, any>
  createdAt: Date
}

export interface CreditSystemService {
  // 
  addCredits(userId: string, amount: number, source: CreditSource, sourceId: string, description: string, expiresAt?: Date): Promise<CreditRecord>
  useCredits(userId: string, amount: number, purpose: string, metadata?: Record<string, any>): Promise<boolean>

  // 
  getUserBalance(userId: string): Promise<UserCreditBalance>
  getAvailableCredits(userId: string): Promise<number>

  // 
  getUserCreditHistory(userId: string, limit?: number): Promise<CreditRecord[]>
  getCreditUsageHistory(userId: string, limit?: number): Promise<CreditUsage[]>

  // 
  expireCredits(): Promise<number>
  getExpiringCredits(userId: string, days?: number): Promise<CreditRecord[]>

  // 
  getCreditStats(userId: string): Promise<{
    totalEarned: number
    totalUsed: number
    totalExpired: number
    averageDaily: number
    topSources: Array<{ source: CreditSource, amount: number }>
  }>
}

export class CreditSystemServiceImpl implements CreditSystemService {
  private db = DatabaseFactory.getInstance();

  async addCredits(
    userId: string,
    amount: number,
    source: CreditSource,
    sourceId: string,
    description: string,
    expiresAt?: Date,
  ): Promise<CreditRecord> {
    try {
      const creditId = generateUUID();
      const createdAt = new Date();

      // 6
      const defaultExpiresAt = expiresAt || new Date(Date.now() + 6 * 30 * 24 * 60 * 60 * 1000);

      await this.db.execute(
        `INSERT INTO credit_records 
         (id, user_id, amount, type, source, source_id, description, expires_at, created_at)
         VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)`,
        [
          creditId,
          userId,
          amount,
          CreditType.EARNED,
          source,
          sourceId,
          description,
          defaultExpiresAt,
          createdAt,
        ],
      );

      // 
      await this.updateUserBalance(userId);

      const creditRecord: CreditRecord = {
        id: creditId,
        userId,
        amount,
        type: CreditType.EARNED,
        source,
        sourceId,
        description,
        expiresAt: defaultExpiresAt,
        createdAt,
      };

      logger.info('Credits added to user', { userId, amount, source, description });
      return creditRecord;

    } catch (error) {
      logger.error('Failed to add credits', { userId, amount, source, error });
      throw error;
    }
  }

  async useCredits(userId: string, amount: number, purpose: string, metadata?: Record<string, any>): Promise<boolean> {
    return await this.db.transaction(async (connection) => {
      try {
        // 
        const availableCredits = await this.getAvailableCredits(userId);
        if (availableCredits < amount) {
          logger.warn('Insufficient credits', { userId, requested: amount, available: availableCredits });
          return false;
        }

        // 
        const earnedCredits = await connection.query<any>(
          `SELECT * FROM credit_records 
           WHERE user_id = ? AND type = ? AND used_at IS NULL AND expires_at > NOW()
           ORDER BY expires_at ASC`,
          [userId, CreditType.EARNED],
        );

        let remainingAmount = amount;
        const usageId = generateUUID();
        const usedAt = new Date();

        for (const credit of earnedCredits) {
          if (remainingAmount <= 0) break;

          const useAmount = Math.min(remainingAmount, credit.amount);

          // 
          await connection.execute(
            `INSERT INTO credit_records 
             (id, user_id, amount, type, source, source_id, description, created_at, used_at)
             VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)`,
            [
              generateUUID(),
              userId,
              -useAmount, // 
              CreditType.USED,
              credit.source,
              credit.source_id,
              `: ${purpose}`,
              usedAt,
              usedAt,
            ],
          );

          // 
          if (useAmount === credit.amount) {
            // 
            await connection.execute(
              'UPDATE credit_records SET used_at = ? WHERE id = ?',
              [usedAt, credit.id],
            );
          } else {
            // 
            await connection.execute(
              'UPDATE credit_records SET amount = ? WHERE id = ?',
              [credit.amount - useAmount, credit.id],
            );
          }

          remainingAmount -= useAmount;
        }

        // 
        await connection.execute(
          `INSERT INTO credit_usage 
           (id, user_id, amount, purpose, metadata, created_at)
           VALUES (?, ?, ?, ?, ?, ?)`,
          [
            usageId,
            userId,
            amount,
            purpose,
            metadata ? JSON.stringify(metadata) : null,
            usedAt,
          ],
        );

        // 
        await this.updateUserBalance(userId);

        logger.info('Credits used successfully', { userId, amount, purpose });
        return true;

      } catch (error) {
        logger.error('Failed to use credits', { userId, amount, purpose, error });
        throw error;
      }
    });
  }

  async getUserBalance(userId: string): Promise<UserCreditBalance> {
    try {
      // 
      const cached = await this.db.query<any>(
        'SELECT * FROM user_credit_balances WHERE user_id = ?',
        [userId],
      );

      if (cached.length > 0) {
        const balance = cached[0];
        return {
          userId: balance.user_id,
          totalEarned: balance.total_earned,
          totalUsed: balance.total_used,
          totalExpired: balance.total_expired,
          availableCredits: balance.available_credits,
          expiringCredits: balance.expiring_credits,
          lastUpdated: balance.last_updated,
        };
      }

      // 
      await this.updateUserBalance(userId);
      return await this.getUserBalance(userId);

    } catch (error) {
      logger.error('Failed to get user balance', { userId, error });
      throw error;
    }
  }

  async getAvailableCredits(userId: string): Promise<number> {
    try {
      const [result] = await this.db.query<{ available: number }>(
        `SELECT COALESCE(SUM(CASE 
           WHEN type = ? AND used_at IS NULL AND expires_at > NOW() THEN amount
           ELSE 0 
         END), 0) as available
         FROM credit_records 
         WHERE user_id = ?`,
        [CreditType.EARNED, userId],
      );

      return result.available;

    } catch (error) {
      logger.error('Failed to get available credits', { userId, error });
      throw error;
    }
  }

  async getUserCreditHistory(userId: string, limit: number = 50): Promise<CreditRecord[]> {
    try {
      const results = await this.db.query<any>(
        `SELECT * FROM credit_records 
         WHERE user_id = ? 
         ORDER BY created_at DESC 
         LIMIT ?`,
        [userId, limit],
      );

      return results.map(row => ({
        id: row.id,
        userId: row.user_id,
        amount: row.amount,
        type: row.type,
        source: row.source,
        sourceId: row.source_id,
        description: row.description,
        expiresAt: row.expires_at,
        createdAt: row.created_at,
        usedAt: row.used_at,
      }));

    } catch (error) {
      logger.error('Failed to get user credit history', { userId, error });
      throw error;
    }
  }

  async getCreditUsageHistory(userId: string, limit: number = 50): Promise<CreditUsage[]> {
    try {
      const results = await this.db.query<any>(
        `SELECT * FROM credit_usage 
         WHERE user_id = ? 
         ORDER BY created_at DESC 
         LIMIT ?`,
        [userId, limit],
      );

      return results.map(row => ({
        id: row.id,
        userId: row.user_id,
        amount: row.amount,
        purpose: row.purpose,
        metadata: row.metadata ? JSON.parse(row.metadata) : undefined,
        createdAt: row.created_at,
      }));

    } catch (error) {
      logger.error('Failed to get credit usage history', { userId, error });
      throw error;
    }
  }

  async expireCredits(): Promise<number> {
    try {
      // 
      const expiredCredits = await this.db.query<any>(
        `SELECT * FROM credit_records 
         WHERE type = ? AND used_at IS NULL AND expires_at <= NOW()`,
        [CreditType.EARNED],
      );

      let expiredCount = 0;

      for (const credit of expiredCredits) {
        // 
        await this.db.execute(
          `INSERT INTO credit_records 
           (id, user_id, amount, type, source, source_id, description, created_at)
           VALUES (?, ?, ?, ?, ?, ?, ?, ?)`,
          [
            generateUUID(),
            credit.user_id,
            -credit.amount,
            CreditType.EXPIRED,
            credit.source,
            credit.source_id,
            `: ${credit.description}`,
            new Date(),
          ],
        );

        // 
        await this.db.execute(
          'UPDATE credit_records SET used_at = NOW() WHERE id = ?',
          [credit.id],
        );

        expiredCount++;

        // 
        await this.updateUserBalance(credit.user_id);
      }

      logger.info('Credits expired', { expiredCount });
      return expiredCount;

    } catch (error) {
      logger.error('Failed to expire credits', { error });
      throw error;
    }
  }

  async getExpiringCredits(userId: string, days: number = 30): Promise<CreditRecord[]> {
    try {
      const expiryDate = new Date();
      expiryDate.setDate(expiryDate.getDate() + days);

      const results = await this.db.query<any>(
        `SELECT * FROM credit_records 
         WHERE user_id = ? AND type = ? AND used_at IS NULL 
         AND expires_at BETWEEN NOW() AND ?
         ORDER BY expires_at ASC`,
        [userId, CreditType.EARNED, expiryDate],
      );

      return results.map(row => ({
        id: row.id,
        userId: row.user_id,
        amount: row.amount,
        type: row.type,
        source: row.source,
        sourceId: row.source_id,
        description: row.description,
        expiresAt: row.expires_at,
        createdAt: row.created_at,
        usedAt: row.used_at,
      }));

    } catch (error) {
      logger.error('Failed to get expiring credits', { userId, days, error });
      throw error;
    }
  }

  async getCreditStats(userId: string): Promise<{
    totalEarned: number
    totalUsed: number
    totalExpired: number
    averageDaily: number
    topSources: Array<{ source: CreditSource, amount: number }>
  }> {
    try {
      // 
      const [earnedResult] = await this.db.query<{ total: number }>(
        `SELECT COALESCE(SUM(amount), 0) as total 
         FROM credit_records 
         WHERE user_id = ? AND type = ?`,
        [userId, CreditType.EARNED],
      );

      // 
      const [usedResult] = await this.db.query<{ total: number }>(
        `SELECT COALESCE(SUM(ABS(amount)), 0) as total 
         FROM credit_records 
         WHERE user_id = ? AND type = ?`,
        [userId, CreditType.USED],
      );

      // 
      const [expiredResult] = await this.db.query<{ total: number }>(
        `SELECT COALESCE(SUM(ABS(amount)), 0) as total 
         FROM credit_records 
         WHERE user_id = ? AND type = ?`,
        [userId, CreditType.EXPIRED],
      );

      // 
      const [firstRecord] = await this.db.query<{ first_date: Date }>(
        `SELECT MIN(created_at) as first_date 
         FROM credit_records 
         WHERE user_id = ? AND type = ?`,
        [userId, CreditType.EARNED],
      );

      let averageDaily = 0;
      if (firstRecord.first_date) {
        const daysSinceFirst = Math.max(1, Math.floor((Date.now() - firstRecord.first_date.getTime()) / (24 * 60 * 60 * 1000)));
        averageDaily = earnedResult.total / daysSinceFirst;
      }

      // 
      const sourceStats = await this.db.query<{ source: CreditSource, amount: number }>(
        `SELECT source, SUM(amount) as amount 
         FROM credit_records 
         WHERE user_id = ? AND type = ? 
         GROUP BY source 
         ORDER BY amount DESC 
         LIMIT 5`,
        [userId, CreditType.EARNED],
      );

      return {
        totalEarned: earnedResult.total,
        totalUsed: usedResult.total,
        totalExpired: expiredResult.total,
        averageDaily: Math.round(averageDaily * 100) / 100,
        topSources: sourceStats,
      };

    } catch (error) {
      logger.error('Failed to get credit stats', { userId, error });
      throw error;
    }
  }

  // 
  private async updateUserBalance(userId: string): Promise<void> {
    try {
      const balance = await this.getUserBalance(userId);
      const availableCredits = await this.getAvailableCredits(userId);
      const expiringCredits = await this.getExpiringCredits(userId, 30);
      const expiringAmount = expiringCredits.reduce((sum, credit) => sum + credit.amount, 0);

      await this.db.execute(
        `INSERT INTO user_credit_balances 
         (user_id, total_earned, total_used, total_expired, available_credits, expiring_credits, last_updated)
         VALUES (?, ?, ?, ?, ?, ?, ?)
         ON DUPLICATE KEY UPDATE
         total_earned = VALUES(total_earned),
         total_used = VALUES(total_used),
         total_expired = VALUES(total_expired),
         available_credits = VALUES(available_credits),
         expiring_credits = VALUES(expiring_credits),
         last_updated = VALUES(last_updated)`,
        [
          userId,
          balance.totalEarned || 0,
          balance.totalUsed || 0,
          balance.totalExpired || 0,
          availableCredits,
          expiringAmount,
          new Date(),
        ],
      );

    } catch (error) {
      logger.error('Failed to update user balance cache', { userId, error });
      // 
    }
  }
}

