/**
 * Historical Data Manager
 *
 * Manages the storage, retrieval, and lifecycle of test execution history data.
 * Provides efficient data access patterns for trend analysis and reporting.
 */

import { EventEmitter } from 'events';

export interface TestExecutionRecord {
  id: string;
  timestamp: Date;
  testSuite: string;
  testFile: string;
  testName: string;
  status: 'passed' | 'failed' | 'skipped';
  duration: number;
  error?: {
    message: string;
    stack?: string;
    type: string;
  };
  coverage?: {
    statements: number;
    branches: number;
    functions: number;
    lines: number;
  };
  performance?: {
    memoryUsage: number;
    cpuUsage: number;
    executionTime: number;
  };
  environment: {
    nodeVersion: string;
    platform: string;
    ci: boolean;
    branch: string;
    commit: string;
  };
  metadata?: Record<string, any>;
}

export interface TestSuiteRecord {
  id: string;
  timestamp: Date;
  suiteName: string;
  totalTests: number;
  passedTests: number;
  failedTests: number;
  skippedTests: number;
  duration: number;
  coverage: {
    statements: number;
    branches: number;
    functions: number;
    lines: number;
  };
  performance: {
    totalMemory: number;
    peakMemory: number;
    averageExecutionTime: number;
  };
  environment: {
    nodeVersion: string;
    platform: string;
    ci: boolean;
    branch: string;
    commit: string;
  };
  tests: TestExecutionRecord[];
}

export interface DataRetentionPolicy {
  maxAge: number; // days
  maxRecords: number;
  compressionThreshold: number; // days after which to compress data
  archiveThreshold: number; // days after which to archive data
}

export interface QueryOptions {
  startDate?: Date;
  endDate?: Date;
  testSuite?: string;
  testFile?: string;
  status?: 'passed' | 'failed' | 'skipped';
  branch?: string;
  limit?: number;
  offset?: number;
  sortBy?: 'timestamp' | 'duration' | 'testName';
  sortOrder?: 'asc' | 'desc';
}

export interface AggregationOptions {
  groupBy: 'day' | 'week' | 'month' | 'testSuite' | 'testFile';
  metrics: ('count' | 'duration' | 'coverage' | 'performance')[];
  startDate?: Date;
  endDate?: Date;
}

export class HistoricalDataManager extends EventEmitter {
  private storage: Map<string, TestSuiteRecord[]> = new Map();
  private testRecords: Map<string, TestExecutionRecord[]> = new Map();
  private retentionPolicy: DataRetentionPolicy;
  private compressionEnabled: boolean = true;
  private archiveEnabled: boolean = true;

  constructor(retentionPolicy?: Partial<DataRetentionPolicy>) {
    super();

    this.retentionPolicy = {
      maxAge: 90, // 90 days
      maxRecords: 100000,
      compressionThreshold: 30, // 30 days
      archiveThreshold: 60, // 60 days
      ...retentionPolicy,
    };

    // Start cleanup interval
    this.startCleanupInterval();
  }

  /**
   * Store a test suite execution record
   */
  async storeTestSuiteRecord(record: TestSuiteRecord): Promise<void> {
    const key = this.generateSuiteKey(record.suiteName);
    const records = this.storage.get(key) || [];

    records.push(record);

    // Sort by timestamp (newest first)
    records.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());

    this.storage.set(key, records);

    // Store individual test records
    for (const test of record.tests) {
      await this.storeTestRecord(test);
    }

    this.emit('suiteRecordStored', record);

    // Apply retention policy
    await this.applyRetentionPolicy(key);
  }

  /**
   * Store an individual test execution record
   */
  async storeTestRecord(record: TestExecutionRecord): Promise<void> {
    const key = this.generateTestKey(record.testFile, record.testName);
    const records = this.testRecords.get(key) || [];

    records.push(record);

    // Sort by timestamp (newest first)
    records.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());

    this.testRecords.set(key, records);

    this.emit('testRecordStored', record);
  }

  /**
   * Query test suite records
   */
  async queryTestSuiteRecords(
    suiteName?: string,
    options?: QueryOptions,
  ): Promise<TestSuiteRecord[]> {
    let allRecords: TestSuiteRecord[] = [];

    if (suiteName) {
      const key = this.generateSuiteKey(suiteName);
      allRecords = this.storage.get(key) || [];
    } else {
      // Get all records from all suites
      for (const records of this.storage.values()) {
        allRecords.push(...records);
      }
      // Sort by timestamp
      allRecords.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
    }

    return this.applyQueryFilters(allRecords, options);
  }

  /**
   * Query individual test records
   */
  async queryTestRecords(
    testFile?: string,
    testName?: string,
    options?: QueryOptions,
  ): Promise<TestExecutionRecord[]> {
    let allRecords: TestExecutionRecord[] = [];

    if (testFile && testName) {
      const key = this.generateTestKey(testFile, testName);
      allRecords = this.testRecords.get(key) || [];
    } else if (testFile) {
      // Get all records for a specific test file
      for (const [key, records] of this.testRecords.entries()) {
        if (key.startsWith(`${testFile}:`)) {
          allRecords.push(...records);
        }
      }
      allRecords.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
    } else {
      // Get all test records
      for (const records of this.testRecords.values()) {
        allRecords.push(...records);
      }
      allRecords.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
    }

    return this.applyTestQueryFilters(allRecords, options);
  }

  /**
   * Get aggregated data for trend analysis
   */
  async getAggregatedData(options: AggregationOptions): Promise<any[]> {
    const allSuiteRecords = await this.queryTestSuiteRecords();

    // Filter by date range
    const filteredRecords = allSuiteRecords.filter(record => {
      if (options.startDate && record.timestamp < options.startDate) return false;
      if (options.endDate && record.timestamp > options.endDate) return false;
      return true;
    });

    // Group data
    const grouped = this.groupRecords(filteredRecords, options.groupBy);

    // Calculate metrics for each group
    const aggregated = Object.entries(grouped).map(([key, records]) => {
      const metrics: any = { group: key, count: records.length };

      if (options.metrics.includes('duration')) {
        metrics.averageDuration = records.reduce((sum, r) => sum + r.duration, 0) / records.length;
        metrics.totalDuration = records.reduce((sum, r) => sum + r.duration, 0);
      }

      if (options.metrics.includes('coverage')) {
        metrics.averageCoverage = {
          statements: records.reduce((sum, r) => sum + r.coverage.statements, 0) / records.length,
          branches: records.reduce((sum, r) => sum + r.coverage.branches, 0) / records.length,
          functions: records.reduce((sum, r) => sum + r.coverage.functions, 0) / records.length,
          lines: records.reduce((sum, r) => sum + r.coverage.lines, 0) / records.length,
        };
      }

      if (options.metrics.includes('performance')) {
        metrics.averagePerformance = {
          totalMemory: records.reduce((sum, r) => sum + r.performance.totalMemory, 0) / records.length,
          peakMemory: Math.max(...records.map(r => r.performance.peakMemory)),
          averageExecutionTime: records.reduce((sum, r) => sum + r.performance.averageExecutionTime, 0) / records.length,
        };
      }

      return metrics;
    });

    return aggregated.sort((a, b) => a.group.localeCompare(b.group));
  }

  /**
   * Get test failure patterns
   */
  async getFailurePatterns(days: number = 30): Promise<any[]> {
    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - days * 24 * 60 * 60 * 1000);

    const failedTests = await this.queryTestRecords(undefined, undefined, {
      startDate,
      endDate,
      status: 'failed',
    });

    // Group by error type and message
    const patterns = new Map<string, {
      count: number;
      tests: string[];
      errorType: string;
      errorMessage: string;
      firstSeen: Date;
      lastSeen: Date;
    }>();

    for (const test of failedTests) {
      if (!test.error) continue;

      const key = `${test.error.type}:${test.error.message}`;
      const existing = patterns.get(key);

      if (existing) {
        existing.count++;
        existing.tests.push(`${test.testFile}:${test.testName}`);
        existing.lastSeen = test.timestamp > existing.lastSeen ? test.timestamp : existing.lastSeen;
      } else {
        patterns.set(key, {
          count: 1,
          tests: [`${test.testFile}:${test.testName}`],
          errorType: test.error.type,
          errorMessage: test.error.message,
          firstSeen: test.timestamp,
          lastSeen: test.timestamp,
        });
      }
    }

    return Array.from(patterns.values())
      .sort((a, b) => b.count - a.count)
      .slice(0, 20); // Top 20 failure patterns
  }

  /**
   * Get flaky test analysis
   */
  async getFlakyTests(days: number = 30, threshold: number = 0.1): Promise<any[]> {
    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - days * 24 * 60 * 60 * 1000);

    const testStats = new Map<string, {
      total: number;
      failed: number;
      testFile: string;
      testName: string;
      flakiness: number;
      recentFailures: Date[];
    }>();

    const allTests = await this.queryTestRecords(undefined, undefined, {
      startDate,
      endDate,
    });

    for (const test of allTests) {
      const key = `${test.testFile}:${test.testName}`;
      const existing = testStats.get(key);

      if (existing) {
        existing.total++;
        if (test.status === 'failed') {
          existing.failed++;
          existing.recentFailures.push(test.timestamp);
        }
      } else {
        testStats.set(key, {
          total: 1,
          failed: test.status === 'failed' ? 1 : 0,
          testFile: test.testFile,
          testName: test.testName,
          flakiness: 0,
          recentFailures: test.status === 'failed' ? [test.timestamp] : [],
        });
      }
    }

    // Calculate flakiness and filter
    const flakyTests = Array.from(testStats.values())
      .map(stat => ({
        ...stat,
        flakiness: stat.total > 0 ? stat.failed / stat.total : 0,
      }))
      .filter(stat => stat.flakiness > threshold && stat.total >= 5) // At least 5 runs
      .sort((a, b) => b.flakiness - a.flakiness);

    return flakyTests;
  }

  /**
   * Export historical data
   */
  async exportData(format: 'json' | 'csv' = 'json'): Promise<string> {
    const allSuiteRecords = await this.queryTestSuiteRecords();

    if (format === 'json') {
      return JSON.stringify({
        exportDate: new Date(),
        totalRecords: allSuiteRecords.length,
        retentionPolicy: this.retentionPolicy,
        data: allSuiteRecords,
      }, null, 2);
    } else {
      // CSV format
      const headers = [
        'timestamp', 'suiteName', 'totalTests', 'passedTests', 'failedTests',
        'skippedTests', 'duration', 'coverageStatements', 'coverageBranches',
        'coverageFunctions', 'coverageLines', 'totalMemory', 'peakMemory',
        'averageExecutionTime', 'nodeVersion', 'platform', 'ci', 'branch', 'commit',
      ];

      const rows = allSuiteRecords.map(record => [
        record.timestamp.toISOString(),
        record.suiteName,
        record.totalTests,
        record.passedTests,
        record.failedTests,
        record.skippedTests,
        record.duration,
        record.coverage.statements,
        record.coverage.branches,
        record.coverage.functions,
        record.coverage.lines,
        record.performance.totalMemory,
        record.performance.peakMemory,
        record.performance.averageExecutionTime,
        record.environment.nodeVersion,
        record.environment.platform,
        record.environment.ci,
        record.environment.branch,
        record.environment.commit,
      ]);

      return [headers.join(','), ...rows.map(row => row.join(','))].join('\n');
    }
  }

  /**
   * Import historical data
   */
  async importData(data: string, format: 'json' | 'csv' = 'json'): Promise<void> {
    if (format === 'json') {
      const parsed = JSON.parse(data);

      if (parsed.data && Array.isArray(parsed.data)) {
        for (const record of parsed.data) {
          // Ensure timestamp is a Date object
          const recordWithDate = {
            ...record,
            timestamp: new Date(record.timestamp),
          };
          await this.storeTestSuiteRecord(recordWithDate);
        }
      }
    } else {
      // CSV format parsing would be implemented here
      throw new Error('CSV import not yet implemented');
    }

    this.emit('dataImported', { format, recordCount: this.getTotalRecords() });
  }

  /**
   * Get storage statistics
   */
  getStorageStats(): {
    totalSuiteRecords: number;
    totalTestRecords: number;
    oldestRecord: Date | null;
    newestRecord: Date | null;
    storageSize: number;
  } {
    let totalSuiteRecords = 0;
    let totalTestRecords = 0;
    let oldestRecord: Date | null = null;
    let newestRecord: Date | null = null;

    for (const records of this.storage.values()) {
      totalSuiteRecords += records.length;

      for (const record of records) {
        if (!oldestRecord || record.timestamp < oldestRecord) {
          oldestRecord = record.timestamp;
        }
        if (!newestRecord || record.timestamp > newestRecord) {
          newestRecord = record.timestamp;
        }
      }
    }

    for (const records of this.testRecords.values()) {
      totalTestRecords += records.length;
    }

    return {
      totalSuiteRecords,
      totalTestRecords,
      oldestRecord,
      newestRecord,
      storageSize: this.calculateStorageSize(),
    };
  }

  /**
   * Clear all historical data
   */
  async clearAllData(): Promise<void> {
    this.storage.clear();
    this.testRecords.clear();
    this.emit('dataCleared');
  }

  /**
   * Private helper methods
   */
  private generateSuiteKey(suiteName: string): string {
    return `suite:${suiteName}`;
  }

  private generateTestKey(testFile: string, testName: string): string {
    return `${testFile}:${testName}`;
  }

  private applyQueryFilters(
    records: TestSuiteRecord[],
    options?: QueryOptions,
  ): TestSuiteRecord[] {
    let filtered = records;

    if (options?.startDate) {
      filtered = filtered.filter(r => r.timestamp >= options.startDate!);
    }

    if (options?.endDate) {
      filtered = filtered.filter(r => r.timestamp <= options.endDate!);
    }

    if (options?.branch) {
      filtered = filtered.filter(r => r.environment.branch === options.branch);
    }

    // Apply sorting
    if (options?.sortBy) {
      filtered.sort((a, b) => {
        let aVal: any, bVal: any;

        switch (options.sortBy) {
          case 'timestamp':
            aVal = a.timestamp.getTime();
            bVal = b.timestamp.getTime();
            break;
          case 'duration':
            aVal = a.duration;
            bVal = b.duration;
            break;
          default:
            aVal = a.suiteName;
            bVal = b.suiteName;
        }

        const result = aVal < bVal ? -1 : aVal > bVal ? 1 : 0;
        return options.sortOrder === 'desc' ? -result : result;
      });
    }

    // Apply pagination
    if (options?.offset || options?.limit) {
      const start = options.offset || 0;
      const end = options.limit ? start + options.limit : undefined;
      filtered = filtered.slice(start, end);
    }

    return filtered;
  }

  private applyTestQueryFilters(
    records: TestExecutionRecord[],
    options?: QueryOptions,
  ): TestExecutionRecord[] {
    let filtered = records;

    if (options?.startDate) {
      filtered = filtered.filter(r => r.timestamp >= options.startDate!);
    }

    if (options?.endDate) {
      filtered = filtered.filter(r => r.timestamp <= options.endDate!);
    }

    if (options?.status) {
      filtered = filtered.filter(r => r.status === options.status);
    }

    if (options?.testSuite) {
      filtered = filtered.filter(r => r.testSuite === options.testSuite);
    }

    if (options?.testFile) {
      filtered = filtered.filter(r => r.testFile === options.testFile);
    }

    if (options?.branch) {
      filtered = filtered.filter(r => r.environment.branch === options.branch);
    }

    // Apply sorting
    if (options?.sortBy) {
      filtered.sort((a, b) => {
        let aVal: any, bVal: any;

        switch (options.sortBy) {
          case 'timestamp':
            aVal = a.timestamp.getTime();
            bVal = b.timestamp.getTime();
            break;
          case 'duration':
            aVal = a.duration;
            bVal = b.duration;
            break;
          case 'testName':
            aVal = a.testName;
            bVal = b.testName;
            break;
          default:
            aVal = a.timestamp.getTime();
            bVal = b.timestamp.getTime();
        }

        const result = aVal < bVal ? -1 : aVal > bVal ? 1 : 0;
        return options.sortOrder === 'desc' ? -result : result;
      });
    }

    // Apply pagination
    if (options?.offset || options?.limit) {
      const start = options.offset || 0;
      const end = options.limit ? start + options.limit : undefined;
      filtered = filtered.slice(start, end);
    }

    return filtered;
  }

  private groupRecords(records: TestSuiteRecord[], groupBy: string): Record<string, TestSuiteRecord[]> {
    const grouped: Record<string, TestSuiteRecord[]> = {};

    for (const record of records) {
      let key: string;

      switch (groupBy) {
        case 'day':
          key = record.timestamp.toISOString().split('T')[0];
          break;
        case 'week':
          const weekStart = new Date(record.timestamp);
          weekStart.setDate(weekStart.getDate() - weekStart.getDay());
          key = weekStart.toISOString().split('T')[0];
          break;
        case 'month':
          key = record.timestamp.toISOString().substring(0, 7);
          break;
        case 'testSuite':
          key = record.suiteName;
          break;
        default:
          key = 'all';
      }

      if (!grouped[key]) {
        grouped[key] = [];
      }
      grouped[key].push(record);
    }

    return grouped;
  }

  private async applyRetentionPolicy(key: string): Promise<void> {
    const records = this.storage.get(key) || [];
    const cutoffDate = new Date(Date.now() - this.retentionPolicy.maxAge * 24 * 60 * 60 * 1000);

    // Remove old records
    const filteredRecords = records.filter(record => record.timestamp > cutoffDate);

    // Limit number of records
    if (filteredRecords.length > this.retentionPolicy.maxRecords) {
      filteredRecords.splice(this.retentionPolicy.maxRecords);
    }

    this.storage.set(key, filteredRecords);

    if (filteredRecords.length < records.length) {
      this.emit('recordsCleanedUp', {
        key,
        removedCount: records.length - filteredRecords.length,
      });
    }
  }

  private startCleanupInterval(): void {
    // Run cleanup every hour
    setInterval(async () => {
      for (const key of this.storage.keys()) {
        await this.applyRetentionPolicy(key);
      }
    }, 60 * 60 * 1000);
  }

  private calculateStorageSize(): number {
    let size = 0;

    for (const records of this.storage.values()) {
      size += JSON.stringify(records).length;
    }

    for (const records of this.testRecords.values()) {
      size += JSON.stringify(records).length;
    }

    return size;
  }

  private getTotalRecords(): number {
    let total = 0;

    for (const records of this.storage.values()) {
      total += records.length;
    }

    return total;
  }

  private generateSuiteKey(suiteName: string): string {
    return `suite:${suiteName}`;
  }

  private generateTestKey(testFile: string, testName: string): string {
    return `${testFile}:${testName}`;
  }
}

/**
 * Quality Predictor
 *
 * Implements machine learning-inspired models to predict test quality metrics,
 * identify potential issues before they occur, and provide proactive recommendations
 * for maintaining high test quality.
 */

import { EventEmitter } from 'events';

import { HistoricalDataManager, TestSuiteRecord } from './HistoricalDataManager';
import { TrendAnalyzer, TrendAnalysis } from './TrendAnalyzer';

export interface QualityMetrics {
  coverage: number;
  passRate: number;
  executionTime: number;
  memoryUsage: number;
  flakiness: number;
  stability: number;
  maintainability: number;
}

export interface QualityPrediction {
  metric: keyof QualityMetrics;
  currentValue: number;
  predictedValue: number;
  confidence: number;
  timeframe: number; // days
  trend: 'improving' | 'degrading' | 'stable';
  riskLevel: 'low' | 'medium' | 'high';
  factors: QualityFactor[];
}

export interface QualityFactor {
  name: string;
  impact: number; // -1 to 1
  confidence: number;
  description: string;
  category: 'code' | 'test' | 'environment' | 'process';
}

export interface RiskAssessment {
  overallRisk: 'low' | 'medium' | 'high';
  riskScore: number; // 0-100
  criticalAreas: string[];
  predictions: QualityPrediction[];
  recommendations: QualityRecommendation[];
  timeToAction: number; // days until action needed
}

export interface QualityRecommendation {
  priority: 'low' | 'medium' | 'high' | 'critical';
  category: 'coverage' | 'performance' | 'stability' | 'maintenance';
  title: string;
  description: string;
  actionItems: string[];
  estimatedImpact: number; // 0-100
  estimatedEffort: 'low' | 'medium' | 'high';
  timeframe: string;
}

export interface ModelFeatures {
  // Historical metrics
  avgCoverage: number;
  avgPassRate: number;
  avgExecutionTime: number;
  avgMemoryUsage: number;

  // Trend features
  coverageTrend: number;
  passRateTrend: number;
  executionTimeTrend: number;
  memoryUsageTrend: number;

  // Volatility features
  coverageVolatility: number;
  passRateVolatility: number;
  executionTimeVolatility: number;

  // Code change features
  testCountChange: number;
  codeChurnRate: number;

  // Environmental features
  ciFailureRate: number;
  deploymentFrequency: number;
}

export interface PredictionModel {
  name: string;
  version: string;
  accuracy: number;
  lastTrained: Date;
  features: string[];
  predict(features: ModelFeatures): number;
  getFeatureImportance(): Record<string, number>;
}

export class QualityPredictor extends EventEmitter {
  private dataManager: HistoricalDataManager;
  private trendAnalyzer: TrendAnalyzer;
  private models: Map<string, PredictionModel> = new Map();
  private predictionCache: Map<string, any> = new Map();
  private cacheTimeout: number = 10 * 60 * 1000; // 10 minutes

  constructor(dataManager: HistoricalDataManager, trendAnalyzer: TrendAnalyzer) {
    super();
    this.dataManager = dataManager;
    this.trendAnalyzer = trendAnalyzer;

    // Initialize prediction models
    this.initializePredictionModels();
  }

  /**
   * Predict quality metrics for the next period
   */
  async predictQualityMetrics(days: number = 7): Promise<QualityPrediction[]> {
    const cacheKey = `quality-predictions-${days}`;
    const cached = this.getCachedResult(cacheKey);
    if (cached) return cached;

    const features = await this.extractModelFeatures();
    const predictions: QualityPrediction[] = [];

    // Predict each quality metric
    const metrics: (keyof QualityMetrics)[] = [
      'coverage', 'passRate', 'executionTime', 'memoryUsage',
      'flakiness', 'stability', 'maintainability',
    ];

    for (const metric of metrics) {
      const model = this.models.get(metric);
      if (model) {
        const prediction = await this.predictMetric(metric, features, days);
        predictions.push(prediction);
      }
    }

    this.setCachedResult(cacheKey, predictions);
    return predictions;
  }

  /**
   * Assess overall quality risk
   */
  async assessQualityRisk(days: number = 30): Promise<RiskAssessment> {
    const predictions = await this.predictQualityMetrics(days);
    const criticalAreas: string[] = [];
    let totalRisk = 0;
    let riskCount = 0;

    // Calculate risk for each prediction
    for (const prediction of predictions) {
      if (prediction.riskLevel === 'high') {
        criticalAreas.push(prediction.metric);
        totalRisk += 80;
        riskCount++;
      } else if (prediction.riskLevel === 'medium') {
        totalRisk += 50;
        riskCount++;
      } else {
        totalRisk += 20;
        riskCount++;
      }
    }

    const riskScore = riskCount > 0 ? totalRisk / riskCount : 0;
    const overallRisk: 'low' | 'medium' | 'high' =
      riskScore > 70 ? 'high' : riskScore > 40 ? 'medium' : 'low';

    // Generate recommendations
    const recommendations = await this.generateQualityRecommendations(predictions);

    // Calculate time to action
    const timeToAction = this.calculateTimeToAction(predictions);

    return {
      overallRisk,
      riskScore,
      criticalAreas,
      predictions,
      recommendations,
      timeToAction,
    };
  }

  /**
   * Generate proactive quality recommendations
   */
  async generateQualityRecommendations(
    predictions?: QualityPrediction[],
  ): Promise<QualityRecommendation[]> {
    if (!predictions) {
      predictions = await this.predictQualityMetrics();
    }

    const recommendations: QualityRecommendation[] = [];

    for (const prediction of predictions) {
      const metricRecommendations = this.generateMetricRecommendations(prediction);
      recommendations.push(...metricRecommendations);
    }

    // Sort by priority and impact
    return recommendations.sort((a, b) => {
      const priorityOrder = { critical: 4, high: 3, medium: 2, low: 1 };
      const priorityDiff = priorityOrder[b.priority] - priorityOrder[a.priority];
      if (priorityDiff !== 0) return priorityDiff;
      return b.estimatedImpact - a.estimatedImpact;
    });
  }

  /**
   * Identify quality factors affecting predictions
   */
  async identifyQualityFactors(metric: keyof QualityMetrics): Promise<QualityFactor[]> {
    const model = this.models.get(metric);
    if (!model) return [];

    const featureImportance = model.getFeatureImportance();
    const factors: QualityFactor[] = [];

    // Convert feature importance to quality factors
    for (const [feature, importance] of Object.entries(featureImportance)) {
      const factor = this.convertFeatureToFactor(feature, importance);
      if (factor) {
        factors.push(factor);
      }
    }

    return factors.sort((a, b) => Math.abs(b.impact) - Math.abs(a.impact));
  }

  /**
   * Train prediction models with historical data
   */
  async trainModels(days: number = 90): Promise<void> {
    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - days * 24 * 60 * 60 * 1000);

    const records = await this.dataManager.queryTestSuiteRecords(undefined, {
      startDate,
      endDate,
      sortBy: 'timestamp',
      sortOrder: 'asc',
    });

    if (records.length < 30) {
      throw new Error('Insufficient data for model training. Need at least 30 records.');
    }

    // Prepare training data
    const trainingData = await this.prepareTrainingData(records);

    // Train each model
    for (const [metricName, model] of this.models.entries()) {
      await this.trainModel(model, trainingData, metricName);
    }

    this.emit('modelsRetrained', {
      recordCount: records.length,
      timeframe: days,
      models: Array.from(this.models.keys()),
    });
  }

  /**
   * Validate model accuracy
   */
  async validateModels(testDays: number = 14): Promise<Record<string, number>> {
    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - testDays * 24 * 60 * 60 * 1000);

    const testRecords = await this.dataManager.queryTestSuiteRecords(undefined, {
      startDate,
      endDate,
    });

    const accuracies: Record<string, number> = {};

    for (const [metricName, model] of this.models.entries()) {
      const accuracy = await this.validateModel(model, testRecords, metricName);
      accuracies[metricName] = accuracy;
    }

    return accuracies;
  }

  /**
   * Get model performance statistics
   */
  getModelStats(): Record<string, any> {
    const stats: Record<string, any> = {};

    for (const [name, model] of this.models.entries()) {
      stats[name] = {
        accuracy: model.accuracy,
        lastTrained: model.lastTrained,
        version: model.version,
        featureCount: model.features.length,
      };
    }

    return stats;
  }

  /**
   * Private helper methods
   */
  private initializePredictionModels(): void {
    // Initialize simple linear regression models for each metric
    const metrics = ['coverage', 'passRate', 'executionTime', 'memoryUsage', 'flakiness', 'stability', 'maintainability'];

    for (const metric of metrics) {
      this.models.set(metric, new LinearRegressionModel(metric));
    }
  }

  private async extractModelFeatures(): Promise<ModelFeatures> {
    const records = await this.dataManager.queryTestSuiteRecords(undefined, {
      limit: 30,
      sortBy: 'timestamp',
      sortOrder: 'desc',
    });

    if (records.length === 0) {
      throw new Error('No historical data available for feature extraction');
    }

    // Calculate averages
    const avgCoverage = records.reduce((sum, r) =>
      sum + (r.coverage.statements + r.coverage.branches + r.coverage.functions + r.coverage.lines) / 4, 0,
    ) / records.length;

    const avgPassRate = records.reduce((sum, r) =>
      sum + (r.totalTests > 0 ? r.passedTests / r.totalTests : 0), 0,
    ) / records.length;

    const avgExecutionTime = records.reduce((sum, r) => sum + r.duration, 0) / records.length;
    const avgMemoryUsage = records.reduce((sum, r) => sum + r.performance.peakMemory, 0) / records.length;

    // Calculate trends (simple slope calculation)
    const coverageTrend = this.calculateSimpleTrend(records.map(r =>
      (r.coverage.statements + r.coverage.branches + r.coverage.functions + r.coverage.lines) / 4,
    ));
    const passRateTrend = this.calculateSimpleTrend(records.map(r =>
      r.totalTests > 0 ? r.passedTests / r.totalTests : 0,
    ));
    const executionTimeTrend = this.calculateSimpleTrend(records.map(r => r.duration));
    const memoryUsageTrend = this.calculateSimpleTrend(records.map(r => r.performance.peakMemory));

    // Calculate volatility (standard deviation)
    const coverageValues = records.map(r =>
      (r.coverage.statements + r.coverage.branches + r.coverage.functions + r.coverage.lines) / 4,
    );
    const coverageVolatility = this.calculateVolatility(coverageValues);

    const passRateValues = records.map(r => r.totalTests > 0 ? r.passedTests / r.totalTests : 0);
    const passRateVolatility = this.calculateVolatility(passRateValues);

    const executionTimeValues = records.map(r => r.duration);
    const executionTimeVolatility = this.calculateVolatility(executionTimeValues);

    // Calculate change metrics
    const testCountChange = records.length > 1 ?
      (records[0].totalTests - records[records.length - 1].totalTests) / records.length : 0;

    const codeChurnRate = this.calculateCodeChurnRate(records);
    const ciFailureRate = records.reduce((sum, r) =>
      sum + (r.environment.ci && r.failedTests > 0 ? 1 : 0), 0,
    ) / records.length;

    return {
      avgCoverage,
      avgPassRate,
      avgExecutionTime,
      avgMemoryUsage,
      coverageTrend,
      passRateTrend,
      executionTimeTrend,
      memoryUsageTrend,
      coverageVolatility,
      passRateVolatility,
      executionTimeVolatility,
      testCountChange,
      codeChurnRate,
      ciFailureRate,
      deploymentFrequency: 1, // Placeholder
    };
  }

  private async predictMetric(
    metric: keyof QualityMetrics,
    features: ModelFeatures,
    days: number,
  ): Promise<QualityPrediction> {
    const model = this.models.get(metric)!;
    const currentValue = this.getCurrentMetricValue(metric, features);
    const predictedValue = model.predict(features);

    // Calculate confidence based on model accuracy and prediction stability
    const confidence = Math.min(model.accuracy * 0.9, 0.95);

    // Determine trend
    const trend: 'improving' | 'degrading' | 'stable' =
      Math.abs(predictedValue - currentValue) < 0.05 ? 'stable' :
      predictedValue > currentValue ? 'improving' : 'degrading';

    // Assess risk level
    const riskLevel = this.assessMetricRisk(metric, currentValue, predictedValue, trend);

    // Get quality factors
    const factors = await this.identifyQualityFactors(metric);

    return {
      metric,
      currentValue,
      predictedValue,
      confidence,
      timeframe: days,
      trend,
      riskLevel,
      factors: factors.slice(0, 5), // Top 5 factors
    };
  }

  private getCurrentMetricValue(metric: keyof QualityMetrics, features: ModelFeatures): number {
    switch (metric) {
      case 'coverage':
        return features.avgCoverage;
      case 'passRate':
        return features.avgPassRate;
      case 'executionTime':
        return features.avgExecutionTime;
      case 'memoryUsage':
        return features.avgMemoryUsage;
      case 'flakiness':
        return features.ciFailureRate;
      case 'stability':
        return 1 - features.passRateVolatility;
      case 'maintainability':
        return 1 - features.codeChurnRate;
      default:
        return 0;
    }
  }

  private assessMetricRisk(
    metric: keyof QualityMetrics,
    current: number,
    predicted: number,
    trend: 'improving' | 'degrading' | 'stable',
  ): 'low' | 'medium' | 'high' {
    const change = Math.abs(predicted - current);
    const relativeChange = current > 0 ? change / current : change;

    // Metric-specific risk thresholds
    const thresholds = {
      coverage: { low: 0.02, medium: 0.05 },
      passRate: { low: 0.02, medium: 0.05 },
      executionTime: { low: 0.1, medium: 0.2 },
      memoryUsage: { low: 0.1, medium: 0.2 },
      flakiness: { low: 0.05, medium: 0.1 },
      stability: { low: 0.02, medium: 0.05 },
      maintainability: { low: 0.05, medium: 0.1 },
    };

    const threshold = thresholds[metric] || { low: 0.05, medium: 0.1 };

    if (trend === 'degrading') {
      if (relativeChange > threshold.medium) return 'high';
      if (relativeChange > threshold.low) return 'medium';
    }

    return 'low';
  }

  private generateMetricRecommendations(prediction: QualityPrediction): QualityRecommendation[] {
    const recommendations: QualityRecommendation[] = [];

    if (prediction.riskLevel === 'high' || prediction.trend === 'degrading') {
      const recommendation = this.createMetricRecommendation(prediction);
      if (recommendation) {
        recommendations.push(recommendation);
      }
    }

    return recommendations;
  }

  private createMetricRecommendation(prediction: QualityPrediction): QualityRecommendation | null {
    const templates: Record<string, Partial<QualityRecommendation>> = {
      coverage: {
        category: 'coverage',
        title: 'Improve Test Coverage',
        description: 'Test coverage is predicted to decline. Focus on adding tests for uncovered code.',
        actionItems: [
          'Identify uncovered code paths',
          'Add unit tests for new features',
          'Implement coverage gates in CI/CD',
          'Review and update existing tests',
        ],
        estimatedEffort: 'medium',
      },
      passRate: {
        category: 'stability',
        title: 'Improve Test Stability',
        description: 'Test pass rate is predicted to decline. Focus on fixing flaky and failing tests.',
        actionItems: [
          'Identify and fix flaky tests',
          'Improve test data management',
          'Review test environment setup',
          'Add better error handling in tests',
        ],
        estimatedEffort: 'high',
      },
      executionTime: {
        category: 'performance',
        title: 'Optimize Test Performance',
        description: 'Test execution time is predicted to increase. Focus on performance optimization.',
        actionItems: [
          'Profile slow tests',
          'Implement parallel test execution',
          'Optimize test data setup',
          'Remove unnecessary test operations',
        ],
        estimatedEffort: 'medium',
      },
      memoryUsage: {
        category: 'performance',
        title: 'Optimize Memory Usage',
        description: 'Memory usage is predicted to increase. Focus on memory optimization.',
        actionItems: [
          'Check for memory leaks in tests',
          'Improve test data cleanup',
          'Optimize test fixtures',
          'Review test isolation',
        ],
        estimatedEffort: 'medium',
      },
    };

    const template = templates[prediction.metric];
    if (!template) return null;

    const priority: 'low' | 'medium' | 'high' | 'critical' =
      prediction.riskLevel === 'high' ? 'critical' :
      prediction.riskLevel === 'medium' ? 'high' : 'medium';

    return {
      priority,
      category: template.category!,
      title: template.title!,
      description: template.description!,
      actionItems: template.actionItems!,
      estimatedImpact: prediction.confidence * 100,
      estimatedEffort: template.estimatedEffort as 'low' | 'medium' | 'high',
      timeframe: `${prediction.timeframe} days`,
    };
  }

  private calculateTimeToAction(predictions: QualityPrediction[]): number {
    const highRiskPredictions = predictions.filter(p => p.riskLevel === 'high');

    if (highRiskPredictions.length === 0) {
      return 30; // No immediate action needed
    }

    // Return the shortest timeframe for high-risk predictions
    return Math.min(...highRiskPredictions.map(p => p.timeframe));
  }

  private convertFeatureToFactor(feature: string, importance: number): QualityFactor | null {
    const factorMap: Record<string, Partial<QualityFactor>> = {
      avgCoverage: {
        name: 'Average Coverage',
        category: 'test',
        description: 'Historical test coverage levels',
      },
      coverageTrend: {
        name: 'Coverage Trend',
        category: 'test',
        description: 'Direction of coverage change over time',
      },
      passRateTrend: {
        name: 'Pass Rate Trend',
        category: 'test',
        description: 'Direction of test pass rate change',
      },
      codeChurnRate: {
        name: 'Code Churn',
        category: 'code',
        description: 'Rate of code changes affecting tests',
      },
      ciFailureRate: {
        name: 'CI Failure Rate',
        category: 'environment',
        description: 'Frequency of CI/CD pipeline failures',
      },
    };

    const factorTemplate = factorMap[feature];
    if (!factorTemplate) return null;

    return {
      name: factorTemplate.name!,
      impact: importance,
      confidence: Math.abs(importance),
      description: factorTemplate.description!,
      category: factorTemplate.category as 'code' | 'test' | 'environment' | 'process',
    };
  }

  private calculateSimpleTrend(values: number[]): number {
    if (values.length < 2) return 0;

    const first = values[values.length - 1];
    const last = values[0];

    return first !== 0 ? (last - first) / first : 0;
  }

  private calculateVolatility(values: number[]): number {
    if (values.length < 2) return 0;

    const mean = values.reduce((sum, v) => sum + v, 0) / values.length;
    const variance = values.reduce((sum, v) => sum + Math.pow(v - mean, 2), 0) / values.length;

    return mean > 0 ? Math.sqrt(variance) / mean : 0;
  }

  private calculateCodeChurnRate(records: TestSuiteRecord[]): number {
    if (records.length < 2) return 0;

    let totalChurn = 0;
    for (let i = 1; i < records.length; i++) {
      const current = records[i - 1];
      const previous = records[i];
      const churn = Math.abs(current.totalTests - previous.totalTests) /
                   Math.max(previous.totalTests, 1);
      totalChurn += churn;
    }

    return totalChurn / (records.length - 1);
  }

  private async prepareTrainingData(records: TestSuiteRecord[]): Promise<any[]> {
    // This would prepare training data for machine learning models
    // For now, return a simplified version
    return records.map(record => ({
      features: {
        coverage: (record.coverage.statements + record.coverage.branches +
                  record.coverage.functions + record.coverage.lines) / 4,
        passRate: record.totalTests > 0 ? record.passedTests / record.totalTests : 0,
        executionTime: record.duration,
        memoryUsage: record.performance.peakMemory,
      },
      timestamp: record.timestamp,
    }));
  }

  private async trainModel(model: PredictionModel, trainingData: any[], metricName: string): Promise<void> {
    // Simplified training - in a real implementation, this would use actual ML algorithms
    model.accuracy = 0.8 + Math.random() * 0.15; // Simulate training accuracy
    model.lastTrained = new Date();
  }

  private async validateModel(model: PredictionModel, testRecords: TestSuiteRecord[], metricName: string): Promise<number> {
    // Simplified validation - in a real implementation, this would test predictions against actual values
    return 0.7 + Math.random() * 0.2; // Simulate validation accuracy
  }

  private getCachedResult(key: string): any {
    const cached = this.predictionCache.get(key);
    if (cached && Date.now() - cached.timestamp < this.cacheTimeout) {
      return cached.data;
    }
    return null;
  }

  private setCachedResult(key: string, data: any): void {
    this.predictionCache.set(key, {
      data,
      timestamp: Date.now(),
    });
  }
}

/**
 * Simple Linear Regression Model Implementation
 */
class LinearRegressionModel implements PredictionModel {
  name: string;
  version: string = '1.0.0';
  accuracy: number = 0;
  lastTrained: Date = new Date();
  features: string[] = [
    'avgCoverage', 'avgPassRate', 'avgExecutionTime', 'avgMemoryUsage',
    'coverageTrend', 'passRateTrend', 'executionTimeTrend', 'memoryUsageTrend',
    'coverageVolatility', 'passRateVolatility', 'executionTimeVolatility',
    'testCountChange', 'codeChurnRate', 'ciFailureRate',
  ];

  private weights: Record<string, number> = {};
  private bias: number = 0;

  constructor(metricName: string) {
    this.name = `${metricName}_predictor`;

    // Initialize with random weights (in a real implementation, these would be learned)
    for (const feature of this.features) {
      this.weights[feature] = (Math.random() - 0.5) * 0.1;
    }
  }

  predict(features: ModelFeatures): number {
    let prediction = this.bias;

    for (const [feature, weight] of Object.entries(this.weights)) {
      const featureValue = (features as any)[feature] || 0;
      prediction += weight * featureValue;
    }

    // Ensure prediction is within reasonable bounds
    return Math.max(0, Math.min(1, prediction));
  }

  getFeatureImportance(): Record<string, number> {
    // Return normalized weights as feature importance
    const maxWeight = Math.max(...Object.values(this.weights).map(Math.abs));
    const importance: Record<string, number> = {};

    for (const [feature, weight] of Object.entries(this.weights)) {
      importance[feature] = maxWeight > 0 ? weight / maxWeight : 0;
    }

    return importance;
  }
}

/**
 * Trend Analyzer
 *
 * Provides intelligent analysis of test execution trends, performance patterns,
 * and quality metrics over time. Generates insights and predictions based on
 * historical data.
 */

import { EventEmitter } from 'events';

import { HistoricalDataManager, TestSuiteRecord, TestExecutionRecord } from './HistoricalDataManager';

export interface TrendPoint {
  timestamp: Date;
  value: number;
  metadata?: Record<string, any>;
}

export interface TrendAnalysis {
  trend: 'increasing' | 'decreasing' | 'stable' | 'volatile';
  slope: number;
  correlation: number;
  confidence: number;
  dataPoints: TrendPoint[];
  prediction?: {
    nextValue: number;
    confidence: number;
    timeframe: number; // days
  };
}

export interface CoverageTrend {
  statements: TrendAnalysis;
  branches: TrendAnalysis;
  functions: TrendAnalysis;
  lines: TrendAnalysis;
  overall: TrendAnalysis;
  recommendations: string[];
}

export interface PerformanceTrend {
  executionTime: TrendAnalysis;
  memoryUsage: TrendAnalysis;
  testCount: TrendAnalysis;
  failureRate: TrendAnalysis;
  recommendations: string[];
}

export interface QualityTrend {
  passRate: TrendAnalysis;
  flakiness: TrendAnalysis;
  testStability: TrendAnalysis;
  codeChurn: TrendAnalysis;
  recommendations: string[];
}

export interface SeasonalPattern {
  pattern: 'daily' | 'weekly' | 'monthly';
  strength: number;
  peaks: number[];
  valleys: number[];
  description: string;
}

export interface Anomaly {
  timestamp: Date;
  metric: string;
  expectedValue: number;
  actualValue: number;
  severity: 'low' | 'medium' | 'high';
  description: string;
  possibleCauses: string[];
}

export interface TrendInsight {
  type: 'improvement' | 'degradation' | 'anomaly' | 'pattern';
  severity: 'low' | 'medium' | 'high';
  title: string;
  description: string;
  metrics: string[];
  timeframe: string;
  actionItems: string[];
  confidence: number;
}

export class TrendAnalyzer extends EventEmitter {
  private dataManager: HistoricalDataManager;
  private analysisCache: Map<string, any> = new Map();
  private cacheTimeout: number = 5 * 60 * 1000; // 5 minutes

  constructor(dataManager: HistoricalDataManager) {
    super();
    this.dataManager = dataManager;
  }

  /**
   * Analyze coverage trends over time
   */
  async analyzeCoverageTrends(days: number = 30): Promise<CoverageTrend> {
    const cacheKey = `coverage-trends-${days}`;
    const cached = this.getCachedResult(cacheKey);
    if (cached) return cached;

    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - days * 24 * 60 * 60 * 1000);

    const records = await this.dataManager.queryTestSuiteRecords(undefined, {
      startDate,
      endDate,
      sortBy: 'timestamp',
      sortOrder: 'asc',
    });

    const coverageTrend: CoverageTrend = {
      statements: await this.analyzeCoverageMetric(records, 'statements'),
      branches: await this.analyzeCoverageMetric(records, 'branches'),
      functions: await this.analyzeCoverageMetric(records, 'functions'),
      lines: await this.analyzeCoverageMetric(records, 'lines'),
      overall: await this.analyzeOverallCoverage(records),
      recommendations: [],
    };

    // Generate recommendations
    coverageTrend.recommendations = this.generateCoverageRecommendations(coverageTrend);

    this.setCachedResult(cacheKey, coverageTrend);
    return coverageTrend;
  }

  /**
   * Analyze performance trends over time
   */
  async analyzePerformanceTrends(days: number = 30): Promise<PerformanceTrend> {
    const cacheKey = `performance-trends-${days}`;
    const cached = this.getCachedResult(cacheKey);
    if (cached) return cached;

    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - days * 24 * 60 * 60 * 1000);

    const records = await this.dataManager.queryTestSuiteRecords(undefined, {
      startDate,
      endDate,
      sortBy: 'timestamp',
      sortOrder: 'asc',
    });

    const performanceTrend: PerformanceTrend = {
      executionTime: await this.analyzeExecutionTimeTrend(records),
      memoryUsage: await this.analyzeMemoryUsageTrend(records),
      testCount: await this.analyzeTestCountTrend(records),
      failureRate: await this.analyzeFailureRateTrend(records),
      recommendations: [],
    };

    // Generate recommendations
    performanceTrend.recommendations = this.generatePerformanceRecommendations(performanceTrend);

    this.setCachedResult(cacheKey, performanceTrend);
    return performanceTrend;
  }

  /**
   * Analyze quality trends over time
   */
  async analyzeQualityTrends(days: number = 30): Promise<QualityTrend> {
    const cacheKey = `quality-trends-${days}`;
    const cached = this.getCachedResult(cacheKey);
    if (cached) return cached;

    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - days * 24 * 60 * 60 * 1000);

    const records = await this.dataManager.queryTestSuiteRecords(undefined, {
      startDate,
      endDate,
      sortBy: 'timestamp',
      sortOrder: 'asc',
    });

    const qualityTrend: QualityTrend = {
      passRate: await this.analyzePassRateTrend(records),
      flakiness: await this.analyzeFlakinessOverTime(days),
      testStability: await this.analyzeTestStabilityTrend(records),
      codeChurn: await this.analyzeCodeChurnTrend(records),
      recommendations: [],
    };

    // Generate recommendations
    qualityTrend.recommendations = this.generateQualityRecommendations(qualityTrend);

    this.setCachedResult(cacheKey, qualityTrend);
    return qualityTrend;
  }

  /**
   * Detect seasonal patterns in test data
   */
  async detectSeasonalPatterns(days: number = 90): Promise<SeasonalPattern[]> {
    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - days * 24 * 60 * 60 * 1000);

    const records = await this.dataManager.queryTestSuiteRecords(undefined, {
      startDate,
      endDate,
      sortBy: 'timestamp',
      sortOrder: 'asc',
    });

    const patterns: SeasonalPattern[] = [];

    // Analyze daily patterns
    const dailyPattern = this.analyzeDailyPattern(records);
    if (dailyPattern.strength > 0.3) {
      patterns.push(dailyPattern);
    }

    // Analyze weekly patterns
    const weeklyPattern = this.analyzeWeeklyPattern(records);
    if (weeklyPattern.strength > 0.3) {
      patterns.push(weeklyPattern);
    }

    return patterns;
  }

  /**
   * Detect anomalies in test metrics
   */
  async detectAnomalies(days: number = 30, sensitivity: number = 2): Promise<Anomaly[]> {
    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - days * 24 * 60 * 60 * 1000);

    const records = await this.dataManager.queryTestSuiteRecords(undefined, {
      startDate,
      endDate,
      sortBy: 'timestamp',
      sortOrder: 'asc',
    });

    const anomalies: Anomaly[] = [];

    // Detect execution time anomalies
    const executionTimeAnomalies = this.detectMetricAnomalies(
      records.map(r => ({ timestamp: r.timestamp, value: r.duration })),
      'execution_time',
      sensitivity,
    );
    anomalies.push(...executionTimeAnomalies);

    // Detect coverage anomalies
    const coverageAnomalies = this.detectMetricAnomalies(
      records.map(r => ({
        timestamp: r.timestamp,
        value: (r.coverage.statements + r.coverage.branches + r.coverage.functions + r.coverage.lines) / 4,
      })),
      'coverage',
      sensitivity,
    );
    anomalies.push(...coverageAnomalies);

    // Detect failure rate anomalies
    const failureRateAnomalies = this.detectMetricAnomalies(
      records.map(r => ({
        timestamp: r.timestamp,
        value: r.totalTests > 0 ? r.failedTests / r.totalTests : 0,
      })),
      'failure_rate',
      sensitivity,
    );
    anomalies.push(...failureRateAnomalies);

    return anomalies.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  /**
   * Generate comprehensive trend insights
   */
  async generateTrendInsights(days: number = 30): Promise<TrendInsight[]> {
    const insights: TrendInsight[] = [];

    // Analyze coverage trends
    const coverageTrends = await this.analyzeCoverageTrends(days);
    insights.push(...this.extractCoverageInsights(coverageTrends, days));

    // Analyze performance trends
    const performanceTrends = await this.analyzePerformanceTrends(days);
    insights.push(...this.extractPerformanceInsights(performanceTrends, days));

    // Analyze quality trends
    const qualityTrends = await this.analyzeQualityTrends(days);
    insights.push(...this.extractQualityInsights(qualityTrends, days));

    // Detect anomalies
    const anomalies = await this.detectAnomalies(days);
    insights.push(...this.convertAnomaliesToInsights(anomalies));

    // Sort by severity and confidence
    return insights.sort((a, b) => {
      const severityOrder = { high: 3, medium: 2, low: 1 };
      const severityDiff = severityOrder[b.severity] - severityOrder[a.severity];
      if (severityDiff !== 0) return severityDiff;
      return b.confidence - a.confidence;
    });
  }

  /**
   * Predict future trends based on historical data
   */
  async predictTrends(metric: string, days: number = 30, forecastDays: number = 7): Promise<TrendPoint[]> {
    const endDate = new Date();
    const startDate = new Date(endDate.getTime() - days * 24 * 60 * 60 * 1000);

    const records = await this.dataManager.queryTestSuiteRecords(undefined, {
      startDate,
      endDate,
      sortBy: 'timestamp',
      sortOrder: 'asc',
    });

    const dataPoints = this.extractMetricValues(records, metric);

    if (dataPoints.length < 5) {
      return []; // Not enough data for prediction
    }

    return this.generatePrediction(dataPoints, forecastDays);
  }

  /**
   * Private helper methods
   */
  private async analyzeCoverageMetric(
    records: TestSuiteRecord[],
    metric: 'statements' | 'branches' | 'functions' | 'lines',
  ): Promise<TrendAnalysis> {
    const dataPoints: TrendPoint[] = records.map(record => ({
      timestamp: record.timestamp,
      value: record.coverage[metric],
      metadata: { suiteName: record.suiteName },
    }));

    return this.analyzeTrend(dataPoints);
  }

  private async analyzeOverallCoverage(records: TestSuiteRecord[]): Promise<TrendAnalysis> {
    const dataPoints: TrendPoint[] = records.map(record => ({
      timestamp: record.timestamp,
      value: (record.coverage.statements + record.coverage.branches +
              record.coverage.functions + record.coverage.lines) / 4,
      metadata: { suiteName: record.suiteName },
    }));

    return this.analyzeTrend(dataPoints);
  }

  private async analyzeExecutionTimeTrend(records: TestSuiteRecord[]): Promise<TrendAnalysis> {
    const dataPoints: TrendPoint[] = records.map(record => ({
      timestamp: record.timestamp,
      value: record.duration,
      metadata: { suiteName: record.suiteName, testCount: record.totalTests },
    }));

    return this.analyzeTrend(dataPoints);
  }

  private async analyzeMemoryUsageTrend(records: TestSuiteRecord[]): Promise<TrendAnalysis> {
    const dataPoints: TrendPoint[] = records.map(record => ({
      timestamp: record.timestamp,
      value: record.performance.peakMemory,
      metadata: { suiteName: record.suiteName },
    }));

    return this.analyzeTrend(dataPoints);
  }

  private async analyzeTestCountTrend(records: TestSuiteRecord[]): Promise<TrendAnalysis> {
    const dataPoints: TrendPoint[] = records.map(record => ({
      timestamp: record.timestamp,
      value: record.totalTests,
      metadata: { suiteName: record.suiteName },
    }));

    return this.analyzeTrend(dataPoints);
  }

  private async analyzeFailureRateTrend(records: TestSuiteRecord[]): Promise<TrendAnalysis> {
    const dataPoints: TrendPoint[] = records.map(record => ({
      timestamp: record.timestamp,
      value: record.totalTests > 0 ? record.failedTests / record.totalTests : 0,
      metadata: { suiteName: record.suiteName, failedTests: record.failedTests },
    }));

    return this.analyzeTrend(dataPoints);
  }

  private async analyzePassRateTrend(records: TestSuiteRecord[]): Promise<TrendAnalysis> {
    const dataPoints: TrendPoint[] = records.map(record => ({
      timestamp: record.timestamp,
      value: record.totalTests > 0 ? record.passedTests / record.totalTests : 0,
      metadata: { suiteName: record.suiteName, passedTests: record.passedTests },
    }));

    return this.analyzeTrend(dataPoints);
  }

  private async analyzeFlakinessOverTime(days: number): Promise<TrendAnalysis> {
    const flakyTests = await this.dataManager.getFlakyTests(days);

    // Group flaky tests by day to create a trend
    const dailyFlakiness = new Map<string, number>();

    for (const test of flakyTests) {
      for (const failure of test.recentFailures) {
        const day = failure.toISOString().split('T')[0];
        dailyFlakiness.set(day, (dailyFlakiness.get(day) || 0) + 1);
      }
    }

    const dataPoints: TrendPoint[] = Array.from(dailyFlakiness.entries()).map(([day, count]) => ({
      timestamp: new Date(day),
      value: count,
      metadata: { day },
    }));

    return this.analyzeTrend(dataPoints);
  }

  private async analyzeTestStabilityTrend(records: TestSuiteRecord[]): Promise<TrendAnalysis> {
    const dataPoints: TrendPoint[] = records.map(record => {
      const stability = record.totalTests > 0 ?
        (record.passedTests + record.skippedTests) / record.totalTests : 1;

      return {
        timestamp: record.timestamp,
        value: stability,
        metadata: { suiteName: record.suiteName },
      };
    });

    return this.analyzeTrend(dataPoints);
  }

  private async analyzeCodeChurnTrend(records: TestSuiteRecord[]): Promise<TrendAnalysis> {
    // Estimate code churn based on test count changes
    const dataPoints: TrendPoint[] = [];

    for (let i = 1; i < records.length; i++) {
      const current = records[i];
      const previous = records[i - 1];

      const churn = Math.abs(current.totalTests - previous.totalTests) /
                   Math.max(previous.totalTests, 1);

      dataPoints.push({
        timestamp: current.timestamp,
        value: churn,
        metadata: {
          suiteName: current.suiteName,
          testCountChange: current.totalTests - previous.totalTests,
        },
      });
    }

    return this.analyzeTrend(dataPoints);
  }

  private analyzeTrend(dataPoints: TrendPoint[]): TrendAnalysis {
    if (dataPoints.length < 2) {
      return {
        trend: 'stable',
        slope: 0,
        correlation: 0,
        confidence: 0,
        dataPoints,
      };
    }

    // Calculate linear regression
    const n = dataPoints.length;
    const xValues = dataPoints.map((_, i) => i);
    const yValues = dataPoints.map(p => p.value);

    const xMean = xValues.reduce((sum, x) => sum + x, 0) / n;
    const yMean = yValues.reduce((sum, y) => sum + y, 0) / n;

    let numerator = 0;
    let denominatorX = 0;
    let denominatorY = 0;

    for (let i = 0; i < n; i++) {
      const xDiff = xValues[i] - xMean;
      const yDiff = yValues[i] - yMean;

      numerator += xDiff * yDiff;
      denominatorX += xDiff * xDiff;
      denominatorY += yDiff * yDiff;
    }

    const correlation = denominatorX === 0 || denominatorY === 0 ? 0 :
      numerator / Math.sqrt(denominatorX * denominatorY);

    const slope = denominatorX === 0 ? 0 : numerator / denominatorX;

    // Determine trend direction
    let trend: 'increasing' | 'decreasing' | 'stable' | 'volatile';
    const absCorrelation = Math.abs(correlation);

    if (absCorrelation < 0.3) {
      trend = 'stable';
    } else if (absCorrelation < 0.6) {
      trend = 'volatile';
    } else {
      trend = slope > 0 ? 'increasing' : 'decreasing';
    }

    // Calculate confidence based on correlation strength and data points
    const confidence = Math.min(absCorrelation * (Math.log(n) / Math.log(10)), 1);

    // Generate prediction if trend is strong enough
    let prediction;
    if (confidence > 0.5 && n >= 5) {
      const lastValue = yValues[yValues.length - 1];
      const nextValue = lastValue + slope;

      prediction = {
        nextValue,
        confidence: confidence * 0.8, // Reduce confidence for predictions
        timeframe: 1, // 1 day
      };
    }

    return {
      trend,
      slope,
      correlation,
      confidence,
      dataPoints,
      prediction,
    };
  }

  private analyzeDailyPattern(records: TestSuiteRecord[]): SeasonalPattern {
    const hourlyData = new Map<number, number[]>();

    for (const record of records) {
      const hour = record.timestamp.getHours();
      if (!hourlyData.has(hour)) {
        hourlyData.set(hour, []);
      }
      hourlyData.get(hour)!.push(record.duration);
    }

    const hourlyAverages = new Map<number, number>();
    for (const [hour, durations] of hourlyData.entries()) {
      const average = durations.reduce((sum, d) => sum + d, 0) / durations.length;
      hourlyAverages.set(hour, average);
    }

    const values = Array.from(hourlyAverages.values());
    const mean = values.reduce((sum, v) => sum + v, 0) / values.length;
    const variance = values.reduce((sum, v) => sum + Math.pow(v - mean, 2), 0) / values.length;
    const strength = variance > 0 ? Math.sqrt(variance) / mean : 0;

    const peaks: number[] = [];
    const valleys: number[] = [];

    for (const [hour, average] of hourlyAverages.entries()) {
      if (average > mean + strength * mean) {
        peaks.push(hour);
      } else if (average < mean - strength * mean) {
        valleys.push(hour);
      }
    }

    return {
      pattern: 'daily',
      strength: Math.min(strength, 1),
      peaks,
      valleys,
      description: `Daily pattern detected with ${peaks.length} peak hours and ${valleys.length} valley hours`,
    };
  }

  private analyzeWeeklyPattern(records: TestSuiteRecord[]): SeasonalPattern {
    const weeklyData = new Map<number, number[]>();

    for (const record of records) {
      const dayOfWeek = record.timestamp.getDay();
      if (!weeklyData.has(dayOfWeek)) {
        weeklyData.set(dayOfWeek, []);
      }
      weeklyData.get(dayOfWeek)!.push(record.duration);
    }

    const weeklyAverages = new Map<number, number>();
    for (const [day, durations] of weeklyData.entries()) {
      const average = durations.reduce((sum, d) => sum + d, 0) / durations.length;
      weeklyAverages.set(day, average);
    }

    const values = Array.from(weeklyAverages.values());
    const mean = values.reduce((sum, v) => sum + v, 0) / values.length;
    const variance = values.reduce((sum, v) => sum + Math.pow(v - mean, 2), 0) / values.length;
    const strength = variance > 0 ? Math.sqrt(variance) / mean : 0;

    const peaks: number[] = [];
    const valleys: number[] = [];

    for (const [day, average] of weeklyAverages.entries()) {
      if (average > mean + strength * mean) {
        peaks.push(day);
      } else if (average < mean - strength * mean) {
        valleys.push(day);
      }
    }

    return {
      pattern: 'weekly',
      strength: Math.min(strength, 1),
      peaks,
      valleys,
      description: `Weekly pattern detected with peak days: ${peaks.map(d => ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'][d]).join(', ')}`,
    };
  }

  private detectMetricAnomalies(
    dataPoints: { timestamp: Date; value: number }[],
    metricName: string,
    sensitivity: number,
  ): Anomaly[] {
    if (dataPoints.length < 10) return [];

    const values = dataPoints.map(p => p.value);
    const mean = values.reduce((sum, v) => sum + v, 0) / values.length;
    const variance = values.reduce((sum, v) => sum + Math.pow(v - mean, 2), 0) / values.length;
    const stdDev = Math.sqrt(variance);

    const anomalies: Anomaly[] = [];

    for (let i = 0; i < dataPoints.length; i++) {
      const point = dataPoints[i];
      const zScore = stdDev > 0 ? Math.abs(point.value - mean) / stdDev : 0;

      if (zScore > sensitivity) {
        const severity: 'low' | 'medium' | 'high' =
          zScore > sensitivity * 2 ? 'high' :
          zScore > sensitivity * 1.5 ? 'medium' : 'low';

        anomalies.push({
          timestamp: point.timestamp,
          metric: metricName,
          expectedValue: mean,
          actualValue: point.value,
          severity,
          description: `${metricName} anomaly detected: ${point.value.toFixed(2)} (expected ~${mean.toFixed(2)})`,
          possibleCauses: this.generateAnomalyCauses(metricName, point.value > mean),
        });
      }
    }

    return anomalies;
  }

  private generateAnomalyCauses(metric: string, isHigh: boolean): string[] {
    const causes: Record<string, { high: string[]; low: string[] }> = {
      execution_time: {
        high: ['Performance regression', 'Increased test complexity', 'Resource contention', 'Network latency'],
        low: ['Performance optimization', 'Reduced test scope', 'Caching improvements', 'Hardware upgrade'],
      },
      coverage: {
        high: ['New tests added', 'Code refactoring', 'Better test practices'],
        low: ['Code added without tests', 'Tests removed', 'Coverage calculation error'],
      },
      failure_rate: {
        high: ['Code regression', 'Environment issues', 'Flaky tests', 'Dependency changes'],
        low: ['Bug fixes', 'Test improvements', 'Code quality improvements'],
      },
    };

    return causes[metric]?.[isHigh ? 'high' : 'low'] || ['Unknown cause'];
  }

  private generateCoverageRecommendations(trends: CoverageTrend): string[] {
    const recommendations: string[] = [];

    if (trends.overall.trend === 'decreasing') {
      recommendations.push('Coverage is declining. Consider implementing coverage gates in CI/CD.');
    }

    if (trends.branches.correlation < trends.statements.correlation) {
      recommendations.push('Branch coverage is less stable than statement coverage. Focus on edge case testing.');
    }

    if (trends.functions.trend === 'stable' && trends.functions.dataPoints.length > 0) {
      const avgCoverage = trends.functions.dataPoints.reduce((sum, p) => sum + p.value, 0) / trends.functions.dataPoints.length;
      if (avgCoverage < 90) {
        recommendations.push('Function coverage is below 90%. Consider adding more unit tests.');
      }
    }

    return recommendations;
  }

  private generatePerformanceRecommendations(trends: PerformanceTrend): string[] {
    const recommendations: string[] = [];

    if (trends.executionTime.trend === 'increasing') {
      recommendations.push('Test execution time is increasing. Consider optimizing slow tests or implementing parallel execution.');
    }

    if (trends.memoryUsage.trend === 'increasing') {
      recommendations.push('Memory usage is growing. Check for memory leaks in tests or test data cleanup issues.');
    }

    if (trends.failureRate.trend === 'increasing') {
      recommendations.push('Failure rate is increasing. Investigate flaky tests and improve test stability.');
    }

    return recommendations;
  }

  private generateQualityRecommendations(trends: QualityTrend): string[] {
    const recommendations: string[] = [];

    if (trends.passRate.trend === 'decreasing') {
      recommendations.push('Pass rate is declining. Focus on fixing failing tests and improving code quality.');
    }

    if (trends.flakiness.trend === 'increasing') {
      recommendations.push('Test flakiness is increasing. Identify and fix unstable tests.');
    }

    if (trends.codeChurn.trend === 'increasing') {
      recommendations.push('Code churn is high. Consider stabilizing the codebase and improving test maintenance.');
    }

    return recommendations;
  }

  private extractCoverageInsights(trends: CoverageTrend, days: number): TrendInsight[] {
    const insights: TrendInsight[] = [];

    if (trends.overall.trend === 'decreasing' && trends.overall.confidence > 0.6) {
      insights.push({
        type: 'degradation',
        severity: 'high',
        title: 'Coverage Declining',
        description: `Test coverage has been declining over the past ${days} days`,
        metrics: ['coverage'],
        timeframe: `${days} days`,
        actionItems: trends.recommendations,
        confidence: trends.overall.confidence,
      });
    }

    return insights;
  }

  private extractPerformanceInsights(trends: PerformanceTrend, days: number): TrendInsight[] {
    const insights: TrendInsight[] = [];

    if (trends.executionTime.trend === 'increasing' && trends.executionTime.confidence > 0.6) {
      insights.push({
        type: 'degradation',
        severity: 'medium',
        title: 'Test Performance Degrading',
        description: `Test execution time has been increasing over the past ${days} days`,
        metrics: ['execution_time'],
        timeframe: `${days} days`,
        actionItems: trends.recommendations,
        confidence: trends.executionTime.confidence,
      });
    }

    return insights;
  }

  private extractQualityInsights(trends: QualityTrend, days: number): TrendInsight[] {
    const insights: TrendInsight[] = [];

    if (trends.passRate.trend === 'decreasing' && trends.passRate.confidence > 0.6) {
      insights.push({
        type: 'degradation',
        severity: 'high',
        title: 'Test Quality Declining',
        description: `Test pass rate has been declining over the past ${days} days`,
        metrics: ['pass_rate'],
        timeframe: `${days} days`,
        actionItems: trends.recommendations,
        confidence: trends.passRate.confidence,
      });
    }

    return insights;
  }

  private convertAnomaliesToInsights(anomalies: Anomaly[]): TrendInsight[] {
    return anomalies.map(anomaly => ({
      type: 'anomaly' as const,
      severity: anomaly.severity,
      title: `${anomaly.metric} Anomaly Detected`,
      description: anomaly.description,
      metrics: [anomaly.metric],
      timeframe: 'recent',
      actionItems: anomaly.possibleCauses.map(cause => `Investigate: ${cause}`),
      confidence: 0.8,
    }));
  }

  private extractMetricValues(records: TestSuiteRecord[], metric: string): TrendPoint[] {
    return records.map(record => {
      let value: number;

      switch (metric) {
        case 'coverage':
          value = (record.coverage.statements + record.coverage.branches +
                  record.coverage.functions + record.coverage.lines) / 4;
          break;
        case 'execution_time':
          value = record.duration;
          break;
        case 'memory_usage':
          value = record.performance.peakMemory;
          break;
        case 'pass_rate':
          value = record.totalTests > 0 ? record.passedTests / record.totalTests : 0;
          break;
        default:
          value = 0;
      }

      return {
        timestamp: record.timestamp,
        value,
        metadata: { suiteName: record.suiteName },
      };
    });
  }

  private generatePrediction(dataPoints: TrendPoint[], forecastDays: number): TrendPoint[] {
    const analysis = this.analyzeTrend(dataPoints);

    if (!analysis.prediction || analysis.confidence < 0.5) {
      return [];
    }

    const predictions: TrendPoint[] = [];
    const lastPoint = dataPoints[dataPoints.length - 1];
    const dailyChange = analysis.slope;

    for (let i = 1; i <= forecastDays; i++) {
      const futureDate = new Date(lastPoint.timestamp.getTime() + i * 24 * 60 * 60 * 1000);
      const predictedValue = lastPoint.value + (dailyChange * i);

      predictions.push({
        timestamp: futureDate,
        value: Math.max(0, predictedValue), // Ensure non-negative values
        metadata: {
          predicted: true,
          confidence: analysis.confidence * (1 - i * 0.1), // Decrease confidence over time
        },
      });
    }

    return predictions;
  }

  private getCachedResult(key: string): any {
    const cached = this.analysisCache.get(key);
    if (cached && Date.now() - cached.timestamp < this.cacheTimeout) {
      return cached.data;
    }
    return null;
  }

  private setCachedResult(key: string, data: any): void {
    this.analysisCache.set(key, {
      data,
      timestamp: Date.now(),
    });
  }
}

/**
 * Quality Gate Manager
 * Manages automated quality gates for CI/CD pipelines
 */

import { QualityGate, QualityGateConfig, TestSummary, CoverageInfo } from './types';

export class QualityGateManager {
  private gates: Map<string, QualityGateConfig> = new Map();
  private results: Map<string, QualityGate> = new Map();

  /**
   * Register a quality gate
   */
  registerGate(gate: QualityGateConfig): void {
    this.gates.set(gate.name, gate);
  }

  /**
   * Evaluate all quality gates
   */
  async evaluateGates(
    testSummary: TestSummary,
    coverage?: CoverageInfo,
    customMetrics?: Record<string, number>,
  ): Promise<QualityGate[]> {
    console.log(' Evaluating quality gates...');

    const results: QualityGate[] = [];

    for (const [name, config] of this.gates) {
      const result = await this.evaluateGate(config, testSummary, coverage, customMetrics);
      results.push(result);
      this.results.set(name, result);
    }

    return results;
  }

  /**
   * Evaluate a single quality gate
   */
  private async evaluateGate(
    config: QualityGateConfig,
    testSummary: TestSummary,
    coverage?: CoverageInfo,
    customMetrics?: Record<string, number>,
  ): Promise<QualityGate> {
    let value: number;
    let status: 'passed' | 'failed' | 'warning';

    switch (config.type) {
      case 'coverage':
        value = this.getCoverageValue(coverage, config.name);
        break;
      case 'performance':
        value = testSummary.duration;
        break;
      case 'security':
        value = customMetrics?.securityScore || 0;
        break;
      case 'custom':
        value = customMetrics?.[config.name] || 0;
        break;
      default:
        value = 0;
    }

    // Evaluate against threshold
    const passed = this.evaluateThreshold(value, config.threshold, config.operator);
    status = passed ? 'passed' : (config.blocking ? 'failed' : 'warning');

    return {
      id: `gate-${config.name}`,
      name: config.name,
      type: config.type,
      status,
      value,
      threshold: config.threshold,
      message: config.message || this.generateMessage(config, value, passed),
      blocking: config.blocking,
      details: {
        operator: config.operator,
        evaluatedAt: new Date().toISOString(),
      },
    };
  }

  /**
   * Get coverage value based on gate name
   */
  private getCoverageValue(coverage: CoverageInfo | undefined, gateName: string): number {
    if (!coverage) return 0;

    switch (gateName.toLowerCase()) {
      case 'statement-coverage':
      case 'statements':
        return coverage.statements;
      case 'branch-coverage':
      case 'branches':
        return coverage.branches;
      case 'function-coverage':
      case 'functions':
        return coverage.functions;
      case 'line-coverage':
      case 'lines':
        return coverage.lines;
      default:
        // Return overall coverage (average)
        return (coverage.statements + coverage.branches + coverage.functions + coverage.lines) / 4;
    }
  }

  /**
   * Evaluate threshold condition
   */
  private evaluateThreshold(value: number, threshold: number, operator: string): boolean {
    switch (operator) {
      case 'gt':
        return value > threshold;
      case 'gte':
        return value >= threshold;
      case 'lt':
        return value < threshold;
      case 'lte':
        return value <= threshold;
      case 'eq':
        return value === threshold;
      default:
        return false;
    }
  }

  /**
   * Generate default message for quality gate
   */
  private generateMessage(config: QualityGateConfig, value: number, passed: boolean): string {
    const status = passed ? 'PASSED' : 'FAILED';
    const operatorText = this.getOperatorText(config.operator);

    return `${config.name} ${status}: ${value} ${operatorText} ${config.threshold}`;
  }

  /**
   * Get human-readable operator text
   */
  private getOperatorText(operator: string): string {
    switch (operator) {
      case 'gt': return '>';
      case 'gte': return '>=';
      case 'lt': return '<';
      case 'lte': return '<=';
      case 'eq': return '==';
      default: return operator;
    }
  }

  /**
   * Check if all blocking gates passed
   */
  canProceed(): boolean {
    for (const result of this.results.values()) {
      if (result.blocking && result.status === 'failed') {
        return false;
      }
    }
    return true;
  }

  /**
   * Get failed gates
   */
  getFailedGates(): QualityGate[] {
    return Array.from(this.results.values()).filter(gate => gate.status === 'failed');
  }

  /**
   * Get blocking failed gates
   */
  getBlockingFailedGates(): QualityGate[] {
    return this.getFailedGates().filter(gate => gate.blocking);
  }

  /**
   * Generate quality gate report
   */
  generateReport(): {
    summary: {
      total: number;
      passed: number;
      failed: number;
      warnings: number;
      blocking: number;
    };
    gates: QualityGate[];
    canProceed: boolean;
  } {
    const gates = Array.from(this.results.values());

    return {
      summary: {
        total: gates.length,
        passed: gates.filter(g => g.status === 'passed').length,
        failed: gates.filter(g => g.status === 'failed').length,
        warnings: gates.filter(g => g.status === 'warning').length,
        blocking: gates.filter(g => g.blocking).length,
      },
      gates,
      canProceed: this.canProceed(),
    };
  }

  /**
   * Create default quality gates
   */
  static createDefaultGates(): QualityGateConfig[] {
    return [
      {
        name: 'test-pass-rate',
        type: 'custom',
        threshold: 95,
        operator: 'gte',
        blocking: true,
        message: 'Test pass rate must be at least 95%',
      },
      {
        name: 'code-coverage',
        type: 'coverage',
        threshold: 80,
        operator: 'gte',
        blocking: true,
        message: 'Code coverage must be at least 80%',
      },
      {
        name: 'build-time',
        type: 'performance',
        threshold: 300000, // 5 minutes
        operator: 'lte',
        blocking: false,
        message: 'Build time should be under 5 minutes',
      },
      {
        name: 'security-score',
        type: 'security',
        threshold: 8,
        operator: 'gte',
        blocking: true,
        message: 'Security score must be at least 8/10',
      },
    ];
  }

  /**
   * Load gates from configuration
   */
  loadGatesFromConfig(config: QualityGateConfig[]): void {
    config.forEach(gate => this.registerGate(gate));
  }

  /**
   * Clear all gates and results
   */
  clear(): void {
    this.gates.clear();
    this.results.clear();
  }

  /**
   * Get gate configuration
   */
  getGateConfig(name: string): QualityGateConfig | undefined {
    return this.gates.get(name);
  }

  /**
   * Get gate result
   */
  getGateResult(name: string): QualityGate | undefined {
    return this.results.get(name);
  }

  /**
   * Update gate threshold
   */
  updateGateThreshold(name: string, threshold: number): boolean {
    const gate = this.gates.get(name);
    if (gate) {
      gate.threshold = threshold;
      return true;
    }
    return false;
  }

  /**
   * Enable/disable gate
   */
  setGateBlocking(name: string, blocking: boolean): boolean {
    const gate = this.gates.get(name);
    if (gate) {
      gate.blocking = blocking;
      return true;
    }
    return false;
  }
}

/**
 * Cross-Platform Test Runner
 * Orchestrates testing across different platforms, Node versions, browsers, and containers
 */

import { BrowserCompatibilityTester } from './BrowserCompatibilityTester';
import { ContainerTestRunner } from './ContainerTestRunner';
import { EnvironmentDetector } from './EnvironmentDetector';
import { NodeVersionTester } from './NodeVersionTester';
import {
  CompatibilityTestResult,
  CompatibilityReport,
  TestEnvironmentConfig,
  SupportMatrix,
  PlatformSupport,
  NodeVersionSupport,
  BrowserSupport,
  ContainerSupport,
} from './types';

export class CrossPlatformTestRunner {
  private nodeVersionTester: NodeVersionTester;
  private browserTester: BrowserCompatibilityTester;
  private containerRunner: ContainerTestRunner;

  constructor() {
    this.nodeVersionTester = new NodeVersionTester();
    this.browserTester = new BrowserCompatibilityTester();
    this.containerRunner = new ContainerTestRunner();
  }

  /**
   * Run comprehensive cross-platform compatibility tests
   */
  async runComprehensiveTests(
    testCommand: string,
    config?: Partial<TestEnvironmentConfig>,
  ): Promise<CompatibilityReport> {
    const startTime = Date.now();
    console.log(' Starting comprehensive cross-platform compatibility tests...');

    const defaultConfig: TestEnvironmentConfig = {
      platforms: ['darwin', 'linux', 'win32'],
      nodeVersions: ['18.17.0', '18.18.0', '20.5.0', '20.8.0'],
      browsers: [
        { name: 'chrome', versions: ['latest', '119'], headless: true },
        { name: 'firefox', versions: ['latest', '118'], headless: true },
      ],
      containers: [
        { runtime: 'docker', image: 'node:18-alpine', nodeVersion: '18.18.0' },
        { runtime: 'docker', image: 'node:20-alpine', nodeVersion: '20.8.0' },
      ],
      parallel: true,
      timeout: 600000,
      retries: 2,
    };

    const finalConfig = { ...defaultConfig, ...config };
    const results: CompatibilityTestResult[] = [];

    try {
      // 1. Test current platform with different Node.js versions
      console.log(' Testing Node.js version compatibility...');
      const nodeResults = await this.testNodeVersions(testCommand, finalConfig);
      results.push(...nodeResults);

      // 2. Test browser compatibility (if applicable)
      if (this.shouldTestBrowsers(testCommand)) {
        console.log(' Testing browser compatibility...');
        const browserResults = await this.testBrowsers(testCommand, finalConfig);
        results.push(...browserResults);
      }

      // 3. Test container environments
      console.log(' Testing container compatibility...');
      const containerResults = await this.testContainers(testCommand, finalConfig);
      results.push(...containerResults);

      // 4. Generate comprehensive report
      const report = await this.generateCompatibilityReport(results, finalConfig);

      const duration = Date.now() - startTime;
      console.log(` Cross-platform testing completed in ${duration}ms`);

      return report;
    } catch (error) {
      console.error(' Cross-platform testing failed:', error);
      throw error;
    }
  }

  /**
   * Run quick compatibility check
   */
  async runQuickCheck(testCommand: string): Promise<{
    compatible: boolean;
    issues: string[];
    recommendations: string[];
  }> {
    console.log(' Running quick compatibility check...');

    const issues: string[] = [];
    const recommendations: string[] = [];

    // Check current environment
    const envCheck = EnvironmentDetector.checkCompatibility();
    if (!envCheck.compatible) {
      issues.push(...envCheck.issues);
    }
    recommendations.push(...envCheck.warnings);

    // Quick Node.js version test
    try {
      const nodeResult = await this.nodeVersionTester.testSingleVersion(
        process.version.slice(1), // Remove 'v' prefix
        testCommand,
      );

      if (!nodeResult.passed) {
        issues.push(`Tests fail with current Node.js version ${process.version}`);
      }
    } catch (error) {
      issues.push(`Failed to test current Node.js version: ${error}`);
    }

    // Check container availability
    const isContainer = EnvironmentDetector.isContainer();
    const containerInfo = EnvironmentDetector.getContainerInfo();

    if (isContainer && containerInfo) {
      recommendations.push(`Running in ${containerInfo.runtime} container`);
    } else {
      recommendations.push('Consider testing in containerized environment for consistency');
    }

    return {
      compatible: issues.length === 0,
      issues,
      recommendations,
    };
  }

  /**
   * Generate platform support matrix
   */
  async generateSupportMatrix(): Promise<SupportMatrix> {
    console.log(' Generating platform support matrix...');

    // Platform support
    const platforms: PlatformSupport[] = [
      {
        platform: 'darwin',
        supported: true,
        minNodeVersion: '18.0.0',
        recommendations: ['Use latest macOS for best compatibility'],
      },
      {
        platform: 'linux',
        supported: true,
        minNodeVersion: '18.0.0',
        recommendations: ['Ubuntu 20.04+ or equivalent recommended'],
      },
      {
        platform: 'win32',
        supported: true,
        minNodeVersion: '18.0.0',
        limitations: ['Path separator differences may cause issues'],
        recommendations: ['Use WSL2 for better compatibility'],
      },
    ];

    // Node.js version support
    const nodeVersionMatrix = await this.nodeVersionTester.getCompatibilityMatrix().catch(() => []);
    const nodeVersions: NodeVersionSupport[] = nodeVersionMatrix.map(item => ({
      version: item.version,
      supported: item.compatible,
      tested: true,
      issues: item.issues,
      recommendations: item.features.length > 0 ?
        [`Supports features: ${item.features.join(', ')}`] : [],
    }));

    // Browser support
    const browserMatrix = await this.browserTester.getBrowserFeatureMatrix().catch(() => []);
    const browsers: BrowserSupport[] = this.aggregateBrowserSupport(browserMatrix);

    // Container support
    const containers: ContainerSupport[] = [
      {
        runtime: 'docker',
        baseImages: ['node:18-alpine', 'node:20-alpine', 'node:18-slim', 'node:20-slim'],
        supported: true,
        recommendations: ['Alpine images are smaller but may have compatibility issues'],
      },
      {
        runtime: 'podman',
        baseImages: ['node:18-alpine', 'node:20-alpine'],
        supported: true,
        recommendations: ['Drop-in replacement for Docker'],
      },
    ];

    return {
      platforms,
      nodeVersions,
      browsers,
      containers,
    };
  }

  /**
   * Test Node.js versions
   */
  private async testNodeVersions(
    testCommand: string,
    config: TestEnvironmentConfig,
  ): Promise<CompatibilityTestResult[]> {
    if (config.parallel) {
      // Run tests in parallel for better performance
      const promises = config.nodeVersions.map(version =>
        this.nodeVersionTester.testSingleVersion(version, testCommand).catch(error => {
          // Return a failed result instead of throwing
          return {
            environment: { platform: process.platform, arch: process.arch, nodeVersion: `v${version}`, npmVersion: 'unknown', osVersion: 'unknown', cpuCount: 0, totalMemory: 0, availableMemory: 0, timezone: 'UTC', locale: 'en-US' },
            testSuite: `node-${version}`,
            passed: false,
            duration: 0,
            errors: [{ type: 'version' as const, message: `Failed to test Node.js ${version}: ${error}`, severity: 'critical' as const, affectedTests: ['all'] }],
            warnings: [],
            performance: { executionTime: 0, memoryUsage: { peak: 0, average: 0, final: 0 }, cpuUsage: { peak: 0, average: 0 } },
          };
        }),
      );
      return await Promise.all(promises);
    } else {
      // Run tests sequentially
      const results: CompatibilityTestResult[] = [];
      for (const version of config.nodeVersions) {
        try {
          const result = await this.nodeVersionTester.testSingleVersion(version, testCommand);
          results.push(result);
        } catch (error) {
          results.push({
            environment: { platform: process.platform, arch: process.arch, nodeVersion: `v${version}`, npmVersion: 'unknown', osVersion: 'unknown', cpuCount: 0, totalMemory: 0, availableMemory: 0, timezone: 'UTC', locale: 'en-US' },
            testSuite: `node-${version}`,
            passed: false,
            duration: 0,
            errors: [{ type: 'version' as const, message: `Failed to test Node.js ${version}: ${error}`, severity: 'critical' as const, affectedTests: ['all'] }],
            warnings: [],
            performance: { executionTime: 0, memoryUsage: { peak: 0, average: 0, final: 0 }, cpuUsage: { peak: 0, average: 0 } },
          });
        }
      }
      return results;
    }
  }

  /**
   * Test browsers
   */
  private async testBrowsers(
    testCommand: string,
    config: TestEnvironmentConfig,
  ): Promise<CompatibilityTestResult[]> {
    if (config.parallel) {
      const promises = config.browsers.map(browser =>
        this.browserTester.testMultipleBrowsers(testCommand, [browser]),
      );
      const results = await Promise.all(promises);
      return results.flat();
    } else {
      const results: CompatibilityTestResult[] = [];
      for (const browser of config.browsers) {
        const browserResults = await this.browserTester.testMultipleBrowsers(testCommand, [browser]);
        results.push(...browserResults);
      }
      return results;
    }
  }

  /**
   * Test containers
   */
  private async testContainers(
    testCommand: string,
    config: TestEnvironmentConfig,
  ): Promise<CompatibilityTestResult[]> {
    if (config.parallel) {
      const promises = config.containers.map(container =>
        this.containerRunner.testSingleContainer(container, testCommand).catch(error => {
          // Return a failed result instead of throwing
          return {
            environment: { platform: 'linux' as NodeJS.Platform, arch: process.arch, nodeVersion: container.nodeVersion, npmVersion: 'unknown', osVersion: 'Container', cpuCount: 0, totalMemory: 0, availableMemory: 0, timezone: 'UTC', locale: 'en-US' },
            testSuite: `${container.runtime}-${container.image}`,
            passed: false,
            duration: 0,
            errors: [{ type: 'platform' as const, message: `Failed to test container ${container.image}: ${error}`, severity: 'critical' as const, affectedTests: ['all'] }],
            warnings: [],
            performance: { executionTime: 0, memoryUsage: { peak: 0, average: 0, final: 0 }, cpuUsage: { peak: 0, average: 0 } },
          };
        }),
      );
      return await Promise.all(promises);
    } else {
      const results: CompatibilityTestResult[] = [];
      for (const container of config.containers) {
        try {
          const result = await this.containerRunner.testSingleContainer(container, testCommand);
          results.push(result);
        } catch (error) {
          results.push({
            environment: { platform: 'linux' as NodeJS.Platform, arch: process.arch, nodeVersion: container.nodeVersion, npmVersion: 'unknown', osVersion: 'Container', cpuCount: 0, totalMemory: 0, availableMemory: 0, timezone: 'UTC', locale: 'en-US' },
            testSuite: `${container.runtime}-${container.image}`,
            passed: false,
            duration: 0,
            errors: [{ type: 'platform' as const, message: `Failed to test container ${container.image}: ${error}`, severity: 'critical' as const, affectedTests: ['all'] }],
            warnings: [],
            performance: { executionTime: 0, memoryUsage: { peak: 0, average: 0, final: 0 }, cpuUsage: { peak: 0, average: 0 } },
          });
        }
      }
      return results;
    }
  }

  /**
   * Check if browser testing is applicable
   */
  private shouldTestBrowsers(testCommand: string): boolean {
    // Check if the test command includes browser-related tests
    const browserKeywords = ['e2e', 'browser', 'playwright', 'puppeteer', 'cypress'];
    return browserKeywords.some(keyword => testCommand.includes(keyword));
  }

  /**
   * Generate comprehensive compatibility report
   */
  private async generateCompatibilityReport(
    results: CompatibilityTestResult[],
    config: TestEnvironmentConfig,
  ): Promise<CompatibilityReport> {
    const totalEnvironments = results.length;
    const passedEnvironments = results.filter(r => r.passed).length;
    const failedEnvironments = results.filter(r => !r.passed).length;
    const warningEnvironments = results.filter(r => r.warnings.length > 0).length;

    // Generate recommendations
    const recommendations = this.generateRecommendations(results);

    // Generate support matrix
    const supportMatrix = await this.generateSupportMatrix();

    return {
      summary: {
        totalEnvironments,
        passedEnvironments,
        failedEnvironments,
        warningEnvironments,
      },
      results,
      recommendations,
      supportMatrix,
    };
  }

  /**
   * Generate recommendations based on test results
   */
  private generateRecommendations(results: CompatibilityTestResult[]): string[] {
    const recommendations: string[] = [];
    const failedResults = results.filter(r => !r.passed);

    if (failedResults.length === 0) {
      recommendations.push(' All environments passed! Your code has excellent cross-platform compatibility.');
    } else {
      recommendations.push(` ${failedResults.length} environments failed. Consider the following:`);

      // Analyze failure patterns
      const nodeFailures = failedResults.filter(r => r.testSuite.startsWith('node-'));
      const browserFailures = failedResults.filter(r => r.testSuite.includes('chrome') || r.testSuite.includes('firefox'));
      const containerFailures = failedResults.filter(r => r.testSuite.includes('docker'));

      if (nodeFailures.length > 0) {
        recommendations.push(' Node.js version issues detected. Consider updating dependencies or using version-specific polyfills.');
      }

      if (browserFailures.length > 0) {
        recommendations.push(' Browser compatibility issues detected. Consider adding polyfills or updating browser targets.');
      }

      if (containerFailures.length > 0) {
        recommendations.push(' Container environment issues detected. Check Dockerfile and container configuration.');
      }
    }

    // Performance recommendations
    const slowResults = results.filter(r => r.duration > 60000); // > 1 minute
    if (slowResults.length > 0) {
      recommendations.push(' Some environments are slow. Consider optimizing test performance or using parallel execution.');
    }

    return recommendations;
  }

  /**
   * Aggregate browser support information
   */
  private aggregateBrowserSupport(browserMatrix: any[]): BrowserSupport[] {
    const supportMap = new Map<string, BrowserSupport>();

    for (const item of browserMatrix) {
      if (!supportMap.has(item.browser)) {
        supportMap.set(item.browser, {
          browser: item.browser,
          versions: [],
          supported: true,
          polyfillsRequired: [],
          limitations: [],
        });
      }

      const support = supportMap.get(item.browser)!;
      support.versions.push(item.version);

      // Check for required polyfills
      const polyfillFeatures = item.features.filter((f: any) => f.polyfillRequired);
      if (polyfillFeatures.length > 0) {
        support.polyfillsRequired = [...new Set([
          ...support.polyfillsRequired || [],
          ...polyfillFeatures.map((f: any) => f.name),
        ])];
      }
    }

    return Array.from(supportMap.values());
  }
}

