/**
 * Real-Time Test Monitoring Dashboard
 *
 * Provides real-time monitoring of test execution status, coverage changes,
 * instant failure notifications, and team collaboration features.
 */

import { EventEmitter } from 'events';

export interface TestExecutionStatus {
  id: string;
  name: string;
  status: 'pending' | 'running' | 'passed' | 'failed' | 'skipped';
  startTime?: Date;
  endTime?: Date;
  duration?: number;
  error?: {
    message: string;
    stack?: string;
  };
  suite: string;
  file: string;
}

export interface CoverageSnapshot {
  timestamp: Date;
  statements: number;
  branches: number;
  functions: number;
  lines: number;
  files: {
    [filePath: string]: {
      statements: number;
      branches: number;
      functions: number;
      lines: number;
    };
  };
}

export interface DashboardMetrics {
  totalTests: number;
  runningTests: number;
  passedTests: number;
  failedTests: number;
  skippedTests: number;
  pendingTests: number;
  executionTime: number;
  coverage: CoverageSnapshot;
  recentFailures: TestExecutionStatus[];
  performanceMetrics: {
    averageTestTime: number;
    slowestTests: Array<{
      name: string;
      duration: number;
      file: string;
    }>;
    memoryUsage: {
      current: number;
      peak: number;
    };
  };
}

export interface NotificationConfig {
  enabled: boolean;
  channels: Array<'console' | 'webhook' | 'email' | 'slack'>;
  thresholds: {
    failureRate: number;
    coverageDropThreshold: number;
    performanceDegradation: number;
  };
  webhookUrl?: string;
  emailRecipients?: string[];
  slackChannel?: string;
}

export interface TeamCollaborationData {
  activeUsers: Array<{
    id: string;
    name: string;
    lastActivity: Date;
    currentTests: string[];
  }>;
  sharedState: {
    currentBranch: string;
    lastCommit: string;
    testSuiteVersion: string;
  };
  collaborativeMetrics: {
    totalContributors: number;
    testsPerContributor: { [userId: string]: number };
    recentActivity: Array<{
      userId: string;
      action: string;
      timestamp: Date;
      details: any;
    }>;
  };
}

export class RealTimeDashboard extends EventEmitter {
  private metrics: DashboardMetrics;
  private testStatuses: Map<string, TestExecutionStatus> = new Map();
  private coverageHistory: CoverageSnapshot[] = [];
  private notificationConfig: NotificationConfig;
  private collaborationData: TeamCollaborationData;
  private updateInterval: NodeJS.Timeout | null = null;
  private isRunning: boolean = false;

  constructor(
    notificationConfig: Partial<NotificationConfig> = {},
    updateIntervalMs: number = 1000,
  ) {
    super();

    this.notificationConfig = {
      enabled: true,
      channels: ['console'],
      thresholds: {
        failureRate: 10, // 10% failure rate threshold
        coverageDropThreshold: 5, // 5% coverage drop threshold
        performanceDegradation: 50, // 50% performance degradation threshold
      },
      ...notificationConfig,
    };

    this.metrics = this.initializeMetrics();
    this.collaborationData = this.initializeCollaborationData();

    // Start real-time updates
    this.startRealTimeUpdates(updateIntervalMs);
  }

  /**
   * Initialize default metrics
   */
  private initializeMetrics(): DashboardMetrics {
    return {
      totalTests: 0,
      runningTests: 0,
      passedTests: 0,
      failedTests: 0,
      skippedTests: 0,
      pendingTests: 0,
      executionTime: 0,
      coverage: {
        timestamp: new Date(),
        statements: 0,
        branches: 0,
        functions: 0,
        lines: 0,
        files: {},
      },
      recentFailures: [],
      performanceMetrics: {
        averageTestTime: 0,
        slowestTests: [],
        memoryUsage: {
          current: 0,
          peak: 0,
        },
      },
    };
  }

  /**
   * Initialize collaboration data
   */
  private initializeCollaborationData(): TeamCollaborationData {
    return {
      activeUsers: [],
      sharedState: {
        currentBranch: 'main',
        lastCommit: '',
        testSuiteVersion: '1.0.0',
      },
      collaborativeMetrics: {
        totalContributors: 0,
        testsPerContributor: {},
        recentActivity: [],
      },
    };
  }

  /**
   * Start real-time updates
   */
  private startRealTimeUpdates(intervalMs: number): void {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
    }

    this.updateInterval = setInterval(() => {
      this.updateMetrics();
      this.emit('metricsUpdated', this.metrics);
    }, intervalMs);

    this.isRunning = true;
  }

  /**
   * Stop real-time updates
   */
  public stop(): void {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
      this.updateInterval = null;
    }
    this.isRunning = false;
  }

  /**
   * Update test execution status
   */
  public updateTestStatus(testStatus: TestExecutionStatus): void {
    const previousStatus = this.testStatuses.get(testStatus.id);
    this.testStatuses.set(testStatus.id, testStatus);

    // Handle status transitions
    if (previousStatus?.status !== testStatus.status) {
      this.handleStatusTransition(previousStatus, testStatus);
    }

    // Update metrics immediately for critical changes
    if (testStatus.status === 'failed') {
      this.updateMetrics();
      this.handleTestFailure(testStatus);
    }

    this.emit('testStatusUpdated', testStatus);
  }

  /**
   * Update coverage snapshot
   */
  public updateCoverage(coverage: CoverageSnapshot): void {
    const previousCoverage = this.metrics.coverage;
    this.metrics.coverage = coverage;
    this.coverageHistory.push(coverage);

    // Keep only last 100 snapshots
    if (this.coverageHistory.length > 100) {
      this.coverageHistory = this.coverageHistory.slice(-100);
    }

    // Check for coverage drops
    this.checkCoverageDrop(previousCoverage, coverage);

    this.emit('coverageUpdated', coverage);
  }

  /**
   * Get current dashboard metrics
   */
  public getMetrics(): DashboardMetrics {
    return { ...this.metrics };
  }

  /**
   * Get coverage history
   */
  public getCoverageHistory(): CoverageSnapshot[] {
    return [...this.coverageHistory];
  }

  /**
   * Get test execution timeline
   */
  public getTestTimeline(): TestExecutionStatus[] {
    return Array.from(this.testStatuses.values())
      .sort((a, b) => {
        const aTime = a.startTime || new Date(0);
        const bTime = b.startTime || new Date(0);
        return bTime.getTime() - aTime.getTime();
      });
  }

  /**
   * Add team member
   */
  public addTeamMember(user: {
    id: string;
    name: string;
    currentTests?: string[];
  }): void {
    const existingUserIndex = this.collaborationData.activeUsers.findIndex(u => u.id === (user.id || (user as any)._id));

    const userData = {
      ...user,
      lastActivity: new Date(),
      currentTests: user.currentTests || [],
    };

    if (existingUserIndex >= 0) {
      this.collaborationData.activeUsers[existingUserIndex] = userData;
    } else {
      this.collaborationData.activeUsers.push(userData);
    }

    this.emit('teamMemberUpdated', userData);
  }

  /**
   * Remove team member
   */
  public removeTeamMember(userId: string): void {
    this.collaborationData.activeUsers = this.collaborationData.activeUsers
      .filter(user => (user.id || (user as any)._id) !== userId);

    this.emit('teamMemberRemoved', userId);
  }

  /**
   * Update shared state
   */
  public updateSharedState(state: Partial<TeamCollaborationData['sharedState']>): void {
    this.collaborationData.sharedState = {
      ...this.collaborationData.sharedState,
      ...state,
    };

    this.emit('sharedStateUpdated', this.collaborationData.sharedState);
  }

  /**
   * Get collaboration data
   */
  public getCollaborationData(): TeamCollaborationData {
    return { ...this.collaborationData };
  }

  /**
   * Configure notifications
   */
  public configureNotifications(config: Partial<NotificationConfig>): void {
    this.notificationConfig = {
      ...this.notificationConfig,
      ...config,
    };
  }

  /**
   * Send notification
   */
  private async sendNotification(
    type: 'failure' | 'coverage_drop' | 'performance_issue',
    message: string,
    data?: any,
  ): Promise<void> {
    if (!this.notificationConfig.enabled) {
      return;
    }

    const notification = {
      type,
      message,
      timestamp: new Date(),
      data,
    };

    for (const channel of this.notificationConfig.channels) {
      try {
        await this.sendToChannel(channel, notification);
      } catch (error) {
        console.error(`Failed to send notification to ${channel}:`, error);
      }
    }

    this.emit('notificationSent', notification);
  }

  /**
   * Send notification to specific channel
   */
  private async sendToChannel(
    channel: string,
    notification: any,
  ): Promise<void> {
    switch (channel) {
      case 'console':
        console.log(`[${notification.type.toUpperCase()}] ${notification.message}`);
        break;

      case 'webhook':
        if (this.notificationConfig.webhookUrl) {
          // In a real implementation, you would use fetch or axios
          console.log(`Webhook notification: ${JSON.stringify(notification)}`);
        }
        break;

      case 'email':
        if (this.notificationConfig.emailRecipients?.length) {
          console.log(`Email notification to ${this.notificationConfig.emailRecipients.join(', ')}: ${notification.message}`);
        }
        break;

      case 'slack':
        if (this.notificationConfig.slackChannel) {
          console.log(`Slack notification to ${this.notificationConfig.slackChannel}: ${notification.message}`);
        }
        break;
    }
  }

  /**
   * Handle status transitions
   */
  private handleStatusTransition(
    previous: TestExecutionStatus | undefined,
    current: TestExecutionStatus,
  ): void {
    // Log status changes
    this.collaborationData.collaborativeMetrics.recentActivity.push({
      userId: 'system',
      action: 'test_status_change',
      timestamp: new Date(),
      details: {
        testId: current.id,
        from: previous?.status,
        to: current.status,
      },
    });

    // Keep only last 50 activities
    if (this.collaborationData.collaborativeMetrics.recentActivity.length > 50) {
      this.collaborationData.collaborativeMetrics.recentActivity =
        this.collaborationData.collaborativeMetrics.recentActivity.slice(-50);
    }
  }

  /**
   * Handle test failure
   */
  private async handleTestFailure(testStatus: TestExecutionStatus): Promise<void> {
    // Add to recent failures
    this.metrics.recentFailures.unshift(testStatus);
    if (this.metrics.recentFailures.length > 10) {
      this.metrics.recentFailures = this.metrics.recentFailures.slice(0, 10);
    }

    // Check failure rate threshold
    const failureRate = (this.metrics.failedTests / this.metrics.totalTests) * 100;
    if (failureRate >= this.notificationConfig.thresholds.failureRate) {
      await this.sendNotification(
        'failure',
        `High failure rate detected: ${failureRate.toFixed(1)}% (${this.metrics.failedTests}/${this.metrics.totalTests})`,
        { failureRate, testStatus },
      );
    }
  }

  /**
   * Check for coverage drops
   */
  private async checkCoverageDrop(
    previous: CoverageSnapshot,
    current: CoverageSnapshot,
  ): Promise<void> {
    const statementsDrop = previous.statements - current.statements;
    const branchesDrop = previous.branches - current.branches;
    const functionsDrop = previous.functions - current.functions;
    const linesDrop = previous.lines - current.lines;

    const maxDrop = Math.max(statementsDrop, branchesDrop, functionsDrop, linesDrop);

    if (maxDrop >= this.notificationConfig.thresholds.coverageDropThreshold) {
      await this.sendNotification(
        'coverage_drop',
        `Coverage drop detected: ${maxDrop.toFixed(1)}% decrease`,
        { previous, current, drop: maxDrop },
      );
    }
  }

  /**
   * Update metrics from current test statuses
   */
  private updateMetrics(): void {
    const statuses = Array.from(this.testStatuses.values());

    this.metrics.totalTests = statuses.length;
    this.metrics.runningTests = statuses.filter(t => t.status === 'running').length;
    this.metrics.passedTests = statuses.filter(t => t.status === 'passed').length;
    this.metrics.failedTests = statuses.filter(t => t.status === 'failed').length;
    this.metrics.skippedTests = statuses.filter(t => t.status === 'skipped').length;
    this.metrics.pendingTests = statuses.filter(t => t.status === 'pending').length;

    // Calculate execution time
    const completedTests = statuses.filter(t => t.endTime && t.startTime);
    this.metrics.executionTime = completedTests.reduce((sum, test) => {
      return sum + (test.duration || 0);
    }, 0);

    // Update performance metrics
    this.updatePerformanceMetrics(completedTests);

    // Update memory usage
    if (typeof process !== 'undefined' && process.memoryUsage) {
      const memUsage = process.memoryUsage();
      this.metrics.performanceMetrics.memoryUsage.current = memUsage.heapUsed / 1024 / 1024; // MB
      this.metrics.performanceMetrics.memoryUsage.peak = Math.max(
        this.metrics.performanceMetrics.memoryUsage.peak,
        this.metrics.performanceMetrics.memoryUsage.current,
      );
    }
  }

  /**
   * Update performance metrics
   */
  private updatePerformanceMetrics(completedTests: TestExecutionStatus[]): void {
    if (completedTests.length === 0) return;

    // Calculate average test time
    const totalDuration = completedTests.reduce((sum, test) => sum + (test.duration || 0), 0);
    this.metrics.performanceMetrics.averageTestTime = totalDuration / completedTests.length;

    // Find slowest tests
    this.metrics.performanceMetrics.slowestTests = completedTests
      .filter(test => test.duration && test.duration > 0)
      .sort((a, b) => (b.duration || 0) - (a.duration || 0))
      .slice(0, 5)
      .map(test => ({
        name: test.name,
        duration: test.duration || 0,
        file: test.file,
      }));
  }

  /**
   * Export dashboard data
   */
  public exportData(): {
    metrics: DashboardMetrics;
    testStatuses: TestExecutionStatus[];
    coverageHistory: CoverageSnapshot[];
    collaborationData: TeamCollaborationData;
  } {
    return {
      metrics: this.getMetrics(),
      testStatuses: this.getTestTimeline(),
      coverageHistory: this.getCoverageHistory(),
      collaborationData: this.getCollaborationData(),
    };
  }

  /**
   * Import dashboard data
   */
  public importData(data: {
    testStatuses?: TestExecutionStatus[];
    coverageHistory?: CoverageSnapshot[];
    collaborationData?: Partial<TeamCollaborationData>;
  }): void {
    if (data.testStatuses) {
      this.testStatuses.clear();
      data.testStatuses.forEach(status => {
        this.testStatuses.set(status.id, status);
      });
    }

    if (data.coverageHistory) {
      this.coverageHistory = [...data.coverageHistory];
    }

    if (data.collaborationData) {
      this.collaborationData = {
        ...this.collaborationData,
        ...data.collaborationData,
      };
    }

    this.updateMetrics();
    this.emit('dataImported', data);
  }

  /**
   * Reset dashboard
   */
  public reset(): void {
    this.testStatuses.clear();
    this.coverageHistory = [];
    this.metrics = this.initializeMetrics();
    this.collaborationData = this.initializeCollaborationData();

    this.emit('dashboardReset');
  }

  /**
   * Get dashboard status
   */
  public getStatus(): {
    isRunning: boolean;
    totalTests: number;
    activeUsers: number;
    lastUpdate: Date;
  } {
    return {
      isRunning: this.isRunning,
      totalTests: this.metrics.totalTests,
      activeUsers: this.collaborationData.activeUsers.length,
      lastUpdate: new Date(),
    };
  }
}

import { execSync } from 'child_process';
import * as fs from 'fs';
import * as path from 'path';

import { IncrementalTestRunner, IncrementalTestResult } from './IncrementalTestRunner';
import { TestResult } from './TestCacheManager';

export interface VerificationResult {
  isAccurate: boolean;
  missedTests: string[];
  extraTests: string[];
  falsePositives: string[];
  falseNegatives: string[];
  accuracy: number;
  precision: number;
  recall: number;
  details: VerificationDetails;
}

export interface VerificationDetails {
  incrementalTests: string[];
  fullTests: string[];
  incrementalResults: Map<string, TestResult>;
  fullResults: Map<string, TestResult>;
  comparisonReport: ComparisonReport[];
}

export interface ComparisonReport {
  testFile: string;
  incrementalStatus: 'passed' | 'failed' | 'skipped' | 'not_run';
  fullStatus: 'passed' | 'failed' | 'skipped';
  match: boolean;
  reason?: string;
}

export interface AccuracyMetrics {
  totalVerifications: number;
  accurateVerifications: number;
  averageAccuracy: number;
  averagePrecision: number;
  averageRecall: number;
  commonMissedPatterns: string[];
  improvementSuggestions: string[];
}

/**
 * 
 * 
 */
export class AccuracyVerifier {
  private projectRoot: string;
  private verificationHistory: VerificationResult[] = [];
  private metricsFile: string;

  constructor(projectRoot: string = process.cwd()) {
    this.projectRoot = projectRoot;
    this.metricsFile = path.join(projectRoot, '.test-cache', 'accuracy-metrics.json');
    this.loadMetricsHistory();
  }

  /**
   * 
   */
  async verifyAccuracy(
    incrementalRunner: IncrementalTestRunner,
    sampleSize: number = 0.1, // 10%
  ): Promise<VerificationResult> {
    try {
      // 1. 
      const incrementalResult = await incrementalRunner.run();

      // 2. 
      const fullResult = await this.runFullTestSuite(sampleSize);

      // 3. 
      const verification = this.compareResults(incrementalResult, fullResult);

      // 4. 
      this.verificationHistory.push(verification);
      this.saveMetricsHistory();

      return verification;

    } catch (error) {
      throw new Error(`Accuracy verification failed: ${error.message}`);
    }
  }

  /**
   * 
   */
  private async runFullTestSuite(sampleSize: number): Promise<Map<string, TestResult>> {
    const allTests = await this.findAllTests();
    const sampleTests = this.sampleTests(allTests, sampleSize);

    const results = new Map<string, TestResult>();

    for (const testFile of sampleTests) {
      try {
        const result = await this.runSingleTest(testFile);
        results.set(testFile, result);
      } catch (error) {
        // 
        results.set(testFile, {
          testFile,
          status: 'failed',
          duration: 0,
          timestamp: new Date(),
          errors: [{ message: error.message }],
        });
      }
    }

    return results;
  }

  /**
   * 
   */
  private sampleTests(allTests: string[], sampleSize: number): string[] {
    if (sampleSize >= 1) {
      return allTests;
    }

    const sampleCount = Math.max(1, Math.floor(allTests.length * sampleSize));
    const sampled: string[] = [];

    // 
    const testsByType = this.groupTestsByType(allTests);

    for (const [type, tests] of testsByType.entries()) {
      const typeCount = Math.max(1, Math.floor(tests.length * sampleSize));
      const typeSample = this.randomSample(tests, typeCount);
      sampled.push(...typeSample);
    }

    return sampled;
  }

  /**
   * 
   */
  private groupTestsByType(tests: string[]): Map<string, string[]> {
    const groups = new Map<string, string[]>();

    for (const test of tests) {
      let type = 'other';

      if (test.includes('/unit/')) {
        type = 'unit';
      } else if (test.includes('/integration/')) {
        type = 'integration';
      } else if (test.includes('/e2e/')) {
        type = 'e2e';
      } else if (test.includes('/api/')) {
        type = 'api';
      } else if (test.includes('/components/')) {
        type = 'component';
      }

      if (!groups.has(type)) {
        groups.set(type, []);
      }
      groups.get(type)!.push(test);
    }

    return groups;
  }

  /**
   * 
   */
  private randomSample<T>(array: T[], count: number): T[] {
    const shuffled = [...array].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
  }

  /**
   * 
   */
  private compareResults(
    incrementalResult: IncrementalTestResult,
    fullResults: Map<string, TestResult>,
  ): VerificationResult {
    const incrementalTests = new Set([
      ...incrementalResult.executionPlan.testsToRun,
      ...incrementalResult.executionPlan.testsFromCache,
    ]);

    const fullTests = new Set(fullResults.keys());
    const incrementalResultsMap = new Map<string, TestResult>();

    // 
    for (const result of incrementalResult.results) {
      incrementalResultsMap.set(result.testFile, result);
    }

    // 
    const missedTests = Array.from(fullTests).filter(test => !incrementalTests.has(test));
    const extraTests = Array.from(incrementalTests).filter(test => !fullTests.has(test));

    // 
    const comparisonReport: ComparisonReport[] = [];
    const falsePositives: string[] = [];
    const falseNegatives: string[] = [];

    for (const testFile of fullTests) {
      const incrementalResult = incrementalResultsMap.get(testFile);
      const fullResult = fullResults.get(testFile)!;

      const incrementalStatus = incrementalResult?.status || 'not_run';
      const fullStatus = fullResult.status;
      const match = incrementalStatus === fullStatus;

      comparisonReport.push({
        testFile,
        incrementalStatus,
        fullStatus,
        match,
        reason: match ? undefined : `Expected ${fullStatus}, got ${incrementalStatus}`,
      });

      // 
      if (!match) {
        if (incrementalStatus === 'passed' && fullStatus === 'failed') {
          falsePositives.push(testFile);
        } else if (incrementalStatus === 'failed' && fullStatus === 'passed') {
          falseNegatives.push(testFile);
        }
      }
    }

    // 
    const totalTests = fullTests.size;
    const correctPredictions = comparisonReport.filter(r => r.match).length;
    const accuracy = totalTests > 0 ? correctPredictions / totalTests : 1;

    // 
    const truePositives = comparisonReport.filter(r =>
      r.incrementalStatus === 'failed' && r.fullStatus === 'failed',
    ).length;
    const predictedPositives = comparisonReport.filter(r =>
      r.incrementalStatus === 'failed',
    ).length;
    const actualPositives = comparisonReport.filter(r =>
      r.fullStatus === 'failed',
    ).length;

    const precision = predictedPositives > 0 ? truePositives / predictedPositives : 1;
    const recall = actualPositives > 0 ? truePositives / actualPositives : 1;

    return {
      isAccurate: accuracy >= 0.95 && missedTests.length === 0,
      missedTests,
      extraTests,
      falsePositives,
      falseNegatives,
      accuracy,
      precision,
      recall,
      details: {
        incrementalTests: Array.from(incrementalTests),
        fullTests: Array.from(fullTests),
        incrementalResults: incrementalResultsMap,
        fullResults,
        comparisonReport,
      },
    };
  }

  /**
   * 
   */
  private async findAllTests(): Promise<string[]> {
    const glob = require('glob');

    return glob.sync('**/*.{test,spec}.{ts,tsx,js,jsx}', {
      cwd: this.projectRoot,
      absolute: true,
      ignore: [
        '**/node_modules/**',
        '**/dist/**',
        '**/build/**',
        '**/.next/**',
      ],
    });
  }

  /**
   * 
   */
  private async runSingleTest(testFile: string): Promise<TestResult> {
    const startTime = Date.now();
    const relativePath = path.relative(this.projectRoot, testFile);

    try {
      const command = `npx jest "${relativePath}" --json --silent`;
      const output = execSync(command, {
        cwd: this.projectRoot,
        encoding: 'utf8',
        timeout: 30000,
      });

      const duration = Date.now() - startTime;
      const result = JSON.parse(output);

      return {
        testFile: relativePath,
        status: result.success ? 'passed' : 'failed',
        duration,
        timestamp: new Date(),
        errors: result.success ? undefined : [{ message: 'Test failed' }],
      };

    } catch (error) {
      const duration = Date.now() - startTime;

      return {
        testFile: relativePath,
        status: 'failed',
        duration,
        timestamp: new Date(),
        errors: [{ message: error.message }],
      };
    }
  }

  /**
   * 
   */
  analyzeAccuracyTrends(): AccuracyMetrics {
    if (this.verificationHistory.length === 0) {
      return {
        totalVerifications: 0,
        accurateVerifications: 0,
        averageAccuracy: 0,
        averagePrecision: 0,
        averageRecall: 0,
        commonMissedPatterns: [],
        improvementSuggestions: [],
      };
    }

    const totalVerifications = this.verificationHistory.length;
    const accurateVerifications = this.verificationHistory.filter(v => v.isAccurate).length;

    const averageAccuracy = this.verificationHistory.reduce((sum, v) => sum + v.accuracy, 0) / totalVerifications;
    const averagePrecision = this.verificationHistory.reduce((sum, v) => sum + v.precision, 0) / totalVerifications;
    const averageRecall = this.verificationHistory.reduce((sum, v) => sum + v.recall, 0) / totalVerifications;

    // 
    const allMissedTests = this.verificationHistory.flatMap(v => v.missedTests);
    const missedPatterns = this.identifyMissedPatterns(allMissedTests);

    // 
    const suggestions = this.generateImprovementSuggestions(averageAccuracy, averagePrecision, averageRecall, missedPatterns);

    return {
      totalVerifications,
      accurateVerifications,
      averageAccuracy,
      averagePrecision,
      averageRecall,
      commonMissedPatterns: missedPatterns,
      improvementSuggestions: suggestions,
    };
  }

  /**
   * 
   */
  private identifyMissedPatterns(missedTests: string[]): string[] {
    const patterns = new Map<string, number>();

    for (const test of missedTests) {
      // 
      const pathParts = test.split('/');
      for (let i = 0; i < pathParts.length - 1; i++) {
        const pattern = pathParts.slice(0, i + 1).join('/');
        patterns.set(pattern, (patterns.get(pattern) || 0) + 1);
      }

      // 
      const fileName = path.basename(test);
      const namePattern = fileName.replace(/\.(test|spec)\.(ts|tsx|js|jsx)$/, '.*');
      patterns.set(namePattern, (patterns.get(namePattern) || 0) + 1);
    }

    // 
    return Array.from(patterns.entries())
      .sort((a, b) => b[1] - a[1])
      .slice(0, 5)
      .map(([pattern]) => pattern);
  }

  /**
   * 
   */
  private generateImprovementSuggestions(
    accuracy: number,
    precision: number,
    recall: number,
    missedPatterns: string[],
  ): string[] {
    const suggestions: string[] = [];

    if (accuracy < 0.9) {
      suggestions.push('Overall accuracy is low. Consider improving dependency analysis.');
    }

    if (precision < 0.8) {
      suggestions.push('High false positive rate. Review test selection criteria.');
    }

    if (recall < 0.8) {
      suggestions.push('High false negative rate. Improve change impact analysis.');
    }

    if (missedPatterns.length > 0) {
      suggestions.push(`Common missed patterns: ${missedPatterns.join(', ')}. Consider adding specific rules for these patterns.`);
    }

    if (suggestions.length === 0) {
      suggestions.push('Incremental testing accuracy is good. Continue monitoring.');
    }

    return suggestions;
  }

  /**
   * 
   */
  generateVerificationReport(result: VerificationResult): string {
    const report = [
      '# Incremental Test Accuracy Verification Report',
      '',
      `**Accuracy**: ${(result.accuracy * 100).toFixed(2)}%`,
      `**Precision**: ${(result.precision * 100).toFixed(2)}%`,
      `**Recall**: ${(result.recall * 100).toFixed(2)}%`,
      `**Is Accurate**: ${result.isAccurate ? ' Yes' : ' No'}`,
      '',
      '## Summary',
      `- Missed Tests: ${result.missedTests.length}`,
      `- Extra Tests: ${result.extraTests.length}`,
      `- False Positives: ${result.falsePositives.length}`,
      `- False Negatives: ${result.falseNegatives.length}`,
      '',
    ];

    if (result.missedTests.length > 0) {
      report.push('## Missed Tests');
      result.missedTests.forEach(test => {
        report.push(`- ${test}`);
      });
      report.push('');
    }

    if (result.falsePositives.length > 0) {
      report.push('## False Positives');
      result.falsePositives.forEach(test => {
        report.push(`- ${test}`);
      });
      report.push('');
    }

    if (result.falseNegatives.length > 0) {
      report.push('## False Negatives');
      result.falseNegatives.forEach(test => {
        report.push(`- ${test}`);
      });
      report.push('');
    }

    // 
    report.push('## Detailed Comparison');
    report.push('| Test File | Incremental | Full | Match |');
    report.push('|-----------|-------------|------|-------|');

    result.details.comparisonReport.forEach(item => {
      const match = item.match ? '' : '';
      report.push(`| ${item.testFile} | ${item.incrementalStatus} | ${item.fullStatus} | ${match} |`);
    });

    return report.join('\n');
  }

  /**
   * 
   */
  async saveVerificationReport(result: VerificationResult, outputPath?: string): Promise<void> {
    const report = this.generateVerificationReport(result);
    const filePath = outputPath || path.join(this.projectRoot, '.test-cache', 'verification-report.md');

    // 
    const dir = path.dirname(filePath);
    if (!fs.existsSync(dir)) {
      fs.mkdirSync(dir, { recursive: true });
    }

    fs.writeFileSync(filePath, report, 'utf8');
  }

  /**
   * 
   */
  private loadMetricsHistory(): void {
    try {
      if (fs.existsSync(this.metricsFile)) {
        const data = JSON.parse(fs.readFileSync(this.metricsFile, 'utf8'));
        this.verificationHistory = data.history || [];
      }
    } catch (error) {
      console.warn(`Failed to load metrics history: ${error.message}`);
      this.verificationHistory = [];
    }
  }

  /**
   * 
   */
  private saveMetricsHistory(): void {
    try {
      const dir = path.dirname(this.metricsFile);
      if (!fs.existsSync(dir)) {
        fs.mkdirSync(dir, { recursive: true });
      }

      const data = {
        lastUpdated: new Date().toISOString(),
        history: this.verificationHistory.slice(-50), // 50
      };

      fs.writeFileSync(this.metricsFile, JSON.stringify(data, null, 2), 'utf8');
    } catch (error) {
      console.warn(`Failed to save metrics history: ${error.message}`);
    }
  }

  /**
   * 
   */
  clearHistory(): void {
    this.verificationHistory = [];
    if (fs.existsSync(this.metricsFile)) {
      fs.unlinkSync(this.metricsFile);
    }
  }
}

