import { EventEmitter } from 'events';
import * as v8 from 'v8';

export interface MemorySnapshot {
  timestamp: number;
  heapUsed: number;
  heapTotal: number;
  external: number;
  rss: number;
  arrayBuffers: number;
  heapSpaceUsed: HeapSpaceUsage[];
  heapStatistics: v8.HeapStatistics;
}

export interface HeapSpaceUsage {
  spaceName: string;
  spaceSize: number;
  spaceUsedSize: number;
  spaceAvailableSize: number;
  physicalSpaceSize: number;
}

export interface MemoryLeak {
  testId: string;
  testName: string;
  startMemory: number;
  endMemory: number;
  leakSize: number;
  leakRate: number; // bytes per second
  duration: number;
  severity: 'low' | 'medium' | 'high' | 'critical';
}

export interface MemoryAnalysis {
  totalSnapshots: number;
  timeRange: { start: number; end: number };
  memoryGrowth: number;
  averageMemoryUsage: number;
  peakMemoryUsage: number;
  memoryLeaks: MemoryLeak[];
  garbageCollectionStats: GCStats;
  recommendations: string[];
}

export interface GCStats {
  totalCollections: number;
  totalTime: number;
  averageTime: number;
  collections: GCEvent[];
}

export interface GCEvent {
  timestamp: number;
  type: string;
  duration: number;
  memoryBefore: number;
  memoryAfter: number;
  memoryFreed: number;
}

export interface TrackerOptions {
  samplingInterval: number; // ms
  leakThreshold: number; // bytes
  maxSnapshots: number;
  enableGCTracking: boolean;
  enableHeapProfiling: boolean;
}

/**
 * 
 * 
 */
export class MemoryUsageTracker extends EventEmitter {
  private options: TrackerOptions;
  private snapshots: MemorySnapshot[] = [];
  private gcEvents: GCEvent[] = [];
  private testMemoryMap: Map<string, { start: MemorySnapshot; end?: MemorySnapshot }> = new Map();
  private samplingInterval?: NodeJS.Timeout;
  private isTracking = false;
  private gcObserver?: any;

  constructor(options: Partial<TrackerOptions> = {}) {
    super();
    this.options = {
      samplingInterval: 500, // 500ms
      leakThreshold: 10 * 1024 * 1024, // 10MB
      maxSnapshots: 2000,
      enableGCTracking: true,
      enableHeapProfiling: false,
      ...options,
    };
  }

  /**
   * 
   */
  startTracking(): void {
    if (this.isTracking) {
      return;
    }

    this.isTracking = true;

    // 
    this.samplingInterval = setInterval(() => {
      this.captureMemorySnapshot();
    }, this.options.samplingInterval);

    // GC
    if (this.options.enableGCTracking) {
      this.enableGCTracking();
    }

    // 
    if (this.options.enableHeapProfiling) {
      this.enableHeapProfiling();
    }

    this.emit('tracking:started');
  }

  /**
   * 
   */
  stopTracking(): void {
    if (!this.isTracking) {
      return;
    }

    this.isTracking = false;

    if (this.samplingInterval) {
      clearInterval(this.samplingInterval);
      this.samplingInterval = undefined;
    }

    if (this.gcObserver) {
      this.gcObserver.disconnect();
      this.gcObserver = undefined;
    }

    this.emit('tracking:stopped');
  }

  /**
   * 
   */
  private captureMemorySnapshot(): void {
    const memUsage = process.memoryUsage();
    const heapSpaces = v8.getHeapSpaceStatistics();
    const heapStats = v8.getHeapStatistics();

    const snapshot: MemorySnapshot = {
      timestamp: Date.now(),
      heapUsed: memUsage.heapUsed,
      heapTotal: memUsage.heapTotal,
      external: memUsage.external,
      rss: memUsage.rss,
      arrayBuffers: memUsage.arrayBuffers,
      heapSpaceUsed: heapSpaces.map(space => ({
        spaceName: space.space_name,
        spaceSize: space.space_size,
        spaceUsedSize: space.space_used_size,
        spaceAvailableSize: space.space_available_size,
        physicalSpaceSize: space.physical_space_size,
      })),
      heapStatistics: heapStats,
    };

    this.snapshots.push(snapshot);

    // 
    if (this.snapshots.length > this.options.maxSnapshots) {
      this.snapshots = this.snapshots.slice(-this.options.maxSnapshots);
    }

    this.emit('snapshot:captured', snapshot);
  }

  /**
   * 
   */
  private enableGCTracking(): void {
    try {
      // GC
      const { PerformanceObserver } = require('perf_hooks');

      this.gcObserver = new PerformanceObserver((list: any) => {
        const entries = list.getEntries();
        entries.forEach((entry: any) => {
          if (entry.entryType === 'gc') {
            const memoryBefore = this.getLastSnapshot()?.heapUsed || 0;

            // GC
            setTimeout(() => {
              const memoryAfter = process.memoryUsage().heapUsed;

              const gcEvent: GCEvent = {
                timestamp: entry.startTime + entry.duration,
                type: this.getGCType(entry.kind),
                duration: entry.duration,
                memoryBefore,
                memoryAfter,
                memoryFreed: Math.max(0, memoryBefore - memoryAfter),
              };

              this.gcEvents.push(gcEvent);

              // GC
              if (this.gcEvents.length > this.options.maxSnapshots) {
                this.gcEvents = this.gcEvents.slice(-this.options.maxSnapshots);
              }

              this.emit('gc:event', gcEvent);
            }, 10);
          }
        });
      });

      this.gcObserver.observe({ entryTypes: ['gc'] });
    } catch (error) {
      console.warn('Failed to enable GC tracking:', error.message);
    }
  }

  /**
   * GC
   */
  private getGCType(kind: number): string {
    const gcTypes: { [key: number]: string } = {
      1: 'Scavenge',
      2: 'Mark-Sweep-Compact',
      4: 'Incremental Marking',
      8: 'Weak Phantom Callback Processing',
      15: 'All',
    };
    return gcTypes[kind] || `Unknown(${kind})`;
  }

  /**
   * 
   */
  private enableHeapProfiling(): void {
    try {
      // 
      setInterval(() => {
        if (this.isTracking) {
          this.analyzeHeapUsage();
        }
      }, 30000); // 30
    } catch (error) {
      console.warn('Failed to enable heap profiling:', error.message);
    }
  }

  /**
   * 
   */
  private analyzeHeapUsage(): void {
    try {
      const heapStats = v8.getHeapStatistics();
      const heapSpaces = v8.getHeapSpaceStatistics();

      const analysis = {
        timestamp: Date.now(),
        heapStatistics: heapStats,
        heapSpaces: heapSpaces,
        fragmentation: this.calculateFragmentation(heapStats, heapSpaces),
      };

      this.emit('heap:analysis', analysis);
    } catch (error) {
      console.warn('Failed to analyze heap usage:', error.message);
    }
  }

  /**
   * 
   */
  private calculateFragmentation(heapStats: v8.HeapStatistics, heapSpaces: v8.HeapSpaceStatistics[]): number {
    const totalAllocated = heapSpaces.reduce((sum, space) => sum + space.space_used_size, 0);
    const totalAvailable = heapSpaces.reduce((sum, space) => sum + space.space_available_size, 0);
    const totalSize = heapSpaces.reduce((sum, space) => sum + space.space_size, 0);

    if (totalSize === 0) return 0;

    //  = ( -  - ) / 
    const fragmented = totalSize - totalAllocated - totalAvailable;
    return fragmented / totalSize;
  }

  /**
   * 
   */
  startTestTracking(testId: string, testName: string): void {
    const snapshot = this.getLastSnapshot() || this.captureMemorySnapshotSync();
    this.testMemoryMap.set(testId, { start: snapshot });
    this.emit('test:memory:start', { testId, testName, snapshot });
  }

  /**
   * 
   */
  endTestTracking(testId: string): void {
    const testMemory = this.testMemoryMap.get(testId);
    if (!testMemory) {
      return;
    }

    const endSnapshot = this.getLastSnapshot() || this.captureMemorySnapshotSync();
    testMemory.end = endSnapshot;

    // 
    const leak = this.detectMemoryLeak(testId, testMemory.start, endSnapshot);
    if (leak) {
      this.emit('memory:leak', leak);
    }

    this.emit('test:memory:end', { testId, startSnapshot: testMemory.start, endSnapshot });
  }

  /**
   * 
   */
  private captureMemorySnapshotSync(): MemorySnapshot {
    const memUsage = process.memoryUsage();
    const heapSpaces = v8.getHeapSpaceStatistics();
    const heapStats = v8.getHeapStatistics();

    return {
      timestamp: Date.now(),
      heapUsed: memUsage.heapUsed,
      heapTotal: memUsage.heapTotal,
      external: memUsage.external,
      rss: memUsage.rss,
      arrayBuffers: memUsage.arrayBuffers,
      heapSpaceUsed: heapSpaces.map(space => ({
        spaceName: space.space_name,
        spaceSize: space.space_size,
        spaceUsedSize: space.space_used_size,
        spaceAvailableSize: space.space_available_size,
        physicalSpaceSize: space.physical_space_size,
      })),
      heapStatistics: heapStats,
    };
  }

  /**
   * 
   */
  private detectMemoryLeak(testId: string, startSnapshot: MemorySnapshot, endSnapshot: MemorySnapshot): MemoryLeak | null {
    const memoryGrowth = endSnapshot.heapUsed - startSnapshot.heapUsed;
    const duration = endSnapshot.timestamp - startSnapshot.timestamp;
    const leakRate = duration > 0 ? memoryGrowth / (duration / 1000) : 0;

    if (memoryGrowth > this.options.leakThreshold) {
      let severity: MemoryLeak['severity'] = 'low';

      if (memoryGrowth > this.options.leakThreshold * 5) {
        severity = 'critical';
      } else if (memoryGrowth > this.options.leakThreshold * 3) {
        severity = 'high';
      } else if (memoryGrowth > this.options.leakThreshold * 2) {
        severity = 'medium';
      }

      return {
        testId,
        testName: testId, // 
        startMemory: startSnapshot.heapUsed,
        endMemory: endSnapshot.heapUsed,
        leakSize: memoryGrowth,
        leakRate,
        duration,
        severity,
      };
    }

    return null;
  }

  /**
   * 
   */
  private getLastSnapshot(): MemorySnapshot | null {
    return this.snapshots.length > 0 ? this.snapshots[this.snapshots.length - 1] : null;
  }

  /**
   * 
   */
  analyzeMemoryUsage(timeWindow?: number): MemoryAnalysis {
    const cutoffTime = timeWindow ? Date.now() - timeWindow : 0;
    const relevantSnapshots = this.snapshots.filter(s => s.timestamp > cutoffTime);

    if (relevantSnapshots.length === 0) {
      return {
        totalSnapshots: 0,
        timeRange: { start: 0, end: 0 },
        memoryGrowth: 0,
        averageMemoryUsage: 0,
        peakMemoryUsage: 0,
        memoryLeaks: [],
        garbageCollectionStats: {
          totalCollections: 0,
          totalTime: 0,
          averageTime: 0,
          collections: [],
        },
        recommendations: [],
      };
    }

    const startSnapshot = relevantSnapshots[0];
    const endSnapshot = relevantSnapshots[relevantSnapshots.length - 1];

    const memoryGrowth = endSnapshot.heapUsed - startSnapshot.heapUsed;
    const averageMemoryUsage = relevantSnapshots.reduce((sum, s) => sum + s.heapUsed, 0) / relevantSnapshots.length;
    const peakMemoryUsage = Math.max(...relevantSnapshots.map(s => s.heapUsed));

    // 
    const memoryLeaks = this.analyzeMemoryLeaks();

    // GC
    const gcStats = this.analyzeGCStats(cutoffTime);

    // 
    const recommendations = this.generateRecommendations(memoryGrowth, averageMemoryUsage, peakMemoryUsage, memoryLeaks, gcStats);

    return {
      totalSnapshots: relevantSnapshots.length,
      timeRange: { start: startSnapshot.timestamp, end: endSnapshot.timestamp },
      memoryGrowth,
      averageMemoryUsage,
      peakMemoryUsage,
      memoryLeaks,
      garbageCollectionStats: gcStats,
      recommendations,
    };
  }

  /**
   * 
   */
  private analyzeMemoryLeaks(): MemoryLeak[] {
    const leaks: MemoryLeak[] = [];

    for (const [testId, testMemory] of this.testMemoryMap.entries()) {
      if (testMemory.end) {
        const leak = this.detectMemoryLeak(testId, testMemory.start, testMemory.end);
        if (leak) {
          leaks.push(leak);
        }
      }
    }

    return leaks.sort((a, b) => b.leakSize - a.leakSize);
  }

  /**
   * GC
   */
  private analyzeGCStats(cutoffTime: number): GCStats {
    const relevantGCEvents = this.gcEvents.filter(gc => gc.timestamp > cutoffTime);

    const totalCollections = relevantGCEvents.length;
    const totalTime = relevantGCEvents.reduce((sum, gc) => sum + gc.duration, 0);
    const averageTime = totalCollections > 0 ? totalTime / totalCollections : 0;

    return {
      totalCollections,
      totalTime,
      averageTime,
      collections: relevantGCEvents,
    };
  }

  /**
   * 
   */
  private generateRecommendations(
    memoryGrowth: number,
    averageMemoryUsage: number,
    peakMemoryUsage: number,
    memoryLeaks: MemoryLeak[],
    gcStats: GCStats,
  ): string[] {
    const recommendations: string[] = [];

    // 
    if (memoryGrowth > 50 * 1024 * 1024) { // 50MB
      recommendations.push('Significant memory growth detected. Consider reviewing object lifecycle management.');
    }

    // 
    if (memoryLeaks.length > 0) {
      recommendations.push(`${memoryLeaks.length} potential memory leaks detected. Review test cleanup procedures.`);

      const criticalLeaks = memoryLeaks.filter(l => l.severity === 'critical');
      if (criticalLeaks.length > 0) {
        recommendations.push(`${criticalLeaks.length} critical memory leaks require immediate attention.`);
      }
    }

    // GC
    if (gcStats.averageTime > 100) { // 100ms
      recommendations.push('High GC pause times detected. Consider optimizing object allocation patterns.');
    }

    if (gcStats.totalCollections > 100) {
      recommendations.push('Frequent garbage collections detected. Review memory allocation strategies.');
    }

    // 
    if (peakMemoryUsage > 1024 * 1024 * 1024) { // 1GB
      recommendations.push('High peak memory usage detected. Consider implementing memory-efficient algorithms.');
    }

    return recommendations;
  }

  /**
   * 
   */
  getSnapshots(): MemorySnapshot[] {
    return [...this.snapshots];
  }

  /**
   * GC
   */
  getGCEvents(): GCEvent[] {
    return [...this.gcEvents];
  }

  /**
   * 
   */
  getMemoryTrends(timeWindow: number = 3600000): {
    timestamps: number[];
    heapUsed: number[];
    heapTotal: number[];
    rss: number[];
    external: number[];
  } {
    const cutoffTime = Date.now() - timeWindow;
    const relevantSnapshots = this.snapshots.filter(s => s.timestamp > cutoffTime);

    return {
      timestamps: relevantSnapshots.map(s => s.timestamp),
      heapUsed: relevantSnapshots.map(s => s.heapUsed),
      heapTotal: relevantSnapshots.map(s => s.heapTotal),
      rss: relevantSnapshots.map(s => s.rss),
      external: relevantSnapshots.map(s => s.external),
    };
  }

  /**
   * 
   */
  reset(): void {
    this.snapshots = [];
    this.gcEvents = [];
    this.testMemoryMap.clear();
    this.emit('tracking:reset');
  }

  /**
   * 
   */
  destroy(): void {
    this.stopTracking();
    this.reset();
    this.removeAllListeners();
  }
}

import { EventEmitter } from 'events';
import * as fs from 'fs';
import * as path from 'path';

export interface PerformanceBaseline {
  testId: string;
  testName: string;
  averageDuration: number;
  averageMemoryUsage: number;
  standardDeviation: number;
  sampleCount: number;
  lastUpdated: Date;
  confidence: number; // 0-1
}

export interface PerformanceRegression {
  testId: string;
  testName: string;
  type: 'duration' | 'memory' | 'both';
  severity: 'minor' | 'moderate' | 'major' | 'critical';
  currentValue: number;
  baselineValue: number;
  regressionPercent: number;
  confidence: number;
  timestamp: Date;
  details: RegressionDetails;
}

export interface RegressionDetails {
  historicalData: PerformanceDataPoint[];
  trendAnalysis: TrendAnalysis;
  possibleCauses: string[];
  recommendations: string[];
}

export interface PerformanceDataPoint {
  timestamp: Date;
  duration: number;
  memoryUsage: number;
  testId: string;
}

export interface TrendAnalysis {
  trend: 'improving' | 'stable' | 'degrading';
  slope: number;
  correlation: number;
  volatility: number;
}

export interface DetectorOptions {
  baselineFile: string;
  minSampleSize: number;
  regressionThreshold: number; // percentage
  confidenceThreshold: number;
  maxHistorySize: number;
  enableTrendAnalysis: boolean;
  autoUpdateBaseline: boolean;
}

/**
 * 
 * 
 */
export class PerformanceRegressionDetector extends EventEmitter {
  private options: DetectorOptions;
  private baselines: Map<string, PerformanceBaseline> = new Map();
  private performanceHistory: Map<string, PerformanceDataPoint[]> = new Map();
  private regressions: PerformanceRegression[] = [];

  constructor(options: Partial<DetectorOptions> = {}) {
    super();
    this.options = {
      baselineFile: path.join(process.cwd(), '.test-performance', 'baselines.json'),
      minSampleSize: 10,
      regressionThreshold: 20, // 20% regression threshold
      confidenceThreshold: 0.8,
      maxHistorySize: 1000,
      enableTrendAnalysis: true,
      autoUpdateBaseline: true,
      ...options,
    };

    this.loadBaselines();
  }

  /**
   * 
   */
  recordPerformance(testId: string, testName: string, duration: number, memoryUsage: number): void {
    const dataPoint: PerformanceDataPoint = {
      timestamp: new Date(),
      duration,
      memoryUsage,
      testId,
    };

    // 
    if (!this.performanceHistory.has(testId)) {
      this.performanceHistory.set(testId, []);
    }

    const history = this.performanceHistory.get(testId)!;
    history.push(dataPoint);

    // 
    if (history.length > this.options.maxHistorySize) {
      history.splice(0, history.length - this.options.maxHistorySize);
    }

    // 
    this.checkForRegression(testId, testName, dataPoint);

    // 
    if (this.options.autoUpdateBaseline) {
      this.updateBaseline(testId, testName);
    }

    this.emit('performance:recorded', { testId, testName, dataPoint });
  }

  /**
   * 
   */
  private checkForRegression(testId: string, testName: string, dataPoint: PerformanceDataPoint): void {
    const baseline = this.baselines.get(testId);
    if (!baseline || baseline.confidence < this.options.confidenceThreshold) {
      return;
    }

    const durationRegression = this.calculateRegression(dataPoint.duration, baseline.averageDuration);
    const memoryRegression = this.calculateRegression(dataPoint.memoryUsage, baseline.averageMemoryUsage);

    let regressionType: PerformanceRegression['type'] | null = null;
    let regressionPercent = 0;
    let currentValue = 0;
    let baselineValue = 0;

    // 
    if (Math.abs(durationRegression) > this.options.regressionThreshold) {
      if (memoryRegression > this.options.regressionThreshold) {
        regressionType = 'both';
        regressionPercent = Math.max(durationRegression, memoryRegression);
        currentValue = dataPoint.duration;
        baselineValue = baseline.averageDuration;
      } else {
        regressionType = 'duration';
        regressionPercent = durationRegression;
        currentValue = dataPoint.duration;
        baselineValue = baseline.averageDuration;
      }
    } else if (memoryRegression > this.options.regressionThreshold) {
      regressionType = 'memory';
      regressionPercent = memoryRegression;
      currentValue = dataPoint.memoryUsage;
      baselineValue = baseline.averageMemoryUsage;
    }

    if (regressionType) {
      const regression = this.createRegressionReport(
        testId,
        testName,
        regressionType,
        regressionPercent,
        currentValue,
        baselineValue,
        baseline.confidence,
      );

      this.regressions.push(regression);
      this.emit('regression:detected', regression);
    }
  }

  /**
   * 
   */
  private calculateRegression(currentValue: number, baselineValue: number): number {
    if (baselineValue === 0) return 0;
    return ((currentValue - baselineValue) / baselineValue) * 100;
  }

  /**
   * 
   */
  private createRegressionReport(
    testId: string,
    testName: string,
    type: PerformanceRegression['type'],
    regressionPercent: number,
    currentValue: number,
    baselineValue: number,
    confidence: number,
  ): PerformanceRegression {
    const severity = this.calculateSeverity(regressionPercent);
    const historicalData = this.performanceHistory.get(testId) || [];
    const trendAnalysis = this.options.enableTrendAnalysis
      ? this.analyzeTrend(historicalData)
      : { trend: 'stable' as const, slope: 0, correlation: 0, volatility: 0 };

    const possibleCauses = this.identifyPossibleCauses(type, regressionPercent, trendAnalysis);
    const recommendations = this.generateRecommendations(type, severity, trendAnalysis);

    return {
      testId,
      testName,
      type,
      severity,
      currentValue,
      baselineValue,
      regressionPercent,
      confidence,
      timestamp: new Date(),
      details: {
        historicalData: historicalData.slice(-50), // 50
        trendAnalysis,
        possibleCauses,
        recommendations,
      },
    };
  }

  /**
   * 
   */
  private calculateSeverity(regressionPercent: number): PerformanceRegression['severity'] {
    const absRegression = Math.abs(regressionPercent);

    if (absRegression >= 100) return 'critical';
    if (absRegression >= 50) return 'major';
    if (absRegression >= 30) return 'moderate';
    return 'minor';
  }

  /**
   * 
   */
  private analyzeTrend(data: PerformanceDataPoint[]): TrendAnalysis {
    if (data.length < 5) {
      return { trend: 'stable', slope: 0, correlation: 0, volatility: 0 };
    }

    // 
    const recentData = data.slice(-20);
    const n = recentData.length;

    // 
    const xValues = recentData.map((_, i) => i);
    const yValues = recentData.map(d => d.duration);

    const slope = this.calculateLinearRegressionSlope(xValues, yValues);
    const correlation = this.calculateCorrelation(xValues, yValues);

    // 
    const mean = yValues.reduce((sum, val) => sum + val, 0) / n;
    const variance = yValues.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / n;
    const volatility = Math.sqrt(variance) / mean; // 

    // 
    let trend: TrendAnalysis['trend'] = 'stable';
    if (Math.abs(slope) > mean * 0.01) { // 1%
      trend = slope > 0 ? 'degrading' : 'improving';
    }

    return { trend, slope, correlation, volatility };
  }

  /**
   * 
   */
  private calculateLinearRegressionSlope(x: number[], y: number[]): number {
    const n = x.length;
    const sumX = x.reduce((sum, val) => sum + val, 0);
    const sumY = y.reduce((sum, val) => sum + val, 0);
    const sumXY = x.reduce((sum, val, i) => sum + val * y[i], 0);
    const sumXX = x.reduce((sum, val) => sum + val * val, 0);

    const denominator = n * sumXX - sumX * sumX;
    if (denominator === 0) return 0;

    return (n * sumXY - sumX * sumY) / denominator;
  }

  /**
   * 
   */
  private calculateCorrelation(x: number[], y: number[]): number {
    const n = x.length;
    const sumX = x.reduce((sum, val) => sum + val, 0);
    const sumY = y.reduce((sum, val) => sum + val, 0);
    const sumXY = x.reduce((sum, val, i) => sum + val * y[i], 0);
    const sumXX = x.reduce((sum, val) => sum + val * val, 0);
    const sumYY = y.reduce((sum, val) => sum + val * val, 0);

    const numerator = n * sumXY - sumX * sumY;
    const denominator = Math.sqrt((n * sumXX - sumX * sumX) * (n * sumYY - sumY * sumY));

    if (denominator === 0) return 0;
    return numerator / denominator;
  }

  /**
   * 
   */
  private identifyPossibleCauses(
    type: PerformanceRegression['type'],
    regressionPercent: number,
    trendAnalysis: TrendAnalysis,
  ): string[] {
    const causes: string[] = [];

    // 
    if (type === 'duration' || type === 'both') {
      causes.push('Algorithm complexity increase');
      causes.push('Inefficient database queries');
      causes.push('Network latency issues');
      causes.push('Resource contention');
    }

    if (type === 'memory' || type === 'both') {
      causes.push('Memory leaks');
      causes.push('Inefficient data structures');
      causes.push('Large object allocations');
      causes.push('Garbage collection pressure');
    }

    // 
    if (trendAnalysis.trend === 'degrading') {
      causes.push('Gradual performance degradation');
      causes.push('Accumulating technical debt');
    }

    if (trendAnalysis.volatility > 0.3) {
      causes.push('Inconsistent test environment');
      causes.push('External dependencies variability');
    }

    // 
    if (regressionPercent > 50) {
      causes.push('Major code changes');
      causes.push('Configuration changes');
      causes.push('Infrastructure changes');
    }

    return causes;
  }

  /**
   * 
   */
  private generateRecommendations(
    type: PerformanceRegression['type'],
    severity: PerformanceRegression['severity'],
    trendAnalysis: TrendAnalysis,
  ): string[] {
    const recommendations: string[] = [];

    // 
    if (severity === 'critical') {
      recommendations.push('Immediate investigation required');
      recommendations.push('Consider rolling back recent changes');
    } else if (severity === 'major') {
      recommendations.push('High priority investigation needed');
      recommendations.push('Review recent code changes');
    }

    // 
    if (type === 'duration' || type === 'both') {
      recommendations.push('Profile code execution to identify bottlenecks');
      recommendations.push('Review algorithm complexity');
      recommendations.push('Check for inefficient database queries');
    }

    if (type === 'memory' || type === 'both') {
      recommendations.push('Analyze memory usage patterns');
      recommendations.push('Check for memory leaks');
      recommendations.push('Review object lifecycle management');
    }

    // 
    if (trendAnalysis.trend === 'degrading') {
      recommendations.push('Establish performance monitoring alerts');
      recommendations.push('Implement regular performance reviews');
    }

    if (trendAnalysis.volatility > 0.3) {
      recommendations.push('Stabilize test environment');
      recommendations.push('Mock external dependencies');
    }

    return recommendations;
  }

  /**
   * 
   */
  private updateBaseline(testId: string, testName: string): void {
    const history = this.performanceHistory.get(testId);
    if (!history || history.length < this.options.minSampleSize) {
      return;
    }

    const recentData = history.slice(-this.options.minSampleSize);
    const durations = recentData.map(d => d.duration);
    const memoryUsages = recentData.map(d => d.memoryUsage);

    const averageDuration = durations.reduce((sum, val) => sum + val, 0) / durations.length;
    const averageMemoryUsage = memoryUsages.reduce((sum, val) => sum + val, 0) / memoryUsages.length;

    // 
    const durationVariance = durations.reduce((sum, val) => sum + Math.pow(val - averageDuration, 2), 0) / durations.length;
    const standardDeviation = Math.sqrt(durationVariance);

    // 
    const coefficientOfVariation = standardDeviation / averageDuration;
    const confidence = Math.min(0.95, Math.max(0.5, 1 - coefficientOfVariation));

    const baseline: PerformanceBaseline = {
      testId,
      testName,
      averageDuration,
      averageMemoryUsage,
      standardDeviation,
      sampleCount: recentData.length,
      lastUpdated: new Date(),
      confidence,
    };

    this.baselines.set(testId, baseline);
    this.emit('baseline:updated', baseline);
  }

  /**
   * 
   */
  setBaseline(testId: string, testName: string, averageDuration: number, averageMemoryUsage: number): void {
    const baseline: PerformanceBaseline = {
      testId,
      testName,
      averageDuration,
      averageMemoryUsage,
      standardDeviation: 0,
      sampleCount: 1,
      lastUpdated: new Date(),
      confidence: 1.0,
    };

    this.baselines.set(testId, baseline);
    this.saveBaselines();
    this.emit('baseline:set', baseline);
  }

  /**
   * 
   */
  getBaseline(testId: string): PerformanceBaseline | null {
    return this.baselines.get(testId) || null;
  }

  /**
   * 
   */
  getAllBaselines(): PerformanceBaseline[] {
    return Array.from(this.baselines.values());
  }

  /**
   * 
   */
  getRegressions(timeWindow?: number): PerformanceRegression[] {
    if (!timeWindow) {
      return [...this.regressions];
    }

    const cutoffTime = new Date(Date.now() - timeWindow);
    return this.regressions.filter(r => r.timestamp > cutoffTime);
  }

  /**
   * 
   */
  getPerformanceHistory(testId: string): PerformanceDataPoint[] {
    return this.performanceHistory.get(testId) || [];
  }

  /**
   * 
   */
  private loadBaselines(): void {
    try {
      if (fs.existsSync(this.options.baselineFile)) {
        const data = fs.readFileSync(this.options.baselineFile, 'utf8');
        const baselines = JSON.parse(data);

        for (const baseline of baselines) {
          baseline.lastUpdated = new Date(baseline.lastUpdated);
          this.baselines.set(baseline.testId, baseline);
        }
      }
    } catch (error) {
      console.warn('Failed to load performance baselines:', error.message);
    }
  }

  /**
   * 
   */
  private saveBaselines(): void {
    try {
      const dir = path.dirname(this.options.baselineFile);
      if (!fs.existsSync(dir)) {
        fs.mkdirSync(dir, { recursive: true });
      }

      const baselines = Array.from(this.baselines.values());
      fs.writeFileSync(this.options.baselineFile, JSON.stringify(baselines, null, 2));
    } catch (error) {
      console.warn('Failed to save performance baselines:', error.message);
    }
  }

  /**
   * 
   */
  reset(): void {
    this.baselines.clear();
    this.performanceHistory.clear();
    this.regressions = [];
    this.emit('detector:reset');
  }

  /**
   * 
   */
  exportData(): {
    baselines: PerformanceBaseline[];
    regressions: PerformanceRegression[];
    historySize: number;
  } {
    return {
      baselines: this.getAllBaselines(),
      regressions: this.getRegressions(),
      historySize: Array.from(this.performanceHistory.values()).reduce((sum, history) => sum + history.length, 0),
    };
  }

  /**
   * 
   */
  destroy(): void {
    this.saveBaselines();
    this.reset();
    this.removeAllListeners();
  }
}

/**
 * Test Performance Monitoring Tools
 *
 * This module provides tools for monitoring and measuring performance
 * during test execution, including execution time, memory usage, and
 * performance regression detection.
 */

import { TestError, TestErrorType } from '../errors/TestError';

export interface PerformanceMetrics {
  executionTime: number;
  memoryUsage: {
    heapUsed: number;
    heapTotal: number;
    external: number;
    rss: number;
  };
  cpuUsage?: {
    user: number;
    system: number;
  };
  timestamp: Date;
}

export interface PerformanceBenchmark {
  name: string;
  baseline: PerformanceMetrics;
  threshold: {
    executionTime?: number; // percentage increase allowed
    memoryUsage?: number;   // percentage increase allowed
  };
}

export interface PerformanceReport {
  testName: string;
  metrics: PerformanceMetrics;
  benchmark?: PerformanceBenchmark;
  regression?: {
    type: 'execution_time' | 'memory_usage';
    severity: 'warning' | 'critical';
    message: string;
    actualIncrease: number;
    allowedIncrease: number;
  };
}

/**
 * Performance monitoring and measurement tools for tests
 */
export class PerformanceMonitor {
  private static instance: PerformanceMonitor;
  private benchmarks: Map<string, PerformanceBenchmark> = new Map();
  private measurements: Map<string, PerformanceMetrics[]> = new Map();
  private activeTimers: Map<string, { startTime: number; startMemory: NodeJS.MemoryUsage }> = new Map();

  static getInstance(): PerformanceMonitor {
    if (!PerformanceMonitor.instance) {
      PerformanceMonitor.instance = new PerformanceMonitor();
    }
    return PerformanceMonitor.instance;
  }

  /**
   * Start measuring performance for a test
   */
  startMeasurement(testName: string): void {
    const startTime = performance.now();
    const startMemory = process.memoryUsage();

    this.activeTimers.set(testName, { startTime, startMemory });
  }

  /**
   * Stop measuring performance and return metrics
   */
  stopMeasurement(testName: string): PerformanceMetrics {
    const timer = this.activeTimers.get(testName);
    if (!timer) {
      throw new TestError(
        TestErrorType.ASSERTION_FAILED,
        `No active measurement found for test: ${testName}`,
      );
    }

    const endTime = performance.now();
    const endMemory = process.memoryUsage();
    const executionTime = endTime - timer.startTime;

    const metrics: PerformanceMetrics = {
      executionTime,
      memoryUsage: {
        heapUsed: endMemory.heapUsed - timer.startMemory.heapUsed,
        heapTotal: endMemory.heapTotal - timer.startMemory.heapTotal,
        external: endMemory.external - timer.startMemory.external,
        rss: endMemory.rss - timer.startMemory.rss,
      },
      timestamp: new Date(),
    };

    // Store measurement
    const testMeasurements = this.measurements.get(testName) || [];
    testMeasurements.push(metrics);
    this.measurements.set(testName, testMeasurements);

    this.activeTimers.delete(testName);
    return metrics;
  }

  /**
   * Measure the performance of a function
   */
  async measureFunction<T>(
    testName: string,
    fn: () => Promise<T> | T,
  ): Promise<{ result: T; metrics: PerformanceMetrics }> {
    this.startMeasurement(testName);

    try {
      const result = await fn();
      const metrics = this.stopMeasurement(testName);
      return { result, metrics };
    } catch (error) {
      // Clean up timer if function throws
      this.activeTimers.delete(testName);
      throw error;
    }
  }

  /**
   * Set a performance benchmark for a test
   */
  setBenchmark(benchmark: PerformanceBenchmark): void {
    this.benchmarks.set(benchmark.name, benchmark);
  }

  /**
   * Get performance benchmark for a test
   */
  getBenchmark(testName: string): PerformanceBenchmark | undefined {
    return this.benchmarks.get(testName);
  }

  /**
   * Validate performance against benchmark
   */
  validatePerformance(testName: string, metrics: PerformanceMetrics): PerformanceReport {
    const benchmark = this.benchmarks.get(testName);
    const report: PerformanceReport = {
      testName,
      metrics,
      benchmark,
    };

    if (benchmark) {
      const regression = this.detectRegression(metrics, benchmark);
      if (regression) {
        report.regression = regression;
      }
    }

    return report;
  }

  /**
   * Assert that performance is within acceptable limits
   */
  assertPerformanceWithinLimits(
    testName: string,
    metrics: PerformanceMetrics,
    limits: {
      maxExecutionTime?: number;
      maxMemoryUsage?: number;
    },
  ): void {
    if (limits.maxExecutionTime && metrics.executionTime > limits.maxExecutionTime) {
      throw new TestError(
        TestErrorType.ASSERTION_FAILED,
        `Test "${testName}" execution time ${metrics.executionTime}ms exceeds limit of ${limits.maxExecutionTime}ms`,
      );
    }

    if (limits.maxMemoryUsage && metrics.memoryUsage.heapUsed > limits.maxMemoryUsage) {
      throw new TestError(
        TestErrorType.ASSERTION_FAILED,
        `Test "${testName}" memory usage ${metrics.memoryUsage.heapUsed} bytes exceeds limit of ${limits.maxMemoryUsage} bytes`,
      );
    }
  }

  /**
   * Get performance statistics for a test
   */
  getPerformanceStats(testName: string): {
    count: number;
    average: PerformanceMetrics;
    min: PerformanceMetrics;
    max: PerformanceMetrics;
    trend: 'improving' | 'degrading' | 'stable';
  } | null {
    const measurements = this.measurements.get(testName);
    if (!measurements || measurements.length === 0) {
      return null;
    }

    const count = measurements.length;
    const executionTimes = measurements.map(m => m.executionTime);
    const memoryUsages = measurements.map(m => m.memoryUsage.heapUsed);

    const average: PerformanceMetrics = {
      executionTime: executionTimes.reduce((a, b) => a + b, 0) / count,
      memoryUsage: {
        heapUsed: memoryUsages.reduce((a, b) => a + b, 0) / count,
        heapTotal: measurements.reduce((a, b) => a + b.memoryUsage.heapTotal, 0) / count,
        external: measurements.reduce((a, b) => a + b.memoryUsage.external, 0) / count,
        rss: measurements.reduce((a, b) => a + b.memoryUsage.rss, 0) / count,
      },
      timestamp: new Date(),
    };

    const minExecTime = Math.min(...executionTimes);
    const maxExecTime = Math.max(...executionTimes);
    const minMemory = Math.min(...memoryUsages);
    const maxMemory = Math.max(...memoryUsages);

    const min: PerformanceMetrics = {
      executionTime: minExecTime,
      memoryUsage: {
        heapUsed: minMemory,
        heapTotal: 0,
        external: 0,
        rss: 0,
      },
      timestamp: new Date(),
    };

    const max: PerformanceMetrics = {
      executionTime: maxExecTime,
      memoryUsage: {
        heapUsed: maxMemory,
        heapTotal: 0,
        external: 0,
        rss: 0,
      },
      timestamp: new Date(),
    };

    const trend = this.calculateTrend(measurements);

    return { count, average, min, max, trend };
  }

  /**
   * Clear all measurements for a test
   */
  clearMeasurements(testName?: string): void {
    if (testName) {
      this.measurements.delete(testName);
    } else {
      this.measurements.clear();
    }
  }

  /**
   * Export performance data
   */
  exportData(): {
    benchmarks: Record<string, PerformanceBenchmark>;
    measurements: Record<string, PerformanceMetrics[]>;
  } {
    return {
      benchmarks: Object.fromEntries(this.benchmarks),
      measurements: Object.fromEntries(this.measurements),
    };
  }

  /**
   * Import performance data
   */
  importData(data: {
    benchmarks?: Record<string, PerformanceBenchmark>;
    measurements?: Record<string, PerformanceMetrics[]>;
  }): void {
    if (data.benchmarks) {
      for (const [name, benchmark] of Object.entries(data.benchmarks)) {
        this.benchmarks.set(name, benchmark);
      }
    }

    if (data.measurements) {
      for (const [name, measurements] of Object.entries(data.measurements)) {
        this.measurements.set(name, measurements);
      }
    }
  }

  // Private helper methods
  private detectRegression(
    metrics: PerformanceMetrics,
    benchmark: PerformanceBenchmark,
  ): PerformanceReport['regression'] | null {
    const { threshold } = benchmark;
    const baseline = benchmark.baseline;

    // Check execution time regression
    if (threshold.executionTime) {
      const increase = ((metrics.executionTime - baseline.executionTime) / baseline.executionTime) * 100;
      if (increase > threshold.executionTime) {
        return {
          type: 'execution_time',
          severity: increase > threshold.executionTime * 2 ? 'critical' : 'warning',
          message: `Execution time increased by ${increase.toFixed(2)}%`,
          actualIncrease: increase,
          allowedIncrease: threshold.executionTime,
        };
      }
    }

    // Check memory usage regression
    if (threshold.memoryUsage) {
      const increase = ((metrics.memoryUsage.heapUsed - baseline.memoryUsage.heapUsed) / baseline.memoryUsage.heapUsed) * 100;
      if (increase > threshold.memoryUsage) {
        return {
          type: 'memory_usage',
          severity: increase > threshold.memoryUsage * 2 ? 'critical' : 'warning',
          message: `Memory usage increased by ${increase.toFixed(2)}%`,
          actualIncrease: increase,
          allowedIncrease: threshold.memoryUsage,
        };
      }
    }

    return null;
  }

  private calculateTrend(measurements: PerformanceMetrics[]): 'improving' | 'degrading' | 'stable' {
    if (measurements.length < 3) return 'stable';

    const recent = measurements.slice(-5); // Last 5 measurements
    const older = measurements.slice(-10, -5); // Previous 5 measurements

    if (older.length === 0) return 'stable';

    const recentAvg = recent.reduce((sum, m) => sum + m.executionTime, 0) / recent.length;
    const olderAvg = older.reduce((sum, m) => sum + m.executionTime, 0) / older.length;

    const change = ((recentAvg - olderAvg) / olderAvg) * 100;

    if (change < -5) return 'improving';
    if (change > 5) return 'degrading';
    return 'stable';
  }
}

/**
 * Decorator for measuring function performance
 */
export function measurePerformance(testName?: string) {
  return function (target: any, propertyName: string, descriptor: PropertyDescriptor) {
    const method = descriptor.value;
    const monitor = PerformanceMonitor.getInstance();

    descriptor.value = async function (...args: any[]) {
      const name = testName || `${target.constructor.name}.${propertyName}`;
      const { result } = await monitor.measureFunction(name, () => method.apply(this, args));
      return result;
    };
  };
}

/**
 * Helper function to create performance benchmarks
 */
export function createPerformanceBenchmark(
  name: string,
  baselineMetrics: PerformanceMetrics,
  thresholds: {
    executionTime?: number;
    memoryUsage?: number;
  },
): PerformanceBenchmark {
  return {
    name,
    baseline: baselineMetrics,
    threshold: thresholds,
  };
}

/**
 * Authorization Security Tester
 *
 * 
 * 
 */

export interface AuthorizationConfig {
  enableRoleBasedTests: boolean;
  enableResourceAccessTests: boolean;
  enablePrivilegeEscalationTests: boolean;
  enableSessionTests: boolean;
  enableTokenTests: boolean;
  customRoles: Role[];
  customResources: Resource[];
  testScenarios: AuthTestScenario[];
}

export interface Role {
  name: string;
  permissions: string[];
  level: number;
  description: string;
}

export interface Resource {
  id: string;
  type: string;
  owner?: string;
  visibility: 'public' | 'private' | 'restricted';
  requiredPermissions: string[];
  requiredRoles: string[];
}

export interface AuthTestScenario {
  name: string;
  description: string;
  user: TestUser;
  resource: Resource;
  action: string;
  expectedResult: 'allow' | 'deny';
  testType: 'role_based' | 'resource_access' | 'privilege_escalation' | 'session' | 'token';
}

export interface TestUser {
  id: string;
  username: string;
  roles: string[];
  permissions: string[];
  sessionId?: string;
  token?: string;
  attributes?: Record<string, any>;
}

export interface AuthorizationTestResult {
  scenario: AuthTestScenario;
  actualResult: 'allow' | 'deny' | 'error';
  passed: boolean;
  executionTime: number;
  errorMessage?: string;
  securityRisk: 'none' | 'low' | 'medium' | 'high' | 'critical';
  details: AuthTestDetails;
}

export interface AuthTestDetails {
  requestedAction: string;
  userContext: TestUser;
  resourceContext: Resource;
  authorizationDecision: string;
  reasonCode?: string;
  additionalInfo?: Record<string, any>;
}

export class AuthorizationTester {
  private config: AuthorizationConfig;
  private defaultScenarios: AuthTestScenario[];

  constructor(config: AuthorizationConfig) {
    this.config = config;
    this.defaultScenarios = this.generateDefaultScenarios();
  }

  /**
   * 
   */
  async runAuthorizationTests(authService: AuthorizationService): Promise<AuthorizationTestResult[]> {
    const results: AuthorizationTestResult[] = [];
    const allScenarios = [...this.defaultScenarios, ...this.config.testScenarios];

    for (const scenario of allScenarios) {
      if (this.shouldRunScenario(scenario)) {
        const result = await this.testScenario(scenario, authService);
        results.push(result);
      }
    }

    return results;
  }

  /**
   * 
   */
  private async testScenario(scenario: AuthTestScenario, authService: AuthorizationService): Promise<AuthorizationTestResult> {
    const startTime = Date.now();

    try {
      const authResult = await authService.authorize(scenario.user, scenario.resource, scenario.action);
      const executionTime = Date.now() - startTime;

      const actualResult = authResult.allowed ? 'allow' : 'deny';
      const passed = actualResult === scenario.expectedResult;
      const securityRisk = this.assessSecurityRisk(scenario, actualResult, passed);

      return {
        scenario,
        actualResult,
        passed,
        executionTime,
        securityRisk,
        details: {
          requestedAction: scenario.action,
          userContext: scenario.user,
          resourceContext: scenario.resource,
          authorizationDecision: authResult.decision,
          reasonCode: authResult.reasonCode,
          additionalInfo: authResult.additionalInfo,
        },
      };
    } catch (error) {
      return {
        scenario,
        actualResult: 'error',
        passed: false,
        executionTime: Date.now() - startTime,
        errorMessage: error instanceof Error ? error.message : String(error),
        securityRisk: 'high',
        details: {
          requestedAction: scenario.action,
          userContext: scenario.user,
          resourceContext: scenario.resource,
          authorizationDecision: 'error',
        },
      };
    }
  }

  /**
   * 
   */
  private generateDefaultScenarios(): AuthTestScenario[] {
    const scenarios: AuthTestScenario[] = [];

    // 
    if (this.config.enableRoleBasedTests) {
      scenarios.push(
        {
          name: 'Admin Full Access',
          description: '',
          user: { id: 'admin1', username: 'admin', roles: ['admin'], permissions: ['*'] },
          resource: { id: 'res1', type: 'document', visibility: 'private', requiredPermissions: ['read'], requiredRoles: ['user'] },
          action: 'read',
          expectedResult: 'allow',
          testType: 'role_based',
        },
        {
          name: 'User Limited Access',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read', 'write'] },
          resource: { id: 'res1', type: 'document', visibility: 'private', owner: 'user1', requiredPermissions: ['read'], requiredRoles: ['user'] },
          action: 'read',
          expectedResult: 'allow',
          testType: 'role_based',
        },
        {
          name: 'Unauthorized User Access',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read'] },
          resource: { id: 'res2', type: 'document', visibility: 'private', owner: 'user2', requiredPermissions: ['read'], requiredRoles: ['user'] },
          action: 'read',
          expectedResult: 'deny',
          testType: 'role_based',
        },
      );
    }

    // 
    if (this.config.enableResourceAccessTests) {
      scenarios.push(
        {
          name: 'Public Resource Access',
          description: '',
          user: { id: 'guest1', username: 'guest', roles: ['guest'], permissions: [] },
          resource: { id: 'pub1', type: 'document', visibility: 'public', requiredPermissions: [], requiredRoles: [] },
          action: 'read',
          expectedResult: 'allow',
          testType: 'resource_access',
        },
        {
          name: 'Restricted Resource Access',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read'] },
          resource: { id: 'rest1', type: 'document', visibility: 'restricted', requiredPermissions: ['read'], requiredRoles: ['premium'] },
          action: 'read',
          expectedResult: 'deny',
          testType: 'resource_access',
        },
        {
          name: 'Owner Resource Access',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read', 'write'] },
          resource: { id: 'priv1', type: 'document', visibility: 'private', owner: 'user1', requiredPermissions: ['read'], requiredRoles: ['user'] },
          action: 'write',
          expectedResult: 'allow',
          testType: 'resource_access',
        },
      );
    }

    // 
    if (this.config.enablePrivilegeEscalationTests) {
      scenarios.push(
        {
          name: 'Horizontal Privilege Escalation',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read', 'write'] },
          resource: { id: 'priv2', type: 'document', visibility: 'private', owner: 'user2', requiredPermissions: ['read'], requiredRoles: ['user'] },
          action: 'read',
          expectedResult: 'deny',
          testType: 'privilege_escalation',
        },
        {
          name: 'Vertical Privilege Escalation',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read', 'write'] },
          resource: { id: 'admin1', type: 'system', visibility: 'restricted', requiredPermissions: ['admin'], requiredRoles: ['admin'] },
          action: 'delete',
          expectedResult: 'deny',
          testType: 'privilege_escalation',
        },
        {
          name: 'Role Manipulation Attempt',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read', 'write'] },
          resource: { id: 'role1', type: 'role', visibility: 'restricted', requiredPermissions: ['admin'], requiredRoles: ['admin'] },
          action: 'update',
          expectedResult: 'deny',
          testType: 'privilege_escalation',
        },
      );
    }

    // 
    if (this.config.enableSessionTests) {
      scenarios.push(
        {
          name: 'Valid Session Access',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read'], sessionId: 'valid_session_123' },
          resource: { id: 'res1', type: 'document', visibility: 'private', owner: 'user1', requiredPermissions: ['read'], requiredRoles: ['user'] },
          action: 'read',
          expectedResult: 'allow',
          testType: 'session',
        },
        {
          name: 'Invalid Session Access',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read'], sessionId: 'invalid_session_456' },
          resource: { id: 'res1', type: 'document', visibility: 'private', owner: 'user1', requiredPermissions: ['read'], requiredRoles: ['user'] },
          action: 'read',
          expectedResult: 'deny',
          testType: 'session',
        },
        {
          name: 'Expired Session Access',
          description: '',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read'], sessionId: 'expired_session_789' },
          resource: { id: 'res1', type: 'document', visibility: 'private', owner: 'user1', requiredPermissions: ['read'], requiredRoles: ['user'] },
          action: 'read',
          expectedResult: 'deny',
          testType: 'session',
        },
      );
    }

    // Token
    if (this.config.enableTokenTests) {
      scenarios.push(
        {
          name: 'Valid Token Access',
          description: 'token',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read'], token: 'valid_jwt_token' },
          resource: { id: 'api1', type: 'api', visibility: 'restricted', requiredPermissions: ['api_access'], requiredRoles: ['user'] },
          action: 'call',
          expectedResult: 'allow',
          testType: 'token',
        },
        {
          name: 'Invalid Token Access',
          description: 'token',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read'], token: 'invalid_jwt_token' },
          resource: { id: 'api1', type: 'api', visibility: 'restricted', requiredPermissions: ['api_access'], requiredRoles: ['user'] },
          action: 'call',
          expectedResult: 'deny',
          testType: 'token',
        },
        {
          name: 'Tampered Token Access',
          description: 'token',
          user: { id: 'user1', username: 'user1', roles: ['user'], permissions: ['read'], token: 'tampered_jwt_token' },
          resource: { id: 'api1', type: 'api', visibility: 'restricted', requiredPermissions: ['api_access'], requiredRoles: ['user'] },
          action: 'call',
          expectedResult: 'deny',
          testType: 'token',
        },
      );
    }

    return scenarios;
  }

  /**
   * 
   */
  private shouldRunScenario(scenario: AuthTestScenario): boolean {
    switch (scenario.testType) {
      case 'role_based':
        return this.config.enableRoleBasedTests;
      case 'resource_access':
        return this.config.enableResourceAccessTests;
      case 'privilege_escalation':
        return this.config.enablePrivilegeEscalationTests;
      case 'session':
        return this.config.enableSessionTests;
      case 'token':
        return this.config.enableTokenTests;
      default:
        return true;
    }
  }

  /**
   * 
   */
  private assessSecurityRisk(scenario: AuthTestScenario, actualResult: string, passed: boolean): 'none' | 'low' | 'medium' | 'high' | 'critical' {
    if (passed) {
      return 'none';
    }

    // 
    if (scenario.expectedResult === 'deny' && actualResult === 'allow') {
      //  - 
      switch (scenario.testType) {
        case 'privilege_escalation':
          return 'critical';
        case 'resource_access':
          return scenario.resource.visibility === 'private' ? 'high' : 'medium';
        case 'role_based':
          return 'high';
        case 'session':
        case 'token':
          return 'medium';
        default:
          return 'medium';
      }
    } else if (scenario.expectedResult === 'allow' && actualResult === 'deny') {
      //  - 
      return 'low';
    }

    return 'medium';
  }

  /**
   * 
   */
  generateAuthorizationReport(results: AuthorizationTestResult[]): AuthorizationReport {
    const totalTests = results.length;
    const passedTests = results.filter(r => r.passed).length;
    const failedTests = results.filter(r => !r.passed).length;

    const criticalRisks = results.filter(r => r.securityRisk === 'critical').length;
    const highRisks = results.filter(r => r.securityRisk === 'high').length;
    const mediumRisks = results.filter(r => r.securityRisk === 'medium').length;
    const lowRisks = results.filter(r => r.securityRisk === 'low').length;

    const averageExecutionTime = results.reduce((sum, r) => sum + r.executionTime, 0) / totalTests;

    const testTypeResults = this.groupResultsByTestType(results);

    return {
      summary: {
        totalTests,
        passedTests,
        failedTests,
        passRate: (passedTests / totalTests) * 100,
        averageExecutionTime,
      },
      riskDistribution: {
        critical: criticalRisks,
        high: highRisks,
        medium: mediumRisks,
        low: lowRisks,
        none: totalTests - criticalRisks - highRisks - mediumRisks - lowRisks,
      },
      testTypeResults,
      testResults: results,
      securityIssues: this.identifySecurityIssues(results),
      recommendations: this.generateRecommendations(results),
      timestamp: new Date(),
    };
  }

  /**
   * 
   */
  private groupResultsByTestType(results: AuthorizationTestResult[]): Record<string, { passed: number; failed: number; total: number }> {
    const grouped: Record<string, { passed: number; failed: number; total: number }> = {};

    for (const result of results) {
      const testType = result.scenario.testType;
      if (!grouped[testType]) {
        grouped[testType] = { passed: 0, failed: 0, total: 0 };
      }

      grouped[testType].total++;
      if (result.passed) {
        grouped[testType].passed++;
      } else {
        grouped[testType].failed++;
      }
    }

    return grouped;
  }

  /**
   * 
   */
  private identifySecurityIssues(results: AuthorizationTestResult[]): SecurityIssue[] {
    const issues: SecurityIssue[] = [];
    const failedResults = results.filter(r => !r.passed && r.securityRisk !== 'none');

    for (const result of failedResults) {
      issues.push({
        type: result.scenario.testType,
        severity: result.securityRisk,
        description: `${result.scenario.name}: ${result.scenario.description}`,
        impact: this.getImpactForTestType(result.scenario.testType),
        recommendation: this.getRecommendationForTestType(result.scenario.testType),
        affectedResource: result.scenario.resource.id,
        testScenario: result.scenario.name,
      });
    }

    return issues;
  }

  /**
   * 
   */
  private generateRecommendations(results: AuthorizationTestResult[]): string[] {
    const recommendations: string[] = [];
    const failedResults = results.filter(r => !r.passed);

    if (failedResults.some(r => r.scenario.testType === 'privilege_escalation')) {
      recommendations.push('');
    }

    if (failedResults.some(r => r.scenario.testType === 'role_based')) {
      recommendations.push('');
    }

    if (failedResults.some(r => r.scenario.testType === 'resource_access')) {
      recommendations.push('');
    }

    if (failedResults.some(r => r.scenario.testType === 'session')) {
      recommendations.push('');
    }

    if (failedResults.some(r => r.scenario.testType === 'token')) {
      recommendations.push('token');
    }

    if (recommendations.length === 0) {
      recommendations.push('');
    }

    return recommendations;
  }

  private getImpactForTestType(testType: string): string {
    const impacts = {
      privilege_escalation: '',
      role_based: '',
      resource_access: '',
      session: '',
      token: 'Token',
    };
    return impacts[testType as keyof typeof impacts] || '';
  }

  private getRecommendationForTestType(testType: string): string {
    const recommendations = {
      privilege_escalation: '',
      role_based: '',
      resource_access: '',
      session: '',
      token: 'token',
    };
    return recommendations[testType as keyof typeof recommendations] || '';
  }
}

