export interface AuthorizationService {
  authorize(user: TestUser, resource: Resource, action: string): Promise<AuthorizationResult>;
}

export interface AuthorizationResult {
  allowed: boolean;
  decision: string;
  reasonCode?: string;
  additionalInfo?: Record<string, any>;
}

export interface SecurityIssue {
  type: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  description: string;
  impact: string;
  recommendation: string;
  affectedResource: string;
  testScenario: string;
}

export interface AuthorizationReport {
  summary: {
    totalTests: number;
    passedTests: number;
    failedTests: number;
    passRate: number;
    averageExecutionTime: number;
  };
  riskDistribution: {
    critical: number;
    high: number;
    medium: number;
    low: number;
    none: number;
  };
  testTypeResults: Record<string, { passed: number; failed: number; total: number }>;
  testResults: AuthorizationTestResult[];
  securityIssues: SecurityIssue[];
  recommendations: string[];
  timestamp: Date;
}

/**
 * Encryption Validation Tester
 *
 * 
 * 
 */
import crypto from 'crypto';

export interface EncryptionConfig {
  enableAlgorithmTests: boolean;
  enableKeyManagementTests: boolean;
  enableDataIntegrityTests: boolean;
  enablePerformanceTests: boolean;
  supportedAlgorithms: EncryptionAlgorithm[];
  keyStrengthRequirements: KeyStrengthConfig;
  testDataSets: TestDataSet[];
}

export interface EncryptionAlgorithm {
  name: string;
  type: 'symmetric' | 'asymmetric' | 'hash';
  keySize: number;
  blockSize?: number;
  mode?: string;
  padding?: string;
  description: string;
}

export interface KeyStrengthConfig {
  minimumSymmetricKeySize: number;
  minimumAsymmetricKeySize: number;
  requiredRandomness: number;
  keyRotationInterval: number;
}

export interface TestDataSet {
  name: string;
  data: string;
  size: number;
  type: 'text' | 'binary' | 'json' | 'xml';
  sensitivityLevel: 'low' | 'medium' | 'high' | 'critical';
}

export interface EncryptionTestResult {
  testName: string;
  algorithm: EncryptionAlgorithm;
  testType: 'algorithm' | 'key_management' | 'data_integrity' | 'performance';
  status: 'passed' | 'failed' | 'warning' | 'error';
  executionTime: number;
  details: EncryptionTestDetails;
  securityLevel: 'weak' | 'acceptable' | 'strong' | 'excellent';
  issues: EncryptionIssue[];
}

export interface EncryptionTestDetails {
  originalData: string;
  encryptedData?: string;
  decryptedData?: string;
  keyInfo: KeyInfo;
  performanceMetrics?: PerformanceMetrics;
  integrityCheck?: IntegrityCheckResult;
}

export interface KeyInfo {
  algorithm: string;
  keySize: number;
  keyStrength: number;
  generationMethod: string;
  isSecure: boolean;
}

export interface PerformanceMetrics {
  encryptionTime: number;
  decryptionTime: number;
  throughput: number;
  memoryUsage: number;
}

export interface IntegrityCheckResult {
  hashMatch: boolean;
  dataCorruption: boolean;
  tamperingDetected: boolean;
  checksumValid: boolean;
}

export interface EncryptionIssue {
  type: 'weak_key' | 'weak_algorithm' | 'poor_performance' | 'integrity_failure' | 'implementation_error';
  severity: 'low' | 'medium' | 'high' | 'critical';
  description: string;
  recommendation: string;
}

export class EncryptionValidator {
  private config: EncryptionConfig;

  constructor(config: EncryptionConfig) {
    this.config = config;
  }

  /**
   * 
   */
  async runEncryptionTests(encryptionService: EncryptionService): Promise<EncryptionTestResult[]> {
    const results: EncryptionTestResult[] = [];

    // 
    if (this.config.enableAlgorithmTests) {
      const algorithmResults = await this.runAlgorithmTests(encryptionService);
      results.push(...algorithmResults);
    }

    // 
    if (this.config.enableKeyManagementTests) {
      const keyManagementResults = await this.runKeyManagementTests(encryptionService);
      results.push(...keyManagementResults);
    }

    // 
    if (this.config.enableDataIntegrityTests) {
      const integrityResults = await this.runDataIntegrityTests(encryptionService);
      results.push(...integrityResults);
    }

    // 
    if (this.config.enablePerformanceTests) {
      const performanceResults = await this.runPerformanceTests(encryptionService);
      results.push(...performanceResults);
    }

    return results;
  }

  /**
   * 
   */
  private async runAlgorithmTests(encryptionService: EncryptionService): Promise<EncryptionTestResult[]> {
    const results: EncryptionTestResult[] = [];

    for (const algorithm of this.config.supportedAlgorithms) {
      for (const testData of this.config.testDataSets) {
        const result = await this.testAlgorithm(algorithm, testData, encryptionService);
        results.push(result);
      }
    }

    return results;
  }

  /**
   * 
   */
  private async testAlgorithm(algorithm: EncryptionAlgorithm, testData: TestDataSet, encryptionService: EncryptionService): Promise<EncryptionTestResult> {
    const startTime = Date.now();
    const issues: EncryptionIssue[] = [];

    try {
      // 
      const keyInfo = await this.generateTestKey(algorithm);

      // 
      const encryptedData = await encryptionService.encrypt(testData.data, algorithm.name, keyInfo);

      // 
      const decryptedData = await encryptionService.decrypt(encryptedData, algorithm.name, keyInfo);

      // 
      const encryptionWorked = encryptedData !== testData.data;
      const decryptionWorked = decryptedData === testData.data;

      const executionTime = Date.now() - startTime;

      // 
      const securityLevel = this.assessAlgorithmSecurity(algorithm);
      if (securityLevel === 'weak') {
        issues.push({
          type: 'weak_algorithm',
          severity: 'high',
          description: ` ${algorithm.name} `,
          recommendation: '',
        });
      }

      // 
      if (!keyInfo.isSecure) {
        issues.push({
          type: 'weak_key',
          severity: 'critical',
          description: '',
          recommendation: '',
        });
      }

      const status = encryptionWorked && decryptionWorked && issues.length === 0 ? 'passed' :
                    issues.some(i => i.severity === 'critical') ? 'failed' : 'warning';

      return {
        testName: `Algorithm Test - ${algorithm.name} with ${testData.name}`,
        algorithm,
        testType: 'algorithm',
        status,
        executionTime,
        details: {
          originalData: testData.data,
          encryptedData,
          decryptedData,
          keyInfo,
        },
        securityLevel,
        issues,
      };
    } catch (error) {
      issues.push({
        type: 'implementation_error',
        severity: 'critical',
        description: `: ${error}`,
        recommendation: '',
      });

      return {
        testName: `Algorithm Test - ${algorithm.name} with ${testData.name}`,
        algorithm,
        testType: 'algorithm',
        status: 'error',
        executionTime: Date.now() - startTime,
        details: {
          originalData: testData.data,
          keyInfo: { algorithm: algorithm.name, keySize: 0, keyStrength: 0, generationMethod: 'unknown', isSecure: false },
        },
        securityLevel: 'weak',
        issues,
      };
    }
  }

  /**
   * 
   */
  private async runKeyManagementTests(encryptionService: EncryptionService): Promise<EncryptionTestResult[]> {
    const results: EncryptionTestResult[] = [];

    // 
    results.push(await this.testKeyGeneration(encryptionService));

    // 
    results.push(await this.testKeyStrength(encryptionService));

    // 
    results.push(await this.testKeyRotation(encryptionService));

    // 
    results.push(await this.testKeyStorage(encryptionService));

    return results;
  }

  /**
   * 
   */
  private async testKeyGeneration(encryptionService: EncryptionService): Promise<EncryptionTestResult> {
    const startTime = Date.now();
    const issues: EncryptionIssue[] = [];

    try {
      const keys: string[] = [];

      // 
      for (let i = 0; i < 10; i++) {
        const key = await encryptionService.generateKey('AES-256');
        keys.push(key);
      }

      // 
      const uniqueKeys = new Set(keys);
      if (uniqueKeys.size !== keys.length) {
        issues.push({
          type: 'weak_key',
          severity: 'critical',
          description: '',
          recommendation: '',
        });
      }

      // 
      const keyLength = Buffer.from(keys[0], 'base64').length * 8;
      if (keyLength < this.config.keyStrengthRequirements.minimumSymmetricKeySize) {
        issues.push({
          type: 'weak_key',
          severity: 'high',
          description: ` ${keyLength}  ${this.config.keyStrengthRequirements.minimumSymmetricKeySize} `,
          recommendation: '',
        });
      }

      return {
        testName: 'Key Generation Test',
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Key generation test' },
        testType: 'key_management',
        status: issues.length === 0 ? 'passed' : issues.some(i => i.severity === 'critical') ? 'failed' : 'warning',
        executionTime: Date.now() - startTime,
        details: {
          originalData: 'key generation test',
          keyInfo: {
            algorithm: 'AES-256',
            keySize: keyLength,
            keyStrength: this.calculateKeyStrength(keys[0]),
            generationMethod: 'crypto.randomBytes',
            isSecure: keyLength >= this.config.keyStrengthRequirements.minimumSymmetricKeySize,
          },
        },
        securityLevel: this.assessKeySecurityLevel(keyLength),
        issues,
      };
    } catch (error) {
      return {
        testName: 'Key Generation Test',
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Key generation test' },
        testType: 'key_management',
        status: 'error',
        executionTime: Date.now() - startTime,
        details: {
          originalData: 'key generation test',
          keyInfo: { algorithm: 'AES-256', keySize: 0, keyStrength: 0, generationMethod: 'unknown', isSecure: false },
        },
        securityLevel: 'weak',
        issues: [{
          type: 'implementation_error',
          severity: 'critical',
          description: `: ${error}`,
          recommendation: '',
        }],
      };
    }
  }

  /**
   * 
   */
  private async testKeyStrength(encryptionService: EncryptionService): Promise<EncryptionTestResult> {
    const startTime = Date.now();
    const issues: EncryptionIssue[] = [];

    try {
      // 
      const weakKeys = ['password', '123456', 'admin', 'qwerty'];

      for (const weakKey of weakKeys) {
        const strength = this.calculateKeyStrength(weakKey);
        if (strength < this.config.keyStrengthRequirements.requiredRandomness) {
          issues.push({
            type: 'weak_key',
            severity: 'critical',
            description: `: ${weakKey}`,
            recommendation: '',
          });
        }
      }

      // 
      const strongKey = await encryptionService.generateKey('AES-256');
      const strongKeyStrength = this.calculateKeyStrength(strongKey);

      return {
        testName: 'Key Strength Test',
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Key strength test' },
        testType: 'key_management',
        status: issues.length === 0 ? 'passed' : 'failed',
        executionTime: Date.now() - startTime,
        details: {
          originalData: 'key strength test',
          keyInfo: {
            algorithm: 'AES-256',
            keySize: 256,
            keyStrength: strongKeyStrength,
            generationMethod: 'secure random',
            isSecure: strongKeyStrength >= this.config.keyStrengthRequirements.requiredRandomness,
          },
        },
        securityLevel: strongKeyStrength >= this.config.keyStrengthRequirements.requiredRandomness ? 'strong' : 'weak',
        issues,
      };
    } catch (error) {
      return {
        testName: 'Key Strength Test',
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Key strength test' },
        testType: 'key_management',
        status: 'error',
        executionTime: Date.now() - startTime,
        details: {
          originalData: 'key strength test',
          keyInfo: { algorithm: 'AES-256', keySize: 0, keyStrength: 0, generationMethod: 'unknown', isSecure: false },
        },
        securityLevel: 'weak',
        issues: [{
          type: 'implementation_error',
          severity: 'critical',
          description: `: ${error}`,
          recommendation: '',
        }],
      };
    }
  }

  /**
   * 
   */
  private async testKeyRotation(encryptionService: EncryptionService): Promise<EncryptionTestResult> {
    const startTime = Date.now();
    const issues: EncryptionIssue[] = [];

    try {
      const testData = 'key rotation test data';

      // 
      const oldKey = await encryptionService.generateKey('AES-256');
      const encryptedWithOldKey = await encryptionService.encrypt(testData, 'AES-256', { key: oldKey });

      // 
      const newKey = await encryptionService.generateKey('AES-256');

      // 
      const encryptedWithNewKey = await encryptionService.encrypt(testData, 'AES-256', { key: newKey });

      // 
      const decryptedWithOldKey = await encryptionService.decrypt(encryptedWithOldKey, 'AES-256', { key: oldKey });

      // 
      const decryptedWithNewKey = await encryptionService.decrypt(encryptedWithNewKey, 'AES-256', { key: newKey });

      const rotationWorked = decryptedWithOldKey === testData && decryptedWithNewKey === testData;

      if (!rotationWorked) {
        issues.push({
          type: 'implementation_error',
          severity: 'high',
          description: '',
          recommendation: '',
        });
      }

      return {
        testName: 'Key Rotation Test',
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Key rotation test' },
        testType: 'key_management',
        status: rotationWorked ? 'passed' : 'failed',
        executionTime: Date.now() - startTime,
        details: {
          originalData: testData,
          encryptedData: encryptedWithNewKey,
          decryptedData: decryptedWithNewKey,
          keyInfo: {
            algorithm: 'AES-256',
            keySize: 256,
            keyStrength: this.calculateKeyStrength(newKey),
            generationMethod: 'secure random',
            isSecure: true,
          },
        },
        securityLevel: 'strong',
        issues,
      };
    } catch (error) {
      return {
        testName: 'Key Rotation Test',
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Key rotation test' },
        testType: 'key_management',
        status: 'error',
        executionTime: Date.now() - startTime,
        details: {
          originalData: 'key rotation test',
          keyInfo: { algorithm: 'AES-256', keySize: 0, keyStrength: 0, generationMethod: 'unknown', isSecure: false },
        },
        securityLevel: 'weak',
        issues: [{
          type: 'implementation_error',
          severity: 'critical',
          description: `: ${error}`,
          recommendation: '',
        }],
      };
    }
  }

  /**
   * 
   */
  private async testKeyStorage(encryptionService: EncryptionService): Promise<EncryptionTestResult> {
    const startTime = Date.now();
    const issues: EncryptionIssue[] = [];

    try {
      // 
      const key = await encryptionService.generateKey('AES-256');

      // 
      await encryptionService.storeKey('test-key-id', key);

      // 
      const retrievedKey = await encryptionService.retrieveKey('test-key-id');

      // 
      const keysMatch = key === retrievedKey;

      if (!keysMatch) {
        issues.push({
          type: 'implementation_error',
          severity: 'critical',
          description: '',
          recommendation: '',
        });
      }

      // 
      await encryptionService.deleteKey('test-key-id');

      try {
        await encryptionService.retrieveKey('test-key-id');
        issues.push({
          type: 'implementation_error',
          severity: 'medium',
          description: '',
          recommendation: '',
        });
      } catch {
        // 
      }

      return {
        testName: 'Key Storage Test',
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Key storage test' },
        testType: 'key_management',
        status: keysMatch && issues.length === 0 ? 'passed' : 'failed',
        executionTime: Date.now() - startTime,
        details: {
          originalData: 'key storage test',
          keyInfo: {
            algorithm: 'AES-256',
            keySize: 256,
            keyStrength: this.calculateKeyStrength(key),
            generationMethod: 'secure random',
            isSecure: true,
          },
        },
        securityLevel: 'strong',
        issues,
      };
    } catch (error) {
      return {
        testName: 'Key Storage Test',
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Key storage test' },
        testType: 'key_management',
        status: 'error',
        executionTime: Date.now() - startTime,
        details: {
          originalData: 'key storage test',
          keyInfo: { algorithm: 'AES-256', keySize: 0, keyStrength: 0, generationMethod: 'unknown', isSecure: false },
        },
        securityLevel: 'weak',
        issues: [{
          type: 'implementation_error',
          severity: 'critical',
          description: `: ${error}`,
          recommendation: '',
        }],
      };
    }
  }

  /**
   * 
   */
  private async runDataIntegrityTests(encryptionService: EncryptionService): Promise<EncryptionTestResult[]> {
    const results: EncryptionTestResult[] = [];

    for (const testData of this.config.testDataSets) {
      const result = await this.testDataIntegrity(testData, encryptionService);
      results.push(result);
    }

    return results;
  }

  /**
   * 
   */
  private async testDataIntegrity(testData: TestDataSet, encryptionService: EncryptionService): Promise<EncryptionTestResult> {
    const startTime = Date.now();
    const issues: EncryptionIssue[] = [];

    try {
      const key = await encryptionService.generateKey('AES-256');

      // 
      const encryptedData = await encryptionService.encrypt(testData.data, 'AES-256', { key });

      // 
      const originalHash = crypto.createHash('sha256').update(testData.data).digest('hex');

      // 
      const decryptedData = await encryptionService.decrypt(encryptedData, 'AES-256', { key });

      // 
      const decryptedHash = crypto.createHash('sha256').update(decryptedData).digest('hex');

      // 
      const integrityCheck: IntegrityCheckResult = {
        hashMatch: originalHash === decryptedHash,
        dataCorruption: testData.data !== decryptedData,
        tamperingDetected: false,
        checksumValid: true,
      };

      // 
      const tamperedData = encryptedData.slice(0, -10) + 'tampered123';
      try {
        await encryptionService.decrypt(tamperedData, 'AES-256', { key });
        issues.push({
          type: 'integrity_failure',
          severity: 'critical',
          description: '',
          recommendation: '',
        });
      } catch {
        integrityCheck.tamperingDetected = true;
      }

      if (!integrityCheck.hashMatch || integrityCheck.dataCorruption) {
        issues.push({
          type: 'integrity_failure',
          severity: 'critical',
          description: '',
          recommendation: '',
        });
      }

      return {
        testName: `Data Integrity Test - ${testData.name}`,
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Data integrity test' },
        testType: 'data_integrity',
        status: issues.length === 0 ? 'passed' : 'failed',
        executionTime: Date.now() - startTime,
        details: {
          originalData: testData.data,
          encryptedData,
          decryptedData,
          keyInfo: {
            algorithm: 'AES-256',
            keySize: 256,
            keyStrength: this.calculateKeyStrength(key),
            generationMethod: 'secure random',
            isSecure: true,
          },
          integrityCheck,
        },
        securityLevel: 'strong',
        issues,
      };
    } catch (error) {
      return {
        testName: `Data Integrity Test - ${testData.name}`,
        algorithm: { name: 'AES-256', type: 'symmetric', keySize: 256, description: 'Data integrity test' },
        testType: 'data_integrity',
        status: 'error',
        executionTime: Date.now() - startTime,
        details: {
          originalData: testData.data,
          keyInfo: { algorithm: 'AES-256', keySize: 0, keyStrength: 0, generationMethod: 'unknown', isSecure: false },
        },
        securityLevel: 'weak',
        issues: [{
          type: 'implementation_error',
          severity: 'critical',
          description: `: ${error}`,
          recommendation: '',
        }],
      };
    }
  }

  /**
   * 
   */
  private async runPerformanceTests(encryptionService: EncryptionService): Promise<EncryptionTestResult[]> {
    const results: EncryptionTestResult[] = [];

    for (const algorithm of this.config.supportedAlgorithms) {
      for (const testData of this.config.testDataSets) {
        const result = await this.testEncryptionPerformance(algorithm, testData, encryptionService);
        results.push(result);
      }
    }

    return results;
  }

  /**
   * 
   */
  private async testEncryptionPerformance(algorithm: EncryptionAlgorithm, testData: TestDataSet, encryptionService: EncryptionService): Promise<EncryptionTestResult> {
    const startTime = Date.now();
    const issues: EncryptionIssue[] = [];

    try {
      const key = await this.generateTestKey(algorithm);

      // 
      const encryptStartTime = process.hrtime.bigint();
      const encryptedData = await encryptionService.encrypt(testData.data, algorithm.name, key);
      const encryptEndTime = process.hrtime.bigint();
      const encryptionTime = Number(encryptEndTime - encryptStartTime) / 1000000; // 

      // 
      const decryptStartTime = process.hrtime.bigint();
      const decryptedData = await encryptionService.decrypt(encryptedData, algorithm.name, key);
      const decryptEndTime = process.hrtime.bigint();
      const decryptionTime = Number(decryptEndTime - decryptStartTime) / 1000000; // 

      // 
      const throughput = (testData.size / (encryptionTime + decryptionTime)) * 1000; // bytes per second

      const performanceMetrics: PerformanceMetrics = {
        encryptionTime,
        decryptionTime,
        throughput,
        memoryUsage: process.memoryUsage().heapUsed,
      };

      // 
      const maxAcceptableTime = 1000; // 1
      if (encryptionTime > maxAcceptableTime || decryptionTime > maxAcceptableTime) {
        issues.push({
          type: 'poor_performance',
          severity: 'medium',
          description: `/: ${encryptionTime}ms, ${decryptionTime}ms`,
          recommendation: '',
        });
      }

      return {
        testName: `Performance Test - ${algorithm.name} with ${testData.name}`,
        algorithm,
        testType: 'performance',
        status: issues.length === 0 ? 'passed' : 'warning',
        executionTime: Date.now() - startTime,
        details: {
          originalData: testData.data,
          encryptedData,
          decryptedData,
          keyInfo: key,
          performanceMetrics,
        },
        securityLevel: 'strong',
        issues,
      };
    } catch (error) {
      return {
        testName: `Performance Test - ${algorithm.name} with ${testData.name}`,
        algorithm,
        testType: 'performance',
        status: 'error',
        executionTime: Date.now() - startTime,
        details: {
          originalData: testData.data,
          keyInfo: { algorithm: algorithm.name, keySize: 0, keyStrength: 0, generationMethod: 'unknown', isSecure: false },
        },
        securityLevel: 'weak',
        issues: [{
          type: 'implementation_error',
          severity: 'critical',
          description: `: ${error}`,
          recommendation: '',
        }],
      };
    }
  }

  // 
  private async generateTestKey(algorithm: EncryptionAlgorithm): Promise<KeyInfo> {
    const keyBuffer = crypto.randomBytes(algorithm.keySize / 8);
    const key = keyBuffer.toString('base64');

    return {
      algorithm: algorithm.name,
      keySize: algorithm.keySize,
      keyStrength: this.calculateKeyStrength(key),
      generationMethod: 'crypto.randomBytes',
      isSecure: algorithm.keySize >= this.config.keyStrengthRequirements.minimumSymmetricKeySize,
      key,
    };
  }

  private calculateKeyStrength(key: string): number {
    // 
    const entropy = this.calculateEntropy(key);
    return Math.min(entropy * key.length, 256);
  }

  private calculateEntropy(str: string): number {
    const freq: Record<string, number> = {};
    for (const char of str) {
      freq[char] = (freq[char] || 0) + 1;
    }

    let entropy = 0;
    const len = str.length;
    for (const count of Object.values(freq)) {
      const p = count / len;
      entropy -= p * Math.log2(p);
    }

    return entropy;
  }

  private assessAlgorithmSecurity(algorithm: EncryptionAlgorithm): 'weak' | 'acceptable' | 'strong' | 'excellent' {
    if (algorithm.keySize >= 256) return 'excellent';
    if (algorithm.keySize >= 128) return 'strong';
    if (algorithm.keySize >= 64) return 'acceptable';
    return 'weak';
  }

  private assessKeySecurityLevel(keySize: number): 'weak' | 'acceptable' | 'strong' | 'excellent' {
    if (keySize >= 256) return 'excellent';
    if (keySize >= 128) return 'strong';
    if (keySize >= 64) return 'acceptable';
    return 'weak';
  }

  /**
   * 
   */
  generateEncryptionReport(results: EncryptionTestResult[]): EncryptionReport {
    const totalTests = results.length;
    const passedTests = results.filter(r => r.status === 'passed').length;
    const failedTests = results.filter(r => r.status === 'failed').length;
    const warningTests = results.filter(r => r.status === 'warning').length;
    const errorTests = results.filter(r => r.status === 'error').length;

    const securityLevels = {
      excellent: results.filter(r => r.securityLevel === 'excellent').length,
      strong: results.filter(r => r.securityLevel === 'strong').length,
      acceptable: results.filter(r => r.securityLevel === 'acceptable').length,
      weak: results.filter(r => r.securityLevel === 'weak').length,
    };

    const allIssues = results.flatMap(r => r.issues);
    const criticalIssues = allIssues.filter(i => i.severity === 'critical').length;
    const highIssues = allIssues.filter(i => i.severity === 'high').length;
    const mediumIssues = allIssues.filter(i => i.severity === 'medium').length;
    const lowIssues = allIssues.filter(i => i.severity === 'low').length;

    return {
      summary: {
        totalTests,
        passedTests,
        failedTests,
        warningTests,
        errorTests,
        passRate: (passedTests / totalTests) * 100,
      },
      securityLevels,
      issueDistribution: {
        critical: criticalIssues,
        high: highIssues,
        medium: mediumIssues,
        low: lowIssues,
      },
      testResults: results,
      recommendations: this.generateEncryptionRecommendations(results),
      timestamp: new Date(),
    };
  }

  private generateEncryptionRecommendations(results: EncryptionTestResult[]): string[] {
    const recommendations: string[] = [];
    const allIssues = results.flatMap(r => r.issues);

    if (allIssues.some(i => i.type === 'weak_algorithm')) {
      recommendations.push('');
    }

    if (allIssues.some(i => i.type === 'weak_key')) {
      recommendations.push('');
    }

    if (allIssues.some(i => i.type === 'poor_performance')) {
      recommendations.push('');
    }

    if (allIssues.some(i => i.type === 'integrity_failure')) {
      recommendations.push('');
    }

    if (allIssues.some(i => i.type === 'implementation_error')) {
      recommendations.push('');
    }

    if (recommendations.length === 0) {
      recommendations.push('');
    }

    return recommendations;
  }
}

// 
export interface EncryptionService {
  encrypt(data: string, algorithm: string, keyInfo: any): Promise<string>;
  decrypt(encryptedData: string, algorithm: string, keyInfo: any): Promise<string>;
  generateKey(algorithm: string): Promise<string>;
  storeKey(keyId: string, key: string): Promise<void>;
  retrieveKey(keyId: string): Promise<string>;
  deleteKey(keyId: string): Promise<void>;
}

export interface EncryptionReport {
  summary: {
    totalTests: number;
    passedTests: number;
    failedTests: number;
    warningTests: number;
    errorTests: number;
    passRate: number;
  };
  securityLevels: {
    excellent: number;
    strong: number;
    acceptable: number;
    weak: number;
  };
  issueDistribution: {
    critical: number;
    high: number;
    medium: number;
    low: number;
  };
  testResults: EncryptionTestResult[];
  recommendations: string[];
  timestamp: Date;
}

/**
 * Accessibility Tester
 *
 * Comprehensive accessibility testing framework that validates
 * WCAG compliance, keyboard navigation, screen reader compatibility,
 * and other accessibility standards.
 */
import { fireEvent, waitFor } from '@testing-library/react';
import { axe, AxeResults, Result as AxeResult } from 'jest-axe';

export interface AccessibilityTestConfig {
  wcagLevel: 'A' | 'AA' | 'AAA';
  includeRules: string[];
  excludeRules: string[];
  tags: string[];
  customRules: AccessibilityRule[];
  keyboardNavigation: boolean;
  screenReaderTesting: boolean;
  colorContrastTesting: boolean;
  focusManagement: boolean;
}

export interface AccessibilityRule {
  id: string;
  name: string;
  description: string;
  impact: 'minor' | 'moderate' | 'serious' | 'critical';
  tags: string[];
  check: (element: HTMLElement) => AccessibilityViolation[];
}

export interface AccessibilityViolation {
  ruleId: string;
  impact: 'minor' | 'moderate' | 'serious' | 'critical';
  message: string;
  element: HTMLElement;
  selector: string;
  help: string;
  helpUrl?: string;
  suggestion: string;
  wcagCriteria: string[];
}

export interface AccessibilityTestResult {
  passed: boolean;
  score: number;
  violations: AccessibilityViolation[];
  summary: AccessibilitySummary;
  keyboardNavigation?: KeyboardNavigationResult;
  screenReader?: ScreenReaderResult;
  colorContrast?: ColorContrastResult;
  focusManagement?: FocusManagementResult;
}

export interface AccessibilitySummary {
  totalElements: number;
  violatingElements: number;
  criticalViolations: number;
  seriousViolations: number;
  moderateViolations: number;
  minorViolations: number;
  wcagLevel: string;
  complianceScore: number;
}

export interface KeyboardNavigationResult {
  passed: boolean;
  focusableElements: number;
  tabbableElements: number;
  trapFocus: boolean;
  skipLinks: boolean;
  issues: KeyboardIssue[];
}

export interface KeyboardIssue {
  type: 'missing-focus' | 'focus-trap' | 'skip-link' | 'tab-order' | 'keyboard-activation';
  element: HTMLElement;
  description: string;
  severity: 'low' | 'medium' | 'high';
  suggestion: string;
}

export interface ScreenReaderResult {
  passed: boolean;
  ariaLabels: number;
  landmarks: number;
  headingStructure: boolean;
  altTexts: number;
  issues: ScreenReaderIssue[];
}

export interface ScreenReaderIssue {
  type: 'missing-label' | 'missing-landmark' | 'heading-structure' | 'missing-alt' | 'aria-invalid';
  element: HTMLElement;
  description: string;
  severity: 'low' | 'medium' | 'high';
  suggestion: string;
}

export interface ColorContrastResult {
  passed: boolean;
  totalChecks: number;
  failedChecks: number;
  issues: ColorContrastIssue[];
}

export interface ColorContrastIssue {
  element: HTMLElement;
  foreground: string;
  background: string;
  ratio: number;
  requiredRatio: number;
  level: 'AA' | 'AAA';
  size: 'normal' | 'large';
  suggestion: string;
}

export interface FocusManagementResult {
  passed: boolean;
  focusIndicators: boolean;
  focusOrder: boolean;
  focusTrapping: boolean;
  issues: FocusIssue[];
}

export interface FocusIssue {
  type: 'missing-indicator' | 'wrong-order' | 'no-trap' | 'lost-focus';
  element: HTMLElement;
  description: string;
  severity: 'low' | 'medium' | 'high';
  suggestion: string;
}

export class AccessibilityTester {
  private config: AccessibilityTestConfig;
  private customRules: Map<string, AccessibilityRule> = new Map();

  constructor(config: Partial<AccessibilityTestConfig> = {}) {
    this.config = {
      wcagLevel: 'AA',
      includeRules: [],
      excludeRules: [],
      tags: ['wcag2a', 'wcag2aa'],
      customRules: [],
      keyboardNavigation: true,
      screenReaderTesting: true,
      colorContrastTesting: true,
      focusManagement: true,
      ...config,
    };

    this.initializeCustomRules();
  }

  /**
   * Run comprehensive accessibility test
   */
  async testAccessibility(container: HTMLElement): Promise<AccessibilityTestResult> {
    const violations: AccessibilityViolation[] = [];

    // Run axe-core tests
    const axeResults = await this.runAxeTests(container);
    violations.push(...this.convertAxeViolations(axeResults.violations));

    // Run custom rule tests
    const customViolations = await this.runCustomRules(container);
    violations.push(...customViolations);

    // Run specific accessibility tests
    const keyboardNavigation = this.config.keyboardNavigation
      ? await this.testKeyboardNavigation(container)
      : undefined;

    const screenReader = this.config.screenReaderTesting
      ? await this.testScreenReaderCompatibility(container)
      : undefined;

    const colorContrast = this.config.colorContrastTesting
      ? await this.testColorContrast(container)
      : undefined;

    const focusManagement = this.config.focusManagement
      ? await this.testFocusManagement(container)
      : undefined;

    // Calculate summary
    const summary = this.calculateSummary(violations, container);

    // Calculate overall score
    const score = this.calculateAccessibilityScore(summary, keyboardNavigation, screenReader, colorContrast, focusManagement);

    return {
      passed: violations.filter(v => v.impact === 'critical' || v.impact === 'serious').length === 0,
      score,
      violations,
      summary,
      keyboardNavigation,
      screenReader,
      colorContrast,
      focusManagement,
    };
  }

  /**
   * Run axe-core accessibility tests
   */
  private async runAxeTests(container: HTMLElement): Promise<AxeResults> {
    const axeConfig = {
      rules: this.buildAxeRulesConfig(),
      tags: this.config.tags,
      exclude: this.config.excludeRules.map(rule => `[data-rule="${rule}"]`),
    };

    return await axe(container, axeConfig);
  }

  /**
   * Convert axe violations to our format
   */
  private convertAxeViolations(axeViolations: AxeResult[]): AccessibilityViolation[] {
    return axeViolations.flatMap(violation =>
      violation.nodes.map(node => ({
        ruleId: violation.id,
        impact: violation.impact as 'minor' | 'moderate' | 'serious' | 'critical',
        message: violation.description,
        element: node.element as HTMLElement,
        selector: node.target.join(' '),
        help: violation.help,
        helpUrl: violation.helpUrl,
        suggestion: this.generateSuggestion(violation.id, node.element as HTMLElement),
        wcagCriteria: violation.tags.filter(tag => tag.startsWith('wcag')),
      })),
    );
  }

  /**
   * Test keyboard navigation
   */
  private async testKeyboardNavigation(container: HTMLElement): Promise<KeyboardNavigationResult> {
    const issues: KeyboardIssue[] = [];
    const focusableElements = this.getFocusableElements(container);
    const tabbableElements = this.getTabbableElements(container);

    // Test focus indicators
    for (const element of focusableElements) {
      if (!this.hasFocusIndicator(element)) {
        issues.push({
          type: 'missing-focus',
          element,
          description: 'Element lacks visible focus indicator',
          severity: 'high',
          suggestion: 'Add CSS focus styles or ensure default focus indicators are visible',
        });
      }
    }

    // Test tab order
    const tabOrderIssues = await this.testTabOrder(tabbableElements);
    issues.push(...tabOrderIssues);

    // Test keyboard activation
    const activationIssues = await this.testKeyboardActivation(focusableElements);
    issues.push(...activationIssues);

    // Test focus trapping (for modals, dialogs)
    const trapFocus = await this.testFocusTrapping(container);

    // Test skip links
    const skipLinks = this.hasSkipLinks(container);

    return {
      passed: issues.filter(i => i.severity === 'high').length === 0,
      focusableElements: focusableElements.length,
      tabbableElements: tabbableElements.length,
      trapFocus,
      skipLinks,
      issues,
    };
  }

  /**
   * Test screen reader compatibility
   */
  private async testScreenReaderCompatibility(container: HTMLElement): Promise<ScreenReaderResult> {
    const issues: ScreenReaderIssue[] = [];

    // Test ARIA labels
    const ariaLabels = this.countAriaLabels(container);
    const missingLabels = this.findMissingLabels(container);
    issues.push(...missingLabels);

    // Test landmarks
    const landmarks = this.countLandmarks(container);
    const landmarkIssues = this.validateLandmarks(container);
    issues.push(...landmarkIssues);

    // Test heading structure
    const headingStructure = this.validateHeadingStructure(container);
    if (!headingStructure.valid) {
      issues.push({
        type: 'heading-structure',
        element: container,
        description: 'Heading structure is not logical',
        severity: 'medium',
        suggestion: 'Ensure headings follow a logical hierarchy (h1  h2  h3, etc.)',
      });
    }

    // Test alt texts
    const altTexts = this.countAltTexts(container);
    const missingAltTexts = this.findMissingAltTexts(container);
    issues.push(...missingAltTexts);

    return {
      passed: issues.filter(i => i.severity === 'high').length === 0,
      ariaLabels,
      landmarks,
      headingStructure: headingStructure.valid,
      altTexts,
      issues,
    };
  }

  /**
   * Test color contrast
   */
  private async testColorContrast(container: HTMLElement): Promise<ColorContrastResult> {
    const issues: ColorContrastIssue[] = [];
    const textElements = container.querySelectorAll('*');
    let totalChecks = 0;
    let failedChecks = 0;

    for (const element of textElements) {
      if (this.hasTextContent(element as HTMLElement)) {
        totalChecks++;
        const contrastIssue = await this.checkColorContrast(element as HTMLElement);
        if (contrastIssue) {
          issues.push(contrastIssue);
          failedChecks++;
        }
      }
    }

    return {
      passed: failedChecks === 0,
      totalChecks,
      failedChecks,
      issues,
    };
  }

  /**
   * Test focus management
   */
  private async testFocusManagement(container: HTMLElement): Promise<FocusManagementResult> {
    const issues: FocusIssue[] = [];

    // Test focus indicators
    const focusIndicators = await this.testFocusIndicators(container);
    if (!focusIndicators) {
      issues.push({
        type: 'missing-indicator',
        element: container,
        description: 'Some elements lack proper focus indicators',
        severity: 'high',
        suggestion: 'Ensure all interactive elements have visible focus indicators',
      });
    }

    // Test focus order
    const focusOrder = await this.testFocusOrder(container);
    if (!focusOrder) {
      issues.push({
        type: 'wrong-order',
        element: container,
        description: 'Focus order does not match visual order',
        severity: 'medium',
        suggestion: 'Adjust tab order to match visual layout',
      });
    }

    // Test focus trapping
    const focusTrapping = await this.testFocusTrapping(container);
    if (!focusTrapping && this.isModal(container)) {
      issues.push({
        type: 'no-trap',
        element: container,
        description: 'Modal does not trap focus',
        severity: 'high',
        suggestion: 'Implement focus trapping for modal dialogs',
      });
    }

    return {
      passed: issues.filter(i => i.severity === 'high').length === 0,
      focusIndicators,
      focusOrder,
      focusTrapping,
      issues,
    };
  }

  /**
   * Run custom accessibility rules
   */
  private async runCustomRules(container: HTMLElement): Promise<AccessibilityViolation[]> {
    const violations: AccessibilityViolation[] = [];

    for (const rule of this.customRules.values()) {
      try {
        const ruleViolations = rule.check(container);
        violations.push(...ruleViolations);
      } catch (error) {
        console.error(`Custom rule ${rule.id} failed:`, error);
      }
    }

    return violations;
  }

  /**
   * Get focusable elements
   */
  private getFocusableElements(container: HTMLElement): HTMLElement[] {
    const focusableSelectors = [
      'a[href]',
      'button:not([disabled])',
      'input:not([disabled])',
      'select:not([disabled])',
      'textarea:not([disabled])',
      '[tabindex]:not([tabindex="-1"])',
      '[contenteditable="true"]',
    ];

    return Array.from(container.querySelectorAll(focusableSelectors.join(', '))) as HTMLElement[];
  }

  /**
   * Get tabbable elements
   */
  private getTabbableElements(container: HTMLElement): HTMLElement[] {
    return this.getFocusableElements(container).filter(element => {
      const tabIndex = element.getAttribute('tabindex');
      return tabIndex !== '-1';
    });
  }

  /**
   * Check if element has focus indicator
   */
  private hasFocusIndicator(element: HTMLElement): boolean {
    // Simulate focus
    element.focus();
    const computedStyle = window.getComputedStyle(element, ':focus');

    // Check for common focus indicators
    const hasOutline = computedStyle.outline !== 'none' && computedStyle.outline !== '0px';
    const hasBoxShadow = computedStyle.boxShadow !== 'none';
    const hasBorder = computedStyle.borderColor !== computedStyle.getPropertyValue('border-color');

    return hasOutline || hasBoxShadow || hasBorder;
  }

  /**
   * Test tab order
   */
  private async testTabOrder(elements: HTMLElement[]): Promise<KeyboardIssue[]> {
    const issues: KeyboardIssue[] = [];

    for (let i = 0; i < elements.length - 1; i++) {
      const current = elements[i];
      const next = elements[i + 1];

      // Check if visual order matches tab order
      const currentRect = current.getBoundingClientRect();
      const nextRect = next.getBoundingClientRect();

      // Simple heuristic: next element should be to the right or below
      if (nextRect.top < currentRect.top ||
          (nextRect.top === currentRect.top && nextRect.left < currentRect.left)) {
        issues.push({
          type: 'tab-order',
          element: next,
          description: 'Tab order does not match visual order',
          severity: 'medium',
          suggestion: 'Adjust tabindex or DOM order to match visual layout',
        });
      }
    }

    return issues;
  }

  /**
   * Test keyboard activation
   */
  private async testKeyboardActivation(elements: HTMLElement[]): Promise<KeyboardIssue[]> {
    const issues: KeyboardIssue[] = [];

    for (const element of elements) {
      if (this.isInteractiveElement(element)) {
        const canActivate = await this.testElementActivation(element);
        if (!canActivate) {
          issues.push({
            type: 'keyboard-activation',
            element,
            description: 'Element cannot be activated with keyboard',
            severity: 'high',
            suggestion: 'Add keyboard event handlers for Enter and Space keys',
          });
        }
      }
    }

    return issues;
  }

  /**
   * Test element activation with keyboard
   */
  private async testElementActivation(element: HTMLElement): Promise<boolean> {
    let activated = false;

    // Add temporary event listener
    const handleActivation = () => { activated = true; };
    element.addEventListener('click', handleActivation);

    try {
      // Test Enter key
      element.focus();
      fireEvent.keyDown(element, { key: 'Enter', code: 'Enter' });
      await waitFor(() => {}, { timeout: 100 });

      if (!activated) {
        // Test Space key
        fireEvent.keyDown(element, { key: ' ', code: 'Space' });
        await waitFor(() => {}, { timeout: 100 });
      }

      return activated;
    } finally {
      element.removeEventListener('click', handleActivation);
    }
  }

  /**
   * Test focus trapping
   */
  private async testFocusTrapping(container: HTMLElement): Promise<boolean> {
    if (!this.isModal(container)) return true;

    const focusableElements = this.getFocusableElements(container);
    if (focusableElements.length === 0) return true;

    const firstElement = focusableElements[0];
    const lastElement = focusableElements[focusableElements.length - 1];

    // Test forward trapping
    lastElement.focus();
    fireEvent.keyDown(lastElement, { key: 'Tab', code: 'Tab' });
    await waitFor(() => {}, { timeout: 100 });

    if (document.activeElement !== firstElement) return false;

    // Test backward trapping
    firstElement.focus();
    fireEvent.keyDown(firstElement, { key: 'Tab', code: 'Tab', shiftKey: true });
    await waitFor(() => {}, { timeout: 100 });

    return document.activeElement === lastElement;
  }

  /**
   * Check if container has skip links
   */
  private hasSkipLinks(container: HTMLElement): boolean {
    const skipLinks = container.querySelectorAll('a[href^="#"]');
    return Array.from(skipLinks).some(link =>
      link.textContent?.toLowerCase().includes('skip') ||
      link.getAttribute('aria-label')?.toLowerCase().includes('skip'),
    );
  }

  /**
   * Count ARIA labels
   */
  private countAriaLabels(container: HTMLElement): number {
    return container.querySelectorAll('[aria-label], [aria-labelledby]').length;
  }

  /**
   * Find elements missing labels
   */
  private findMissingLabels(container: HTMLElement): ScreenReaderIssue[] {
    const issues: ScreenReaderIssue[] = [];
    const interactiveElements = container.querySelectorAll('button, input, select, textarea');

    interactiveElements.forEach(element => {
      if (!this.hasAccessibleName(element as HTMLElement)) {
        issues.push({
          type: 'missing-label',
          element: element as HTMLElement,
          description: 'Interactive element lacks accessible name',
          severity: 'high',
          suggestion: 'Add aria-label, aria-labelledby, or associated label element',
        });
      }
    });

    return issues;
  }

  /**
   * Check if element has accessible name
   */
  private hasAccessibleName(element: HTMLElement): boolean {
    return !!(
      element.getAttribute('aria-label') ||
      element.getAttribute('aria-labelledby') ||
      element.getAttribute('title') ||
      element.textContent?.trim() ||
      (element.tagName === 'INPUT' && element.getAttribute('placeholder'))
    );
  }

  /**
   * Count landmarks
   */
  private countLandmarks(container: HTMLElement): number {
    const landmarkSelectors = [
      'main', 'nav', 'aside', 'header', 'footer', 'section',
      '[role="main"]', '[role="navigation"]', '[role="complementary"]',
      '[role="banner"]', '[role="contentinfo"]', '[role="region"]',
    ];

    return container.querySelectorAll(landmarkSelectors.join(', ')).length;
  }

  /**
   * Validate landmarks
   */
  private validateLandmarks(container: HTMLElement): ScreenReaderIssue[] {
    const issues: ScreenReaderIssue[] = [];

    // Check for main landmark
    const mainLandmarks = container.querySelectorAll('main, [role="main"]');
    if (mainLandmarks.length === 0) {
      issues.push({
        type: 'missing-landmark',
        element: container,
        description: 'Page lacks main landmark',
        severity: 'medium',
        suggestion: 'Add <main> element or role="main" to identify main content',
      });
    } else if (mainLandmarks.length > 1) {
      issues.push({
        type: 'missing-landmark',
        element: container,
        description: 'Multiple main landmarks found',
        severity: 'medium',
        suggestion: 'Use only one main landmark per page',
      });
    }

    return issues;
  }

  /**
   * Validate heading structure
   */
  private validateHeadingStructure(container: HTMLElement): { valid: boolean; issues: string[] } {
    const headings = Array.from(container.querySelectorAll('h1, h2, h3, h4, h5, h6'));
    const issues: string[] = [];
    let previousLevel = 0;

    for (const heading of headings) {
      const level = parseInt(heading.tagName.charAt(1), 10);

      if (level > previousLevel + 1) {
        issues.push(`Heading level ${level} follows level ${previousLevel}, skipping levels`);
      }

      previousLevel = level;
    }

    return {
      valid: issues.length === 0,
      issues,
    };
  }

  /**
   * Count alt texts
   */
  private countAltTexts(container: HTMLElement): number {
    return container.querySelectorAll('img[alt]').length;
  }

  /**
   * Find missing alt texts
   */
  private findMissingAltTexts(container: HTMLElement): ScreenReaderIssue[] {
    const issues: ScreenReaderIssue[] = [];
    const images = container.querySelectorAll('img');

    images.forEach(img => {
      if (!img.hasAttribute('alt')) {
        issues.push({
          type: 'missing-alt',
          element: img,
          description: 'Image lacks alt attribute',
          severity: 'high',
          suggestion: 'Add descriptive alt text or empty alt="" for decorative images',
        });
      }
    });

    return issues;
  }

  /**
   * Check if element has text content
   */
  private hasTextContent(element: HTMLElement): boolean {
    return !!(element.textContent?.trim() || element.getAttribute('aria-label'));
  }

  /**
   * Check color contrast
   */
  private async checkColorContrast(element: HTMLElement): Promise<ColorContrastIssue | null> {
    const computedStyle = window.getComputedStyle(element);
    const color = computedStyle.color;
    const backgroundColor = computedStyle.backgroundColor;

    if (color === 'rgba(0, 0, 0, 0)' || backgroundColor === 'rgba(0, 0, 0, 0)') {
      return null; // Skip transparent elements
    }

    const ratio = this.calculateContrastRatio(color, backgroundColor);
    const fontSize = parseFloat(computedStyle.fontSize);
    const fontWeight = computedStyle.fontWeight;

    const isLargeText = fontSize >= 18 || (fontSize >= 14 && (fontWeight === 'bold' || parseInt(fontWeight, 10) >= 700));
    const requiredRatio = this.config.wcagLevel === 'AAA'
      ? (isLargeText ? 4.5 : 7)
      : (isLargeText ? 3 : 4.5);

    if (ratio < requiredRatio) {
      return {
        element,
        foreground: color,
        background: backgroundColor,
        ratio,
        requiredRatio,
        level: this.config.wcagLevel,
        size: isLargeText ? 'large' : 'normal',
        suggestion: `Increase contrast ratio to at least ${requiredRatio}:1`,
      };
    }

    return null;
  }

  /**
   * Calculate contrast ratio between two colors
   */
  private calculateContrastRatio(color1: string, color2: string): number {
    const luminance1 = this.getLuminance(color1);
    const luminance2 = this.getLuminance(color2);

    const lighter = Math.max(luminance1, luminance2);
    const darker = Math.min(luminance1, luminance2);

    return (lighter + 0.05) / (darker + 0.05);
  }

  /**
   * Get luminance of color
   */
  private getLuminance(color: string): number {
    const rgb = this.parseColor(color);
    if (!rgb) return 0;

    const [r, g, b] = [rgb.r, rgb.g, rgb.b].map(c => {
      c = c / 255;
      return c <= 0.03928 ? c / 12.92 : Math.pow((c + 0.055) / 1.055, 2.4);
    });

    return 0.2126 * r + 0.7152 * g + 0.0722 * b;
  }

  /**
   * Parse color string to RGB
   */
  private parseColor(color: string): { r: number; g: number; b: number } | null {
    // Handle rgb() format
    const rgbMatch = color.match(/rgb\((\d+),\s*(\d+),\s*(\d+)\)/);
    if (rgbMatch) {
      return {
        r: parseInt(rgbMatch[1], 10),
        g: parseInt(rgbMatch[2], 10),
        b: parseInt(rgbMatch[3], 10),
      };
    }

    // Handle hex format
    const hexMatch = color.match(/^#([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i);
    if (hexMatch) {
      return {
        r: parseInt(hexMatch[1], 16),
        g: parseInt(hexMatch[2], 16),
        b: parseInt(hexMatch[3], 16),
      };
    }

    return null;
  }

  /**
   * Test focus indicators
   */
  private async testFocusIndicators(container: HTMLElement): Promise<boolean> {
    const focusableElements = this.getFocusableElements(container);
    return focusableElements.every(element => this.hasFocusIndicator(element));
  }

  /**
   * Test focus order
   */
  private async testFocusOrder(container: HTMLElement): Promise<boolean> {
    const tabbableElements = this.getTabbableElements(container);
    const issues = await this.testTabOrder(tabbableElements);
    return issues.length === 0;
  }

  /**
   * Check if element is interactive
   */
  private isInteractiveElement(element: HTMLElement): boolean {
    const interactiveTags = ['button', 'a', 'input', 'select', 'textarea'];
    const interactiveRoles = ['button', 'link', 'menuitem', 'tab'];

    return interactiveTags.includes(element.tagName.toLowerCase()) ||
           interactiveRoles.includes(element.getAttribute('role') || '') ||
           element.hasAttribute('onclick') ||
           element.hasAttribute('tabindex');
  }

  /**
   * Check if container is a modal
   */
  private isModal(container: HTMLElement): boolean {
    return !!(
      container.getAttribute('role') === 'dialog' ||
      container.getAttribute('role') === 'alertdialog' ||
      container.hasAttribute('aria-modal') ||
      container.classList.contains('modal')
    );
  }

  /**
   * Build axe rules configuration
   */
  private buildAxeRulesConfig(): Record<string, { enabled: boolean }> {
    const rules: Record<string, { enabled: boolean }> = {};

    // Enable included rules
    this.config.includeRules.forEach(rule => {
      rules[rule] = { enabled: true };
    });

    // Disable excluded rules
    this.config.excludeRules.forEach(rule => {
      rules[rule] = { enabled: false };
    });

    return rules;
  }

  /**
   * Generate suggestion for violation
   */
  private generateSuggestion(ruleId: string, element: HTMLElement): string {
    const suggestions: Record<string, string> = {
      'color-contrast': 'Increase the contrast ratio between text and background colors',
      'label': 'Add an accessible name using aria-label, aria-labelledby, or a label element',
      'button-name': 'Provide accessible text content or aria-label for the button',
      'link-name': 'Ensure links have accessible text content or aria-label',
      'image-alt': 'Add descriptive alt text to images',
      'heading-order': 'Use heading elements in a logical, hierarchical order',
      'landmark-one-main': 'Use only one main landmark per page',
      'region': 'Add landmarks to identify page regions',
      'focus-order-semantics': 'Ensure focus order matches the visual order',
      'keyboard': 'Make all interactive elements keyboard accessible',
    };

    return suggestions[ruleId] || 'Review element for accessibility compliance';
  }

  /**
   * Calculate summary statistics
   */
  private calculateSummary(violations: AccessibilityViolation[], container: HTMLElement): AccessibilitySummary {
    const totalElements = container.querySelectorAll('*').length;
    const violatingElements = new Set(violations.map(v => v.element)).size;

    const criticalViolations = violations.filter(v => v.impact === 'critical').length;
    const seriousViolations = violations.filter(v => v.impact === 'serious').length;
    const moderateViolations = violations.filter(v => v.impact === 'moderate').length;
    const minorViolations = violations.filter(v => v.impact === 'minor').length;

    const complianceScore = Math.max(0, 100 - (
      criticalViolations * 25 +
      seriousViolations * 15 +
      moderateViolations * 10 +
      minorViolations * 5
    ));

    return {
      totalElements,
      violatingElements,
      criticalViolations,
      seriousViolations,
      moderateViolations,
      minorViolations,
      wcagLevel: this.config.wcagLevel,
      complianceScore,
    };
  }

  /**
   * Calculate overall accessibility score
   */
  private calculateAccessibilityScore(
    summary: AccessibilitySummary,
    keyboardNav?: KeyboardNavigationResult,
    screenReader?: ScreenReaderResult,
    colorContrast?: ColorContrastResult,
    focusManagement?: FocusManagementResult,
  ): number {
    let score = summary.complianceScore;

    // Adjust score based on specific test results
    if (keyboardNav && !keyboardNav.passed) score -= 10;
    if (screenReader && !screenReader.passed) score -= 10;
    if (colorContrast && !colorContrast.passed) score -= 15;
    if (focusManagement && !focusManagement.passed) score -= 10;

    return Math.max(0, Math.min(100, score));
  }

  /**
   * Initialize custom accessibility rules
   */
  private initializeCustomRules(): void {
    // Add custom rules from config
    this.config.customRules.forEach(rule => {
      this.customRules.set(rule.id, rule);
    });

    // Add built-in custom rules
    this.addBuiltInRules();
  }

  /**
   * Add built-in custom rules
   */
  private addBuiltInRules(): void {
    // Custom rule for form validation
    this.customRules.set('form-validation', {
      id: 'form-validation',
      name: 'Form Validation Accessibility',
      description: 'Ensures form validation messages are accessible',
      impact: 'serious',
      tags: ['forms', 'validation'],
      check: (container: HTMLElement) => {
        const violations: AccessibilityViolation[] = [];
        const forms = container.querySelectorAll('form');

        forms.forEach(form => {
          const inputs = form.querySelectorAll('input, select, textarea');
          inputs.forEach(input => {
            const hasValidation = input.hasAttribute('aria-describedby') ||
                                input.hasAttribute('aria-invalid');

            if (!hasValidation) {
              violations.push({
                ruleId: 'form-validation',
                impact: 'serious',
                message: 'Form input lacks validation accessibility attributes',
                element: input as HTMLElement,
                selector: this.generateSelector(input as HTMLElement),
                help: 'Add aria-describedby and aria-invalid attributes for form validation',
                suggestion: 'Use aria-describedby to reference validation messages and aria-invalid to indicate validation state',
                wcagCriteria: ['wcag2a'],
              });
            }
          });
        });

        return violations;
      },
    });

    // Custom rule for interactive elements
    this.customRules.set('interactive-elements', {
      id: 'interactive-elements',
      name: 'Interactive Elements Accessibility',
      description: 'Ensures interactive elements are properly accessible',
      impact: 'serious',
      tags: ['interactive', 'keyboard'],
      check: (container: HTMLElement) => {
        const violations: AccessibilityViolation[] = [];
        const interactiveElements = container.querySelectorAll('[onclick], [onkeydown]');

        interactiveElements.forEach(element => {
          const htmlElement = element as HTMLElement;
          if (!this.isInteractiveElement(htmlElement) && !htmlElement.hasAttribute('role')) {
            violations.push({
              ruleId: 'interactive-elements',
              impact: 'serious',
              message: 'Interactive element lacks proper role or semantic meaning',
              element: htmlElement,
              selector: this.generateSelector(htmlElement),
              help: 'Add appropriate role attribute or use semantic HTML elements',
              suggestion: 'Use button, link, or other semantic elements, or add role="button" with keyboard support',
              wcagCriteria: ['wcag2a'],
            });
          }
        });

        return violations;
      },
    });
  }

  /**
   * Generate selector for element
   */
  private generateSelector(element: HTMLElement): string {
    if (element.id) return `#${element.id}`;
    if (element.className) return `.${element.className.split(' ')[0]}`;
    return element.tagName.toLowerCase();
  }

  /**
   * Add custom accessibility rule
   */
  addRule(rule: AccessibilityRule): void {
    this.customRules.set(rule.id, rule);
  }

  /**
   * Remove accessibility rule
   */
  removeRule(ruleId: string): boolean {
    return this.customRules.delete(ruleId);
  }

  /**
   * Get all accessibility rules
   */
  getRules(): AccessibilityRule[] {
    return Array.from(this.customRules.values());
  }

  /**
   * Update configuration
   */
  updateConfig(config: Partial<AccessibilityTestConfig>): void {
    this.config = { ...this.config, ...config };
  }

  /**
   * Generate accessibility report
   */
  generateReport(result: AccessibilityTestResult): string {
    let report = '# Accessibility Test Report\n\n';

    report += `**Overall Score:** ${result.score}/100\n`;
    report += `**Status:** ${result.passed ? 'PASSED' : 'FAILED'}\n`;
    report += `**WCAG Level:** ${result.summary.wcagLevel}\n\n`;

    report += '## Summary\n';
    report += `- Total Elements: ${result.summary.totalElements}\n`;
    report += `- Violating Elements: ${result.summary.violatingElements}\n`;
    report += `- Critical Violations: ${result.summary.criticalViolations}\n`;
    report += `- Serious Violations: ${result.summary.seriousViolations}\n`;
    report += `- Moderate Violations: ${result.summary.moderateViolations}\n`;
    report += `- Minor Violations: ${result.summary.minorViolations}\n\n`;

    if (result.violations.length > 0) {
      report += '## Violations\n\n';
      result.violations.forEach((violation, index) => {
        report += `### ${index + 1}. ${violation.message}\n`;
        report += `- **Impact:** ${violation.impact}\n`;
        report += `- **Element:** ${violation.selector}\n`;
        report += `- **Help:** ${violation.help}\n`;
        report += `- **Suggestion:** ${violation.suggestion}\n\n`;
      });
    }

    return report;
  }

  /**
   * Get current configuration
   */
  getConfig(): AccessibilityTestConfig {
    return { ...this.config };
  }

  /**
   * Cleanup resources
   */
  cleanup(): void {
    this.customRules.clear();
  }
}

/**
 * Create accessibility tester instance
 */
export function createAccessibilityTester(config?: Partial<AccessibilityTestConfig>): AccessibilityTester {
  return new AccessibilityTester(config);
}

/**
 * Component Test Framework
 *
 * Comprehensive testing framework for React components including
 * rendering tests, interaction simulation, style regression testing,
 * and accessibility validation.
 */
import { QueryClient } from '@tanstack/react-query';
import { render, screen, fireEvent, waitFor, within, renderHook, act } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import { axe, toHaveNoViolations } from 'jest-axe';
import React from 'react';
// Mock theme provider for testing
const MockThemeProvider: React.FC<{ children: React.ReactNode; attribute?: string; defaultTheme?: string }> = ({ children }) => <React.Fragment>{children}</React.Fragment>;

// Mock query client provider for testing
const MockQueryClientProvider: React.FC<{ children: React.ReactNode; client: any }> = ({ children }) => <React.Fragment>{children}</React.Fragment>;

// Extend Jest matchers
expect.extend(toHaveNoViolations);

export interface ComponentTestConfig {
  theme?: 'light' | 'dark' | 'system';
  viewport?: ViewportSize;
  mockData?: Record<string, any>;
  providers?: React.ComponentType<any>[];
  accessibility?: AccessibilityConfig;
  performance?: PerformanceConfig;
  styles?: StyleTestConfig;
}

export interface ViewportSize {
  width: number;
  height: number;
  devicePixelRatio?: number;
}

export interface AccessibilityConfig {
  enabled: boolean;
  rules?: string[];
  tags?: string[];
  skipRules?: string[];
}

export interface PerformanceConfig {
  enabled: boolean;
  renderTimeThreshold?: number;
  memoryThreshold?: number;
  reRenderLimit?: number;
}

export interface StyleTestConfig {
  enabled: boolean;
  snapshotStyles?: boolean;
  checkResponsive?: boolean;
  breakpoints?: number[];
}

export interface ComponentTestResult {
  renderTime: number;
  memoryUsage: number;
  reRenderCount: number;
  accessibilityViolations: any[];
  styleSnapshot?: string;
  interactions: InteractionResult[];
}

export interface InteractionResult {
  action: string;
  element: string;
  success: boolean;
  duration: number;
  error?: string;
}

export interface ComponentTestSuite {
  name: string;
  component: React.ComponentType<any>;
  props?: Record<string, any>;
  variants?: ComponentVariant[];
  interactions?: ComponentInteraction[];
  config?: ComponentTestConfig;
}

export interface ComponentVariant {
  name: string;
  props: Record<string, any>;
  description?: string;
}

export interface ComponentInteraction {
  name: string;
  action: (user: any, container: HTMLElement) => Promise<void>;
  assertions: (container: HTMLElement) => Promise<void>;
  timeout?: number;
}

export class ComponentTestFramework {
  private config: ComponentTestConfig;
  private queryClient: QueryClient;
  private performanceObserver?: PerformanceObserver;
  private renderCount: number = 0;

  constructor(config: ComponentTestConfig = {}) {
    this.config = {
      theme: 'light',
      viewport: { width: 1024, height: 768 },
      accessibility: { enabled: true },
      performance: { enabled: true, renderTimeThreshold: 100 },
      styles: { enabled: true },
      ...config,
    };

    this.queryClient = {
      clear: jest.fn(),
      // Mock query client for testing
    };

    this.setupViewport();
    this.setupPerformanceMonitoring();
  }

  /**
   * Render component with all necessary providers and wrappers
   */
  renderComponent(
    component: React.ReactElement,
    options: ComponentTestConfig = {},
  ) {
    const mergedConfig = { ...this.config, ...options };
    this.renderCount = 0;

    const AllProviders: React.FC<{ children: React.ReactNode }> = ({ children }) => {
      this.renderCount++;

      let wrappedChildren = children;

      // Wrap with theme provider
      if (mergedConfig.theme) {
        wrappedChildren = (
          <MockThemeProvider attribute="class" defaultTheme={mergedConfig.theme}>
            {wrappedChildren}
          </MockThemeProvider>
        );
      }

      // Wrap with query client
      wrappedChildren = (
        <MockQueryClientProvider client={this.queryClient}>
          {wrappedChildren}
        </MockQueryClientProvider>
      );

      // Wrap with custom providers
      if (mergedConfig.providers) {
        mergedConfig.providers.forEach(Provider => {
          wrappedChildren = <Provider>{wrappedChildren}</Provider>;
        });
      }

      return <React.Fragment>{wrappedChildren}</React.Fragment>;
    };

    const startTime = performance.now();
    const result = render(component, {
      wrapper: AllProviders,
      ...options,
    });
    const renderTime = performance.now() - startTime;

    return {
      ...result,
      renderTime,
      renderCount: this.renderCount,
    };
  }

  /**
   * Run comprehensive component test suite
   */
  async runTestSuite(suite: ComponentTestSuite): Promise<ComponentTestResult> {
    const startTime = performance.now();
    const memoryBefore = this.getMemoryUsage();

    // Render base component
    const { container, renderTime } = this.renderComponent(
      React.createElement(suite.component, suite.props || {}),
    );

    const result: ComponentTestResult = {
      renderTime,
      memoryUsage: 0,
      reRenderCount: this.renderCount,
      accessibilityViolations: [],
      interactions: [],
    };

    try {
      // Test component variants
      if (suite.variants) {
        for (const variant of suite.variants) {
          await this.testVariant(suite.component, variant, suite.config);
        }
      }

      // Run accessibility tests
      if (this.config.accessibility?.enabled) {
        result.accessibilityViolations = await this.testAccessibility(container);
      }

      // Run style tests
      if (this.config.styles?.enabled) {
        result.styleSnapshot = await this.testStyles(container);
      }

      // Run interaction tests
      if (suite.interactions) {
        for (const interaction of suite.interactions) {
          const interactionResult = await this.testInteraction(
            container,
            interaction,
          );
          result.interactions.push(interactionResult);
        }
      }

      // Calculate memory usage
      const memoryAfter = this.getMemoryUsage();
      result.memoryUsage = memoryAfter - memoryBefore;

      return result;
    } catch (error) {
      throw new Error(`Component test suite failed: ${error}`);
    }
  }

  /**
   * Test component variant
   */
  private async testVariant(
    Component: React.ComponentType<any>,
    variant: ComponentVariant,
    config?: ComponentTestConfig,
  ): Promise<void> {
    const { container } = this.renderComponent(
      React.createElement(Component, variant.props),
      config,
    );

    // Basic rendering test
    expect(container.firstChild).toBeInTheDocument();

    // Accessibility test for variant
    if (this.config.accessibility?.enabled) {
      const violations = await this.testAccessibility(container);
      expect(violations).toHaveLength(0);
    }
  }

  /**
   * Test component accessibility
   */
  async testAccessibility(container: HTMLElement): Promise<any[]> {
    const config = this.config.accessibility;
    if (!config?.enabled) return [];

    const axeConfig = {
      rules: config.rules ? this.buildRulesConfig(config.rules) : undefined,
      tags: config.tags,
      exclude: config.skipRules,
    };

    const results = await axe(container, axeConfig);
    return results.violations;
  }

  /**
   * Test component styles and responsive behavior
   */
  async testStyles(container: HTMLElement): Promise<string> {
    const config = this.config.styles;
    if (!config?.enabled) return '';

    let styleSnapshot = '';

    // Capture base styles
    if (config.snapshotStyles) {
      styleSnapshot = this.captureStyleSnapshot(container);
    }

    // Test responsive behavior
    if (config.checkResponsive && config.breakpoints) {
      for (const breakpoint of config.breakpoints) {
        await this.testResponsiveBreakpoint(container, breakpoint);
      }
    }

    return styleSnapshot;
  }

  /**
   * Test component interaction
   */
  async testInteraction(
    container: HTMLElement,
    interaction: ComponentInteraction,
  ): Promise<InteractionResult> {
    const user = userEvent.setup();
    const startTime = performance.now();

    try {
      // Execute interaction
      await interaction.action(user, container);

      // Run assertions
      await interaction.assertions(container);

      const duration = performance.now() - startTime;
      return {
        action: interaction.name,
        element: container.tagName,
        success: true,
        duration,
      };
    } catch (error) {
      const duration = performance.now() - startTime;
      return {
        action: interaction.name,
        element: container.tagName,
        success: false,
        duration,
        error: error instanceof Error ? error.message : String(error),
      };
    }
  }

  /**
   * Test responsive breakpoint
   */
  private async testResponsiveBreakpoint(
    container: HTMLElement,
    breakpoint: number,
  ): Promise<void> {
    // Simulate viewport resize
    Object.defineProperty(window, 'innerWidth', {
      writable: true,
      configurable: true,
      value: breakpoint,
    });

    // Trigger resize event
    window.dispatchEvent(new Event('resize'));

    // Wait for any responsive changes
    await waitFor(() => {
      // Component should still be rendered
      expect(container.firstChild).toBeInTheDocument();
    });
  }

  /**
   * Capture style snapshot for regression testing
   */
  private captureStyleSnapshot(container: HTMLElement): string {
    const elements = container.querySelectorAll('*');
    const styles: string[] = [];

    elements.forEach(element => {
      const computedStyle = window.getComputedStyle(element);
      const relevantStyles = [
        'display', 'position', 'width', 'height', 'margin', 'padding',
        'color', 'background-color', 'font-size', 'font-weight',
      ];

      const elementStyles = relevantStyles
        .map(prop => `${prop}: ${computedStyle.getPropertyValue(prop)}`)
        .join('; ');

      styles.push(`${element.tagName.toLowerCase()}: ${elementStyles}`);
    });

    return styles.join('\n');
  }

  /**
   * Build accessibility rules configuration
   */
  private buildRulesConfig(rules: string[]): Record<string, { enabled: boolean }> {
    const config: Record<string, { enabled: boolean }> = {};
    rules.forEach(rule => {
      config[rule] = { enabled: true };
    });
    return config;
  }

  /**
   * Setup viewport for testing
   */
  private setupViewport(): void {
    const { width, height, devicePixelRatio = 1 } = this.config.viewport!;

    Object.defineProperty(window, 'innerWidth', {
      writable: true,
      configurable: true,
      value: width,
    });

    Object.defineProperty(window, 'innerHeight', {
      writable: true,
      configurable: true,
      value: height,
    });

    Object.defineProperty(window, 'devicePixelRatio', {
      writable: true,
      configurable: true,
      value: devicePixelRatio,
    });
  }

  /**
   * Setup performance monitoring
   */
  private setupPerformanceMonitoring(): void {
    if (!this.config.performance?.enabled) return;

    if ('PerformanceObserver' in window) {
      this.performanceObserver = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        entries.forEach(entry => {
          if (entry.entryType === 'measure' && entry.name.includes('React')) {
            const threshold = this.config.performance?.renderTimeThreshold || 100;
            if (entry.duration > threshold) {
              console.warn(`Slow render detected: ${entry.name} took ${entry.duration}ms`);
            }
          }
        });
      });

      this.performanceObserver.observe({ entryTypes: ['measure', 'navigation'] });
    }
  }

  /**
   * Get current memory usage
   */
  private getMemoryUsage(): number {
    if ('memory' in performance) {
      return (performance as any).memory.usedJSHeapSize;
    }
    return 0;
  }

  /**
   * Create test utilities for common patterns
   */
  createTestUtils() {
    return {
      // Find elements by test ID
      findByTestId: (testId: string) => screen.findByTestId(testId),
      getByTestId: (testId: string) => screen.getByTestId(testId),
      queryByTestId: (testId: string) => screen.queryByTestId(testId),

      // Find elements by role
      findByRole: (role: string, options?: any) => screen.findByRole(role, options),
      getByRole: (role: string, options?: any) => screen.getByRole(role, options),
      queryByRole: (role: string, options?: any) => screen.queryByRole(role, options),

      // User interactions
      click: async (element: HTMLElement) => {
        const user = userEvent.setup();
        await user.click(element);
      },

      type: async (element: HTMLElement, text: string) => {
        const user = userEvent.setup();
        await user.type(element, text);
      },

      hover: async (element: HTMLElement) => {
        const user = userEvent.setup();
        await user.hover(element);
      },

      // Wait utilities
      waitForElement: (callback: () => HTMLElement) => waitFor(callback),
      waitForElementToBeRemoved: (element: HTMLElement) =>
        waitFor(() => expect(element).not.toBeInTheDocument()),

      // Accessibility utilities
      expectAccessible: async (container: HTMLElement) => {
        const results = await axe(container);
        expect(results).toHaveNoViolations();
      },

      // Style utilities
      expectVisible: (element: HTMLElement) => {
        expect(element).toBeVisible();
      },

      expectHidden: (element: HTMLElement) => {
        expect(element).not.toBeVisible();
      },

      // Performance utilities
      measureRenderTime: async (renderFn: () => void) => {
        const start = performance.now();
        renderFn();
        await waitFor(() => {});
        return performance.now() - start;
      },
    };
  }

  /**
   * Cleanup resources
   */
  cleanup(): void {
    if (this.performanceObserver) {
      this.performanceObserver.disconnect();
    }
    this.queryClient.clear();
  }
}

/**
 * Create component test framework instance
 */
export function createComponentTestFramework(config?: ComponentTestConfig): ComponentTestFramework {
  return new ComponentTestFramework(config);
}

/**
 * Higher-order function for component testing
 */
export function withComponentTest<P extends object>(
  Component: React.ComponentType<P>,
  config?: ComponentTestConfig,
) {
  return (props: P) => {
    const framework = createComponentTestFramework(config);
    return framework.renderComponent(<Component {...props} />);
  };
}

/**
 * Test suite builder for components
 */
export class ComponentTestSuiteBuilder {
  private suite: Partial<ComponentTestSuite> = {};

  component(component: React.ComponentType<any>): this {
    this.suite.component = component;
    return this;
  }

  name(name: string): this {
    this.suite.name = name;
    return this;
  }

  props(props: Record<string, any>): this {
    this.suite.props = props;
    return this;
  }

  variant(name: string, props: Record<string, any>, description?: string): this {
    if (!this.suite.variants) this.suite.variants = [];
    this.suite.variants.push({ name, props, description });
    return this;
  }

  interaction(
    name: string,
    action: ComponentInteraction['action'],
    assertions: ComponentInteraction['assertions'],
    timeout?: number,
  ): this {
    if (!this.suite.interactions) this.suite.interactions = [];
    this.suite.interactions.push({ name, action, assertions, timeout });
    return this;
  }

  config(config: ComponentTestConfig): this {
    this.suite.config = config;
    return this;
  }

  build(): ComponentTestSuite {
    if (!this.suite.component || !this.suite.name) {
      throw new Error('Component and name are required for test suite');
    }
    return this.suite as ComponentTestSuite;
  }
}

/**
 * Create component test suite builder
 */
export function createTestSuite(): ComponentTestSuiteBuilder {
  return new ComponentTestSuiteBuilder();
}

/**
 * Hook
 */
'use client';

import { useState, useEffect, useCallback, useRef } from 'react';

import { logger } from '@/lib/logging/logger';

/**
 * 
 */
export interface DataLazyLoadConfig {
  // 
  cacheKey?: string;
  // 
  cacheTTL?: number;
  // 
  enableCache?: boolean;
  // 
  retries?: number;
  // 
  retryDelay?: number;
  // 
  timeout?: number;
  // 
  autoLoad?: boolean;
  // 
  dependencies?: any[];
  // 
  onError?: (error: Error) => void;
  // 
  onSuccess?: (data: any) => void;
  // 
  onLoadStart?: () => void;
}

/**
 * 
 */
export interface DataLazyLoadState<T> {
  data: T | null;
  isLoading: boolean;
  error: Error | null;
  isLoaded: boolean;
  lastUpdated: Date | null;
  retryCount: number;
}

/**
 * 
 */
interface CacheItem<T> {
  data: T;
  timestamp: number;
  ttl: number;
}

/**
 * 
 */
const globalCache = new Map<string, CacheItem<any>>();

/**
 * Hook
 */
export function useDataLazyLoad<T>(
  fetchFn: () => Promise<T>,
  config: DataLazyLoadConfig = {},
): [DataLazyLoadState<T>, () => void, () => void] {
  const {
    cacheKey,
    cacheTTL = 5 * 60 * 1000, // 5
    enableCache = true,
    retries = 3,
    retryDelay = 1000,
    timeout = 10000,
    autoLoad = false,
    dependencies = [],
    onError,
    onSuccess,
    onLoadStart,
  } = config;

  const [state, setState] = useState<DataLazyLoadState<T>>({
    data: null,
    isLoading: false,
    error: null,
    isLoaded: false,
    lastUpdated: null,
    retryCount: 0,
  });

  const abortControllerRef = useRef<AbortController | null>(null);
  const mountedRef = useRef(true);

  // 
  const getFromCache = useCallback((): T | null => {
    if (!enableCache || !cacheKey) {
      return;
    }

    const cached = globalCache.get(cacheKey);
    if (!cached) {
      return;
    }

    const now = Date.now();
    if (now - cached.timestamp > cached.ttl) {
      globalCache.delete(cacheKey);
      return;
    }

    return cached.data;
  }, [enableCache, cacheKey]);

  // 
  const setCache = useCallback((data: T) => {
    if (!enableCache || !cacheKey) {
      return;
    }

    globalCache.set(cacheKey, {
      data,
      timestamp: Date.now(),
      ttl: cacheTTL,
    });
  }, [enableCache, cacheKey, cacheTTL]);

  // 
  const load = useCallback(async (retryCount = 0) => {
    if (!mountedRef.current) {
      return;
    }

    // 
    const cachedData = getFromCache();
    if (cachedData && retryCount === 0) {
      setState({
        data: cachedData,
        isLoading: false,
        error: null,
        isLoaded: true,
        lastUpdated: new Date(),
        retryCount: 0,
      });
      onSuccess && onSuccess(cachedData);
      return;
    }

    // 
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }

    abortControllerRef.current = new AbortController();
    const { signal } = abortControllerRef.current;

    setState({ ...state, isLoading: true,
      error: null,
      retryCount,
     });

    onLoadStart && onLoadStart();

    try {
      // 
      const timeoutPromise = new Promise<never>((_, reject) => {
        setTimeout(() => reject(new Error('Request timeout')), timeout);
      });

      const fetchPromise = fetchFn();
      const data = await Promise.race([fetchPromise, timeoutPromise]);

      if (!mountedRef.current || signal.aborted) {
        return;
      }

      // 
      setCache(data);

      setState({
        data,
        isLoading: false,
        error: null,
        isLoaded: true,
        lastUpdated: new Date(),
        retryCount: 0,
      });

      onSuccess && onSuccess(data);

      logger.debug('Data lazy load completed', {
        cacheKey,
        retryCount,
        dataSize: JSON.stringify(data).length,
      });

    } catch (error) {
      if (!mountedRef.current || signal.aborted) {
        return;
      }

      const err = error instanceof Error ? error : new Error(String(error));

      logger.error('Data lazy load failed', err, {
        cacheKey,
        retryCount,
        maxRetries: retries,
      });

      // 
      if (retryCount < retries) {
        setTimeout(() => {
          load(retryCount + 1);
        }, retryDelay * (retryCount + 1));
        return;
      }

      setState({ ...state, isLoading: false,
        error: err,
        retryCount,
       });

      onError && onError(err);
    }
  }, [fetchFn, getFromCache, setCache, timeout, retries, retryDelay, onLoadStart, onSuccess, onError, cacheKey]);

  // 
  const reload = useCallback(() => {
    // 
    if (cacheKey) {
      globalCache.delete(cacheKey);
    }
    load();
  }, [load, cacheKey]);

  // 
  useEffect(() => {
    if (autoLoad) {
      load();
    }
  }, [autoLoad, load]);

  // 
  useEffect(() => {
    if (dependencies.length > 0 && state.isLoaded) {
      reload();
    }
  }, dependencies); // eslint-disable-line react-hooks/exhaustive-deps

  // 
  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, []);

  return [state, load, reload];
}

/**
 * Hook
 */
export function usePaginatedDataLazyLoad<T>(
  fetchFn: (page: number, pageSize: number) => Promise<{ data: T[]; total: number; hasMore: boolean }>,
  pageSize: number = 20,
  config: Omit<DataLazyLoadConfig, 'autoLoad'> = {},
) {
  const [allData, setAllData] = useState<T[]>([]);
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [hasMore, setHasMore] = useState(true);

  const paginatedFetchFn = useCallback(async () => {
    const result = await fetchFn(page, pageSize);
    return result;
  }, [fetchFn, page, pageSize]);

  const [state, load, reload] = useDataLazyLoad(paginatedFetchFn, {
    ...config,
    cacheKey: config.cacheKey ? `${config.cacheKey}-page-${page}` : undefined,
    onSuccess: (result) => {
      if (page === 1) {
        setAllData(result.data);
      } else {
        setAllData(prev => [...prev, ...result.data]);
      }
      setTotal(result.total);
      setHasMore(result.hasMore);
      config.onSuccess?.(result);
    },
  });

  const loadMore = useCallback(() => {
    if (hasMore && !state.isLoading) {
      setPage(prev => prev + 1);
    }
  }, [hasMore, state.isLoading]);

  const reset = useCallback(() => {
    setAllData([]);
    setPage(1);
    setTotal(0);
    setHasMore(true);
  }, []);

  // 
  useEffect(() => {
    if (page > 1) {
      load();
    }
  }, [page, load]);

  return {
    data: allData,
    total,
    hasMore,
    page,
    state,
    loadMore,
    reset,
    reload: () => {
      reset();
      reload();
    },
  };
}

/**
 * Hook
 */
export function useSearchDataLazyLoad<T>(
  searchFn: (query: string) => Promise<T[]>,
  config: DataLazyLoadConfig & {
    debounceDelay?: number;
    minQueryLength?: number;
  } = {},
) {
  const {
    debounceDelay = 300,
    minQueryLength = 1,
    ...lazyLoadConfig
  } = config;

  const [query, setQuery] = useState('');
  const [debouncedQuery, setDebouncedQuery] = useState('');
  const debounceTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  const searchFetchFn = useCallback(async () => {
    if (debouncedQuery.length < minQueryLength) {
      return [];
    }
    return await searchFn(debouncedQuery);
  }, [searchFn, debouncedQuery, minQueryLength]);

  const [state, load, reload] = useDataLazyLoad(searchFetchFn, {
    ...lazyLoadConfig,
    cacheKey: lazyLoadConfig.cacheKey ? `${lazyLoadConfig.cacheKey}-search-${debouncedQuery}` : undefined,
  });

  // 
  useEffect(() => {
    if (debounceTimeoutRef.current) {
      clearTimeout(debounceTimeoutRef.current);
    }

    debounceTimeoutRef.current = setTimeout(() => {
      setDebouncedQuery(query);
    }, debounceDelay);

    return () => {
      if (debounceTimeoutRef.current) {
        clearTimeout(debounceTimeoutRef.current);
      }
    };
  }, [query, debounceDelay]);

  // 
  useEffect(() => {
    if (debouncedQuery.length >= minQueryLength) {
      load();
    }
  }, [debouncedQuery, minQueryLength, load]);

  const search = useCallback((newQuery: string) => {
    setQuery(newQuery);
  }, []);

  const clear = useCallback(() => {
    setQuery('');
    setDebouncedQuery('');
  }, []);

  return {
    query,
    debouncedQuery,
    data: state.data || [],
    state,
    search,
    clear,
    reload,
  };
}

/**
 * Hook
 */
export function useConditionalDataLazyLoad<T>(
  fetchFn: () => Promise<T>,
  condition: boolean | (() => boolean),
  config: DataLazyLoadConfig = {},
) {
  const shouldLoad = typeof condition === 'function' ? condition() : condition;

  const conditionalFetchFn = useCallback(async () => {
    if (!shouldLoad) {
      throw new Error('Condition not met');
    }
    return await fetchFn();
  }, [fetchFn, shouldLoad]);

  const [state, load, reload] = useDataLazyLoad(conditionalFetchFn, {
    ...config,
    autoLoad: config.autoLoad && shouldLoad,
  });

  // 
  useEffect(() => {
    if (shouldLoad && !state.isLoaded && !state.isLoading) {
      load();
    }
  }, [shouldLoad, state.isLoaded, state.isLoading, load]);

  return [state, load, reload] as const;
}

/**
 * 
 */
export const cacheUtils = {
  /**
   * 
   */
  clearAll: () => {
    globalCache.clear();
    logger.debug('All data lazy load cache cleared');
  },

  /**
   * 
   */
  clear: (key: string) => {
    globalCache.delete(key);
    logger.debug('Data lazy load cache cleared', { key });
  },

  /**
   * 
   */
  getStats: () => {
    const now = Date.now();
    let validCount = 0;
    let expiredCount = 0;
    let totalSize = 0;

    for (const [key, item] of globalCache.entries()) {
      const isExpired = now - item.timestamp > item.ttl;
      if (isExpired) {
        expiredCount++;
      } else {
        validCount++;
      }
      totalSize += JSON.stringify(item.data).length;
    }

    return {
      totalItems: globalCache.size,
      validItems: validCount,
      expiredItems: expiredCount,
      totalSize,
    };
  },

  /**
   * 
   */
  cleanup: () => {
    const now = Date.now();
    let cleanedCount = 0;

    for (const [key, item] of globalCache.entries()) {
      if (now - item.timestamp > item.ttl) {
        globalCache.delete(key);
        cleanedCount++;
      }
    }

    logger.debug('Expired data lazy load cache cleaned', { cleanedCount });
    return cleanedCount;
  },
};

// 
if (typeof window !== 'undefined') {
  setInterval(() => {
    cacheUtils.cleanup();
  }, 5 * 60 * 1000); // 5
}

export default useDataLazyLoad;

import { useState, useEffect } from 'react';

/**
 * Hook - 
 * @param value - 
 * @param delay - 
 * @returns 
 */
export function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}

/**
 * Hook - 
 * @param callback - 
 * @param delay - 
 * @param deps - 
 * @returns 
 */
export function useDebouncedCallback<T extends(...args: any[]) => any>(
  callback: T,
  delay: number,
  deps: React.DependencyList = [],
): T {
  const [debouncedCallback, setDebouncedCallback] = useState<T>(() => callback);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedCallback(() => callback);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [callback, delay, ...deps]);

  return debouncedCallback;
}

