import { useState, useEffect } from 'react';

import { useToast } from '@/shared/hooks/use-toast';

interface InviteCode {
  id: string
  code: string
  inviterId: string
  createdAt: Date
  expiresAt: Date
  isActive: boolean
  usageCount: number
  maxUsage: number
  inviteLink: string
}

interface InviteStats {
  totalInvites: number
  successfulRegistrations: number
  activeInvitees: number
  totalRewardsEarned: number
  conversionRate: number
}

export const useInvitation = (userId: string) => {
  const [inviteCode, setInviteCode] = useState<InviteCode | null>(null);
  const [inviteStats, setInviteStats] = useState<InviteStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [generating, setGenerating] = useState(false);

  const { toast } = useToast();

  const loadInvitationData = async () => {
    try {
      setLoading(true);

      // 
      const inviteResponse = await fetch(`/api/invite/user/${userId}`);
      if (inviteResponse.ok) {
        const inviteData = await inviteResponse.json();
        setInviteCode(inviteData.data);
      }

      // 
      const statsResponse = await fetch(`/api/invite/stats/${userId}`);
      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        setInviteStats(statsData.data);
      }
    } catch (error) {
      console.error('Failed to load invitation data:', error);
      toast({
        title: '',
        description: '',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const generateNewInviteCode = async () => {
    try {
      setGenerating(true);

      const response = await fetch('/api/invite/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId }),
      });

      if (response.ok) {
        const data = await response.json();
        setInviteCode(data.data);
        toast({
          title: '',
          description: '',
        });
        return data.data;
      } else {
        throw new Error('Failed to generate invite code');
      }
    } catch (error) {
      console.error('Failed to generate invite code:', error);
      toast({
        title: '',
        description: '',
        variant: 'destructive',
      });
      return;
    } finally {
      setGenerating(false);
    }
  };

  const recordShareEvent = async (platform: string) => {
    if (!inviteCode) return;

    try {
      await fetch('/api/invite/share', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          inviteCodeId: inviteCode.id,
          platform,
          userId,
        }),
      });
    } catch (error) {
      console.error('Failed to record share event:', error);
    }
  };

  useEffect(() => {
    if (userId) {
      loadInvitationData();
    }
  }, [userId]);

  return {
    inviteCode,
    inviteStats,
    loading,
    generating,
    loadInvitationData,
    generateNewInviteCode,
    recordShareEvent,
  };
};

'use client';

import {
  ComponentType,
  ReactNode,
  createContext,
  createElement,
  useEffect,
  useRef,
  useState,
} from 'react';

interface LazyLoadMetrics {
  componentName: string;
  loadStartTime: number;
  loadEndTime?: number;
  loadDuration?: number;
  isLoading: boolean;
  error?: Error;
}

interface PerformanceData {
  totalComponents: number;
  loadedComponents: number;
  averageLoadTime: number;
  slowestComponent?: LazyLoadMetrics;
  fastestComponent?: LazyLoadMetrics;
  failedComponents: LazyLoadMetrics[];
}

/**
 * Hook
 * 
 */
export function useLazyLoadPerformance() {
  const [metrics, setMetrics] = useState<LazyLoadMetrics[]>([]);
  const [performanceData, setPerformanceData] = useState<PerformanceData>({
    totalComponents: 0,
    loadedComponents: 0,
    averageLoadTime: 0,
    failedComponents: [],
  });

  const metricsRef = useRef<Map<string, LazyLoadMetrics>>(new Map());

  // 
  const startTracking = (componentName: string) => {
    const metric: LazyLoadMetrics = {
      componentName,
      loadStartTime: performance.now(),
      isLoading: true,
    };

    metricsRef.current.set(componentName, metric);
    setMetrics(prev => [...prev.filter(m => m.componentName !== componentName), metric]);
  };

  // 
  const finishTracking = (componentName: string, error?: Error) => {
    const metric = metricsRef.current.get(componentName);
    if (!metric) return;

    const loadEndTime = performance.now();
    const loadDuration = loadEndTime - metric.loadStartTime;

    const updatedMetric: LazyLoadMetrics = {
      ...metric,
      loadEndTime,
      loadDuration,
      isLoading: false,
      error,
    };

    metricsRef.current.set(componentName, updatedMetric);
    setMetrics(prev => prev.map(m =>
      m.componentName === componentName ? updatedMetric : m,
    ));
  };

  // 
  useEffect(() => {
    const allMetrics = Array.from(metricsRef.current.values());
    const loadedMetrics = allMetrics.filter(m => !m.isLoading && !m.error);
    const failedMetrics = allMetrics.filter(m => m.error);

    const loadTimes = loadedMetrics
      .map(m => m.loadDuration)
      .filter((time): time is number => time !== undefined);

    const averageLoadTime = loadTimes.length > 0
      ? loadTimes.reduce((sum, time) => sum + time, 0) / loadTimes.length
      : 0;

    const slowestComponent = loadedMetrics.reduce((slowest, current) => {
      if (!slowest || !current.loadDuration) return slowest;
      if (!slowest.loadDuration || current.loadDuration > slowest.loadDuration) {
        return current;
      }
      return slowest;
    }, undefined as LazyLoadMetrics | undefined);

    const fastestComponent = loadedMetrics.reduce((fastest, current) => {
      if (!fastest || !current.loadDuration) return fastest;
      if (!fastest.loadDuration || current.loadDuration < fastest.loadDuration) {
        return current;
      }
      return fastest;
    }, undefined as LazyLoadMetrics | undefined);

    setPerformanceData({
      totalComponents: allMetrics.length,
      loadedComponents: loadedMetrics.length,
      averageLoadTime,
      slowestComponent,
      fastestComponent,
      failedComponents: failedMetrics,
    });
  }, [metrics]);

  // 
  const getPerformanceReport = () => {
    const report = {
      summary: performanceData,
      details: metrics,
      recommendations: [] as string[],
    };

    // 
    if (performanceData.averageLoadTime > 1000) {
      report.recommendations.push('1');
    }

    if (performanceData.failedComponents.length > 0) {
      report.recommendations.push(`${performanceData.failedComponents.length}`);
    }

    if (performanceData.slowestComponent && performanceData.slowestComponent.loadDuration! > 2000) {
      report.recommendations.push(`"${performanceData.slowestComponent.componentName}"`);
    }

    return report;
  };

  // 
  const clearMetrics = () => {
    metricsRef.current.clear();
    setMetrics([]);
    setPerformanceData({
      totalComponents: 0,
      loadedComponents: 0,
      averageLoadTime: 0,
      failedComponents: [],
    });
  };

  return {
    metrics,
    performanceData,
    startTracking,
    finishTracking,
    getPerformanceReport,
    clearMetrics,
  };
}

/**
 * HOC
 */
export function withLazyLoadTracking<T extends object>(
  WrappedComponent: ComponentType<T>,
  componentName: string,
) {
  return function TrackedComponent(props: T) {
    const { startTracking, finishTracking } = useLazyLoadPerformance();

    useEffect(() => {
      startTracking(componentName);

      return () => {
        finishTracking(componentName);
      };
    }, []);

    return createElement(WrappedComponent, props);
  };
}

/**
 * 
 */
export const LazyLoadPerformanceContext = createContext<{
  startTracking:(componentName: string) => void;
  finishTracking: (componentName: string, error?: Error) => void;
  getPerformanceReport: () => any;
} | null>(null);

export function LazyLoadPerformanceProvider({ children }: { children: ReactNode }) {
  const performance = useLazyLoadPerformance();

  return createElement(
    LazyLoadPerformanceContext.Provider,
    { value: performance },
    children,
  );
}

